INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (1,1,100,'Intro Asian American Studies',1,'Interdisciplinary introduction to the basic concepts and approaches in Asian American Studies. Surveys the various dimensions of Asian American experiences including history, social organization, literature, arts, and politics.'),
 (2,1,120,'Intro to Asian Am Pop Culture',2,'Introductory understanding of the way U.S. popular culture has affected Asian Americans and the contributions Asian Americans have made to U.S. media and popular culture since the mid 1880\'s.'),
 (3,1,184,'Asian American Cultures',3,'Same as ANTH 184, and SOC 124. See ANTH 184.'),
 (4,1,199,'Undergraduate Open Seminar',4,'This course may be repeated to a maximum of 6 credit hours.'),
 (5,1,224,'Asian Am Historical Sociology',5,'Same as SOC 224. See SOC 224.'),
 (6,1,260,'Intro Asian American Theatre',6,'Same as THEA 260. See THEA 260.'),
 (7,1,281,'Constructing Race in America',7,'Same as AFRO 281, HIST 281, and LLS 281. See HIST 281.'),
 (8,1,283,'Asian American History',8,'Same as HIST 283. See HIST 283.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (9,1,284,'Adv Topics in Asian America',9,'Same as ANTH 284. See ANTH 284.'),
 (10,1,286,'Asian American Literature',10,'Same as ENGL 286. See ENGL 286.'),
 (11,1,290,'Individual Study',11,'Supervised reading and research in Asian American Studies chosen by the student with instructor approval. May be repeated to a maximum of 6 hours.  Prerequisite: AAS 100.'),
 (12,1,291,'Hinduism in the United States',12,'Introduction to the historical, religious, and socio-cultural aspects of Hinduism in the US.  The role of Hinduism in the maintenance of the ethnic identity of Indians in the US will be examined in the context of the rituals, languages, temples, family, and other social organizations.  The maintenance and/or shift of the features of traditional (Indian) Hinduism in the transplanted counterpart in the US will be examined.   Same as RLST 291.  Prerequisite: RLST 104 or RLST 286 or consent of instructor.'),
 (13,1,310,'Race and Cultural Diversity',13,'Same as AFRO 310, EPS 310, and LLS 310. See EPS 310.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (14,1,317,'Asian American Politics',14,'Same as PS 317. See PS 317.'),
 (15,1,328,'Asian Americans & Inequalities',15,'Same as SOC 328.  See SOC 328.'),
 (16,1,365,'Asian American Media and Film',16,'An examination of media generally and films and videos more specifically (experimental, documentary, independent, and Hollywood features) by, for, and about Asian Americans.   Same as COMM 365 and CINE 365.   Prerequisite:  Any AAS course at the 100 or 200 level or consent of instructor.'),
 (17,1,397,'Asian Families in America',17,'Same as SOCW 397. See SOCW 397.'),
 (18,1,402,'Asian American Education',18,'Same as EPS 402. See EPS 402.'),
 (19,1,435,'Commodifying Difference',19,'Same as LLS 435, AFRO 435, COMM 432, and GWS 435.  See LLS 435.'),
 (20,1,450,'Asian American Ethnic Groups',20,'Intensive interdisciplinary study of a particular Asian American ethnic group (specific ethnic group focus will change every semester).    3 undergraduate hours, 4 graduate hours.  This course may be repeated in the same or separate semesters, up to a maximum of 9 undergraduate hours or 12 graduate hours, if topics vary.  Students may register in more than one section per term if topics vary.  Prerequisite:  Any AAS course at the 100 or 200 level or consent of instructor.'),
 (21,1,465,'Race, Sex, and Deviance',21,'Same as AFRO 465, GWS 465, and LLS 465. See LLS 465.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (22,1,470,'Asian American Psychology',22,'Same as PSYC 470.  See PSYC 470.'),
 (23,1,484,'Asian Diasporas',23,'Same as ANTH 484. See ANTH 484.'),
 (24,1,490,'Adv Topics in Asian Am Studies',24,'Research seminar on specialized topics in Asian American Studies. May be repeated if topics vary. Students may register in more than one section per term if topics vary. Prerequisite: AAS 100 or any Asian American Studies course, or consent of instructor.'),
 (25,1,589,'Readings in Asian Am Studies',25,'Individual guidance in intensive readings in the literature of one or more subdivisions of the field of Asian American Studies. May be repeated to a maximum of 8 hours. Students may register in more than one section per term if topics vary. Prerequisite: Graduate standing or consent of instructor.'),
 (26,1,590,'Asian Am Studies Seminar',26,'May be repeated to a maximum of 8 hours.  Prerequisite: Graduate standing or consent of instructor.'),
 (27,2,100,'Intro to Agr Engineering',27,'Introduction to the engineering profession with career opportunities in the agricultural engineering discipline. Interactive class activities include concepts necessary for becoming a successful engineer including time management, design concepts, ethics, and teambuilding. Students become familiar with laboratories, computer facilities, internships and other opportunities that are available to agricultural engineering students. A team design experience is included. Class emphasis on technical communication and problem-solving skills as well as career planning. Approved for both letter and S/U grading.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (28,2,199,'Undergraduate Open Seminar',28,'Experimental course on a special topic in Agricultural Engineering. May be repeated to a maximum of 12 hours.'),
 (29,2,221,'Agr & Bio Engineering I',29,'Introduction to engineering methods used in the design and management of agricultural, biological and environmental systems. Topics covered include the hydrologic cycle, soil-water properties and relationships, water runoff, surveying, soil erosion, water management, engine power, fluid power, traction and weight transfer, and off-road vehicle systems. Prerequisite: One of MATH 220, MATH 221, MATH 234.'),
 (30,2,222,'Agr & Bio Engineering II',30,'Introduction to biomaterials and bioprocess design. Includes structure and composition of biomaterials, mass balances, force-deformation of biomaterials and non-Newtonian fluid flow. Principles of environmental control for biological structures, psychrometrics, mass and heat transfer through buildings, and ventilation requirements. Prerequisite: One of MATH 220, MATH 221, MATH 234.'),
 (31,2,361,'Princ of Off-Road Machines',31,'Design and development concepts of agricultural and industrial machines; analysis and synthesis of tillage, planting, harvesting, chemical application, material handling mechanisms, and precision farming tools. Includes laboratory. Prerequisite: ABE 221and TAM 212.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (32,2,374,'Env Control for Bio Buildings',32,'Application of bioenvironmental engineering principles to control agricultural building environments. Psychrometrics, room air distribution, fluids, heat transfer, ventilation equipment, environmental physiology and design topics. Includes laboratory.  Prerequisite: ABE 222.'),
 (33,2,396,'UG Honors Research or Thesis',33,'Individual research, special problems, thesis, development and/or design work for James Scholars under the direction of the honors advisor. May be repeated to a maximum of 8 hours. Prerequisite: Consent of instructor.'),
 (34,2,397,'Independent Study',34,'Individual research, special problems, thesis, development and/or design work under the supervision of a member of the faculty. May be repeated to a maximum of 8 hours.'),
 (35,2,398,'Special Topics',35,'Group discussion or an experimental course on a special topic in agricultural engineering. May be repeated to a maximum of 12 hours.'),
 (36,2,420,'Kinem and Dynm of Mechanl Sys',36,'Same as ME 440. See ME 440.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (37,2,425,'Eng Measurement Systems',37,'Emphasizes fundamental and integrated skills in designing engineering measurement systems and performing engineering measurements. Main topics include design of engineering measurement systems, principles of sensors, signal conditioning, computer data acquisition, engineering test design, and data analysis. Includes laboratory.  Credit is not given for both ABE 425 and ME 360. Prerequisite: ECE 205.'),
 (38,2,426,'Applied Machine Vision',38,'The convergence of computer imaging, pattern recognition, and artificial intelligence have made it possible to quantify complex physical phenomena that commonly occur in food and agricultural systems. This course introduces students to basic principles required for machine vision applications. Hardware and software aspects for machine vision applications will be studied. Includes laboratory. Prerequisite: CS 101 and MATH 225.'),
 (39,2,430,'Project Management',39,'Engineering team effectiveness; project definition; assessing related technologies; marketing and business planning related to engineering; budgeting and financial analyses of engineering projects; safety, ethics and environmental considerations; intellectual property; engineering proposal presentation. Same as TSM 430.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (40,2,436,'Renewable Energy Systems',40,'A comprehensive overview of renewable energy sources and applications, including solar, geothermal, wind, and biomass. Examines the environmental consequences of energy conversion including how renewable energy can reduce air pollution and global climate change. A capstone group project encompasses the design of a system for collecting and converting renewable energy into thermal or electrical energy. Same as TSM 436. 3 undergraduate hours. 4 graduate hours. Prerequisite: PHYS 211.'),
 (41,2,440,'Applied Statistical Methods I',41,'Same as ANSC 440, CPSC 440, FSHN 440, and NRES 440. See CPSC 440.'),
 (42,2,445,'Statistical Methods',42,'Same as ANSC 445, and NRES 445. See ANSC 445.'),
 (43,2,455,'Erosion and Sediment Control',43,'Processes, estimation, and control of soil erosion by water, wind and resultant sedimentation. Upland, in-channel, urban, agricultural, disturbed (both military training and mining), and forested environments are discussed. Site planning and design constitute a capstone laboratory exercise. Same as CEE 455 and TSM 455. 2 undergraduate hours. 2 graduate hours. Prerequisite: CEE 350 or NRES 401; CEE 380 or NRES 201.'),
 (44,2,456,'Land and Water Resources Eng',44,'Hydrology, hydraulics, design, construction and cost estimating of structures for the conservation and quality control of soil and water resources; relationship of topography, soils, crops, climate, and cultural practices in conservation and quality control of soil and water for agriculture. Includes laboratory.  3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: Credit or concurrent registration in TAM 335.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (45,2,459,'Drainage and Water Management',45,'Design, construction, performance, and maintenance of agricultural drainage systems to meet both production and water quality objectives. Modeling drainage systems. Principles of conservation drainage. Includes laboratory. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: Credit or concurrent registration in TAM 335.'),
 (46,2,466,'Engineering Off-Road Vehicles',46,'Engineering aspects of design and application of off-road vehicles for farm and construction use; thermodynamics of engines; measurement of power and efficiencies; power transmission and traction; chassis mechanics; and operator environment. Includes laboratory. Prerequisite: ME 300.'),
 (47,2,469,'Industry-Linked Design Project',47,'Emphasizes open-ended design projects which utilize principles of design, engineering analysis and functional operation of engineering systems. Projects are submitted and sponsored by industrial clients, design team developed, concepts visualized, alternatives evaluated and geometry created using CAD systems. Emphases on communication skills, technical writing, and interaction with industry representatives. Students operate in a team-based industrial environment with \"real life\" industrial projects. Prerequisite: One of ABE 361, CHBE 421, TAM 335; or credit or concurrent registration in ME 370.'),
 (48,2,476,'Indoor Air Quality Engineering',48,'Principles and applications of indoor air quality. Topics include particle mechanics, gas kinetics, air quality sampling principles and techniques, air cleaning technologies (i.e. filters, cyclones, electrostatic precipitation) for indoor environments, and ventilation effectiveness. Includes laboratory. Prerequisite: PHYS 213, MATH 385, and TAM 335.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (49,2,479,'Design of Agr & Bio Structures',49,'Complete design of light frame building components including timber, concrete, beams and connectors. Design of heating and ventilation systems. Includes laboratory.'),
 (50,2,482,'Package Engineering',50,'Same as FSHN 469. See FSHN 469.'),
 (51,2,483,'Eng Properties of Food Mat',51,'Physical properties of foods and biological materials; design of processing equipment and the sensing and control of food processes; thermal, electromagnetic radiation, rheological, and other mechanical properties. Includes laboratory. Prerequisite: TAM 251; either CHBE 421 or both ME 330 and TAM 335.'),
 (52,2,485,'Food and Process Eng Design',52,'Design of equipment, processes, and facilities for food, pharmaceutical, biotechnology, and related process industries. Prerequisite: ABE 483.'),
 (53,2,487,'Grain Drying and Conditioning',53,'Psychrometric principles of air modification for dehydration and conditioning of moist products, emphasizing the drying of cereal grains; industrial dryers; design of drying, cooling, and aeration systems. Includes laboratory. Prerequisite: ABE 222. Recommended ME 300.'),
 (54,2,488,'Bioprocessing Grains for Fuels',54,'Study of engineering and scientific principles involved in bioprocessing of cereal grains for production of ethanol and other fermentation products.  Process unit operations, conventional and alternative feed stock materials, recovery of value-added coproducts and other variables involved in producing fuel ethanol will be discussed. Process simulation and economic analysis will also be addressed.  Prerequisite: CHBE 321 and TAM 251.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (55,2,489,'Process Des for Corn Milling',55,'Engineering and scientific principles involved in the major corn fractionation processes of wet milling, dry milling and alkali cooking, including structural and diffusional characteristics of corn, steeping phenomena and chemical and mechanical fractionation methods. Principles of process design and mill operation. Prerequisite: One of CHBE 421, ME 300, ME 320.'),
 (56,2,497,'Independent Study',56,'Individual research, special problems, thesis, development and/or design work under the supervision of a member of the faculty. 1 to 4 undergraduate hours. May be repeated to a maximum of 8 hours. Prerequisite: Consent of instructor.'),
 (57,2,498,'Special Topics',57,'Group discussion or an experimental course on a special topic in agricultural engineering. May be repeated to a maximum of 16 hours.'),
 (58,2,501,'Graduate Research I',58,'Basic research orientation, research methods, presentation skills, laboratory practices, case studies, and professional and ethical conduct.'),
 (59,2,502,'Graduate Research II',59,'Research methodology, teaching methods, lecture preparation and delivery, critical review of scientific articles, peer review and publishing, mentoring and peer relationships, time management, and intellectual property.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (60,2,561,'Off-Road Vehicle Mechatronics',60,'Emphasizes integrated skills in designing mechatronic systems with regard to off-road vehicles. Main topics include fundamentals for mechatronic systems design; modeling of off-road vehicle systems, on-vehicle information management, and intelligent vehicle controls. Prerequisite: One of ABE 425, ME 261, MFGE 430; ECE 486 or ME 460.'),
 (61,2,594,'Graduate Seminar',61,'Presentations of thesis research by graduate students; other presentations on teaching or current research issues related to agriculture, biology, and engineering. Approved for S/U grading only. May be repeated up to a maximum of 6 times.'),
 (62,2,597,'Independent Study',62,'Individual investigations or studies of any phases of agricultural engineering selected by the student and approved by the advisor and the faculty member who will supervise the study. May be repeated to a maximum of 16 hours. Prerequisite: Consent of instructor.'),
 (63,2,599,'Thesis Research',63,'May be repeated. Approved for S/U grading only.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (64,3,199,'Undergraduate Open Seminar',64,'May be repeated.'),
 (65,3,200,'Fundamentals of Accounting',65,'Survey course in the principles of accounting for students registered in schools and colleges other than the College of Business. Credit is not given for both ACCY 200 and either ACCY 201 or ACCY 202. Prerequisite: Sophomore standing.'),
 (66,3,201,'Accounting and Accountancy, I',66,'Introduction to the role of accounting information in establishing organization objectives and goals and identification of strategies to best achieve such objectives and goals. Topics focus on the utility of information necessary for the formation, execution and monitoring of the variety of contracts embedded in organization strategies. Projects facilitate self-discovery of knowledge and development of a variety of professional skills and attitudes. Credit is not given for both ACCY 201 and ACCY 200. Prerequisite: ECON 102 and ECON 103 or equivalents.'),
 (67,3,202,'Accounting and Accountancy, II',67,'Continuation of ACCY 201 with focus on strategic management of economic resources, together with acquisition of such resources, and financial and non-financial measures of organizational performance. Credit is not given for both ACCY 202 and ACCY 200. Prerequisite: ACCY 201 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (68,3,290,'Prof Internship in Accountancy',68,'Formalized learning experience in combination with practice of accounting while engaged in an internship with a public accounting firm, business, or other off-campus organization; prior approval of learning plan and a summary report of learning experience are required. May be repeated in the same or subsequent terms to a maximum of 3 hours. Approved for both letter and S/U grading. Prerequisite: Open only to undergraduate accountancy majors with junior or senior standing; completion of 300-level accountancy courses appropriate to internship learning plan; and consent of department.'),
 (69,3,301,'Atg Measurement & Disclosure',69,'Introduction to measurement and reporting of organizational performance for strategic and operational purposes with a focus on a variety of financial and non-financial performance measures suitable for both internal and external decision-making. Projects, together with a series of practical workshops, facilitate self-discovery of knowledge and development of a variety of professional skills and attitudes.  Prerequisite: ACCY 202 or equivalent and concurrent enrollment in ACCY 302 by students majoring in accountancy (recommended for non-accountancy majors); or consent of department.'),
 (70,3,302,'Decision Making for Atg',70,'Decision making implications of information provided to organization managers and to external stakeholders such as investors, creditors, customers, and regulators. Concepts from economics, statistics, and psychology emphasize the use of quantitative techniques to comprehend uncertainty and risk. Projects, together with a series of practical workshops, facilitate self-discovery of knowledge and development of a variety of professional skills and attitudes.  Prerequisite: ACCY 202 or equivalent; ECON 203 or equivalent or concurrent enrollment; and concurrent enrollment in ACCY 301 by students majoring in Accountancy (recommended for non-Accountancy majors); or consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (71,3,303,'Atg Institutions and Reg',71,'Regulation theory and practice as applied to accounting information. A general framework for regulation of accounting procedures is developed. This framework is applied to reporting, taxation, and regulated business activities. Projects facilitate self-discovery of knowledge and the development of professional attitudes and skills with emphasis on professional research.  Prerequisite: ACCY 301 and ECON 302 and FIN 221; or consent of department.'),
 (72,3,304,'Accounting Control Systems',72,'Broad perspective on accounting and control that considers attainment of all goals of an organization, including those concerned with financial objectives. Topics include the conceptual foundations of control and application of practical, analytical tools to the evaluation of an organization\'s control environment. Cases, class discussion and field research projects emphasize independent thinking, group processes, and communication.  Prerequisite: ACCY 301 and ACCY 302 and BADM 310; or consent of department.'),
 (73,3,312,'Principles of Taxation',73,'Introduction to the tax system faced by businesses operating in the United States with a focus on the impact that the tax system has on business decisions. Topics include the tax environment, tax provisions relevant to businesses and their owners, taxation of individuals and of corporations, and multi-jurisdictional issues. Projects facilitate self-discovery of knowledge and development of a variety of professional skills and attitudes.  Prerequisite: ACCY 202 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (74,3,321,'Principles of Public Policy',74,'Same as BADM 303, and PS 321. See PS 321.'),
 (75,3,352,'Database Design and Management',75,'Same as BADM 352. See BADM 352.'),
 (76,3,353,'Info Sys Analysis and Design',76,'Same as BADM 353. See BADM 353.'),
 (77,3,398,'Practical Problems in Atg',77,'Course covers the professional standards relating to corporate financial reporting, taxation, auditing and public sector reporting. Credit not granted toward UIUC degree requirements. Prerequisite: Concurrent registration in the University\'s CPA Review course.'),
 (78,3,405,'Assurance and Attestation',78,'Conceptual introduction to diverse means by which assurers improve the quality of information used by third parties for contracting purposes, with emphases on the credibility- and relevance-enhancement properties of assurers\' services. Topics include the economics of assurance and attestation, and concepts including independence, risk, evidence, and control. Projects facilitate self-discovery of knowledge and development of professional skills and attitudes. 4 undergraduate hours. Prerequisite: ACCY 304 or consent of department.'),
 (79,3,410,'Fin Atg Reporting Standards',79,'Current authoritative accounting standards and applications to accounting practice. Topics do not represent the full range of financial reporting issues, but are selected based on relevance of the underlying business transaction, complexity of the topic, consistency of applicable standard with underlying reporting concepts, and transferability of the standard to other accounting issues. This course is for students in the Certificate in Accountancy program. 4 undergraduate hours. Prerequisite: ACCY 303 or consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (80,3,415,'Auditing Stds and Practice',80,'Framework for understanding and evaluating the professional auditing standards for assurance services. Model of financial reporting provides an overview of the types of information disseminated by companies to external users, and provides the basis for identifying professional standards areas for future standards\' development. This course is for students in the Certificate in Accountancy program. 4 undergraduate hours. Prerequisite: ACCY 405 or consent of department.'),
 (81,3,423,'Intro to International Atg',81,'Explores similarities and differences of accounting principles and procedures between the United States and other countries with special emphasis on worldwide and regional standardization; emphasizes consolidation of foreign subsidiaries, performance evaluation of foreign operations, statement analysis, translation, solutions to inflation accounting, and taxation of multinationals.  Prerequisite: ACCY 301 and ACCY 302, or equivalent; or ACCY 501.'),
 (82,3,431,'Accounting Systems Design',82,'Examines the fundamentals of accounting systems design, including systems analysis and design techniques; surveys hardware and software considerations; analyzes accounting applications within functional areas of the firm; and studies the control of computerized systems in a business environment.  Prerequisite: ACCY 202 and CS 105, or equivalent');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (83,3,432,'Intro to Mgt Info Systems',83,'Analyzes information systems from a management control perspective, emphasizing organization environment, technology, decision models and performance evaluation as determinants of information processing requirements; cases and design projects explore the management of information processing systems, major functional applications and impacts of information technology on individuals and society. Same as BADM 432. 3 undergraduate hours. 2 to 4 graduate hours. Prerequisite: CS 105 or equivalent, or consent of department.'),
 (84,3,451,'Advanced Income Tax Problems',84,'Practical and theoretical training in the more common and important provisions of the federal income tax, advanced problems, and tax case research and preparation. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: Senior standing and ACCY 312'),
 (85,3,455,'Mgt Info and Control Systems',85,'Integration of behavioral, quantitative, and system design concepts in relation to professional work in the management information systems area. Same as BADM 459. 3 undergraduate hours. 2 to 4 graduate hours.  Prerequisite: BADM 353 or consent of Department'),
 (86,3,493,'Seminar in Accountancy',86,'Selected topics in the field of accountancy. 3 or 4 undergraduate hours. 4 graduate hours. May be repeated in the same or subsequent semesters to a maximum of 8 hours. Prerequisite: Senior or graduate standing in accountancy and course prerequisites indicated by specific sections; or consent of Department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (87,3,499,'Senior Research',87,'Research and readings course for students majoring in accountancy. May be taken by students in the college honors program in partial fulfillment of the honors requirements. May be repeated to a maximum of 6 hours. 2 to 4 undergraduate hours. Prerequisite: Cumulative grade-point average of 3.0., honors in the junior year, or consent of department; senior standing.'),
 (88,3,501,'Accounting Analysis, I',88,'Uses of accounting information; collection, processing, and communication of accounting information; measurement of assets, liabilities, equities, and income; and accounting system design. Prerequisite: Enrollment in graduate degree program or consent of department.'),
 (89,3,502,'Accounting Analysis, II',89,'In-depth study of accounting valuation processes, accounting income measurement, and special reporting problems of multiple-entity organizations. Prerequisite: ACCY 501 or equivalent; enrollment in graduate degree program or consent of department.'),
 (90,3,503,'Managerial Accounting',90,'Introduction to management accounting as part of the firm\'s information system, in terms of modern cost accounting and budgetary systems for planning and controlling business operations. Prerequisite: Credit or concurrent registration in ACCY 501 or equivalent; enrollment in graduate degree program or consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (91,3,504,'Auditing',91,'Introduction to conceptual and applied material in the field of auditing. Emphasizes the audit process, reporting, and professional responsibilities.  Prerequisite: Credit or concurrent registration in ACCY 502, or equivalent; enrollment in graduate degree program or consent of Department.'),
 (92,3,505,'Federal Taxation',92,'Introduction to historical and conceptual as well as applied material in the accounting area of federal taxation; emphasizes the provisions of the tax law relevant to accounting measurement methods. Students may not receive credit for both ACCY 312 and ACCY 505. Prerequisite: ACCY 501; enrollment in graduate degree program or consent of department.'),
 (93,3,510,'Financial Reporting Standards',93,'Stakeholders\' needs for reliable and relevant information about the performance of firms, as well as managers; economic self-interests, influence managers\' selection of accounting policies and financial reporting methods.  This course selectively surveys both academic research and professional standards to focus on the measurement, classification and disclosure of financial transactions.  Cases, class discussion and research projects emphasize independent thinking, group processes, and communication. Prerequisite: ACCY 303, FIN 300 and enrollment in the BS/MS in Accountancy program or consent of department.'),
 (94,3,511,'Concepts and Principles',94,'Fundamental structure of accounting theory developed through the study of concepts characteristic of accounting and an examination of the literature dealing with the concise formulation of accounting principles. Prerequisite: Enrollment in graduate accounting degree program or consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (95,3,512,'Management Accounting, I',95,'Examines recent conceptual and analytical developments in the area of management accounting; includes a study of modern and relevant planning and control techniques and their underlying concepts as applied to the various functional areas within the firm.  Prerequisite: Enrollment in graduate accounting degree program or consent of Department; ACCY 511'),
 (96,3,515,'Auditing & Assurance Standards',96,'Role of professional and ethical standards in the conduct of auditing and assurance services and the role of auditing and assurance services in corporate governance.  This course selectively surveys both academic and professional literature to focus on the conduct of auditing and assurance services.  Cases, class discussion and research projects emphasize the importance of independent thinking, group processes, and communication for professional accounting practice.   Prerequisite: ACCY 405 and enrollment in the BS/MS in Accountancy program or consent of department.'),
 (97,3,517,'Financial Statement Analysis',97,'Examines tools and techniques of financial statement analysis from the perspective of investors and creditors; emphasizes theoretical and empirical properties of financial ratios. Prerequisite: ACCY 501, FIN 520, BADM 572; or equivalent; and enrollment in graduate degree program or consent of Department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (98,3,531,'Theory of Atg System Design',98,'Problems and procedures in connection with designing and installing accounting systems.  Prerequisite: Enrollment in graduate degree program or consent of Department'),
 (99,3,551,'Corporate Income Taxation',99,'Analyzes the tax treatment, problems, planning techniques, and underlying governmental policies involving corporations and their shareholders; coverage includes formations, operations, distributions, liquidations, reorganizations, and affiliations. Prerequisite: ACCY 451 or equivalent or consent of Department.'),
 (100,3,552,'Partnership Income Taxation',100,'Analyzes the tax treatment, problems, planning techniques, and underlying governmental policies involving partnerships and their partners, including Subchapter S corporations and their shareholders.  Prerequisite: ACCY 312 or equivalent'),
 (101,3,553,'Selected Topics in Fed Tax',101,'Seminar on federal tax topics of current interest in specialized areas; topics include international taxation, deferred compensation, problems of closely-held businesses, estate planning, taxation of trusts, and new developments. May be repeated with the consent of the department. Prerequisite: ACCY 451 or consent of Department.'),
 (102,3,556,'Tax Research',102,'Provides the student with a working knowledge of tax research methodology utilized by accountants in public practice. Aims to develop the student\'s capacity for either solving or defending his/her position with respect to a particular tax issue. Prerequisite: Graduate standing or consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (103,3,559,'Income Tax Development',103,'Theoretical and historical approach to the study of the development of federal income taxation, together with some research on tax cases and critical appraisal of the current law and proposals for its revision.  Prerequisite: Enrollment in graduate degree program or consent of Department'),
 (104,3,571,'Multinational Enterprise Atg',104,'Analysis of accounting for operations of multinational enterprises which are subject to a wide variety of regulatory, social, and environmental influences; emphasizes financial and managerial accounting systems and their functions as evaluative, control, and reporting tools; and examines social accounting, foreign taxation, and nonmonetary evaluation methods.  Prerequisite: Undergraduate degree in accountancy or equivalent; or ACCY 501 and consent of Department.'),
 (105,3,572,'Atg Under Different Social Sys',105,'Analyzes and compares accounting systems under different social systems with emphasis on the impact of regulatory and political structures on accounting; compares both macro and micro accounting systems for politically centralized and decentralized planning.  Prerequisite: Undergraduate degree in accounting'),
 (106,3,585,'Constructs in Atg Research',106,'Examines the role of information in economic and behavioral models of decision making under uncertainty; presents major paradigms underlying contemporary accounting research. Interdisciplinary approach; readings drawn from the accounting, behavioral, economics, and finance literature.  Prerequisite: MATH 463, ACCY 591, and ECON 502');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (107,3,590,'Adv Prof Internship in ACCY',107,'A formalized learning experience in combination with practice of accounting while engaged in an internship with a public accounting firm, business, or other off-campus organization; prior approval of learning plan and a summary report of learning experience required. Approved for both letter and S/U grading.  May be repeated to a maximum of 4 hours.  Prerequisite: Open only to accountancy majors enrolled in the department\'s integrated bachelor/master program or students with graduate standing in accountancy; completion of 300-level accountancy courses appropriate to internship learning plan; and consent of department.'),
 (108,3,591,'Professional Research',108,'Instruction in research methods, materials, and techniques together with individual practice in conducting and reporting specific professional research projects.  Prerequisite: Enrollment in graduate accounting degree program or consent of Department'),
 (109,3,592,'Intro to ACCY Research',109,'Comparative study of alternative methodologies and conceptual frameworks and their application to selected current research issues central to the development of accounting thought, both theoretical and empirical.  Prerequisite: ACCY 511 and ACCY 512 and courses in behavioral science, mathematics, and economics; or equivalent background and admission to the accountancy Ph.D. program; or consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (110,3,593,'Special Research Problems',110,'Individual investigations or research projects selected by the students, subject to approval by the graduate adviser and the executive officer of the Department. Prerequisite: Enrollment in graduate accounting degree program or consent of department.'),
 (111,3,594,'Doctoral Research Seminar',111,'Seminars in various accounting areas designed to enhance the research abilities of doctoral students and to assist them in preparing research proposals; these include Behavioral Dimensions, Public Sector, Tax, Auditing, Managerial, and others announced in the Class Schedule. May be repeated. Prerequisite: Credit or concurrent registration in ACCY 592 or consent of department.'),
 (112,3,595,'Models of Decision and Choice',112,'Same as PSYC 534. See PSYC 534.'),
 (113,3,599,'Thesis Research',113,'Individual direction and guidance in writing theses; seminar discussion of progress made. Approved for S/U grading only.'),
 (114,4,100,'Agr Cons and Resource Econ',114,'Principles of microeconomics; demand, production, supply, elasticity, markets, and trade are presented and used in the analysis of decisions of individuals relating to agricultural production, food and textile consumption, and natural resource use. Macroeconomic concepts are also introduced. Students receiving credit for ECON 102 may not receive credit for ACE 100.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (115,4,107,'Agr Cons and Env Systems',115,'Systems approach to analyzing problem situations from a holistic perspective, emphasizing human activities and processes, is developed using classroom illustration. This approach is then applied to a case study of a current issue impacting agriculture and rural areas. There may be a field trip to an area affected by the chosen issue. Same as ANSC 107.'),
 (116,4,161,'Microcomputer Applications',116,'Instruction and practice in solving data-related problems with microcomputers and general purpose software packages.'),
 (117,4,182,'Issues in Textile Marketing',117,'Multi-disciplinary examination of consumer issues and fundamentals of global business, both generally and specifically as related to textile marketing. Active learning environment promoted. Consumer issues focus on consumer rights, ethics, public policy. Business fundamentals examined include textile production, distribution,marketing, management, finance and regulation.'),
 (118,4,199,'Undergraduate Open Seminar',118,'Experimental course on a special topic in agricultural and consumer economics. Topic may not be repeated except in accordance with the Code. May be repeated up to 5 hours in a semester, to a maximum of 12 hours. Approved for both letter and S/U grading.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (119,4,210,'Environmental Economics',119,'Economic issues surrounding environmental quality, including: costs and benefits of environmental protection; economics of environmental policies (such as those dealing with toxics, water, and air pollution, and municipal solid waste); and economics of international environmental problems (such as ozone depletion and climate change). Same as ECON 210, ENVS 210, NRES 210, and UP 210. Prerequisite: ACE 100 or ECON 102.'),
 (120,4,222,'Agricultural Marketing',120,'Examines factors affecting the size of the market for agricultural products and the scope of marketing activities;functions and services performed; pricing agricultural products, including the nature and causes of price fluctuations; and costs of marketing and efforts to reduce costs and improve the marketing system. Prerequisite: ACE 100 or ECON 102.'),
 (121,4,231,'Food and Agribusiness Mgt',121,'Overview of management in the food and agribusiness sector. Major topics covered include: introduction to the food and agribusiness sector; the environment of the firm; fundamentals, structural design, and change in organizations; leadership, motivation, communication; and planning and control. Coverage is at the introductory level with a focus on textbook material and current issues.  Prerequisite: Sophomore standing, ACE 100 or ECON 102, and ACE 161.'),
 (122,4,232,'Management of Farm Enterprises',122,'Economic principles are applied to the management of farms using budgeting system analysis, record analysis, financial management, and lease analysis. Problems related to resource appraisal and business organization are also addressed. Three hours credit without home farm problem, or four hours credit with home farm problem. Prerequisite: ACE 100 or ECON 102.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (123,4,245,'Personal Finance',123,'Examination of principles of personal finance with attention given to research findings on the interdependence of financial decisions and energy, time, and other resources used to attain goals and maintain values. Prerequisite: Sophomore standing and 3 hours of sociology, psychology, or economics.'),
 (124,4,251,'The World Food Economy',124,'Examination of global food production, consumption, and trade; problems of hunger and population; the role of agricultural development, trade, and aid in relieving hunger. Prerequisite: ACE 100 or ECON 102.'),
 (125,4,254,'Economic Systems in Africa',125,'Examines systems of production and exchange in Africa. Through lectures, discussions, readings and films participants will study the ways African people interact in local markets and the impact of national and international markets on their welfare. Same as AFST 254.'),
 (126,4,255,'Econ of Rural Poverty and Dev',126,'Examination of rural poverty and development issues, with particular attention to current anti-poverty policies and programs and alternative programs. Topics include measurement of poverty; causes of rural poverty; income maintenance, education, and employment policies and their consequences; and rural development strategies. Prerequisite: ACE 100 or ECON 102 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (127,4,261,'Applied Statistical Methods',127,'Statistical methods and computer applications for agricultural and consumer economics, including descriptive statistics, probability distribution, interval estimation, hypothesis testing, analysis of variance, simple and multiple regression, and non-parametric methods. Students who have received credit for ECON 202, CPSC 440, STAT 100, or equivalent may not receive credit for this course. Prerequisite: MATH 124 or MATH 125.'),
 (128,4,270,'Consumer Economics',128,'Introduction to the study of the consumer in the American economy; sources of consumer information and consumer protection; and examination of current consumer issues within an economic framework. Prerequisite: ACE 100 or ECON 102 or consent of instructor.'),
 (129,4,286,'Cultural Analysis of Textiles',129,'Cross-cultural variations in form, function, and meaning of textile products analyzed in relation to physiological, psychological, and sociological needs of humans; analysis of process of acculturation; case studies.'),
 (130,4,293,'Off-Campus Internship',130,'Supervised, off-campus experience in a field directly pertaining to a subject matter in agricultural and consumer economics. May be repeated up to 4 hours in a semester to a maximum of 10 hours. Approved for S/U grading only. Prerequisite: Sophomore standing, cumulative GPA of 2. 5 or above at the time the internship is arranged, and consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (131,4,294,'On-Campus Internship',131,'Supervised, on-campus, learning experience with faculty engaged in research. Approved for S/U grading only.  May be repeated up to 4 hours in a semester, to a maximum of 10 hours. Prerequisite: Sophomore standing, cumulative GPA of 2. 5 or above at the time the internship is arranged, and consent of instructor.'),
 (132,4,295,'Research or Thesis',132,'Individual research, special problems, thesis, development and/or design work under the supervision of an appropriate member of the faculty. Approved for both S/U and letter grading.  May be repeated in the same or subsequent terms as topics vary. May be repeated up to 4 hours in a semester, but no more than 12 hours of special problems, research, thesis and/or individual studies may be counted toward the degree. Prerequisite: Junior standing, cumulative GPA of 2.5 or above at the time the activity is arranged, and consent of instructor.'),
 (133,4,303,'Rural Taxation',133,'Federal, state, and local taxation with emphasis on their application to farm income, farm property, farm property transfers, and agricultural cooperatives; introductory material on the uses and sources of revenue.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (134,4,310,'Natural Resource Economics',134,'Economic principles are used to analyze a broad range of natural resource policy and management issues. Economic concepts developed include public goods, social welfare, discounting, dynamic efficiency, and resource scarcity. Natural resources examined include biodiversity, fisheries, forests, minerals, soil, and water resources. Same as ENVS 310, and NRES 310. Prerequisite: ACE 100 or ECON 102.'),
 (135,4,320,'Commodity Marketing',135,'Examination of the structure, operations, and efficiency of grain and livestock markets; product demand and linkages between grain and livestock; problems in transportation and quality standards; price discovery and market performance; role of world trade and government policy in markets. Prerequisite: ECON 102 and ACE 222.'),
 (136,4,332,'Farm Management',136,'Students develop expertise in evaluating and making decisions similar to those faced by farm operators and managers. Prerequisite: ACE 232; credit or concurrent registration in ACE 340.'),
 (137,4,334,'Retail Market Analysis',137,'Analysis of retail markets, competition, and role of the retailer in relationship to primary and secondary markets and the consumer. Prerequisite: BADM 320 or concurrent registration in BADM 320.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (138,4,340,'Agricultural Finance',138,'Introduction to agricultural finance including study of financial markets and institutions providing debt and equity capital to agricultural firms, development of skills in applying principles and methods of financial management to agricultural firms Prerequisite: ACE 232 or ACCY 201, or equivalent.'),
 (139,4,341,'Contemp Issues in AgAccy&Fin',139,'Students study contemporary issues and career opportunities in AgriAccounting and AgriFinance in this course.  An in-depth dialogue with industry professionals helps develop an understanding of the skill sets needed to succeed in each of the different career paths discussed.  This course may not be repeated for credit.'),
 (140,4,387,'Textiles in the Global Economy',140,'History of the development of fiber, fabric, apparel, and related industries; present structure, organization, domestic and international operations; interrelationships of these industries; textile trade policies; trends of the major sectors of the primary and secondary markets; and application of the principles of marketing to the textile complex.'),
 (141,4,396,'Honors Research or Thesis',141,'Individual research, special problems, thesis, development and/or design work under the direction of the Honors advisor. May be repeated in the same or subsequent terms as topics vary. May be repeated up to 4 hours in a semester, but no more than 12 hours of special problems, research, thesis and/or individual studies may be counted toward the degree. Prerequisite: Junior standing, admission to the ACES Honors Program, and consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (142,4,398,'Seminar',142,'Group discussion on a special topic in a field of study directly pertaining to subject matter in agricultural and consumer economics. Approved for both letter and S/U grading.  May be repeated to 3 hours in a semester, up to a maximum of 12 total hours. Prerequisite: Junior standing and consent of instructor.'),
 (143,4,403,'Agricultural Law',143,'Relation of common-law principles and statutory law to land tenure, farm tenancy, farm labor, farm management, taxation, and other problems involving agriculture. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: Junior standing.'),
 (144,4,406,'Environmental Law',144,'Examination of environmental law issues. Topics include common-law pollution control; role of administrative agencies and courts; federal and state power; air and water pollution; regulation of toxic substances; protection of land, soil and other natural resources. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: ACE 403, or BADM 300, or BADM 301 recommended.'),
 (145,4,411,'Environment and Development',145,'Relationship between economic development and environmental sustainability through application of cost-benefit analysis and environmental economics. Developing and developed country issues are considered with an emphasis on hands-on applications of project appraisal, social benefit-cost analysis, green accounting, and non-market valuation. 3 undergraduate hours. 4 graduate hours. Prerequisite: ECON 302 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (146,4,427,'Commodity Price Analysis',146,'A comprehensive and in-depth survey of commodity price analysis with emphasis on the fundamental factors affecting prices of agricultural products; sources of information relating to production and demand factors; government activities as they relate to prices of agricultural products; technical analysis of agricultural product prices; and market efficiency and forecasting. Prerequisite: ACE 100 or ECON 102; ACE 261, or equivalent.'),
 (147,4,428,'Commodity Futures and Options',147,'Development of futures trading; operation and governance of commodity exchanges; economic functions of futures trading; operational procedures and problems in using futures markets; public regulation of futures trading; evaluation of market performance. Field trips required; see Class Schedule for approximate cost. Prerequisite: ACE 100 or ECON 102.'),
 (148,4,430,'Food Marketing',148,'Performance of the food system; marketing margins; transportation, advertising, and retailing of food products; structure, conduct, and performance of food marketing firms and industries; government and public interest in the food system. Same as FSHN 425. Prerequisite: ACE 100 or ECON 102, ACE 222 recommended.'),
 (149,4,431,'Agri-food Strategic Management',149,'Process of strategic decision-making in food and agribusiness firms; methods for analysis of business and regulatory environment; organizational issues in strategy choice for firms and supply chains. Same as BADM 438. Prerequisite: ACE 231, BADM 320, or ACE 222; or consent of instructor,');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (150,4,433,'Agribusiness Planning',150,'Important aspects in the development of a marketing plan for a new or existing product or service in the agribusiness sector. Includes development of a complete business plan for a specific firm or segment with special attention to the organization of the firm and the markets in which the firm will operate. In addition, a marketing plan will be developed for a chosen product including: market analysis; business strategy and goals; implementation; financial analysis; monitoring, and contingency planning. Computerized planning packages and presentation packages are used in the development of the final presentation. Prerequisite: ACE 231 or consent of instructor.'),
 (151,4,435,'Global Agribusiness Management',151,'Examination of the economic and strategic management of food, textile, and agribusiness firms within a global business environment; topics include the global business environment and its institutions, organizational strategies and policies, and business operations in global agricultural, food and textile industries. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: ACE 231, ACE 222, or BADM 320 or consent of instructor.'),
 (152,4,439,'Agri-food Management Practicum',152,'Capstone course in Agribusiness Markets and Management. The role and practice of senior management in food and agribusiness industries are examined with an emphasis on identification, analysis, and management of strategic issues in the sector. In-depth dialogue with executives-in-residence from the agribusiness sector are an integral part of the experience. Same as BADM 439. Prerequisite: ACE 431 and consent of instructor. Course cannot be taken credit /no credit.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (153,4,441,'Case Studies in AgAccy&Finance',153,'This is a capstone course for students specializing in accountancy and finance.  Students apply business concepts and tools to real-world situations in agribusiness.  Industry professionals participate in the learning experience.   3 undergraduate hours.  This course may not be repeated for credit.  Prerequisite:  ACCY 301 or FIN 300.'),
 (154,4,442,'Modeling App in Agr Finance',154,'Analysis of the financial management/performance of agribusiness and cooperative firms: interpretation of financial statements, analysis of capital structure, working capital management, capital budgeting, and introduction to real options. Course involves computerized model development paralleling text material. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: ACE 340, or FIN 221, or equivalent.'),
 (155,4,443,'Inter Agricultural Finance',155,'Examination of finance principles applied to commercial agriculture at an intermediate level; farm financial and investment analysis, risk and liquidity analysis, capital structure and leasing in agriculture; and organization, structure, and analysis of rural financial markets and institutions. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: ACE 340 and ACE 261, or equivalent.'),
 (156,4,445,'Financial Plan and Counseling',156,'Financial planning and counseling philosophies, techniques, and procedures.  Course will use case studies and problem-solving activities to construct financial plans for individuals and families in various life cycle stages and family structures. Prerequisite: ACE 245 and junior standing or consent of instructor; FIN 230 or FIN 232 and FIN 434 are recommended.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (157,4,448,'Rural Real Estate Appraisal',157,'Valuation methods and value bases of rural real estate; legal aspects of property rights, appraisal theory and procedures, condemnation appraisal, characteristics of the rural land market, soil identification and productivity, and other legal, economic, agronomic, and engineering aspects of real estate valuation. Laboratory field trips, including a practice appraisal; see Class Schedule for approximate cost. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: NRES 201 and ACE 232, or equivalent.'),
 (158,4,451,'Agriculture in Intl Dev',158,'Economics of agricultural development and the relationships between agriculture and other sectors of the economy in developing nations; agricultural productivity and levels of living in the less developed areas of the world; and studies of agricultural development in different world regions including Africa, Asia, and Latin America. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: ECON 302 or consent of instructor.'),
 (159,4,452,'The Latin American Economies',159,'Same as ECON 452. See ECON 452.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (160,4,453,'Econ Dev in S and SE Asia',160,'Analysis of plans and progress toward economic development in South and Southeast Asia; economic characteristics of the area and their significance for economic development. 3 undergraduate hours. 2 to 4 graduate hours. Prerequisite: ECON 302 or consent of instructor.'),
 (161,4,454,'Econ Dev of Tropical Africa',161,'Types of African economies and growth of the exchange economy; development of natural resources, industry, trade, finance, and education; analysis of economic integration, governmental planning, and development projects; and demographic, land tenure, and institutional influences on development. 3 undergraduate hours. 2 to 4 graduate hours. Prerequisite: ECON 302 or consent of instructor.'),
 (162,4,455,'Intl Trade in Food and Agr',162,'Economic theory used to analyze trends and patterns of international trade in major agricultural commodities and to understand interaction between economic development, policy, and trade; welfare implications of policies affecting production, consumption, and trade; implications of protechtionism, free trade, regional trade blocs, and multilateral trade liberalization, and the role for international trade institutions. Prerequisite: ECON 302 or consent of instructor.'),
 (163,4,456,'Agr and Food Policies',163,'Analysis of agricultural and food policies and programs and their effects on producers and consumers of agricultural products. Formulation of agricultural and food policies are examined with an emphasis on historical and current economic problems affecting agriculture and rural America. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: ECON 302 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (164,4,471,'Consumer Economic Policy',164,'Analysis of choice-making, buying, using, and disposing of consumer goods by families, social policy Perspectives considered. 3 undergraduate hours. Prerequisite: ACE 100 or equivalent and junior standing.'),
 (165,4,474,'Econ of Consumption',165,'Concepts, theories, and methods for analysis of the micro and macro aspects of consumption; includes standards and content of consumption and description of consumption patterns and trends in the USA and selected other countries. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: ECON 302 or consent of instructor; a course in statistics; junior standing.'),
 (166,4,476,'Family Economics',166,'Economic welfare of American families, application of economic theory to the behavior of families and individuals with respect to time allocation between the home and the market; family forms; human capital accumulation; gender differences in income; income inequality; and poverty. Role of public policy is considered. 3 undergraduate hours. 2 to 4 graduate hours. Prerequisite: ECON 302 or consent of instructor; a course in statistics; senior standing.'),
 (167,4,480,'Fiber and Textile Performance',167,'Same as NRES 480. See NRES 480.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (168,4,486,'Business and Public Policy',168,'Non-market environment (economic, legal, political, technological, and social) of business with a focus on business-government relationships (domestic and international), public policy, and corporate social responsibility. Issues, interests, institutions, and information that comprise environment and affect business performance are examined. Prerequisite: BADM 320 and ACE 387, or consent of instructor.'),
 (169,4,487,'Consumers in the Marketplace',169,'Analysis of the interdependent relationships between marketing stimuli and the day-to-day lives of consumers with an emphasis on the processes involved when individuals or groups select, purchase, use, or dispose of products and services to satisfy their needs and desires. Consumer phenomena are discussed from multi-disciplinary perspectives and include research and theory from consumer behavior, cultural anthropology, marketing, sociology, and psychology. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: Six hours of social science.'),
 (170,4,496,'Practicum',170,'Cooperatively supervised field experience in management and administration in a textile marketing business. Only four hours may be applied to the total required for a graduate degree. At the undergraduate level, up to four hours may be counted toward the hours required in Agricultural and Consumer Economics. Approved for both letter and S/U grading.  Prerequisite: ACE 387 or ACE 334 and consent of instructor. Not available to students on probation.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (171,4,499,'Seminar',171,'Group discussion or an experimental course on a special topic in agricultural and consumer economics. Approved for both letter and S/U grading. May be repeated in the same semester to 4 hours, or subsequent terms to a maximum of 12 hours as topics vary.'),
 (172,4,500,'Applied Economic Theory',172,'Provides an understanding of theory of the firm, consumer economics and various market models necessary to conduct applied professional economic research with special emphasis on applications relevant to agricultural, consumer, development, and resource economics. Multivariate calculus and optimization methods are used.'),
 (173,4,501,'Risk and Info: Theory and App',173,'Applications of the theory of economic behavior under uncertainty and asymmetric information. Analysis of individual decision making under uncertainty includes: tests of the expected utility hypothesis; comparative statistics of changes in risk preferences and risk; and moment based models of decision making. Analysis of economic equilibrium under uncertainty and asymmetric information includes tests for complete markets and applications of noncooperative game theory. Prerequisite: Concurrent enrollment in ECON 500 and ECON 506.'),
 (174,4,502,'Demand/Supply/Firms/Households',174,'Applications of demand and supply theories and applications of firm and household behavior. Topics include demand and supply systems, aggregation and separability, dynamics, formation and boundaries of the firm, household decision making, intrahousehold allocation, allocation of time, human capital, and hedonics. Same as ECON 513. Prerequisite: ECON 500 and ACE 501.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (175,4,503,'Equilibrium and Welfare Econ',175,'Provides a theoretical and applied treatment of economic equilibrium and the consequences of displacement of equilibrium for the welfare levels of economic agents. Displacement of equilibrium will be shown to be brought about by changes in government policy, technology, and consumer preferences. Welfare measures under partial equilibrium, general equilibrium, and multi-market models will be presented. Includes various applications of welfare economics in the analysis of policy and technological change. Prerequisite: ECON 500 and at least two semesters of college calculus.'),
 (176,4,510,'Adv Natural Resource Economics',176,'Economic theory is used to examine the allocation of renewable and efficiency issues that arise from natural resource policy and management issues. Same as ECON 515, ENVS 510, and NRES 510. Prerequisite: ECON 302 or equivalent.'),
 (177,4,516,'Environmental Economics',177,'Same as ECON 516, and ENVS 511. See ECON 516.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (178,4,520,'Food Commodity Markets',178,'Examination of selected economic problems in marketing agricultural products and relevant theory and empirical methods for analyzing and interpreting research results. Topics include: operational efficiency in marketing firms and industries; efficient allocation over space, form, and time; price making institutions; and research in demand stimulation and selected issues in trade. Prerequisite: ACE 562 and ACE 563, and ECON 500; or equivalent.'),
 (179,4,527,'Advanced Price Analysis',179,'Study of methods used to analyze factors affecting agricultural prices; analysis of agricultural prices and price movements with respect to time, space, and form; and examination of methods of price forecasting and techniques of time series analysis. Prerequisite: ACE 562 or ECON 507 and ECON 500; or equivalent.'),
 (180,4,528,'Research in Futures Markets',180,'Research literature on commodity futures and options markets, both theoretical and empirical; topics include: supply of storage, basis models, theory of the firm and hedging under uncertainty, optimal hedging, speculative returns, market performance, pricing efficiency and option pricing. Prerequisite: ACE 328 or equivalent, and ECON 500 or equivalent.'),
 (181,4,530,'Applied Production Economics',181,'Econometric applications of the economic theory of production. Basic theory includes production functions; duality; cost functions; profit functions and parameters that can be derived from them such as returns to scale or substitution elasticity. Econometric issues include simultaneity bias, panel data, instrumental variables, generalized method of moments, functional forms, nonparametrics, and dynamics. Prerequisite: ECON 500 and ACE 562.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (182,4,541,'Research in Agr Finance',182,'Current research issues in agricultural finance; topics include simulation and optimization modeling and applications, economics of organization, principal agent issues, and firm-level performance evaluation. Emphasis is placed on developing a framework to evaluate research and communicate results. Prerequisite: ACE 562; ACE 563; ACE 443 or consent of instructor.'),
 (183,4,542,'Advanced Agricultural Finance',183,'Theory of financial decision making as applied to farms and firms related to agriculture. Topics include asset pricing models, financial markets, capital structure, farmland control, term structure of interest rates, risk management and credit evaluation. Prerequisite: ECON 500, calculus, and mathematical statistics, or equivalent; at least one course in finance strongly recommended; or consent of instructor.'),
 (184,4,551,'International Food Policy I',184,'Economic theory and empirical analyses are used to study economic development, emphasizing the structural transformation of an economy and the role of public policies in supporting or hindering that process. Topics include growth, determinants of supply and demand, and measuring and evaluating the effects of public policies. Special attention is paid to the role of the agricultural sector, as the home of most productive resources in the early stages of development. Prerequisite: ECON 500 or equivalent.'),
 (185,4,556,'International Food Policy II',185,'Economic theory is used to study both the effects and the causes of public policies in an international context that influence agricultural industries, consumers, and taxpayers. Neoclassical models of government intervention are used to study the welfare effects of income redistribution and stabilization policies and macroeconomic policies as they affect agriculture. Formal models of political economy and public choice are used to analyze the underlying causes of public policy. Emphasis is placed on the political power of interest groups as an explanation of public policy decisions. Prerequisite: ECON 500, and ACE 503 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (186,4,560,'Research Methods',186,'Survey of research methods that address practical and theoretical issues involved in designing, conducting, and evaluating research in agricultural and consumer economics. Prerequisite: A graduate level course in statistics.'),
 (187,4,561,'Adv Res and Scholarly Comm',187,'Seminar intended for Ph.D. students who have completed written preliminary examinations. Develops a comprehensive understanding of the research process. Discussions include identification of research topics, structure of research proposals, review of literature, effective communication, management of research activities, and contributions to scholarly debate. Prerequisite: Consent of instructor.'),
 (188,4,562,'Applied Regression Models I',188,'Application of simple regression methods to problems in agricultural and consumer economics with emphasis on foundational probability, random variable, and distribution concepts, development of the simple, two-variable regression model; estimation of model parameters; hypothesis testing; and prediction. Prerequisite: ACE 261 or equivalent; one of MATH 220, MATH 221, MATH 234.'),
 (189,4,563,'Optimization Methods',189,'Application of mathematical programming methods to discrete models in agricultural economics; Kuhn-Tucker theorem, Lagrange multipliers, duality, simplex method as applied to linear and quadratic programming, and input-output analysis models in agriculture. Prerequisite: MATH 124; one of MATH 220, MATH 221, MATH 234.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (190,4,564,'Applied Regression Models II',190,'Application of multiple regression methods to problems in agricultural and consumer economics with emphasis on extensions to the simple, two-variable regression model, development of the multiple regression model; and problems created by violations of basic model assumptions. Prerequisite: ACE 562 or equivalent.'),
 (191,4,565,'Modeling Dynamic Econ Systems',191,'Computer simulation modeling as a tool for studying the behavior of dynamic economic systems with an emphasis on applications of the dynamic simulation approach to problems in resource economics and management. STELLA, a computer simulation software, is used in the course. Prerequisite: ACE 562 or ACE 563, or equivalent.'),
 (192,4,566,'Mathematics for Applied Econ',192,'Applications of concepts of linear algebra, calculus, and multivariate optimization to equilibrium analysis, comparative statistics, and other topics in agricultural and consumer economics.'),
 (193,4,567,'Adv Programming for App Econ',193,'Advanced mathematical programming methods with particular emphasis on applications in agricultural and consumer economics. Includes linear programming and extensions, nonlinear programming, sector modeling, risk modeling, and methodological issues in mathematical programming modeling of agricultural systems. Prerequisite: ACE 563 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (194,4,570,'Family and Consumption Econ',194,'Discussion of current topics and review of the literature in family and consumption economics. Prerequisite: ECON 500 or equivalent.'),
 (195,4,572,'Economics of the Family',195,'Discussion and analysis of advanced literature on the economics of the family, developed within the models of human capital and allocation of time, emphasizing the theory and empirical applications. Prerequisite: ECON 500 or ECON 502; ECON 506 or SOC 485, or equivalent.'),
 (196,4,582,'Textile Finishing',196,'Same as NRES 582. See NRES 582.'),
 (197,4,584,'Topics in Consumer Marketing',197,'Analysis of consumer marketing with emphasis on research needs and future research directions. Prerequisite: A course in marketing or consent of instructor.'),
 (198,4,585,'High Performance Fibers',198,'Same as NRES 585. See NRES 585.'),
 (199,4,588,'Political Econ, World Textiles',199,'Examination of the structure and environment of the world textile industry with an emphasis on dynamic interactions between states, corporation, and markets. Related literature, research, and theory are reviewed.'),
 (200,4,591,'Independent Study',200,'Individual research work under the supervision of an appropriate member of the faculty.  Approved for both S/U and letter grading.  May be repeated to a maximum of 8 hours if topics vary.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (201,4,592,'Special Topics',201,'Group instruction on a special topic under the direction of one or more members of the faculty. Approved for both letter and S/U grading.  If topics vary, may be repeated in a semester to 8 hours, up to a maximum of 24 total hours.'),
 (202,4,594,'Seminars and Workshops',202,'Participation in a seminar or workshop with other graduate students and faculty members. May be repeated. Approved for both letter and S/U grading.'),
 (203,4,599,'Thesis Research',203,'Individual research under supervision of members of the graduate teaching faculty in their respective fields. May be repeated. Approved for S/U grading only.'),
 (204,5,100,'Contemporary Issues in ACES',204,'Study of contemporary issues in the human, food and natural resource systems, and an overview of the role of the College of Agricultural, Consumer and Environmental Sciences and the University of Illinois in these systems. Required of and limited to freshmen enrolled in the College of ACES.'),
 (205,5,179,'History of Ag in IL Since 1860',205,'An introduction to the history of agriculture in the rural Midwest with an emphasis on Illinois based on an analysis of the attitudes of indigenous peoples, immigrants, farmers and agribusiness interests toward land, labor, crop selection and production, and technology.  The course compares the regional characteristics of the rural Midwest to other U.S. regions, and explores factors that created the American \"breadbasket,\" a region recognized for the commodities, equipment and ideas that it exports to the world.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (206,5,199,'Undergraduate Open Seminar',206,'Experimental course on a special topic in the College of Agricultural, Consumer and Environmental Sciences. Topic may not be repeated except in accordance with the Code.  Approved both Standard and S/U grading.  May be repeated in the same or subsequent term. No more than 12 hours may be counted toward graduation.'),
 (207,5,200,'ACES Transfer Orientation',207,'Introduction to College of ACES and campus resources for students new to the College of ACES. Required of all off campus transfer students and optional for Inter College Transfer students. First eight weeks course. Approved for S/U grading only.'),
 (208,5,293,'International Internship',208,'Supervised learning experience designed for ACES students registering for an academic term abroad and/or for non-degree exchange students enrolling for an academic term at Illinois. The nature of the experience and the setting in which it takes place must be approved in advance by ACES faculty and by representative(s) of institutions/organizations/agencies that cooperate with the College of ACES in student exchange/study abroad programs. May be repeated to a maximum of 10 hours. (Summer Session) 0 to 3 undergraduate hours. Approved for both letter and S/U grading. Prerequisite: Written consent of ACES Study Abroad Office.'),
 (209,5,295,'Undergrad Research or Thesis',209,'Individual research, special problems, thesis, development and/or design work under the supervision of an appropriate member of the faculty. May be repeated to a maximum of 12 hours. Students may register in more than one section per term. Prerequisite: GPA of 2.5 or above at the time the activity is arranged, and consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (210,5,298,'International Experience',210,'International experience in agricultural, consumer and environmental sciences related areas involving foreign travel and study without enrollment in another institution. Experience must be planned and approved in advance through consultation with a College of Agricultural, Consumer and Environmental Sciences faculty member. Approved for both letter and S/U grading.  May be repeated to a maximum of 9 hours. Not open to students on probation. Prerequisite: Written consent of ACES Study Abroad Office.'),
 (211,5,299,'ACES Study Abroad',211,'Provides campus credit in the College of Agricultural, Consumer and Environmental Sciences for study at accredited foreign institutions. Final determination of credit granted is made upon the student\'s successful completion of work. Approved for both S/U and letter grading.  (Summer session) 0 to 8 undergraduate hours. May be repeated to a maximum of 36 hours within one calendar year. Prerequisite: Consent of major department, college, and Study Abroad Office.'),
 (212,6,199,'Undergraduate Seminar',212,'May be repeated to a maximum of 12 hours in separate semesters, if topics vary.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (213,6,300,'Introduction to Advertising',213,'Introduction to the practice and profession of advertising. Course material covers various functional areas of advertising and integrated brand promotion, including account planning, creative, media, research, consumer behavior, sales promotion and interactive advertising. Topics also include how advertising relates to society in cultural, social, ethical and regulatory contexts. Open to all undergraduate majors.'),
 (214,6,400,'Special Problems',214,'Special projects, research, and independent reading in advertising for students capable of individual work under the guidance of a faculty adviser. 2 or 3 undergraduate hours. May be repeated in the same or in multiple semesters, if topics vary. No graduate credit. Prerequisite: Written research proposal and consent of department.'),
 (215,6,410,'Consumer Comm and the Public',215,'Addresses the interface between marketing activity and various public institutions and actions: government agencies, consumer groups, boycotts, litigation. 3 undergraduate hours. No graduate credit.'),
 (216,6,411,'Classic Campaigns',216,'Examines the advertising campaigns that have been seen as the best examples of this genre during the past century. Includes the writings of famous advertising authors on the rhetorical principles of advertising. 3 undergraduate hours. No graduate credit.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (217,6,412,'Advertising History',217,'Teaches the important events, forces, people, and technologies that helped advertising to become an important institution in America.  Prerequisite: ADV 300. 3 undergraduate hours. No graduate credit.'),
 (218,6,450,'Creative Strategy and Tactics',218,'Explores theories of creativity; situates creativity and creative practices within the social structure of an advertising agency; examines the role of creative strategy in the development of advertising; exposes students to the practice of creating advertisements.  3 undergraduate hours. No graduate credit.  Prerequisite: ADV 300.'),
 (219,6,452,'Creative Concepts I',219,'Planning and execution of advertising across media, with emphasis on the creation of campaigns 3 undergraduate hours. 4 graduate hours.  Prerequisite: ADV 450 and consent of instructor (required).'),
 (220,6,481,'Advertising Research Methods',220,'Overview of basic concepts of research methodology with particular emphasis on advertising research. Computer analysis and interpretation of actual data sets; measurement with both structured and unstructured techniques; principles of survey and experimental design. Prerequisite: ADV 300 and a specified course in statistical methods. 3 undergraduate hours. No graduate credit.'),
 (221,6,483,'Audience Analysis',221,'Analyzes the markets served by various advertising media and factors to consider in the selection and evaluation of media.  Prerequisite: ADV 481. 3 undergraduate hours. No graduate credit.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (222,6,491,'Advertising Management Plan.',222,'Application of analytical planning concepts to advertising planning and decision making; covers all of the decision making areas of advertising.  Prerequisite: ADV 300. 3 undergraduate hours. No graduate credit.'),
 (223,6,493,'Social and Cult Context of Adv',223,'Studies advertising as a cultural force and social institution and its role in communications, society, and economics.  Prerequisite: ADV 300. 3 undergraduate hours. No graduate credit.'),
 (224,6,494,'Persuasion Consumer Response',224,'Addresses what makes a mass-mediated message persuasive by reviewing theories of mass communication and persuasion, consumer information-processing, and advertising effectiveness measures.  Prerequisite: ADV 481. 3 undergraduate hours. No graduate credit.'),
 (225,6,550,'Foundations of Advertising',225,'Evaluation of key periods, events, and individuals having significant impact on the evolution of American advertising philosophy, structure, and performance. Prerequisite: Consent of department.'),
 (226,6,582,'Res Meth in Adv and Comm',226,'Treatment of basic research concepts and procedures in the social sciences with emphasis on advertising and communications; examines both non-quantitative and quantitative methods. Prerequisite: ADV 481, a basic course in statistical methods, and consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (227,6,583,'Advertising in Communication',227,'Advertising messages from the perspective of attitude and persuasive communication theories. Application of theory to advertising communication issues. Prerequisite: ADV 481 or equivalent undergraduate research course and consent of department.'),
 (228,6,584,'Advertising Consumer Behavior',228,'Examines consumer behavior as a means of shaping the communications message; use of the behavioral sciences in communication strategy. Prerequisite: Consent of department.'),
 (229,6,585,'Adv Plan and Decision Making',229,'Examines the theoretical foundations of decision theory as they relate to planning and decision making in advertising; reviews concepts of strategic planning and client side operations; case studies utilized extensively. Prerequisite: Consent of department.'),
 (230,6,587,'Graduate Seminar',230,'Provides advertising students and faculty the opportunity to interact on current topics. Must be repeated by master\'s program graduate students for a total of 4 hours. Prerequisite: Consent of department.'),
 (231,6,590,'Special Topics in Advertising',231,'May be repeated in the same or in multiple semesters if topics vary. Prerequisite: Consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (232,6,599,'Thesis Research',232,'Approved for S/U grading only. Prerequisite: Graduate standing in advertising.'),
 (233,7,100,'Intro to Aerospace Engineering',233,'Introduction to the Aerospace Engineering curriculum and career.  Typical section topics include aircraft and rocket design and flight.  Overviews of the topics are presented along with theory to be experimentally verified.'),
 (234,7,199,'Undergraduate Open Seminar',234,''),
 (235,7,201,'Aerospace Systems Principles',235,'Fundamental principles of aerospace systems are introduced through a systems design approach. Aeronautical engineering topics of aerodynamics, propulsion, structures and flight mechanics, and astronautical engineering topics of orbital mechanics, rockets and spacecraft systems are presented. The principles are demonstrated through design projects.  Prerequisite: Credit or concurrent registration in AE 252.'),
 (236,7,252,'Intro to Aerospace Dynamics',236,'Kinematics and dynamics of particle motion; methods of work-energy and impulse-momentum; kinematics of plane motion of rigid bodies; moving reference frames; moments of inertia. Credit is not given for both AE 252 and TAM 212. Prerequisite: TAM 210.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (237,7,302,'Aerospace Flight Mechanics',237,'Introduction to the dynamics of aircraft and spacecraft, and to orbital mechanics; aircraft performance in various flight attitudes; aircraft stability and control; spacecraft attitude dynamics and control; the two-body problem of orbital mechanics; orbit transfer.  Prerequisite: AE 201 and AE 352.'),
 (238,7,311,'Incompressible Flow',238,'Equations of motion for incompressible flow, both inviscid and viscous; potential flow theory, inviscid airfoil theory: two- and three-dimensional, Navier-Stokes equations, laminar boundary layer and transition to turbulence. Prerequisite: CS 101; credit or concurrent registration in MATH 241 (formerly MATH 243) or MATH 380.'),
 (239,7,312,'Compressible Flow',239,'Compressible flow aerodynamics; conservation of mass, momentum, and energy; one-dimensional and quasi-one-dimensional flow; oblique shock waves and Prandtl-Meyer expansion waves; unsteady wave motion. Application to nozzles, diffusers, supersonic airfoils, and shock tubes. Prerequisite: AE 311, CS 101, MATH 385, ME 300; credit or concurrent registration in AE 201.'),
 (240,7,321,'Aerospace Structures I',240,'Fundamental concepts in the linear theory of elasticity, including stress, strain, equilibrium, compatibility, material constitution and properties. Introduction to failure mechanisms and criteria. Application to plane stress/strain problems, beams in extension and bending, and shafts in torsion. Prerequisite: MATH 385 and TAM 210.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (241,7,322,'Aerospace Structures II',241,'Analysis of beams and shafts of monocoque and semi-monocoque construction. Energy methods. Theory of elastic stability with applications to buckling of columns. Introduction to finite element structural analysis - with application to trusses, frames, and plane stress/strain problems.  Prerequisite: AE 321; CS 101; MATH 241 (formerly MATH 243) or MATH 380.'),
 (242,7,352,'Aerospace Dynamics',242,'Particle kinematics and dynamics; Lagrange\'s equations; vibration of multiple degree-of-freedom systems; rotational kinematics and dynamics of rigid bodies.  Credit is not given for both AE 352 and TAM 412. Prerequisite: AE 252, MATH 225, and MATH 385.'),
 (243,7,353,'Aerospace Control Systems',243,'Modeling of linear dynamic systems; Laplace transform techniques; linear feedback control systems; stability criteria; design techniques.  Credit is no given for both AE 353 and either GE 320 or ME 340. Prerequisite: AE 252, MATH 225, and MATH 385.'),
 (244,7,360,'Structures & Control Lab',244,'Examines theory and application of experimental techniques in  aerospace engineering with emphasis on structural mechanics, vibrations, dynamics, and control systems.  Prerequisite: Credit or concurrent registration in AE 322, AE 352, and AE 353.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (245,7,395,'Honors Project',245,'Special aerospace engineering project or reading course for James Scholars in engineering. Prerequisite: Consent of instructor.'),
 (246,7,396,'Honors Seminar',246,'Special lecture sequences and/or discussion groups arranged each term to bring James Scholars in engineering into direct contact with the various aspects of engineering practices and philosophy. Prerequisite: Consent of instructor.'),
 (247,7,397,'Independent Study',247,'Independent theoretical and experimental projects in aerospace engineering. May be repeated. Prerequisite: Consent of instructor.'),
 (248,7,402,'Orbital Mechanics',248,'Analysis of orbits in an inverse-square gravitational field; elementary rocket dynamics, impulsive orbit transfer and rendezvous, and Lambert\'s Theorem with applications; patched-conic trajectories, planetary  gravity-assist maneuvers, and linearized orbit theory with application to simplified analytical models; perturbations. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: AE 302.'),
 (249,7,403,'Spacecraft Attitude Control',249,'Theory and applications of spacecraft attitude dynamics and control; Euler angles, direction cosines, quaternions, and Gibbs-Rodrigues parameters; attitude sensors and control actuators; spin, three-axis active, reaction wheel, control moment gyro, and gravity gradient control systems; environmental effects. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: AE 352 and AE 353.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (250,7,410,'Computational Aerodynamics',250,'Introduction of computational technologies as solution tools for various aerodynamic problems; modeling and solution of one-and two-dimensional, incompressible and compressible, steady and unsteady inviscid external flow fields. Computational laboratory for practical experience. Same as CSE 461. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: AE 311.'),
 (251,7,412,'Viscous Flow and Heat Transfer',251,'Momentum and thermal transport in wall boundary-layer and free shear flows, solutions to the Navier-Stokes equations for heat conducting laminar and turbulent shear flows; similarity concepts; thermal boundary layers in ducts and high-speed aerodynamic boundary layers. Same as ME 411 and TAM 438. 4 undergraduate hours. 4 graduate hours. Prerequisite: AE 311 or ME 310.'),
 (252,7,413,'Ideal Aerodynamics',252,'Governing equations for incompressible flow; vorticity, circulation, and Kelvin\'s and Helmholtz\'s theorems; velocity potential and stream function; three-dimensional steady and nonsteady flows, d\'Alembert\'s paradox, and apparent mass; two-dimensional steady flows, complex potential and velocity, and mapping of flows; two-dimensional airfoils and Joukowski transformation and airfoils; and thin airfoil theory. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: AE 311.'),
 (253,7,416,'Applied Aerodynamics',253,'Two-dimensional and finite wing theory with emphasis on the mechanisms of lift and drag generation; Reynolds number and Mach number effects; drag analysis; high-lift wing systems; propeller and rotor aerodynamics; control surface design; and application of V/STOL aerodynamics. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: AE 311.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (254,7,419,'Aircraft Flight Mechanics',254,'Steady and quasi-steady aircraft flight performance; take-off and landing, climbing and diving, cruise, level turn, and introduction to energy methods; longitudinal, directional, and lateral static stability and control; and introduction to longitudinal and lateral motion and dynamic stability. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: AE 302 and AE 353.'),
 (255,7,420,'Intro to Finite Element Anlys',255,'Same as CSE 451 and ME 471. See ME 471.'),
 (256,7,427,'Mechanics of Polymers',256,'Same as TAM 427. See TAM 427.'),
 (257,7,428,'Mechanics of Composites',257,'Same as TAM 428. See TAM 428.'),
 (258,7,433,'Aerospace Propulsion',258,'Fundamentals of rocket and airbreathing jet propulsion devices; prediction of thrust, combustion reactions, specific fuel consumption, and operating performance; ramjets; turbojets; turbofans; turboprops; aerothermodynamics of inlets, combustors, and nozzles; compressors, turbines; and component matching. 3 undergraduate hours.  Prerequisite: AE 312 and CS 101.'),
 (259,7,434,'Rocket Propulsion',259,'Basic principles of chemical rocket propulsion and performance, propellants and their influence on design of rockets, internal ballistics, combustion processes, design of components, and flight performance. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: AE 312 and AE 433.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (260,7,435,'Electric Propulsion',260,'Elements of propulsion as applied to deep space missions; physics of ionized gases; plasmadynamics; electrothermal, electromagnetic, and electrostatic acceleration of gases to high velocity; high-impulse thruster design and performance; and the resistojet, arcjet, ion engine, MPD arc, and plasma gun.  3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: AE 433.'),
 (261,7,440,'Aerospace Systems Design I',261,'Introduction to the design of aerospace flight systems. The principles of systems engineering, as they apply to the design process, are presented. A general design methodology is introduced. These concepts are then applied to the initial sizing of both aircraft and spacecraft systems. Involves intensive technical writing. AE 440 and AE 441 taken in sequence fulfill the Advanced Composition Requirement. 3 undergraduate hours. No graduate credit. Prerequisite: Credit or concurrent registration in AE 302, AE 311, AE 322, AE 352, and AE 433.'),
 (262,7,441,'Aerospace Systems Design II',262,'Conceptual design project of either an aircraft or spacecraft flight system to satisfy a given set of requirements. Project team organization. Emphasis on sizing, trade studies and design optimization, subsystem integration, and technical communication skills. To fulfill the Advanced Composition Requirement, credit must be earned for both AE 440 and AE 441. 3 undergraduate hours. No graduate credit. Prerequisite: AE 440.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (263,7,451,'Aeroelasticity',263,'Advanced fundamental treatment of aerodynamic and dynamic structural phenomena associated with flexible airplanes and missiles; divergence of linear and nonlinear elastic lifting surfaces; effect of elastic and inelastic deformations on lift distributions and stability; elastic flutter of straight and swept wings; equations of disturbed motion of elastic and inelastic aircraft; dynamic response to forces, gusts, and continuous atmospheric turbulence; creep divergence of lifting surfaces; flutter in the presence of creep; and effect of temperature on inelastic divergence and flutter. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: AE 352 or TAM 412; AE 322 or TAM 251.'),
 (264,7,460,'Aerodynamics & Propulsion Lab',264,'Examines theory and application of experimental techniques in  aerospace engineering with emphasis on fluid dynamic, aerodynamic, thermal, combustion, and propulsion phenomena. 2 undergraduate hours. No graduate credit. Prerequisite: AE 311; credit or concurrent registration in AE 433.'),
 (265,7,468,'Optical Remote Sensing',265,'Same as ECE 468 and ATMS 468.  See ECE 468.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (266,7,470,'Aerospace Numerical Methods',266,'Introduction to numerical methods used in aerospace engineering. Finite difference method; Variational principles and Rayleigh-Ritz method; finite element method; applications from simple structural mechanics and aerodynamics problems encountered in aerospace engineering. 3 undergraduate hours. No graduate credit. Prerequisite: CS 101, AE 311, AE 312, AE 321, and AE 322.'),
 (267,7,481,'Wind Power Technology',267,'Aerodynamic, electromechanical, and structural design of wind power systems; classical windmills; modern wind power generators; wind characteristics and distribution; instrumentation and measurement; energy storage considerations; socioeconomics of wind power systems; performance of large and small scale wind turbines; and current design approaches. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: A fluids course, an electrical course, and a course in mechanics, all at the 200 level or higher.'),
 (268,7,497,'Independent Study',268,'Independent theoretical and experimental projects in aerospace engineering. Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (269,7,498,'Special Topics',269,'May be repeated in the same or separate terms if topics vary to a maximum of 9 undergraduate hours or 12 graduate hours. Prerequisite: As specified for each topic offering, see Schedule or departmental course information.'),
 (270,7,502,'Advanced Orbital Mechanics',270,'Circular restricted three body problem; surfaces of zero velocity, libration points, halo orbits; perturbed two body motion; Gauss and Lagrange planetary equations, Hamilton\'s principle, canonical equations and the Delaunay variables, application to artificial Earth satellites; orbit determination. Prerequisite: AE 402.'),
 (271,7,504,'Optimal Aerospace Systems',271,'Formulation of parameter and functional optimization problems for dynamic systems; applications of optimization principles to the control and performance of aerospace vehicles, including optimal flight paths, trajectories, and feedback control. Prerequisite: AE 352.'),
 (272,7,508,'Optimal Space Trajectories',272,'Optimal rocket trajectories in inverse-square and linearized gravitational fields; orbital transfer, intercept, and rendezvous; high-thrust (impulsive) and low-thrust (continuous) trajectories; primer vector theory and applications; cooperative rendezvous. Prerequisite: Credit or concurrent registration in AE 504 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (273,7,511,'Transonic Aerodynamics',273,'Fundamentals of transonic flows; transonic characteristics and flow modeling, shock wave development, properties of shock wave, transonic similarity, shock-boundary layer interactions, three-dimensional effects, transonic solution techniques, transonic design, transonic testing. Prerequisite: ME 410.'),
 (274,7,514,'Boundary Layer Theory',274,'Theories of the boundary layer of a compressible fluid and their solutions, laminar and turbulent; boundary layer in hypersonic flows.  Prerequisite: AE 412.'),
 (275,7,515,'Wing Theory',275,'Theoretical analysis of the aerodynamic characteristics of two- and three-dimensional wings and multiple-body systems in subsonic and supersonic flows.  Prerequisite: AE 413 or AE 416.'),
 (276,7,517,'Fundamentals of Gas Kinetics',276,'Fundamental concepts required to study gas dynamic problems from the viewpoint of kinetic theory; derivation of the Boltzmann equation from classical mechanics; reduced and truncated distribution functions and the BBGKY hierarchy; molecular collisions; flux vectors and equations of change; moment equations; summational invariants; H-theorem and Maxwellian distribution; inclusion of the effect of solid surfaces in kinetic theory; existence theory for the Boltzmann equation; iteration procedures; moment methods; Chapman-Enskog procedure; and first and second approximations to the distribution function, heat flux vector, and stress tensor.  Prerequisite: ME 410 and AE 412.'),
 (277,7,518,'Theory of Rarefied Gas Flow',277,'Application of kinetic theory to rarefied gas flow problems; free-molecule flow; near free-molecule flow; linearized problems; and flows with appreciable deviation from equilibrium.  Prerequisite: AE 517.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (278,7,525,'Advanced Composite Materials',278,'Advanced analysis of composite materials (extension of AE 428/TAM 428); anisotropic elasticity; micromechanical theories; behavior of composite plates and beams under bending, buckling, and vibration; advanced elasticity solution techniques; hygrothermal behavior of polymer composites; strength prediction theories and failure mechanisms in composites; processing of metal, ceramic, and polymer composites; analysis of residual stresses. Same as TAM 525.  Prerequisite: AE 428.'),
 (279,7,526,'Composites Manufacturing',279,'Review of the manufacturing methods for polymer-matrix composite materials; analysis of fiber processing techniques, interfacial treatments, and composites fabrication methods; analytical treatment of process modeling including heat transfer, cure kinetics, resin flow, and residual stresses. A term project is required. Same as ME 555 and TAM 526.  Prerequisite: AE 428.'),
 (280,7,528,'Nonlinear Continuous Media',280,'Fundamental concepts of large deformations in nonlinear elasticity and inelasticity with applications: generalized tensors, finite deformations, stress-strain relations in terms of strain energy functions, solutions of tension, shear and bending problems, finite plane strain, theory of successive approximations, fiber-reinforced beams, plates and cylinders, thermodynamics of deformable media, stability considerations, and constituent relations for inelasticity. Prerequisite: TAM 428/AE 428.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (281,7,529,'Viscoelasticity Theory',281,'Fundamental concepts of viscoelasticity with applications: elastic-viscoelastic analogies, creep and relaxation functions, thermomechanical reciprocity relations, variational principles, model fitting, shear center motion, thick-walled cylinders under pressure and inertia loads with material annihilation, sandwich plates, propagation of viscoelastic waves, vibration of bars, plates and shells, nonlinear elastic-viscoelastic analogy, properties of nonlinear viscoelastic stress-strain laws, creep rupture, and torsion of nonlinear bars and shells. Same as TAM 529. Prerequisite: TAM 428/AE 428.'),
 (282,7,538,'Combustion Fundamentals',282,'Fundamentals of kinetic theory, transport phenomena, chemical equilibria, and reaction kinetics; flames, their gross properties, structure, and gas dynamics including oscillatory and turbulent burning; solid and liquid propellant combustion; one-dimensional detonation theory including structure and initiation; three-dimensional and other complex detonation waves; and supersonic burning. Same as ME 501.  Prerequisite: AE 311 or ME 410.'),
 (283,7,552,'Stochastic Structural Dynamics',283,'Structural dynamics problems treated from a probabilistic point of view; theory of probability and random processes introduced as mathematical tools; response of structures under random excitation is studied in order of increasing complexity; and probability of failure for such structures is discussed. Same as TAM 517.  Prerequisite: AE 352 or TAM 412.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (284,7,554,'Dynamical Systems Theory',284,'Fundamental concepts of nonlinear oscillations, structural stability, local and global bifurcations in the context of ordinary and partial differential equations; introduction to dynamic systems, structural stability and Lyapunov-Schmidt Reduction, bifurcations of equilibrium points, limit cycles and tori, the center manifold and Poincare normal forms, co-dimension two and higher order bifurcations, bifurcation theory of maps, the Birkhoff-Smale homoclinic theorem and horseshoes, Melnikov\'s method and Silnikov phenomena, period doubling, and other routes to chaos. Applications to many engineering problems, such as aircraft at high angles of attack, pipes conveying fluid, and panel flutter will be demonstrated.  Prerequisite: AE 352 or TAM 412.'),
 (285,7,555,'Multivariable Control Design',285,'Frequency response design-specifications; algebraic and analytic constraints in scalar systems; uncertainty representation; Nyquist stability theory, small gain condition, multi-input multi-output systems; singular value decomposition; robustness and u-function; linear quadratic regulator based design; recovery of LQ Design properties; Kalman filter; Riccati equations; H-infinity based design; reduction; balanced truncation; Hankel singular values; coprime factor reduction; loop shaping. Same as GE 521.  Prerequisite: ECE 515.'),
 (286,7,556,'Robust Control',286,'Signal and system spaces; stability, robustness, and the small gain theorem; factorization and parameterization of all stabilizing controllers; performance and achievable closed loop maps; model matching; design of optimal single-input single-output systems in H-infinity, H2, L1 senses: extensions to multi-output systems; structured and unstructured uncertainty; robust performance analysis and synthesis; multi-objective control.  Prerequisite:  ECE 515 and MATH 446.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (287,7,590,'Seminar',287,'Presentation by graduate students, staff, and guest lecturers of current topics in aerospace engineering. Approved for S/U grading only.'),
 (288,7,597,'Independent Study',288,'Independent theoretical and experimental projects in aerospace engineering. Prerequisite: Consent of instructor.'),
 (289,7,598,'Special Topics',289,'Special topics in Aerospace Engineering. May be repeated in the same or separate terms as topics vary to a maximum of 12 hours. Prerequisite: As specified for each topic offering; see Schedule or departmental course information.'),
 (290,7,599,'Thesis Research',290,'Research in the various areas of aerospace engineering. Approved for S/U grading only.  May be repeated.'),
 (291,8,102,'Leadership Laboratory',291,'Aerospace Studies Leadership Laboratory (LLAB) is a co-requisite with all Air Force Aerospace Studies courses. LLAB is the application of personal leadership skills, demonstration of command, effective communication, individual leadership instruction, physical fitness training, and knowledge of U. S. Air Force customs and courtesies. May be repeated. Approved for S/U grading only. Prerequisite: Consent of instructor.'),
 (292,8,111,'Found of the US Air Force I',292,'Survey course focusing on the organizational structure and missions of Air Force organizations, military customs and courtesies, officership and core values, and an introduction to written and oral communication skills. Requires concurrent enrollment with AFAS 102.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (293,8,112,'Found of the US Air Force II',293,'Continuation of AFAS 111. Survey course focusing on the organizational structure and missions of Air Force organizations, military customs and courtesies, officership and core values, and an introduction to written and oral communication skills Requires concurrent enrollment with AFAS 102. Prerequisite: AFAS 111 or consent of instructor'),
 (294,8,221,'Dev of Air and Space Power I',294,'Historical survey of trends, events, and policies that led to the emergence of air power through the Korean Conflict. Also provides an introduction to basic leadership and management skills, ethical decision making, and basic communication skills. Requires concurrent enrollment with AFAS 102. Prerequisite: AFAS 112 or consent of instructor.'),
 (295,8,222,'Dev of Air and Space Power II',295,'Continuation of AFAS 221. Historical survey of trends, events, and policies that led to the emergence of air power through Operation ALLIED FORCE. Also provides an introduction to basic leadership and management skills, ethical decision making, and basic communication skills Requires concurrent enrollment with AFAS 102. Prerequisite: AFAS 221 or consent of instructor.'),
 (296,8,331,'AF Leadership and Mgt I',296,'Study of leadership and quality management fundamentals, professional knowledge, leadership ethics, and communication skills required of an Air Force junior officer. Case studies are used to examine Air Force leadership and management situations as a means of demonstrating and exercising practical application of the concepts. Requires concurrent enrollment with AFAS 102. Prerequisite: AFAS 222 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (297,8,332,'AF Leadership and Mgt II',297,'Continuation of AFAS 331. Study of leadership and quality management fundamentals, professional knowledge, leadership ethics, and communication skills required of an Air Force junior officer. Case studies are used to examine Air Force leadership and management situations as a means of demonstrating and exercising practical application of the concepts Requires concurrent enrollment with AFAS 102. Prerequisite: AFAS 331 or consent of instructor'),
 (298,8,341,'National Security Studies I',298,'Study of the Armed Forces as an integral element in contemporary society with specific emphasis on the military profession, civil-military interaction, and the formulation, organization, and implementation of U. S. national security policy. In addition, students study leadership and management, ethical decision making, and communication skills. Requires concurrent enrollment with AFAS 102. Prerequisite: AFAS 332 or consent of instructor.'),
 (299,8,342,'National Security Studies II',299,'Continuation of AFAS 341. Study of the Armed Forces as an integral element in contemporary society with specific emphasis on the military profession, civil-military interaction, and the formulation, organization, and implementation of U. S. national security policy. In addition, students study leadership and management, ethical decision making, and communication skills Requires concurrent enrollment with AFAS 102. Prerequisite: AFAS 341 or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (300,9,100,'Intro to African American St',300,'Interdisciplinary introduction to the basic concepts and literature in the disciplines covered by African American studies; surveys the major approaches to the study of African Americans across several academic disciplines including economics, education, psychology, literature, political science, sociology and others.'),
 (301,9,101,'Black America, 1619-Present',301,'Sociohistorical survey of African American experiences from the West African background to North America, from the 17th century to the present. Same as HIST 174.'),
 (302,9,102,'Researching the African Am Exp',302,'Introduction to research and documentation of the African American experience.  Approved for both S/U and letter grading.'),
 (303,9,103,'Black Women in the Diaspora',303,'Explores the historical, social, economic, cultural and political realities of black women in the African diaspora with an emphasis on the U.S., Canada, Britain, Africa and the English speaking Caribbean.  How macro structures such as slavery, imperialism, colonialism, capitalism, and globalization shaped and continue to circumscribe the lives of black women across various geographic regions.  Discussion of the multiple strategies/efforts that black women employ both in the past and present to ensure the survival of the self and the community.   Same as AFST 103 and GWS 103.'),
 (304,9,199,'Undergraduate Open Seminar',304,'May be repeated.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (305,9,201,'US Racial & Ethnic Politics',305,'Same as LLS 201, and PS 201. See PS 201.'),
 (306,9,211,'Intro to African-American Film',306,'Same as CINE 211.  See CINE 211.'),
 (307,9,220,'Intro to Research Methods AfAm',307,'Introduction to various methodologies to be employed in the interdisciplinary field of African American/Africana studies.  Access to a personal computer and SPSS software is required.   Prerequisite: AFRO 100.'),
 (308,9,224,'Humanist Persp of Afro-Am Exp',308,'Presents the Afro-centric world view as it was manifested in traditional African society and in the Afro-American slave community. Shows that this world view merged with European notions of art and humanity, as revealed in modern Afro-American literature, art, and music. Same as CWL 226.  Approved for both S/U and letter grading.  Prerequisite: AFRO 100 or consent of instructor.'),
 (309,9,225,'Race and Ethnicity',309,'Same as SOC 225. See SOC 225.'),
 (310,9,226,'Black Women Contemp US Society',310,'Sociological perspective of the experience of African American women in the contemporary United States.  Specifically, an examination of relationships between the economy, state policy, culture, work and motherhood for this demographic group.  Same as GWS 226 and SOC 223.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (311,9,231,'Lang Diff Dis: American Persp',311,'Same as SHS 231.  See SHS 231.'),
 (312,9,243,'Pan Africanism',312,'Same as AFST 243, PS 243, and SOC 267. See PS 243.'),
 (313,9,259,'Afro-American Literature I',313,'Same as CWL 259, and ENGL 259. See ENGL 259.'),
 (314,9,260,'Afro-American Literature II',314,'Same as CWL 260, and ENGL 260. See ENGL 260.'),
 (315,9,261,'Intro to the African Diaspora',315,'Introduction to the origin, development, and maturation of the African diaspora in the Americas and the Caribbean, beginning with the transatlantic slave trade and up to the end of the 20th century. Same as ANTH 261.'),
 (316,9,272,'Minority Images in Amer Film',316,'Same as ENGL 272. See ENGL 272.'),
 (317,9,275,'Afro-American History to 1877',317,'Same as HIST 275. See HIST 275.'),
 (318,9,276,'Afro-American Hist Since 1877',318,'Same as HIST 276. See HIST 276.'),
 (319,9,281,'Constructing Race in America',7,'Same as AAS 281, HIST 281, and LLS 281. See HIST 281.'),
 (320,9,282,'Displaced Peoples of Latin Am',319,'Same as ANTH 282. See ANTH 282.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (321,9,287,'African-American Women',320,'Same as GWS 287, and HIST 287. See HIST 287.'),
 (322,9,298,'Spec Topics African-Am Studies',321,'Seminar on selected topics with particular emphasis on current research trends.   May be repeated up to a maximum of 6 Undergraduate Hours.  Prerequisite:  AFRO 100 or AFRO 101, or consent of instructor.'),
 (323,9,310,'Race and Cultural Diversity',13,'Same as AAS 310, EPS 310, and LLS 310. See EPS 310.'),
 (324,9,315,'African American Politics',322,'Same as PS 315. See PS 315.'),
 (325,9,340,'Dancing Black Popular Cult',323,'Same as DANC 340. See DANC 340.'),
 (326,9,342,'Black Men in U.S. Society',324,'The sociological study of African American men in the contemporary United States.  Specifically, the experiences of this demographic group as it relates to the economy, state, policy, and institutions such as family, criminal justice system, and education.  Same as SOC 325.  Prerequisite: Introductory social science course.'),
 (327,9,344,'Soc Sci Persp Afro-Am Studies',325,'Focuses on unique aspects of Afro-American life through a review of social science perspectives. An interdisciplinary analysis of racial inequalities will emphasize trends in white and black racial attitudes and related social psychological issues. Critically reviews traditional Black American literature and compares it with Afro-centric perspectives  Prerequisite: AFRO 100 or equivalent; or an introductory course in sociology, economics, anthropology, political science, or history; or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (328,9,372,'Class Politics & Blk Community',326,'Exploration of the complex history of class relations among African Americans during the twentieth century, examining both the internal and external shapers of black class stratification. Considers the historical development of contemporary black \"underclass\", and the parallel expansion of the black middle class today. Same as HIST 384. Prerequisite: AFRO 101, HIST 276, or SOC 225 or consent of instructor.'),
 (329,9,373,'AfAm Cultr Politic Mid20C',327,'Focusing on African American culture and history from World War II until the early 1960\'s, topics include citizenship, migration, urban life, the African Diaspora, Civil Rights Movement, and art forms. Approved for both S/U and letter grading. Prerequisite: AFRO 100 and AFRO 101, AFRO 261, ENGL 260 or HIST 276.'),
 (330,9,378,'Race and Revolutions',328,'Focus on the relationship between race and slavery during the revolutions in American and Haiti, respectively.  We will seek to understand how the themes of slavery, revolution and race affected blacks, whites and indigenous Americans.  We will learn about life during the Revolutionary era by reading the biographies, political pamphlets and personal letters of former slaves, Revolutionaries and everyday men and women as well as historical scholarship.  Same as HIST 389.  Prerequisite: One African American Studies or History course at either the 100 or 200 level or with the consent of instructor.'),
 (331,9,380,'Black Women Hist & Cultures',329,'Same as GWS 380. See GWS 380.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (332,9,381,'Black Women and Film',330,'An examination of the contribution of Black women film directors to cinema.  The study of documentary, experimental, animated, fictional shorts, and feature films will reveal their unique approach to constructions of the intersection of race and gender.  Starting from the 1920\'s up to the present, the course considers themes, aesthetics, historical contexts, and ideological discourses presented in the films.   Same as CINE 381.  Prerequisite: College level film course or consent of instructor.'),
 (333,9,383,'Hist of Blk Women\'s Activism',331,'Examination of the history of twentieth century black women\'s activism, specifically concerned with how African American female activists have been critical to building, sustaining and leading black freedom movements.  Same as GWS 383 and HIST 383.  Prerequisite: AFRO 100 or AFRO 101 or AFRO 103 or consent of instructor.'),
 (334,9,398,'Spec Topics Afro-Am Studies',332,'Advanced seminar on selected topics with particular emphasis on current research trends. May be repeated to a maximum of 6 hours.  Prerequisite: Junior status and one of the following: AFRO 224, or HIST 275 or HIST 276, or ENGL 259 or ENGL 260.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (335,9,400,'African Diasporic Lit Americas',333,'Critical examination of the contributions of writers of African descent from the Caribbean (English, French, Spanish) and the United States. Major works of fiction, poetry, drama and essays from Cuba, Guadeloupe, Guyana, Haiti, St. Lucia, the United States and other countries are analyzed within a post-colonial theoretical framework. Same as CWL 400. 3 undergraduate hours. 4 graduate hours.  Prerequisite: AFRO 224 or AFRO 259 or AFRO 260 or consent of instructor.'),
 (336,9,407,'Slavery & Race in Latin Am',334,'Same as HIST 407. See HIST 407.'),
 (337,9,410,'Hate Crimes',335,'Hate crimes represent the manifestation of intergroup bias and aggression. Examples of these crimes will be examined while analyzing longstanding theories in social psychology Same as PSYC 410.  Prerequisite: PSYC 201 or AFRO 344 or consent of instructor.'),
 (338,9,411,'African American Psychology',336,'Introduction to the research, theories, and paradigms developed to understand the attitudes, behaviors, and psychological and educational realities of African Americans.   Same as PSYC 416.  3 undergraduate hours. 4 graduate hours.  Prerequisite: AFRO 100 or one psychology course.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (339,9,415,'Africana Feminisms',337,'Explores readings and research from the perspective of feminists throughout the African diaspora, with a focus on Black feminist thought emanating from the United States.   Same as AFST 420 and GWS 415.  3 undergraduate hours.  4  graduate hours.  Prerequisite: AFRO 103 and an additional 300 or 400-level African American Studies course or consent of the instructor.'),
 (340,9,421,'Racial and Ethnic Families',338,'Same as EPS 421, HDFS 424, and SOC 421. See EPS 421.'),
 (341,9,435,'Commodifying Difference',339,'Same as LLS 435, AAS 435, COMM 432, and GWS 435.  See LLS 435.'),
 (342,9,453,'Plantation Soc in Americas',340,'Comparative and interdisciplinary approach to study of the development of New World societies with focus on plantation agriculture from the 15th to 19th centuries. Course considers Portuguese, Spanish, British, French, and Dutch colonization. Students will study the relative importance of culture versus economy and demography in determining social structure Same as HIST 470. 3 undergraduate hours. 4 graduate hours.  Prerequisite: A survey course in early United States history and/or western civilization; junior status, or consent of the instructor.'),
 (343,9,460,'Slavery in the United States',341,'Examination of slavery in the U.S. using primary sources (slave narratives, songs and tales, plantation records, laws and newspapers) from the 18th century through emancipation.  Same as HIST 482. 3 undergraduate hours. 4 graduate hours. Prerequisite: AFRO 100 or AFRO 101 and one 300-level AFRO course.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (344,9,465,'Race, Sex, and Deviance',21,'Same as AAS 465, GWS 465, and LLS 465. See LLS 465.'),
 (345,9,466,'Race & Science',342,'Examination of the historical development of scientific theories of race, focusing on biology, anthropology, mind sciences and modern genetics. Same as HIST 483.  3 undergraduate hours. 4 graduate hours. Prerequisite:  AFRO 100 or AFRO 101 and one 300-level AFRO course.'),
 (346,9,474,'Black Freed Move, 1955-Present',343,'Presents the struggle of African Americans for self-definition, self-development, and self-determination from the inception of the civil rights movement to the contemporary period. Same as HIST 478. 3 undergraduate hours. 4 graduate hours.  Prerequisite: AFRO 101, HIST 276, or consent of instructor.'),
 (347,9,476,'The South in American History',344,'Same as HIST 477. See HIST 477.'),
 (348,9,490,'Theory in African American St',345,'Introduction to various theories and methodologies rising out of the study of the Black world based on African American intellectual traditions. 3 undergraduate hours. 4 graduate hours. Prerequisite:  AFRO 100 and one additional 400-level AFRO course, or consent of instructor.'),
 (349,9,491,'Methodology in African Amer St',346,'Introduction to various methodologies to be employed in the interdisciplinary field of African American/Africana studies.  Access to personal computer SPSS software is required.   3 undergraduate hours.  4 graduate hours.  Prerequisite: AFRO 100 and AFRO 220 and an additional 300 or 400-level African American Studies course or consent of instructor.');
