 (3949,94,508,'Seminar in Latin American Hist',3500,'(HIST 461)'),
 (3950,94,510,'Problems in African History',403,'Same as AFST 510.'),
 (3951,94,511,'Seminar in African History',404,'Same as AFST 511.  Prerequisite: One upper-level African history course.'),
 (3952,94,519,'Colonialism & Postcolonialism',691,'Same as ANTH 504. See ANTH 504.'),
 (3953,94,520,'Problems in Chinese History',2302,'Same as EALC 520.'),
 (3954,94,521,'Seminar in Chinese History',3501,'Research seminar in Chinese history designed to provide training to graduate students in research skills with an emphasis on the use of source materials in Chinese language.  Same as: EALC 522.  May be repeated to a maximum of 8 graduate hours.  Prerequisite: Proficiency in written Classical or modern Chinese, EALC 500 for EALC graduate students, and HIST 520 for History graduate students, or the permission of the instructor.'),
 (3955,94,522,'Topics in Korean History',2310,'Same as EALC 562. See EALC 562.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3956,94,526,'Problems in Japanese History',2305,'Period covered will alternate between the Early Modern (1550 - 1850) and Modern (1850 - present) eras. Same as EALC 526. May be repeated to a maximum of 8 hours.'),
 (3957,94,527,'Seminar in Japanese History',2306,'Period covered will alternate between the Early Modern (1550 - 1850) and Modern (1850 - present) eras. Same as EALC 527. May be repeated to a maximum of 8 hours if topics vary.  Prerequisite: Graduate standing in HIST, EALC, or other related discipline and reading knowledge of Japanese, or consent of instructor.'),
 (3958,94,535,'Prob Middle Eastern History',3502,'Covers, in depth, major problems of specific periods and areas and the relevant literature of Near and Middle Eastern History, which will vary from term to term. May be repeated to a maximum of 8 hours if topics vary.'),
 (3959,94,536,'Seminar in Middle Eastern Hist',3503,'Investigates research topics in Near and Middle Eastern history in accordance with students\' needs. Topics may vary from term to term. Students will prepare oral and written reports. May be repeated to a maximum of 8 hours.'),
 (3960,94,542,'Problems in Medieval History',3504,'Same as MDVL 542.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3961,94,543,'Seminar in Medieval History',3505,'Same as MDVL 543.'),
 (3962,94,544,'Prob European Hist 1350-1648',3506,'(HIST 484)'),
 (3963,94,545,'Seminar in Early Modern Europe',3507,'(HIST 417)'),
