INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3975,94,572,'Prob in US Hist Since 1815',3519,'May be repeated in the same or subsequent terms as topics vary.'),
 (3976,94,573,'Seminar Amer Hist Since 1789',3520,'(HIST 453)'),
 (3977,94,574,'Current Legal Problems',3521,'This is an umbrella course listing for specialty topics of current legal issues of interest. May be repeated.'),
 (3978,94,575,'Problems African American Hist',350,'Covers in depth, major problems in the African American experience and in the historiography of that experience, including historical periods, themes and paradigms.  Same as AFRO 501.  Approved for both letter and S/U grading.  May be repeated to a maximum of 8 hours.'),
 (3979,94,590,'History of Historiography',3522,'Introduction to the great historians from early times to the present.  Prerequisite: Graduate standing in history or consent of instructor.'),
 (3980,94,591,'History and Social Theory',3523,'Introduces recent historical work drawing upon theories and concepts from the social sciences; considers fields of inquiry which include family history, demographic history, labor history, prosopographical and entrepreneurial studies, local and regional studies, and others.'),
 (3981,94,592,'Quant Tech for Historians',3524,'Focuses on the use of quantitative techniques in historical research, exploring problems in research design, data management and computer techniques, and the evaluation of statistics used by historians.  Prerequisite: SOC 485 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3982,94,596,'Individual Research Project',3525,'Directed research in special fields; may be taken in lieu of seminars in fields in which seminars are seldom offered.'),
 (3983,94,597,'Reading Course',3526,'Directed readings in special fields. Primarily, but not exclusively, for students with a master\'s degree or equivalent, who are preparing for the preliminary examination in history and who need instruction in areas not provided by current course offerings. Approved for both letter and S/U grading.  May be repeated in the same or subsequent terms as topics vary.  Prerequisite: Consent of instructor.'),
 (3984,94,598,'Teaching of College History',3527,'May be repeated. Approved for S/U grading only. Prerequisite: Candidate for Ph.D. degree in history.'),
 (3985,94,599,'Thesis Research',3528,'Individual direction in research and guidance in writing theses for advanced degrees. May be repeated. Approved for S/U grading only.'),
 (3986,95,199,'Undergraduate Open Seminar',3529,'May be repeated.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3987,95,201,'Elementary Hindi-Urdu I',3530,'Introduction to the Hindi/Urdu language; includes conversation with a native Hindi/Urdu-speaking tutor under the direction of a linguist instructor, and a minimum of formal grammar and Devanagari writing; introduction to Arabic-Persian script by arrangement. Participation in the language laboratory is required.'),
 (3988,95,202,'Elementary Hindi-Urdu II',3531,'Second term of spoken Hindi/Urdu; includes conversation with a native Hindi/Urdu-speaking tutor under the direction of a linguist instructor, formal grammar based on conversational materials, and work on written Hindi; concentration on written Urdu by arrangement. Participation in the language laboratory is required.   Prerequisite: HNDI 201'),
 (3989,95,403,'Intermediate Hindi I',3532,'First term of second year of the Hindi language, including drill for more advanced conversational fluency; introduction to a greater variety of styles and levels of discourse and usage; and increasing study of the written language and more formal grammar.  Participation in the language laboratory is required.  5 undergraduate hours. 4 graduate hours.  Prerequisite: HNDI 202 or equivalent.'),
 (3990,95,404,'Intermediate Hindi II',3533,'Concentration on ability to engage in reasonably fluent discourse in Hindi, on comprehensive knowledge of formal grammar, and on ability to read ordinary texts in Hindi.  Participation in the language laboratory is required.  5 undergraduate hours. 4 graduate hours.  Prerequisite: HNDI 403 or equivalent');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3991,95,405,'Advanced Hindi I',3534,'Course for advanced knowledge of spoken and written Hindi.  Participation in the language laboratory is required.   Prerequisite: HNDI 404 or consent of instructor'),
 (3992,95,406,'Advanced Hindi II',3535,'Course for advanced knowledge of spoken and written Hindi with emphasis on modern Hindi literature and language.  Participation in the language laboratory is required.   Prerequisite: HNDI 405 or consent of instructor'),
 (3993,95,408,'Intro to South Asian Lit',3536,'Introduces selected literatures of South Asia in a cross-cultural and comparative perspective: emphasizes relating literary texts and trends to the historical, sociocultural, political, and literary contexts of the subcontinent. Texts for South Asian languages are offered in English translation; in addition, there will be texts by South Asian authors written in English. Knowledge of a South Asian language not required.   Prerequisite: Consent of course coordinator.'),
 (3994,96,100,'Introduction to Horticulture',3537,'Basic principles of plant growth and development as they apply to the production, marketing, and utilization of fruits, vegetables, and ornamental plants.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3995,96,105,'Vegetable Gardening',3538,'The science and art of growing vegetables and the connection between gardening and food. Topics include nutrient and pest management, history, folklore, growing requirements, and quality characteristics of vegetables. Lecture and laboratory. Credit is not given to horticulture majors. All other students receive credit for HORT 105 or HORT 364, but not for both.'),
 (3996,96,106,'Home Horticulture',3539,'Fundamentals of home gardening and the effective use of ornamentals as a part of the home environment. Subjects include the selection, culture, and use of garden annuals, biennials, perennials, bulbs, and house plants; garden tools and equipment; soil preparation; plant propagation; principles of design and planting methods; garden maintenance; use of fertilizers; pest control; training and pruning; lawn care; hybridizing; growing structures; and care of cut flowers. Not open to students in the Horticulture curriculum.'),
 (3997,96,107,'Introduction to Floral Design',3540,'Introduces the art of arranging flowers, foliages, and accessories according to the principles of design. Lecture and lab.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (3998,96,180,'Medicinal Plants and Herbology',1876,'The use of cultivated and wild plants in medicines and health products according to Eastern and Western medical traditions. Consideration of herbal medicine use from ancient times to the present, important medicinal chemicals produced by plants, and the evaluation of plant chemical products as potential human medicines. Same as CPSC 180.'),
 (3999,96,199,'Undergraduate Open Seminar',3541,'Experimental course on a special topic in horticulture. Topic may not be repeated except in accordance with the Code. May be repeated in the same or subsequent terms. No more than 12 hours may be counted toward graduation.'),
 (4000,96,226,'Introduction to Weed Science',1878,'Same as CPSC 226. See CPSC 226.'),
 (4001,96,236,'Intro to Turfgrass Management',3542,'Examines the principles and practical knowledge necessary for the establishment and maintenance of high-quality turfgrass stands for use as home lawns, golf courses, athletic fields, parks, and other commercial uses. Presents an integrated approach to management that considers conservation of resources and environmental impacts in relationship to turfgrass quality. Prerequisite: IB 103.'),
 (4002,96,240,'Plant Propagation',3543,'Examines theoriesy and methods employed in propagation of plants, emphasizing anatomical, physiological, and ecological principles involved in sexual propagation (seeds) and asexual propagation (division, cuttings, budding, grafting, tissue culture, etc.) Prerequisite: IB 103.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4003,96,246,'Floral Design I',3544,'Applies principles of design to the composition and decorative use of flowers, foliages, and accessories. Prerequisite: Enrollment in Horticulture, Human and Community Development, or Hospitality Management.'),
 (4004,96,250,'Floral Crop Quality Evaluation',3545,'Teaches students industry standards for acceptable potted flowers, potted foliage, and cut flower crops.  Students activiely participate in staging plant classes for judging, judge plant material, and justify class placement.  The top four students are invited to represent the University of Illinois in the National Intercollegiate Floral Quality Evaluation and Design Competition.   May be repeated to a total of 2 hours if topics vary.'),
 (4005,96,261,'Biotechnology in Agriculture',1880,'Same as CPSC 261. See CPSC 261.'),
 (4006,96,293,'Professional Internship',3546,'Off-campus experience in a field directly pertaining to a subject matter in horticulture.  May be repeated to a maximum of 4 hours. Approved for both letter and S/U grading. Prerequisite: Consent of academic advisor or Department Internship Coordinator.'),
 (4007,96,294,'Resident Internship',3547,'Supervised, on-campus, learning experience with faculty engaged in research. May be repeated to a maximum of 4 hours. Approved for both letter and S/U grading. Prerequisite: Consent of academic advisor or Department Internship Coordinator.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4008,96,295,'Undergrad Research or Thesis',3548,'Individual research, special problems, thesis, development and/or design work under the supervision of an appropriate member of the faculty. May be repeated in the same or subsequent terms. No more than 12 hours of special problems, research, thesis and/or individual studies may be counted toward degree. Prerequisite: Junior standing, cumulative GPA of 2.5 or above at the time the activity is arranged, and consent of instructor.'),
 (4009,96,298,'Undergraduate Seminar',3549,'Group discussion on a special topic in a field of study directly pertaining to subject matter in horticulture. May be repeated to a maximum of 12 hours. Prerequisite: Junior standing.'),
 (4010,96,301,'Woody Landscape Plants I',3550,'Systematic approach to the identification, ornamental characters, culture, and use of woody landscape deciduous trees and shrubs with special emphasis on cultivated varieties. Prerequisite: IB 102 or IB 103.'),
 (4011,96,302,'Woody Landscape Plants II',3551,'Systematic approach to the identification, ornamental characters, culture, and use of woody landscape conifers, broadleaf evergreens, vines, ground covers, and woody ornamental deciduous trees and shrubs with special emphasis on cultivated varieties.  Required field trip. Prerequisite: NRES 302 or HORT 301.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4012,96,341,'Greenhouse Mgmt and Production',3552,'Survey of topics relating to commercial greenhouse operations, management, and production. Examines design, location, and glazing of greenhouse structures; greenhouse operations such as heating, cooling, environmental control, and irrigation systems; production factors including light, temperature, root media, fertilization, watering, and integrated pest management; and management concepts such as industry trends and cost analysis. Production of fall potted crops will be emphasized. Prerequisite: NRES 201and HORT 100.'),
 (4013,96,343,'Herbaceous Plants I',3553,'Course includes identification, culture, and landscape use of herbaceous, frost-tender ornamental plants. Emphasis on flowering annuals, tropical foliage plants used for outdoor displays, and foliage plants used for interiorscaping. Elements of design will be addressed; design projects will integrate concepts. Prerequisite: IB 103.'),
 (4014,96,344,'Herbaceous Plants II',3554,'Course includes identification, cultural requirements, and landscape uses of herbaceous perennials and hardy bulbs. The design of perennial borders for continuous flowering will be emphasized. Prerequisite: IB 103.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4015,96,346,'Floristry and Floral Design II',3555,'Examines flower shop management including establishment, financing and creation of a business plan. Covers pricing, buying, delivery display, and advanced floral design skills. Lab fee required. Prerequisite: HORT 246.'),
 (4016,96,358,'Arboriculture',3556,'Evaluates criteria for woody landscape plant selection, cultivation, valuation, and maintenance; links the technical skills and practices for commercial arborists to an understanding of woody plant physiology and anatomy; emphasizes marketing and promotion of horticultural expertise. Offered in alternate years.  Prerequisite:NRES 302 or HORT 301 or concurrent enrollment and NRES 201.'),
 (4017,96,359,'Landscape Plants Production',3557,'Emphasizes woody landscape plant production, nursery operation, and nursery business management techniques; includes both traditional and computer-aided management tools; examines industry scope and diversity through nursery visits, presentations by nursery operators, and field trips. Offered in alternate years. Prerequisite: NRES 302 or HORT 301 and HORT 240.'),
 (4018,96,361,'Small Fruits and Viticulture',3558,'Technological application of biological principles to the culture of strawberry, grape, blueberry, raspberry, blackberry, currant, gooseberry, and miscellaneous small fruits. Prerequisite: HORT 100 or IB 103.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4019,96,362,'Tree Fruit Production',3559,'Examines biological principles and cultural practices involved in the growth and production of apple, pear, peach, cherry, plum, apricot, almond, and miscellaneous citrus and nut crops. Offered in alternate years. Prerequisite: HORT 100 or IB 103.'),
 (4020,96,364,'Vegetable Crop Production',3560,'Vegetable production with emphasis on cultural considerations, harvest, and handling of selected vegetable crops; integrates principles of plant growth and vegetable physiology into vegetable production schemes; covers vegetable classification, growing practices and handling. Students may not receive credit for both HORT 364 and HORT 105. Offered in alternate years. Prerequisite: NRES 201 and HORT 100.'),
 (4021,96,396,'Ug Honors Research or Thesis',3561,'Individual research, special problems, thesis, development and/or design work under the direction of the Honors advisor. May be repeated in the same or subsequent terms. No more than 12 hours of special problems, research, thesis and/or individual studies may be counted toward degree. Prerequisite: Junior standing, admission to the ACES Honors Program, and consent of instructor.'),
 (4022,96,421,'Horticultural Physiology',3562,'Horticultural crop growth is examined in relation to plant structure, environment, and cultural practices. Emphasizes environmental control of whole plant growth as influenced by the supply of the raw materials required for growth: water, carbon dioxide, radiant energy, including the influence of temperature and photoperiod on plant growth and development. The shoot and root interactions with the environment are characterized relative to cultural practices. Prerequisite: HORT 100 or IB 103 and junior standing.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4023,96,422,'Plant Physiology Laboratory',1914,'Same as CPSC 485, and IB 422. See IB 422.'),
 (4024,96,436,'Perennial Grass Ecosystems',1889,'Examins different levels of ecological organization in perennial grass ecosystems. Provides advanced study for students in turfgrass management. Cultural programs are derived from an understanding of interrelationships between different components of the ecosystem. Field trips. Same as CPSC 416. Offered in alternate years. Prerequisite: HORT 236.'),
 (4025,96,441,'Floricultural Crops Production',3563,'Examines the commercial production cycles of floricultural crops grown as potted flowering plants, cut flowers, and bedding plants. Cultural practices are derived from an understanding of the interrelationships among environmental conditions, species requirements, production systems, and floricultural physiology. Crop scheduling, production and cost analysis are emphasized in an applied setting. Field trip required. Prerequisite: HORT 240 and HORT 341.'),
 (4026,96,442,'Plant Nutrition',3564,'Mechanisms and factors affecting the absorption, transport, distribution, and functions of the essential elements required by higher plants. Offered in alternate years. Prerequisite: NRES 201 and IB 334 or IB 420.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4027,96,447,'Horticultural Plant Breeding',3565,'Methodology, objectives, and constraints of breeding for improved cultivars of flowers, woody ornamentals, turfgrasses, fruits, and vegetables. Emphasis on breeding objectives unique to horticultural commodities such as color, appearance, flavor, shelf-life, nutritional value, and other characteristics that determine product quality. Offered in alternate years. Prerequisite: CPSC 352.'),
 (4028,96,450,'Landscape Contracting',3566,'Interpreting landscape plans; writing landscape installation and construction specifications; bidding and estimating costs and quantities of landscape construction materials; landscape surveying techniques; selecting materials, preparing the site and constructing landscape structures; evaluating landscape business management practices. Field trip required. Students may not receive credit for both HORT 450 and LA 343. Prerequisite: NRES 302 or HORT 301.'),
 (4029,96,453,'Principles of Plant Breeding',1903,'Same as CPSC 453. See CPSC 453.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4030,96,455,'Residential Site Design I',3567,'Theory and practice of landscape design at a residential scale. Emphasis on developing graphic communication techniques, design vocabulary, and site design skills that use plants, paving, and garden structures. Prerequisite: HORT 302.'),
 (4031,96,456,'Residential Site Design II',3568,'Emphasis on the design process, comprehensive development of residential sites, advanced graphic communication techniques, and theories regarding human-environment interactions. Prerequisite: HORT 455.'),
 (4032,96,464,'International Hort Products',3569,'Survey of the international trade in and production of horticultural foods, beverages, herbs, spices, floricultural crops, interior plants, and landscape plants. Important export and import crops will be discussed. Legal and environmental issues are explored. Term project required. Prerequisite: CPSC 112, or HORT 100 or IB 103.'),
 (4033,96,465,'Ethics in Biotechnology',518,'Covers principles of ethics related to developments in biotechnology, the impact of biotechnology on environments, health, and food, and societal perception and conflict in addressing biotechnology. The course includes discussion, debate, and conflict resolution. Same as ANSC 465 and CPSC 465. Prerequisite: CPSC 261 or CPSC 265.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4034,96,466,'Growth and Dev of Hort Crops',3570,'Factors affecting growth, development, and quality of horticultural crops, such as photoperiodism, growth regulators, and carbon dioxide levels. Prerequisite: CHEM 104 and NRES 426 or HORT 421 or IB 420.'),
 (4035,96,467,'Postharvest Phys of Hort Crops',3571,'Physiology, biochemistry, and anatomy of fruits and vegetables during development, maturation, and ripening in situ and in storage. Offered in alternate years. Prerequisite: IB 103 and CHEM 104.'),
 (4036,96,482,'Plant Tissue Culture',1912,'Survey, description, and applications of cell and tissue culture strategies for plant research and production. Topics include culture environment, media composition, tissue manipulation, organogenesis, embryogenesis, somatic hybridization, bioreactors and use of these techniques for plant propagation and physiological and biochemical research. Independent research project is conducted by each student. Same as CPSC 482. Prerequisite: CHEM 232 and IB 103.'),
 (4037,96,489,'Controlling Turfgrass Pests',3572,'Principles and strategies of integrated pest management (IPM), turf management practices, and common pests of turf are examined. The biology, habitat, identifying features, and management strategies of each pest are described. Offered in alternate years. Prerequisite: HORT 236 and CPSC 270 or NRES 280; and PLPA 204.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4038,96,499,'Experimental Graduate Courses',3573,'Experimental course on a special topic in Horticulture. May be repeated in the same or separate terms as topics vary to a maximum of 12 hours.'),
 (4039,96,505,'Research Methods in Hort',3574,'Lectures, discussions, demonstrations, and laboratory exercises dealing with methods and apparatus used in horticultural research.'),
 (4040,96,566,'Plant Gene Regulation',1926,'Same as CPSC 566. See CPSC 566.'),
 (4041,96,588,'Plant Biochemistry',1930,'Same as CPSC 588, and IB 524. See CPSC 588.'),
 (4042,97,199,'Undergraduate Open Seminar',3575,'May be repeated.'),
 (4043,97,400,'Principles of HRE',3576,'Study of the basic concepts and practices of education for and about work: its philosophical foundations and historical development, mission and goals, structure and function, curricular areas of emphasis, learner audiences served and settings in which programs are conducted, and issues and trends affecting program change.'),
 (4044,97,401,'Training in Business/Industry',3577,'Study of the status of education, training and development within business and industry; includes an overview of the systemic process for planning, delivery, and evaluation of training programs; and explores major problems, trends, and issues associated with the field.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4045,97,402,'Business Principles for HRD',3578,'Study of essential business understandings, knowledge, and skills required for HRD professionals to interact effectively with others in the business community.'),
 (4046,97,411,'Instructional Design',3579,'Provides instruction and practice in the selection, organization, and preparation of content for instructional programs in business and technical settings. Provides students with a theoretical orientation to instructional design as well as the opportunity to experience the instructional design process as it applies to business and technical settings through the development of instructional materials'),
 (4047,97,412,'Instructional Techniques',3580,'Provides a research- based exploration of effective teaching techniques for instructors of business, industry, and community college technical programs. Equips students with a conceptual framework for instruction and provides guidance and experience in the planning, delivery and evaluation of instruction'),
 (4048,97,415,'Diversity in the Workplace',3581,'Assists educators, as well as trainers and managers in business and industry, to effectively recognize and understand diversity in school and work settings. Activities focus on understanding the nature of diverse populations, their unique learning needs, and potential collaborative efforts between educators and work place personnel.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4049,97,470,'Design of Learning Systems',3582,'Provides theoretical and practical learning experiences integrating the fields of Instructional Design and Instructional Technology through the study and development of technology-based learning environments'),
 (4050,97,472,'Learning Technologies',1720,'The course addresses two important needs of educators. First, educators should be aware of recent developments in the area of instructional technology. Second, educators must be able to select, develop, and effectively use appropriate instructional technologies to enhance learning and communication. To meet these needs, this course covers a wide range of instructional technologies that are used for instructional and administrative purposes. Traditional instructional media are considered in the course although significant emphasis is placed on more recent developments that involve the use of the computer and its applications in education. Instructional technologies such as computer-based instruction, computer-based testing, distance learning, interactive video, and intelligent instructional technologies are covered. Through course readings, discussions, and projects, students in the course are expected to gain skills in choosing appropriate instructional technologies, designing effective presentations that rely on those technologies, and properly using instructional technologies to enhance communication with an audience. Same as CI 484. Prerequisite: HRE 411 or equivalent course in instructional design.'),
 (4051,97,474,'Evaluating Learning Technology',2806,'Same as EPSY 474. See EPSY 474.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4052,97,475,'Project Management for HRE',3583,'Study of the basic principles and techniques related to managing personnel, time and resources in education and training projects. Through group and individual activities, including case study review and project simulation, students will apply project management tools and techniques in international training and educational setting.'),
 (4053,97,490,'Issues and Developments in HRE',3584,'Special course for experimentation or for seminar on topics not treated by regularly scheduled courses. Topics vary; consult Class Schedule for specific section offerings. May be repeated to a maximum of 8 hours.'),
 (4054,97,491,'Professional Skill Development',3585,'Designed to teach practitioner-oriented skills in specialized areas of human resource education. Topics vary; consult Class Schedule for specific section offerings. May be repeated to a maximum of 8 hours.'),
 (4055,97,492,'Supervised Internship in HRE',3586,'While employed in approved cooperating organizations, students observe the relationship between HRE and organizational performance.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4056,97,495,'Special Study & Investigation',3587,'Offers opportunity for an individual to study, on or off campus, selected problems, trends, and new developments or to conduct specialized technological investigations for the improvement of instructional programs in areas related to education and training. May be repeated to a maximum of 8 hours.'),
 (4057,97,501,'The Community College',2720,'Same as EOL 573. See EOL 573.'),
 (4058,97,510,'Expertise and Its Development',3588,'Covers developments in cognitive-based research as they relate to the design and implementation of technical instruction. Through readings, discussions, and projects, students gain an understanding of how people learn complex information, how skills are developed, and how instructors can better guide their students toward the development of expertise.'),
 (4059,97,517,'Community College Program Dev',3589,'Synthesizes selected sociological, psychological, and epistemological foundations for curriculum development in education and training; application of theories from fundamental disciplines to practice in existing and emerging curricula involving perceptual and psychomotor learning.'),
 (4060,97,530,'Organization Development',3590,'Addresses the history, concepts, theories, and techniques of Organization Development as applied in Human Resource Education; emphasis on creating, managing, and sustaining system-wide change in public and private organizations; organized around diagnosis, implementation, and evaluation of individual, team, and organization-wide interventions');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4061,97,531,'Quality Process Improvement',2725,'Examines quality and process improvement philosophies, theories, and strategies as they apply to the practice of professionals in human resource education. Based on a critical analysis of the historical antecedents, theoretical foundations, and empirical research results of Total Quality Management (TQM) and Continuous Process Improvement (CPI), students will be able to apply improvement strategies and evaluate the merits and limitations in public and private settings. Same as EOL 587.'),
 (4062,97,532,'Strategic HRD',3591,'Study of the theories, research, and applications of strategic human resource development in a variety of organizational settings.'),
 (4063,97,533,'Management of HRD',3592,'Study of management fundamentals related to planning, organizing, staffing, leading, and controlling the HRD function in organizations.'),
 (4064,97,534,'Economics of Human Resources',3593,'Same as LIR 545. See LIR 545.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4065,97,535,'Consulting in HRD',3594,'Analysis of key elements of consulting in the human resource development profession. Emphasis is placed on subject matter expertise, consulting skills, marketing, organization, business management, communication, and life/work balance. The course examines both the internal and external consulting practices. Issues of education and training of consultants for work in industry, business, government, and non-profit sectors are covered in detail'),
 (4066,97,536,'International HRD',3595,'Course is designed to provide insights into international HRD at macro and micro levels. Course will cover: cross-cultural issues in international HRD; design and delivery of international HRD programs; HRD practices and programs in different regions of the world; national HRD programs; expatriate training and training in multinational corporations.'),
 (4067,97,570,'Technology Transfer',3596,'Examines the processes involved in transferring technologies from one organization or culture to another. Special emphasis is placed on the change process and its relationship to the diffusion of technology. Concludes with the identification of strategies that can be used to facilitate successful technology transfer. Students are required to complete extensive readings on the relevant topics, participate in discussions, and examine case studies related to technology transfer'),
 (4068,97,580,'Disciplined Inquiry in Educ',3597,'Provides an analysis and synthesis of disciplined inquiry in human resource education including an historical perspective, formulation of the research process, and the utilization and communication of research.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4069,97,582,'Thesis Dissert Proposal Prep',3598,'Designed to take students through the entire process of proposal development, this course is intended for masters or doctoral students who are ready to prepare a thesis or dissertation proposal. Students will learn to use a systematic and comprehensive approach to develop the research proposal and how each step in the research process is related.'),
 (4070,97,585,'Program Evaluation',3599,'Theory and techniques of evaluation in cognitive, affective, and psychomotor domains at different educational levels and settings; development and analysis of activities and instruments for students and program evaluation, follow-up studies, and interpretation of results for self-evaluation and for administrative decision making.'),
 (4071,97,590,'Seminar for Advanced Students',3600,'Seminar open to persons who have been admitted for doctoral study in human resource education May be repeated to a maximum of 8 hours.  Approved for both letter and S/U grading.'),
 (4072,97,591,'Field Study & Thesis Seminar',3601,'Assists doctoral candidates in planning field studies and thesis problems; students present their studies at each of four stages: (1) the inception, delimitation, tentative design stage; (2) the proposed design stage; (3) the revised design stage; and (4) the final design stage. Students are expected to analyze critically all presentations.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4073,97,592,'Special Topics in HRE',3602,'Introduction to significant problems, points of view, and trends in the field; explores significant research relating to organization, content, and techniques. Topics vary; consult Class Schedule for specific section offerings. May be repeated with approval.'),
 (4074,97,595,'Independent Study',3603,'Offers opportunity and challenge of self-directive, independent study, that is, develops the individual\'s ability as an independent student and enables the student to pursue needed study in a field in which appropriate courses are not being offered during a given term. May be repeated with approval.  Prerequisite: Approval of study outline by adviser prior to enrollment.'),
 (4075,97,599,'Thesis Research',3604,'Individual direction of research and thesis writing. May be repeated. Approved for S/U grading only.'),
 (4076,98,141,'Intro to American Civ I',3605,'Introduction to the multidisciplinary study of major aspects, events, and periods of the American experience; includes a series of topics, each focusing on one society, movement, or historical event as reflected in literature, art, history, and politics.'),
 (4077,98,142,'Intro to American Civ II',3606,'Continuation of HUM 141.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4078,98,191,'Freshman Honors Tutorial',3607,'Study of selected topics on an individually arranged basis. Open only to honors majors or to Cohn Scholars. May be repeated up to 1 time(s).  Prerequisite: Consent of departmental honors adviser.'),
 (4079,98,199,'Undergraduate Open Seminar',3608,'May be repeated.'),
 (4080,98,390,'Individual Study',3609,'Supervised reading and research on interdisciplinary humanities topics chosen by the student in consultation with a faculty member. May be repeated to a maximum of 8 hours.  Prerequisite: Consent of humanities adviser (an approved Learning Agreement must be submitted to 270 Lincoln Hall, 702 S. Wright Street, Urbana, not later than the second week of the semester or the first week of the summer session).'),
 (4081,98,395,'Special Topics',3610,'Interdisciplinary topics in the humanities; topics vary, but are normally related to one of the options in the humanities major. May be repeated if topics vary. Students may register in more than one section per term.'),
 (4082,98,397,'Special Topics Junior',3611,'Interdisciplinary seminar and tutorial in selected topics related to one of the options in the humanities major. May be repeated to a maximum of 6 hours.  Prerequisite: Junior standing and consent of humanities adviser (tutorial students must submit an Approved Learning Agreement to 270 Lincoln Hall, 702 S. Wright Street, Urbana, not later than the second week of the semester or the first week of the summer session).');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4083,98,471,'Intro Second Lang Learn Tchg',1612,'Same as CHIN 471, FR 471, GER 469, JAPN 471, LAT 471, RUSS 471, and SPAN 471. See SPAN 471.'),
 (4084,98,482,'Computer Foreign Lang Tchg',1813,'Theory and practice of computer-assisted instruction, with special emphasis on problems and techniques of foreign-language instruction. General principles; survey of existent and probable future CAI systems; and practical experience with lesson design and programming on the IBM and Macintosh personal computers. Linguistics majors are advised to complete LING 406 before registering for this course. Same as CLCV 482, EIL 482, FR 482, GER 482, ITAL 482, LING 486, PORT 482, SLAV 482, and SPAN 482.  Prerequisite: Two years college language or equivalent, and consent of instructor.'),
 (4085,98,488,'French & Comparative Cinema I',1779,'Same as CINE 488, CWL 488, and FR 488. See FR 488.'),
 (4086,98,489,'French & Comparative Cinema II',1780,'Same as CINE 489, CWL 489, and FR 489. See FR 489.'),
 (4087,98,492,'Senior Thesis',3612,'Individual research for majors in humanities leading to the completion of a thesis. May be repeated to a maximum of 8 hours.  No graduate credit.  Prerequisite: Senior standing, a declared option in humanities major, and consent of advisor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4088,98,495,'Special Advanced Topics',3613,'Offers interdisciplinary topics in the humanities; topics vary, but normally relate to the interdisciplinary areas of study within the humanities major or to the special humanities facilities (e.g., the Language Learning Laboratory). 3 undergraduate hours. 4 graduate hours. May be repeated as topics vary to a maximum of 6 undergraduate hours, or 8 graduate hours. Prerequisites will vary according to topic. See Schedule.'),
 (4089,98,496,'Advanced Topics',3614,'Offers interdisciplinary topics in the humanities; topics vary.  Approved for both letter and S/U grading.  May be repeated in the same or subsequent semesters to a maximum of 6 undergraduate hours or 8 graduate hours.  Prerequisite: Prerequisites will vary according to topic.  See Schedule.'),
 (4090,98,498,'Special Topics Senior',3615,'Interdisciplinary seminar and tutorial in selected topics related to one of the options in the humanities major. 3 undergraduate hours. No graduate credit. May be repeated to a maximum of 6 hours.  Prerequisite: Senior standing and consent of humanities adviser (tutorial students must submit an approved Learning Agreement to 270 Lincoln Hall, 702 S. Wright Street, Urbana, not later than the second week of the semester or the first week of the summer session.'),
 (4091,99,100,'Biological Sciences',3616,'Introduction to biology for the non-major. In-depth focus on three contemporary problems-maintaining a livable environment, issues of human health, and evolution. Credit is not given for both IB 100 and IB 101.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4092,99,101,'Biological Sciences',3617,'Introduction to biology for the non-major, including laboratory. In-depth focus on three contemporary problems-maintaining a livable environment, issues of human health, and evolution. Emphasis in laboratory is on science as a process. Lecture and laboratory. Credit is not given for both IB 101 and IB 100.'),
 (4093,99,102,'Plants, People & Environment',3618,'Designed primarily to give the nonscience student an introduction to plants, and their biological, economic, and environmental importance in today\'s world. Discussions and demonstrations emphasize practical aspects of plant biology and science as they relate to current environmental issues. Lecture and discussion. Credit is not given for both IB 102 and IB 103.'),
 (4094,99,103,'Introduction to Plant Biology',3619,'Basic principles of growth and form, physiology, genetics, evolution, and ecology in plant biology. Lecture and laboratory. Credit is not given for both IB 103 and IB 102.'),
 (4095,99,104,'Animal Biology',3620,'Introductory zoological concepts with emphasis on the diversity and comparative anatomy of animals and the fundamentals of physiology, genetics, evolution, and behavior.  Lecture and laboratory. The laboratory includes vertebrate dissection.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4096,99,105,'Environmental Biology',3621,'Introduction to ecological principles in relation to understanding environmental problems; emphasizes impacts upon ecosystems by human activities such as air and water pollution, usage of pesticides and pest control measures, expansion of agriculture in tropics and arid regions, harvesting the oceans, and development of energy sources.'),
 (4097,99,109,'Insects and People',3622,'Fundamentals of insect biology as reflected in human culture; insect physiology, ecology, and behavior discussed in the context of art, literature, movies, medicine, sports, law, and history. Optional two-hour laboratory for 1 hour additional credit.'),
 (4098,99,150,'Organismal & Evolutionary Biol',3623,'Introduction to function, genetics, and evolution of organisms, and their ecology and diversity.'),
 (4099,99,151,'Organismal & Evol Biol Lab',3624,'Topics follow lecture topics in IB 150 and include labs in ecology, plant and animal function, and genetics and evolution. Designed for non-majors needing a year of biology with lab. Credit for IB 151 cannot be counted for Integrative Biology or Molecular and Cellular Biology majors. Prerequisite: Credit or concurrent registration in IB 150.'),
 (4100,99,199,'Undergraduate Open Seminar',3625,'Approved for both letter and S/U grading.  May be repeated to a maximum of 5 hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4101,99,201,'Genetics and Evolution',3626,'Principles of Mendelian genetics, origins of genetic variation, natural selection, and evolution. The laboratory emphasizes genetics, biodiversity, plant and animal phylogeny, and evolution.  Prerequisite: IB 150 and MCB 150.'),
 (4102,99,202,'Structure and Function',3627,'How organisms function in acquiring, processing, and allocating resources in the face of environmental constraints. The laboratory emphasizes the variation among organisms in their form and function.  The laboratory includes vertebrate dissection. Prerequisite: IB 150 and MCB 150.'),
 (4103,99,203,'Ecology',3628,'The links between evolution and ecology, population dynamics, community structure and function, and ecosystem function on local and global scales. Basic ecology needed to understand environmental problems and to conserve biodiversity. Investigations in both field and laboratory included.  Prerequisite: IB 150 and MCB 150.'),
 (4104,99,220,'Applied Entomology',1882,'Same as CPSC 270, and NRES 270. See CPSC 270.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4105,99,243,'Sociality of the Great Apes',577,'Same as ANTH 243. See ANTH 243.'),
 (4106,99,270,'Evolution of Molecules & Cells',3629,'The major evolutionary transitions of biomolecules and cells including: energy acquisition and metabolism; information inheritance, system regulation, and genomes; the origin of life and of the prokaryotic cell, eukaryotic cell, and multicellularity.  Lecture and laboratory.   Students may not receive credit for this course and MCB 250 or MCB 252.  Prerequisite: Admission to the IB honors biology option; credit or concurrent registration in organic chemistry.'),
 (4107,99,271,'Organismal Biology',3630,'Integrated study of the diversity and structure and function of plants and animals in evolutionary and environmental contexts.  Conceptual themes and techniques of molecular and cellular levels of biological organization will be integrated as well.  Lecture and laboratory.  Students may not receive credit for this course and IB 202.  Prerequisite: IB 270; good standing in the honors biology option.'),
 (4108,99,280,'Forest and Landscape Insects',3631,'Same as NRES 280. See NRES 280.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4109,99,301,'Evolutionary Biology',3632,'Introduction to the evidence for evolution and the origin and types of genetic variation, stressing various modes of selection and modern observations and experiments illustrating the evolutionary process.  Prerequisite: IB 201; or consent of instructor.'),
 (4110,99,311,'Behavior of Domestic Animals',490,'Same as ANSC 363. See ANSC 363.'),
 (4111,99,331,'Biology of Reproduction',486,'Same as ANSC 331. See ANSC 331.'),
 (4112,99,334,'Organismal Biology of Plants',3633,'Physiological and morphological attributes that underlie the biosynthesis, growth, and reproduction of flowering plants in relation to the environment.  Prerequisite: IB 102, IB 103, or IB 150; CHEM 104 and CHEM 105; or consent of instructor.'),
 (4113,99,335,'Systematics of Plants',3634,'Introduces the principles and methods of the identification, naming, classification, systematics, and evolution of flowering plants; includes a survey of selected flowering plant families with information on their interrelationships.  Prerequisite: One of the following: IB 100, IB 101, IB 102, IB 103, or IB 150; consent of the instructor.'),
 (4114,99,348,'Fish and Wildlife Ecology',3635,'Same as NRES 348. See NRES 348.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4115,99,363,'Plants and Their Uses',608,'Consideration of plants which are useful or harmful: their origins and history, botanical relationships, chemical constituents which make them economically important, and their roles in prehistoric and modern cultures and civilizations. Same as ANTH 378.  Prerequisite: IB 102, IB 103, or IB 150; or consent of instructor.'),
 (4116,99,372,'Ecology and Evolution',3636,'Integrated study of ecology, population genetics, and evolution.  Conceptual themes and techniques from the molecular, cellular, and organismal levels of biology will be integrated as well.  Lecture, laboratory, and field work.   Students may not receive credit for this course and either IB 201 or IB 203.  Prerequisite: IB 271; good standing in the IB honors biology option.'),
 (4117,99,390,'Introductory Research',3637,'Laboratory and/or field research and/or reading supervised by faculty members in the School of Integrative Biology. May be repeated. Approved for S/U grading only. No more than a combined maximum of 10 hours of IB 390 or IB 490 may count toward graduation for IB majors.  Prerequisite: Consent of instructor.'),
 (4118,99,402,'Molecular Evolution',3638,'Introduction to evidence for evolutionary change at the molecular and cellular levels of organization; origin and changes in macromolecules, genes, cells, and their organelles emphasized.  Prerequisite: IB 201; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4119,99,403,'Behavioral Inference & Fossils',644,'Same as ANTH 446.  See ANTH 446.'),
 (4120,99,405,'Ecological Genetics',3639,'Study of the genetics of natural populations, stressing empirical observations and experiments. Emphasis on recent theories of genotype/environmental interactions and their relationship to evolutionary processes.  Prerequisite: IB 201; or consent of instructor.'),
 (4121,99,406,'Evolution of Adaptive Systems',3640,'Evolutionary mechanisms underlying adaptations; emphasizes origin and subsequent modification of major complex systems; pertinent evidence considered from several disciplines, including population biology, developmental biology, structural analysis and paleobiology.  Prerequisite: IB 150 and MCB 150; IB 201 recommended; or consent of instructor.'),
 (4122,99,407,'Plant Molecular Biology',3641,'Presents the basic concepts of plant gene expression, the structure and expression of the three plant genomes, and special topics on plant vectors, plant viruses, and transposable elements. Same as MCB 438. Offered in alternate years. Prerequisite: MCB 250 or consent of instructor.'),
 (4123,99,416,'Population Genetics',509,'Same as ANSC 446. See ANSC 446.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4124,99,417,'Quantitative Genetics',510,'Same as ANSC 447. See ANSC 447.'),
 (4125,99,420,'Plant Physiology',1913,'General course concerned with plant functions, including water relations, mineral nutrition, metabolism, growth, and reproduction. Same as CPSC 484.  Prerequisite: IB 103 or IB 150 and MCB 150; CHEM 232; IB 202 recommended; or consent of instructor.'),
 (4126,99,421,'Photosynthesis',1331,'Same as BIOP 432, and CPSC 489. See BIOP 432.'),
 (4127,99,423,'Plant Anatomy',3642,'Lecture and laboratory course dealing with the structural characteristics of mature and developing cells, tissues, and organs of vascular plants, with special emphasis on the vegetative parts of flowering plants.  Prerequisite: IB 103 or IB 150; IB 202 recommended; or consent of instructor.'),
 (4128,99,424,'Plant Development',3643,'Mechanisms underlying plant development: cytodifferentiation and the cell cycle, regulation of gene expression, induction, determination, morphogenesis, and pattern formation. Offered in alternate years. Prerequisite: IB 103 or IB 150; and MCB 150; IB 202 recommended; or consent of instructor.'),
 (4129,99,425,'Plant Secondary Metabolism',3644,'The natural products of plants with emphasis on biosynthesis, distribution and function of relevant compounds of ecological, pharmacological, toxicological, and economic interest.  Offered in alternate years. Prerequisite: MCB 354 or MCB 450; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4130,99,426,'Env and Evol Physl of Animals',3645,'Physiological adaptations of invertebrate and vertebrate animals to diverse aquatic and terrestrial environments and the extreme habitats embodied therein (hydrothermal, deep sea, salt lakes, desert and polar regions).  Prerequisite: IB 150 and MCB 150; CHEM 232; IB 202 recommended; or consent of instructor.'),
 (4131,99,427,'Insect Physiology',3646,'The principal physiological and biochemical functions of insects. Lecture and laboratory.  Offered in alternate years.  Prerequisite: IB 150 and MCB 150; IB 201, IB 202, and IB 203; or consent of instructor.'),
 (4132,99,428,'Primate Form and Behavior',641,'Same as ANTH 443. See ANTH 443.'),
 (4133,99,429,'Animal Behavior',519,'Introductory course emphasizing how patterns of behavior promote survival, change through evolution, and are modified by the environment. Same as ANSC 466, and ANTH 442.  Prerequisite: IB 150 and MCB 150; or consent of instructor.  Credit is not given for both IB 429 and ANSC 363.'),
 (4134,99,430,'Hormones and Behavior',3647,'Same as NEUR 444, and PSYC 444. See PSYC 444.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4135,99,431,'Behavioral Ecology',3648,'In-depth examination of areas of current interest at the interface of behavior, ecology, and evolution; focuses on communication, foraging, and social behavior. Offered in alternate years. Prerequisite: IB 429; or consent of instructor.'),
 (4136,99,432,'Genes and Behavior',3649,'Concepts, methods, and problems in the analysis of the relationship between genes and behavior, the complex neurobiological processes that mediate action on behavior, in appropriate ecological and evolutionary contexts.   Same as PSYC 432, ANTH 432, and NEUR 432.  Prerequisite: IB 150 and either IB 201 or IB 429 or consent of instructor.'),
 (4137,99,433,'Comparative Vertebrate Morphol',3650,'Comparative structure, evolution, and classification of chordate animals emphasizing vertebrates.  Strong attention to relationships of fossils to present animals.  Function of parts, their evolution, and some developmental aspects.  Lab involves dissection of vertebrates.  Lecture and Laboratory.   Prerequisite: IB 201, IB 202 or consent of instructor.'),
 (4138,99,440,'Plants and Global Change',1894,'Same as CPSC 431, and NRES 431. See CPSC 431.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4139,99,441,'Plant Ecology',3651,'Principles of ecology in relation to plants as individuals, populations, and communities, and as a component of ecosystems.  Lecture and field course.  5 undergraduate hours. 4 graduate hours.  Prerequisite: IB 203; or consent of instructor.'),
 (4140,99,442,'Functional Ecology of Trees',3652,'Lecture course providing a synthesis of the physiological and morphological mechanisms defining the ecological performance of trees and other woody plants in natural communities. Practical experience with field physiological measurements will be provided. Offered in alternate years. Same as NRES 442. Prerequisite:  IB 420, IB 441, or NRES 426 recommended; or consent of instructor.'),
 (4141,99,443,'Evolutionary Ecology',3653,'Emphasizes the evolution of life-history strategies in plants and animals (reproductive rates, life cycles, sex ratios, breeding and mating systems) and the coevolution of animals and plants (pollination, dispersal, and herbivory). Offered in alternate years. Prerequisite: IB 203 or equivalent; or consent of instructor.'),
 (4142,99,444,'Insect Ecology',3654,'Discussion of the practical and theoretical aspects of ecology in relation to insects as individuals, populations, and communities; emphasis on the role of insects in the environment. Offered in alternate years. Lecture only, 3 hours; with laboratory, 4 hours. Prerequisite:  IB 150 and MCB 150 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4143,99,445,'Chemical Ecology',3655,'Chemical bases of ecological interactions among organisms; topics include the chemical structures and functions of messenger compounds important in inter- and intraspecific interactions among plants, insects, higher animals, fungi, microbes, and their environments. Offered in alternate years. Prerequisite: IB 150 and MCB 150 and CHEM 232; or consent of instructor.'),
 (4144,99,446,'Tropical Ecology',3656,'Ecological principles as they apply to plants, animals, and humans in tropical habitats; topics include climate, soils and ecosystem processes; seasonality and habitat diversity; community structure, species diversity, and plant-animal interactions; regrowth following natural and human disturbances; and human use and abuse of tropical forests. Offered in alternate years. Prerequisite: IB 203; or consent of instructor.'),
 (4145,99,447,'Field Ecology',3657,'Study of habitats in various sections of North America during spring vacation or intersession.  Outdoor cooking and camping; transportation in University cars. May be repeated to a maximum of 3 hours.  Prerequisite: IB 203; or consent of instructor.'),
 (4146,99,449,'Limnology',3658,'Fresh water biology; study of the lake, pond, and river with emphasis on the physical environment as well as on the plants and animals which live in fresh water. Lectures, discussions, laboratory, and field work. Offered in alternate years. 5 undergraduate hours. 4 graduate hours.  Prerequisite: IB 203; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4147,99,450,'Stream Ecology',1400,'Same as CEE 432. See CEE 432.'),
 (4148,99,451,'Conservation Biology',1897,'Synthesis of conservation biology with an emphasis on the preservation of biological diversity and its evolutionary potential. Laboratory includes an introduction to the use of modern molecular techniques in conversation biology, computer simulation modeling, and field conservation problem solving. Same as CPSC 436, and ENVS 420.  Offered in alternate years. Prerequisite: IB 203 or consent of instructor.'),
 (4149,99,452,'Ecosystem Ecology',3659,'Distribution and structure of ecosystems on earth; integration of multiple disciplines to gain a holistic view of ecosystem function; ecosystem concepts as they apply to understand natural and anthropogenic environmental change. Same as NRES 462.  Prerequisite:  IB 203 or IB 441; CHEM 102 and CHEM 104; or consent of instructor.'),
 (4150,99,453,'Community Ecology',3660,'The direct and indirect interactions among species that determine the structure and composition of plant and animal communities.  Emphasis will be on the maintenance of species diversity and its consequences at both local and regional scales. Same as NRES 452.  Offered in alternate years. Prerequisite: IB 203, IB 441, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4151,99,454,'Latin American Ethnobotany',615,'Same as ANTH 401, HIST 409, and LAST 401. See LAST 401.'),
 (4152,99,460,'Introduction to Entomology',3661,'Integrated studies of the principal morphological, physiological, ecological and behavioral relationships among insects.  Lecture and laboratory.   An insect collection will be required for 4 hours credit.  Prerequisite:  IB 150; or consent of instructor.'),
 (4153,99,461,'Ornithology',3662,'Structure, function, ecology, behavior, and evolution of the birds of the world; laboratory devoted to anatomy and identification; and field studies devoted to identification and behavior of birds. Independent research project and two optional weekend field trips. Same as NRES 461. Offered in alternate years. Prerequisite: IB 203; or consent of instructor.'),
 (4154,99,462,'Mammalogy',3663,'Classification, distribution, structure, function, life history, evolution and identification of mammals. Lecture/discussions, laboratory and field work. The laboratory includes vertebrate dissection.  Offered in alternate years. Prerequisite: IB 202 and IB 203; or consent of instructor.'),
 (4155,99,463,'Ichthyology',3664,'Classification, anatomy, ecology, behavior, distribution, and evolution of fishes of the world. Emphasis is on morphological, ecological, and behavioral diversification of fishes in a phylogenetic context. Laboratory devoted to anatomy and identification.     Offered in alternate years.  Prerequisite: IB 201; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4156,99,464,'Herpetology',3665,'Classification, diversity, structure, function, ecology, behavior and evolution of amphibians and reptiles. Laboratory devoted to anatomy and identification. Offered in alternate years.  Prerequisite: IB 201; or consent of instructor.'),
 (4157,99,466,'Invertebrate Zoology',3666,'Invertebrates; structure and development; application of biological principles; specific and comparative morphology of the invertebrates; and coordination of structure and function, origin, development, and life histories. Lecture and laboratory. Offered in alternate years. Prerequisite: IB 201; IB 203 recommended; or consent of instructor.'),
 (4158,99,467,'Principles of Systematics',3667,'Comprehensive survey of the theory and methodology of systematics as they are applied today to all groups of organisms, with a practical experience in the acquisition and analysis of systematic data.  Offered in alternate years. Prerequisite: IB 201, and IB 335 or IB 468; or consent of instructor.'),
 (4159,99,468,'Insect Classification and Evol',3668,'Analytical survey of the classification and evolution of the orders and principal families of insects, with practical experience in the identification of insects at these taxonomic levels; field trips required. Lecture and laboratory.  Offered in alternate years.  Prerequisite: IB 460 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4160,99,469,'Evolutionary Survey of Plants',3669,'Lecture and laboratory course dealing with the structure, reproduction, and evolutionary origins of land plants as represented by living mosses, liverworts, ferns, lycopods, horsetails, conifers, cycads and flowering plants.  Prerequisite: IB 102 or IB 103 or IB 150;  IB 201 recommended; or consent of instructor.'),
 (4161,99,470,'Field Botany',3670,'Identification and classification of native and naturalized flowering plants of eastern North America. Lecture and laboratory.  Offered in the summer session only. Prerequisite: IB 103 or IB 150; or consent of instructor.'),
 (4162,99,471,'General Mycology',3671,'Structure, classification, and identification of fungi, including those of economic importance. Lecture and laboratory.  Offered in alternate years. Prerequisite: IB 150 and MCB 150; IB 201 recommended; or consent of instructor.'),
 (4163,99,474,'Plant Proteomics',1906,'Broad introduction to plant proteomics, including a survey of contemporary methods and their applications for protein identification and the study of posttranslational modifications and protein-protein interactions. These topics will be studied in relation to the proteomes of organs and subcellular compartments, stress, nutrition, and signal transduction. Same as CPSC 468. Prerequisite: MCB 354; IB 472; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4164,99,475,'Plant Metabolomics',1907,'An introduction to plant metabolomics. Metabolites are not only compounds being modified in pathways, but also are signals that connect metabolic homeostasis to growth and development of organs and cells in plants. Introduces the complexities of metabolism profiling, the dynamics of metabolite changes in plants under stress, and the current concepts on pathway tracing and elucidation. Includes an introduction to basic and advanced research methods, including the use of statistical methods and bioinformatics. Same as CPSC 469. Prerequisite: MCB 354; IB 472; or consent of instructor.'),
 (4165,99,480,'Ecological Parasitology',3672,'Ecological perspective on parasite-host associations and parasitic diseases of vertebrates. Parasite life cycles, and the major parasitic groups; protozoa, platyheminthes, nematodes and arthropods will be covered. The relation of parasitism to other ecological associations such as predation and competition will be emphasized. Offered in alternate years. Same as PATH 523.  Prerequisite: IB 203, or consent of instructor.'),
 (4166,99,481,'Biology of Disease Vectors',3673,'The major groups of arthropods and associated pathogens that affect the health and well-being of humans and other animals. Training will include identification, classification, methods of injury, habits, vector competence, and control of insects, ticks and mites that are predators, parasites, or vectors of disease. The course will examine and use both classical and molecular technologies to address epidemiological, ecological, and diagnostic factors associated with arthropod-borne diseases. Graduate students required to write a term paper. Offered in alternate years. Prerequisite: One year college biology, IB 460; or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4167,99,482,'Insect Pest Management',1911,'The principles underlying the control of important insect pests of agriculture and of human and animal health; emphasis on integrated pest management involving a systems approach which combines biological, cultural, and chemical suppressive factors into ecologically sound and socially and economically acceptable technology. Lecture and laboratory. Offered in alternate years. Same as CPSC 479.  Prerequisite: IB 150 or equivalent; or consent of department.'),
 (4168,99,483,'Insect Pathology',1909,'The general principles of pathology as they apply to insects; includes non-infectious and infectious diseases caused by viruses, bacteria, fungi, protozoa, and nematodes. Studies the epizootiology of naturally occurring insect disease and the use of insect pathogens as microbial control agents. Lecture and laboratory. Offered every three years. Same as CPSC 475 and NRES 443.  Prerequisite: IB 150 and MCB 150 or consent of instructor.'),
 (4169,99,484,'Biological Control',1910,'Biological control is an exercise in applied ecology that is a powerful tool in the management of natural resources and in pest management.  The course will highlight the theory behind this discipline and the current and potential applications.  Students will also get an understanding of a practical biological control program. Same as CPSC 477.  Offered in alternate years.  Prerequisite: IB 444 or IB 482; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4170,99,485,'Environmental Toxicology',1654,'Explores toxicological, environmental, public health, occupational and ecological aspects of the use and release of toxic substances in the environment; features case histories of environmental contamination that illustrate ecological, health, and social aspects of pollution; emphasizes biochemical mechanisms and ecosystem consequences. Same as CHLH 461, CPSC 435, and ENVS 431.  Prerequisite: A college chemistry course and a college biology course; or consent of instructor.'),
 (4171,99,486,'Pesticide Toxicology',2686,'Examines the biological effects of major classes of insecticides and herbicides, and of selected individual fungicides, including: toxicity to nontarget organisms, persistence and fate in the environment, biotransformation, and ecological consequences. Current regulations on pesticide testing will also be presented. The mechanism of action on target species will be discussed only in relation to effects on nontarget organisms. Offered in alternate years. Same as ENVS 433, and VB 534. 3 undergraduate hours. 4 graduate hours.  Prerequisite: One year of college chemistry and one year of college biology; or consent of instructor.'),
 (4172,99,487,'Math Modeling in Life Sciences',511,'Same as ANSC 448, and STAT 458. See ANSC 448.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4173,99,490,'Independent Study',3674,'Laboratory and/or field research supervised by faculty members in the School of Integrative Biology.  A written report is required.  May be repeated. No more than a combined maximum of 10 hours of IB 390 or IB 490 may count toward graduation for IB majors. Prerequisite: Consent of instructor.'),
 (4174,99,491,'Biological Modeling',512,'Same as ANSC 449, CPSC 448, and GEOG 468. See GEOG 468.'),
 (4175,99,492,'Spatial Ecosystem Modeling',1901,'Same as CPSC 449, GEOG 469, and NRES 469. See GEOG 469.'),
 (4176,99,493,'Statistical Ecology',3675,'Study of methods used in the collection and analyses of ecological data. Emphasis on sampling, experimental design, multivariate techniques, exploratory analyses, and computer intensive applications such as exact tests and permutation procedures. Laboratory emphasis on analyses and interpretation of ecological data with statistical software. Offered in alternate years. Same as NRES 493.  Prerequisite: One course in ecology such as IB 203 including basic concepts in population and community ecology and one course in statistics such as IB 488 or CPSC 440 including basic concepts of sampling, hypothesis testing/inference, and techniques such as t-tests and ANOVA; or consent of instructor.'),
 (4177,99,494,'History of Biology',3476,'Same as HIST 465. See HIST 465.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4178,99,495,'Philosophy of Biology',3676,'Same as PHIL 473. See PHIL 473.'),
 (4179,99,496,'Special Courses',3677,'Experimental and temporary courses. May be repeated as topics vary. 1 to 5 undergraduate hours, or 1 to 4 graduate hours. Approved for both letter and S/U grading. Prerequisite: Consent of instructor.'),
 (4180,99,504,'Genomic Analysis of Insects',3678,'Comprehensive and integrated presentation of insect genomic analysis from the molecular level to that of the population; concepts are applied to certain aspects of insect population regulation.  Offered in alternate years.  Prerequisite: IB 201, or consent of instructor.'),
 (4181,99,510,'Discussions in Plant Biology',3679,'All graduate students in plant biology, except those with conflicting teaching assignments, are required to register in and attend the general seminar. Approved for both letter and S/U grading.  No credit given except to those students presenting the results of their Ph.D. thesis research.'),
 (4182,99,513,'Disc in Plant Physiology',3680,'May be repeated. Approved for both letter and S/U grading.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4183,99,514,'Disc in Systematics Biology',3681,'Approved for both letter and S/U grading.'),
 (4184,99,516,'Ecosystem Biogeochemistry',3682,'Same as NRES 516. See NRES 516.'),
 (4185,99,518,'Disc in Plant Ecology',3683,'Approved for both letter and S/U grading.  May be repeated to a maximum of 6 hours.'),
 (4186,99,519,'Disc in Photosynthesis',3684,'May be repeated to a maximum of 6 hours. Approved for both letter and S/U grading.'),
 (4187,99,524,'Plant Biochemistry',1930,'Same as CPSC 588, and HORT 588. See CPSC 588.'),
 (4188,99,526,'Seminar in Entomology',3685,'Discussions, reviews, and appraisals of special topics in the field of entomology. May be repeated to a maximum of 4 hours. Approved for both letter and S/U grading.'),
 (4189,99,542,'Environmental Plant Physiology',1920,'The interaction of plants and environment at the level of the whole organism, extending to the cell and the community; emphasis on heat and mass transfer, plant and soil potentials, and effects of light on growth. Offered in alternate years. Same as CPSC 538.  Prerequisite: IB 420; consent of instructor.'),
 (4190,99,543,'Seminar in Primate Ecology',706,'Same as ANTH 543. See ANTH 543.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4191,99,545,'Fish and Wildlife Ecol Seminar',3686,'Modern ecological principles and concepts to specific problems in fisheries and wildlife. Approved for both letter and S/U grading.  Offered in alternate years.'),
 (4192,99,546,'Topics in Ecology & Evolution',3687,'Speaker seminar series featuring discussion, review and critical analysis of general concepts and specific problems in ecology and evolution. May be repeated to a maximum of 10 hours. Approved for both letter and S/U grading.'),
 (4193,99,552,'Concepts in Ecology',3688,'Discussion, review, and critical analysis of general concepts and specific problems in ecology with new topics each term May be repeated. Approved for both letter and S/U grading.'),
 (4194,99,590,'Individual Topics',3689,'Individual topics in research conducted under the supervision of faculty members in the School of Integrative Biology. Designed for graduate students who would like to become more familiar with specialized fields of study prior to committing themselves to a specific area for their doctorate degree. May be repeated to a maximum of 16 hours. Approved for S/U grading only. Prerequisite: Consent of instructor.'),
 (4195,99,591,'Design/Analysis Biomed Exper',3690,'Same as PATH 591. See PATH 591.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4196,99,592,'Biostatistics',1672,'Same as CHLH 590, and PATH 524. See PATH 524.'),
 (4197,100,199,'Undergraduate Open Seminar',3691,'May be repeated.'),
 (4198,100,300,'Analysis of Data',3692,'Nature of probabilistic models for observed data; discrete and continuous distribution function models; inferences on universe parameters based on sample values; introduction to control charts, acceptance sampling, and measurement theory. Credit is not given for both IE 300 and either CEE 202 or GE 331. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242.'),
 (4199,100,310,'Intro to Operations Research',3693,'Introduction to deterministic and stochastic models in operations research. Topics include: linear programming, integer programming, network models and nonlinear programming, review of basic probability, Bernoulli processes, Markov chains, Markov processes, and queuing theory. Credit is not given for both IE 310 and either CEE 201 or GE 330. Prerequisite: IE 300; credit or concurrent registration in MATH 415.'),
 (4200,100,330,'Industrial Quality Control',3694,'Contemporary concepts and methods for quality and productivity design and improvement; philosophies of Deming, Taguchi, and others leading the quality management and engineering movement; Shewhart\'s methods for statistical process control; process capability analysis; statistical methods for tolerance assessment; process control methods employing attribute data; introduction to design of experiments, concepts, and methods. Prerequisite: IE 300.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4201,100,340,'Human Factors',1146,'Same as AVI 358 and PSYC 358. See PSYC 358.'),
 (4202,100,360,'Facilities Planning and Design',3695,'Reviews the process of facility planning, plant layout design, and materials handling analysis; includes the determination of facilities requirements, site selection, materials flow, use of analytical and computerized techniques including simulation, and applications to several areas such as manufacturing, warehousing, and office planning. Prerequisite: IE 310.'),
 (4203,100,361,'Production Planning and Ctrl',3696,'Examines the scope of production systems, and the activities involved in their design, establishment, management, operation, and maintenance; mathematical and computer models for planning and control of facilities, human resources, projects, products, material, and information in production systems.  Prerequisite: IE 310.'),
 (4204,100,390,'Seminar',3697,'Series of lectures by faculty and invited authorities from the profession concerning the ethics and practices of industrial engineering in their relationship to other fields of engineering, economics, and the problems of society.  Approved S/U grading only.'),
 (4205,100,400,'Des and Anlys of Experiments',3698,'Concepts and methods of design of experiments for quality design, improvement and control; simple comparative experiments, including concepts of randomization and blocking, and analysis of variance techniques; factorial and fractional factorial designs; Taguchi\'s concepts and methods; second-order designs, response surface methodology. All topics are treated through engineering applications and case studies. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: IE 300.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4206,100,410,'Stochastic Proc and App',3699,'Modeling and analysis of stochastic processes. Familiarity with discrete-time Markov chains, Poisson processes, and birth-and-death processes is assumed. Topics include the transient and steady-state behavior of continuous-time Markov chains; renewal processes; models of queuing systems (birth-and-death models, embedded-Markov-chain models, queuing networks); reliability models; and inventory models. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: IE 310.'),
 (4207,100,411,'Optimization of Large Lin Sys',3700,'Practical methods of optimization of large-scale linear systems including extreme point algorithms, duality theory, parametric linear programming, generalized upper bounding technique, price-directive and resource-directive decomposition techniques, Lagrangian duality, Karmarkar\'s algorithm, applications in engineering systems and use of state-of-the-art computer codes. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: IE 310 and MATH 415.'),
 (4208,100,412,'OR Models for Mfg Systems',3701,'Provides an introduction to the use of operations research techniques to problems in manufacturing and distribution. Topics covered include single and multi-stage lot sizing problems, scheduling and sequencing problems, and performance evaluation of manufacturing systems. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: IE 310.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4209,100,413,'Simulation',3702,'Introduction to the use of discrete-event simulation in the modeling and analysis of complex systems using a simulation package. Topics covered: components of simulation software, including data structures and event-list processing; verification and validation of simulation models; input modeling, including selection of probability distributions and random variate generation; statistical analysis of output data. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: CS 101 and IE 310.'),
 (4210,100,430,'Economic Found of Quality Sys',3703,'Introduction to total quality systems for planning, developing, and manufacturing world-class products. Covers the economic foundations of total quality. Subjects covered include product value, cost, pricing, environmental quality, activity-based costing, design for assembly, organization structure, lead time, innovation, Taguchi methods, simulation-based significance testing, Strategic Quality Deployment, statistical process control, and conjoint analysis. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: IE 300.'),
 (4211,100,435,'Intro to Reliability Eng',3059,'Same as GE 411. See GE 411.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4212,100,440,'Occupational Biomechanics',3704,'Biomechanical concepts and principles that underlie manual work. Contemporary tools and methods for measuring human physical attributes, evaluating human mechanical capabilities, and modeling human musculoskeletal system or subsystems. Applications of concepts, principles, data, and models to the design of work situations for the purposes of improving human performance and preventing musculoskeletal traumas and disorders. Prerequisite: IE 340 or TAM 212.'),
 (4213,100,441,'Interactive Sys Model and Des',1154,'Analysis and modeling of human-machine interaction in large-scale dynamic systems, development of graphical user interface and interactive real-time simulation environments, human performance evaluation. Utilizes Motif-based user interfaces and the C++ object-oriented programming language. Students work in teams to design, implement, and evaluate graphical interactive simulation environments for complex engineering systems such as manufacturing systems. Same as AVI 441. Prerequisite: CS 101, IE 300, and IE 340.'),
 (4214,100,442,'Safety Engineering',3705,'Study of engineering principles applied to industrial accident prevention; safe plant layout; safety in maintenance; boilers and pressure vessels; design and application of machine guards; material handling and storage; hand and power tools; welding hazards; electrical hazards; flammable liquids and fire protection; industrial health engineering; and toxic materials.3 undergraduate hours. 3 or 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4215,100,445,'Hum Perf and Eng Psych',1157,'Same as AVI 456 and PSYC 456. See PSYC 456.'),
 (4216,100,446,'Hum Comp Interaction Lab',1153,'Same as AVI 429 and PSYC 429. See PSYC 429.'),
 (4217,100,450,'Computer-Aided Mfg Systems',3706,'The application of computer technology and operations research in manufacturing systems; includes the use of minicomputers and microprocessors for direct numeric control of machine tools, adaptive control and optimization, and integrated manufacturing systems, including applications of industrial robots. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: ME 350.'),
 (4218,100,451,'Num Control of Mfg Processes',3707,'Study of numerical control systems, manufacturing processes, principles and practices basic to numerical control, and programming methodology for numerical control. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: CS 101 and ME 350.'),
 (4219,100,470,'Senior Design Project',3708,'Students are required to solve an actual, real world design problem; to develop, evaluate, and recommend alternative solutions; and to satisfy realistic constraints that include most of the following considerations: economic; environmental; sustainability; manufacturability; ethical; health and safety; social; and political. 3 undergraduate hours. No graduate credit. Approval of the department is required to register. Prerequisite: Concurrent enrollment in no more than two required IE courses; completion of all required courses.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4220,100,485,'Intro MEM Devices & Systems',2383,'Same as ECE 485 and ME 485. See ECE 485.'),
 (4221,100,496,'Honors Project',3709,'Special project or reading course for James Scholars in engineering. 1 to 4 undergraduate hours. No graduate credit. Prerequisite: Consent of instructor.'),
 (4222,100,497,'Independent Study',3710,'Independent study of advanced problems related to industrial engineering.'),
 (4223,100,498,'Special Topics',3711,'Lectures on special topics in selected areas of industrial engineering. May be repeated in the same or separate terms if topics vary to a maximum of 9 hours. Prerequisite: As specified for each topic offering; see Schedule or departmental course information.'),
 (4224,100,510,'Applied Nonlinear Programming',3712,'Optimization of nonlinear systems, including a survey of classical methods and concepts such as the Lagrangian method, the Jacobian method, and Kuhn-Tucker conditions; emphasizes modern algorithms, numerical methods for digital computers, applications in engineering design, and use of state-of-the-art computer codes. Prerequisite: IE 310.'),
 (4225,100,511,'Integer Programming',3713,'Optimization of linear systems involving integer variables and discrete alternatives. Covers: modeling; computational complexity; matroids; branch and bound methods; Langrangian and surrograte duality; cutting plane methods and polyhedral theory; and special structured problems such as knapsack, set packing and covering, traveling salesman, etc. Prerequisite: IE 411 or MATH 482.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4226,100,512,'Sys Method and Network Techniq',1438,'Study of basic concepts, theories, and techniques of systems analysis, including modeling of large scale systems, forecasting, planning, control, and information handling; emphasizes the modeling of systems with network techniques, including distance, flow, and project networks. Discusses advanced network topics such as out-of-kilter algorithm and project resource analysis. Same as CEE 516. Prerequisite: IE 361 or CEE 201.'),
 (4227,100,540,'Anlys and Des of Man-Mach Sys',3714,'Input-output models of man as an information processor, controller, and decision maker are critically evaluated and applied to the analysis and design of specific man-machine systems. Intended for graduate students working in areas of man-machine systems, engineering psychology, control systems, or operations research. Prerequisite: ME 340 and IE 300.'),
 (4228,100,541,'Mental Models in Complex Sys',3715,'Examines how human expertise develops, particularly in the context of complex industrial systems and various types of professional practice (diagnosis, decision-making, etc.). Topics include: cognitive skill acquisition; how expert knowledge is mentally represented; different knowledge elicitation techniques; and ways of supporting the human expert at work, such as decision support systems and expert systems. Same as PSYC 542. Prerequisite: At least two of PSYC 224, PSYC 248, IE 340, IE 445, and IE 446.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4229,100,542,'Cooperative Problem Solving',1161,'Advanced graduate seminar on problem-solving models and taxonomies, models of coordination of activity and communication among multiple agents, design of human-machine cooperative problem-solving systems, adaptive automation, and intelligent decision support. Readings drawn from work in pragmatics, distributed artificial intelligence, cognitive engineering, and other related areas. Same as AVI 542. Prerequisite: Credit or concurrent registration in at least one of CS 440, IE 540, PSYC 527.'),
 (4230,100,550,'Automated Plan of Mfg Proc',3716,'Representation of mechanical solids; reasoning about shape of a single solid, manufacturing and design features -- representation and recognition; reasoning about multiple solids -- machining path generation, assembly sequencing, path planning and obstacle avoidance, task-level robot programming, and inspection planning. Prerequisite: IE 450 or IE 451.'),
 (4231,100,551,'Accuracy and Ctrl of Mach Sys',3717,'Course addresses the problem of modeling machining processes and machine tools. Emphasis is on mechanistic modeling of machining processes, machine-tool errors, characterization of machined surfaces, machine-tool system dynamics and stability, and topics in motion control. Prerequisite: ME 340 or equivalent course in dynamics and controls, ME 350.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4232,100,590,'Seminar',3718,'Required of all graduate students each term with the exception of doctoral candidates who have passed their preliminary examination. Presentation and discussion of significant developments in industrial engineering. May be repeated. Approved for S/U grading only.'),
 (4233,100,597,'Independent Study',3719,'Independent study of advanced problems related to industrial engineering. May be repeated in the same or separate terms if topics vary to a maximum of 12 hours. Prerequisite: Consent of instructor.'),
 (4234,100,598,'Special Topics',3720,'Lectures on special topics in selected areas of industrial engineering. May be repeated in same term. Prerequisite: As specified for each topic offering; see Schedule or departmental course information.'),
 (4235,100,599,'Thesis Research',3721,'May be repeated. Approved for S/U grading only.'),
 (4236,101,101,'Elementary Italian I',3722,'For students who have no credit in Italian.'),
 (4237,101,102,'Elementary Italian II',3723,'Continuation of ITAL 101.  Prerequisite: ITAL 101 or one year of high school Italian.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4238,101,103,'Intermediate Italian I',3724,'Rapid reading, review of grammar, composition, and conversation.  Prerequisite: ITAL 102 or two years of high school Italian.'),
 (4239,101,104,'Intermediate Italian II',3725,'Continuation of ITAL 103.  Prerequisite: ITAL 103 or three years of high school Italian.'),
 (4240,101,191,'Freshman Honors Tutorial',3726,'Study of selected topics on an individually arranged basis. Open only to honors majors or to Cohn Scholars and Associates. May be repeated up to 1 time(s) to a maximum of 6 hours.  Prerequisite: Consent of departmental honors adviser in Italian.'),
 (4241,101,199,'Undergraduate Open Seminar',3727,'May be repeated to a maximum of 5 hours. Approved for both letter and S/U grading.'),
 (4242,101,200,'Intro Italian Literature',3728,'Emphasis on methodology for critical analysis of literary texts and on major periods and movements in their cultural and historical contexts.  Prerequisite: ITAL 104 or consent of instructor.'),
 (4243,101,210,'Practical Review Italian',3729,'Reviews major challenges in Italian grammar, with particular emphasis on the verb system (major tenses and moods, morphology, and aspect) and areas of contrast with English.  Prerequisite: Credit or concurrent enrollment in ITAL 104, or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4244,101,220,'Comtemp Italian Oral & Written',3730,'Training in oral-aural skill and in writing.  Prerequisite: ITAL 210, or consent of instructor.'),
 (4245,101,240,'Italy Middle Ages & Renaiss',2091,'The development of Medieval Italian civilization in a literary context from the Sicilian School of love poetry to the early Renaissance in Florence; lectures and readings are in English. Same as CWL 240, and MDVL 240.'),
 (4246,101,270,'Introduction to Italian Cinema',3731,'Introduction to major films, movements and directors in the Italian tradition, paying particular attention to questions of national identity, gender and political and social history. Knowledge of Italian not required.'),
 (4247,101,310,'Advanced Grammar',3732,'Study of the structure of modern Italian in both its phonological and syntactic aspects for the student who already has a functional command of the language, with an emphasis on developing ability to analyze and interpret grammatical structures.  Prerequisite: ITAL 210 or consent of instructor.'),
 (4248,101,380,'Ital Business & Profess',3733,'Builds preexisting language skills through the study of Italian business practices: financial systems, transactions, banking, import/export and commercial correspondence.  Prerequisite: ITAL 210 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4249,101,390,'Spec Topics Italian Studies',3734,'Selected substantive readings for independent study on a given special topic of Italian literature, culture, language, or linguistics. May be repeated.  Prerequisite: ITAL 104 and consent of instructor.'),
 (4250,101,400,'Italian Speakers Rom Langs',3735,'Accelerated language learning course designed for speakers of Romance languages. The focus will be primarily on those linguistic structures specific to Italian which differ significantly from equivalents in other Romance languages. Early emphasis on production skills; comprehension-based skills will be introduced in rapid succession. Students may not receive credit for ITAL 400 and ITAL 101 and ITAL 102. Prerequisite: Native or near-native proficiency in a Romance language (SPAN, FR, PORT, or RMLG).'),
 (4251,101,402,'Composition & Stylistics',3736,'Refinement of written discourse for academic and professional expectations and requirements. In addition to quizzes and a final examination, a major, formal paper on an assigned topic will be required.  Prerequisite: ITAL 310 or equivalent; or consent of instructor.'),
 (4252,101,406,'Italian Culture',3737,'Introduction to factors that have shaped present-day Italy; basic concepts contributing to understanding its present social and cultural development; taught in Italian.  Prerequisite: ITAL 200 or ITAL 220, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4253,101,413,'Dante',2131,'Interpretation of Dante\'s Divine Comedy with special attention to its position in the medieval world; a knowledge of Italian not required. Same as CWL 413, and MDVL 413.'),
 (4254,101,414,'Petrarch & Boccaccio',2132,'Studies in Petrarch and Boccaccio; nonmajors in Italian may read the works in translation; lectures are in English. Same as CWL 414, and MDVL 414.  Prerequisite: Fulfillment of campus rhetoric requirement.'),
 (4255,101,420,'Masterpieces Renaiss Lit',2135,'Reading of masterpieces of the 1400 and 1500s and a study of their predecessors and influence; nonconcentrators in Italian may read the works in translation; lectures are in English. Content rotates. Same as CWL 420, and MDVL 420. May be repeated to a maximum of 6 hours with consent of instructor.  Prerequisite: Fulfillment of campus rhetoric requirement.'),
 (4256,101,430,'From Baroque-Romanticism',3738,'Major literary developments in Italy from the end of the Renaissance to the New Italy of the Risorgimento (Baroque, Arcadia, Enlightenment, Neoclassicism, Romanticism).  Prerequisite: ITAL 200 or consent of instructor.'),
 (4257,101,435,'Intro Romance Ling',3739,'Same as SPAN 435, FR 462, LING 462, PORT 435, and RMLG 435.  See SPAN 435.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4258,101,440,'Modern Italian Novel',3740,'Appreciation of the modern Italian novel through a close reading of some representative works (e.g., Verga, Moravia, Vittorini, Pavese).  Prerequisite: ITAL 200 or consent of instructor.'),
 (4259,101,442,'Modern Italian Poetry',3741,'Appreciation of modern Italian poetry through a close reading of some representative works (e.g., D\'Annunzio, Pascoli, Montale, Quasimodo, Saba, Ungaretti, Novissimi, Zanzotto).  Prerequisite: ITAL 200 or consent of instructor.'),
 (4260,101,450,'Italian Syntax & Phonology',3742,'Introduction to the essential syntactic and phonological structures of Modern Standard Italian in combination with appropriate discussion of corresponding linguistic concepts.  Prerequisite: ITAL 310 and ITAL 402, or consent of instructor.'),
 (4261,101,460,'Principles of Language Testing',2545,'Same as EIL 460, EPSY 487, FR 460, GER 460, PORT 460, SLS 460, and SPAN 460. See EIL 460.'),
 (4262,101,470,'Topics in Italian Cinema',1778,'An in-depth examination of a particular director, genre or school from the Italian cinematic tradition (e.g., Fellini, Italian horror, or noerealism); topic will very each semester.  No knowledge of Italian is required.   Same as CINE 470. May be repeated to a maximum of 6 Undergraduate Hours or 8 Graduate Hours.  3 Undergraduate Hours.  4 Graduate Hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4263,101,482,'Computer Foreign Lang Tchg',1813,'Same as CLCV 482, EIL 482, FR 482, GER 482, HUM 482, LING 486, PORT 482, SLAV 482, and SPAN 482. See HUM 482.'),
 (4264,101,489,'Theoretical Foundations of SLA',2548,'Same as EIL 489, FR 481, GER 489, LING 489, PORT 489, SLS 489, and SPAN 489. See EIL 489.'),
 (4265,101,491,'Honors Senior Thesis',3743,'For candidates for honors in Italian. 2 undergraduate hours. May be repeated.'),
 (4266,101,559,'Sem Romance Ling',2971,'Same as FR 559, LING 559, PORT 559, RMLG 559, and SPAN 557. See SPAN 557.'),
 (4267,101,563,'College Teaching Foreign Langs',2553,'Same as EIL 563, FR 563, GER 563, PORT 563, RUSS 563, and SPAN 563. See FR 563.'),
 (4268,101,571,'Proseminar For Lang Tchg',3744,'Same as PORT 571, and SPAN 571. See SPAN 571.'),
 (4269,101,572,'Theory and Literary Criticism',3745,'Same as PORT 572, and SPAN 572. See SPAN 572.'),
 (4270,101,580,'Classroom Lang Acquisition',2556,'Same as EIL 580, FR 580, GER 580, PORT 580, SLS 580, and SPAN 580. See SPAN 580.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4271,101,581,'Ling Psych Found of Lang Tchg',2557,'Same as EIL 581, FR 581, GER 581, PORT 581, RUSS 581, and SPAN 581. See FR 581.'),
 (4272,101,584,'Theories in SLA',1756,'Same as CI 584, EALC 584, EIL 584, EPSY 563, FR 584, GER 584, LING 584, PORT 584, SLS 584, and SPAN 584. See SPAN 584.'),
 (4273,101,587,'Lang and Social Interaction II',2552,'Same as EIL 556, PORT 587, and SPAN 587. See EIL 556.'),
 (4274,101,588,'Sem Second Lang Learn',2312,'Same as EALC 588, EIL 590, FR 588, GER 588, LING 588, PORT 588, SLS 588, and SPAN 588. See SPAN 588.'),
 (4275,101,595,'Spec Topics in Italian',3746,'Independent study/research under the direction of a faculty member. May or may not fulfill requirements for a particular degree program in Spanish, Italian, and Portuguese. Consult graduate advisor. May be repeated in same or subsequent terms to a maximum of 8 hours.'),
 (4276,101,599,'Thesis Reseach',3747,'May be repeated. Approved for S/U grading only.'),
 (4277,102,199,'Undergraduate Open Seminar',3748,'May be repeated.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4278,102,201,'Elementary Japanese I',3749,'Introduction to Japanese, spoken language skills and the reading and writing of hirigana, katakana, and kanji.'),
 (4279,102,202,'Elementary Japanese II',3750,'Continuation of JAPN 201.  Prerequisite: JAPN 201.'),
 (4280,102,203,'Intermediate Japanese I',3751,'Prerequisite: JAPN 202 or equivalent.'),
 (4281,102,204,'Intermediate Japanese II',3752,'Continuation of JAPN 203.  Prerequisite: JAPN 203 or equivalent.'),
 (4282,102,305,'Advanced Japanese I',3753,'Readings in graded Japanese texts with oral practice designed to help students acquire the sophisticated vocabulary and grammatical structures of written Japanese.  Prerequisite: JAPN 204 or placement test for students who have Japanese background or who have previously taken a course(s) in Japanese.'),
 (4283,102,306,'Advanced Japanese II',3754,'Continuation of JAPN 305.  Prerequisite: JAPN 305 or be placement test.'),
 (4284,102,407,'Intro to Classical Japanese',3755,'Introduction to the grammar, morphology, vocabulary, and style of classical Japanese language as found in premodern Japanese literary and historical writings.  Prerequisite: Three years of modern Japanese language or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4285,102,408,'Readings in Classical Japanese',3756,'Readings in texts in classical Japanese selected from historical and literary sources of the premodern period. Attention is given to grammatical, morphological, and stylistic features and to problems in translation. Introduction to reading of classical syllabaries and manuscript texts.  Prerequisite: JAPN 407 or equivalent.'),
 (4286,102,409,'Social Science Rdgs Japanese',3757,'Readings in Japanese social science materials, including articles from newspapers, periodicals, and learned journals. 3 undergraduate hours. 4 graduate hours. May be repeated to a maximum of 9 undergraduate hours, or 12 graduate hours. Prerequisite: JAPN 306 or equivalent.'),
 (4287,102,440,'Fourth Year Japanese I',3758,'Further developments of skills in sophisticated Japanese language use, including readings in authentic materials in a wide variety of writing styles, writing for formal occasions, and speaking appropriately according to the situation while using precise vocabulary in correct level of speech. 3 undergraduate hours. 4 graduate hours.  Prerequisite: JAPN 306 or equivalent.'),
 (4288,102,441,'Fourth Year Japanese II',3759,'Continuation of JAPN 440. 3 undergraduate hours. 4 graduate hours.  Prerequisite: JAPN 440 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4289,102,460,'Japanese as a 2nd Language I',3760,'Introduction to basic theory of Japanese pedagogy; teaching methods, and theory and practice of teaching Japanese grammar. 3 undergraduate hours. 4 graduate hours.'),
 (4290,102,461,'Japanese as a 2nd Language II',3761,'Application of pedalinguistics of Japanese; theory and method of instructional exercise development for teaching Japanese in practice teaching of Japanese in the classroom. 3 undergraduate hours. 4 graduate hours.  Prerequisite: JAPN 460 or equivalent.'),
 (4291,102,471,'Intro Second Lang Learn Tchg',1612,'Same as CHIN 471, FR 471, GER 469, HUM 471, LAT 471, RUSS 471, and SPAN 471.  See SPAN 471.'),
 (4292,102,475,'Intro to Comm Lang Tchg',1613,'Same as CHIN 475, FR 475, GER 475, LAT 475, RUSS 475, and SPAN 475.  See SPAN 475.'),
 (4293,102,478,'Topics Secondary Lang Tchg',1615,'Same as CHIN 478, FR 478, GER 478, LAT 478, RUSS 478, and SPAN 478.  See SPAN 478.'),
 (4294,102,490,'Readings in Japanese Lit',3762,'Guided readings in Japanese literature in the vernacular with regular individual conferences and a paper. 3 undergraduate hours. 4 graduate hours. May be repeated to a maximum of 6 undergraduate hours, or 8 graduate hours. Prerequisite: Reading knowledge of Japanese and consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4295,102,499,'Study Abroad',3763,'Lectures, seminars, and practical work in the Japanese language, literature, and civilization, and in other academic areas appropriate to the student\'s course of study. 0 to 16 undergraduate hours. Approved for both letter and S/U grading.  Prerequisite: Junior standing and a GPA of 3.00.'),
 (4296,103,199,'Undergraduate Open Seminar',3764,'A changing array of courses focusing on special topics in journalism. May be repeated up to 12 hours in separate semesters, if topics vary.'),
 (4297,103,200,'Introduction to Journalism',3765,'Discussion of the history, freedom, technologies, ethics, and functions of the news media. Training in clear, descriptive writing techniques, using journalistic models.  Prerequisite: Completion of Composition I general education requirement.'),
 (4298,103,400,'Reporting 1',3766,'Fundamentals of journalistic writing; reporting news of public affairs.  Prerequisite: JOUR 200.'),
 (4299,103,405,'History of American Journalism',3767,'Surveys the history of the field of journalism since pre-colonial times. Includes the evolution of the media in the United States and the evolution of cultural concepts concerning the media, including rights granted under the First Amendment.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4300,103,411,'Law and Communications',3768,'Detailed analysis of the theories of freedom of expression, the legal doctrines of greatest concern to mass communicators, and contemporary issues related to free speech and press, including libel, copyright, and news-gathering.'),
 (4301,103,415,'Reporting 2',3769,'Study and extensive practice of in-depth public affairs reporting - its concepts, techniques, traditions, ethics, and social obligations.  Prerequisite: JOUR 400.'),
 (4302,103,420,'News Editing',3770,'Newspaper editing and headline writing, news judgment, ethics and leadership. Prerequisite: JOUR 400.'),
 (4303,103,425,'Graphics and Design',3771,'Principles of visual reporting and editing. Introduction to newspaper page design, information graphics research and design, photojournalism, online design, and project planning. For current fees, see the Class Schedule.  Prerequisite: JOUR 400.'),
 (4304,103,435,'Radio Journalism',3772,'Reporting and writing news for radio news programs. For current fees, see the Class Schedule. Prerequisite: JOUR 400.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4305,103,440,'Television Journalism 1',3773,'Introduces TV news studio and field production and principles of field news reporting and editing; principles of planning, producing, and directing news and public affairs programs. For current fees, see the Class Schedule. Prerequisite: JOUR 435.'),
 (4306,103,445,'Television Journalism 2',3774,'Advanced techniques for reporting, producing, writing, shooting, and editing television news stories and for producing and airing regularly scheduled news programs on deadline.  Prerequisite: JOUR 440.'),
 (4307,103,450,'Media and Public Opinion',3775,'Theory of public opinion and communications; relation of communication systems to public opinion, social systems, and the political order.'),
 (4308,103,451,'Research Methods in Journalism',3776,'Introduction to social science principles of measurement, sampling, statistical inferences and logic of research design in collection, analysis and interpretation of information used in journalism and mass media.  Prerequisite: Completion of Quantitative Reasoning I and Introduction to Journalism (JOUR 200) or graduate standing.'),
 (4309,103,455,'Press and Modern Presidency',3777,'Traces historical development of press commentary about the President, press conferences, news flow from Washington, radio and television coverage of the White House, the White House press corps and more since the Hoover Administration. Reporters\' personal relationships with chief executives and the influence of news organizations upon national policy and issues will be covered.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4310,103,460,'Special Topics',3778,'A changing array of special projects, research or reading in journalism. May be repeated in the same or in multiple semesters, if topics vary.'),
 (4311,103,465,'Photojournalism',3779,'This course is designed to acquaint students with fundamentals of photojournalism. Instruction will include techniques to produce photographs; the conception and planning of pictures; the pictorial coverage of news events and human interest situation; the planning and execution of photo layouts. Cameras provided by college. Prerequisite: College of Communications major or consent of instructor.'),
 (4312,103,470,'International Reporting',3780,'Role of international news in daily lives. Examines those who report it and those who pioneered it. Students monitor how U.S. and international media cover selected countries and learn how to write international news.'),
 (4313,103,475,'Magazine Writing',3781,'Preparation of feature stories and articles; techniques of marketing, market analysis, and publishing articles written in the course. Prerequisite: JOUR 400');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4314,103,480,'Advanced Reporting Topics',3782,'Advanced reporting projects or techniques, with separate sections for a varying array of topics such as investigative reporting, immersion journalism, literary journalism, business and financial journalism, online publishing, radio news features, sports writing, broadcast documentary production, digital journalism, and photo journalism. May be repeated in the same or in multiple semesters, if topics vary. Prerequisite: JOUR 400.'),
 (4315,103,485,'Magazine Editing',3783,'Basic principles of editing for consumer, business, trade, and company magazines; communications theory, market analysis, editorial process, design process, production process, and distribution process as they relate to magazine publishing. Prerequisite: JOUR 420, JOUR 425 and JOUR 475.'),
 (4316,103,490,'Professional Project',3784,'Individual and team-produced advanced enterprise projects in specialized fields typically with separate sections for news-editorial and broadcast journalism students. May be repeated in the same or in multiple semesters, if topics vary. Prerequisite: Either JOUR 415, JOUR 420 and JOUR 425 or JOUR 435, JOUR 440 and JOUR 445.'),
 (4317,103,495,'Internship Seminar',3785,'Seminar based on internship experience. Offered for students who participated in pre-internship orientation then completed an approved internship. Approved for both letter and S/U grading. Prerequisite: JOUR 400.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4318,103,500,'Issues in Journalism',3786,'Seminar on issues of contemporary importance in journalism in their historical, multicultural contexts. Emphasis on ethical, legal, social, professional aspects of those issues. Aimed at helping students to develop their own journalism philosophies and high standards of conduct. Prerequisite: Consent of department.'),
 (4319,103,505,'Master\'s Proseminar',3787,'Introduction to scholarship and research in journalism and mass communication examining theoretical approaches to the meanings, uses, and effects of mass media in society; discussion of media freedom and accountability; humanistic and social scientific contributions to understanding mass communication.  Prerequisite: Consent of department'),
 (4320,103,510,'Master\'s Readings',3788,'Readings in journalism analyzes journalism texts through written assignments in which students compare and contrast the works selected. Prerequisite: Must be a journalism graduate student.'),
 (4321,103,515,'Master\'s Project',3789,'A professional journalism project demonstrating development of analytical and critical thinking abilities appropriate to the profession and effective application of journalism methodology. Prerequisite: Consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4322,104,100,'Development Activities',3790,'Skills and knowledge essential for leisure-time activities which are classified as developmental activities. Prerequisites and descriptions for each developmental activity are provided in the Class Schedule. More than one activity (Sections A through Z) may be taken in the same term.'),
 (4323,104,101,'Dance Activities',3791,'Skills and knowledge essential for leisure-time activities which are classified as dance activities.  Prerequisites for each dance activity are provided in the Class Schedule.  More than one activity (Sections A through Z) may be taken in the same term.'),
 (4324,104,102,'Individual and Dual Activities',3792,'Skills and knowledge essential for leisure-time activities which are classified as individual and dual activities. Prerequisites for each individual or dual activity are provided in the Class Schedule. More than one activity (Sections A through Z) may be taken in the same term.'),
 (4325,104,103,'Indoor Court Activities',3793,'Skills and knowledge essential for leisure-time activities which are classified as indoor court activities. Prerequisites for each indoor court activity are provided in the Class Schedule.  More than one activity (Sections A through Z) may be taken in the same term.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4326,104,104,'Skating Activities',3794,'Skills and knowledge essential for leisure-time activities which are classified as skating activities. Prerequisites for each skating activity are provided in the Class Schedule. More than one activity (Sections A through Z) may be taken in the same term.'),
 (4327,104,106,'Swimming Activities',3795,'Skills and knowledge essential for leisure-time activities which are classified as swimming activities. Prerequisites for each swimming activity are provided in the Class Schedule. More than one activity (Sections A through Z) may be taken in the same term if these activities are offered on an 8-week basis.'),
 (4328,104,107,'Aquatic Sport Activities',3796,'Skills and knowledge essential for leisure-time activities which are classified as aquatic sport activities. Prerequisites for each aquatic sport activity are provided in the Class Schedule.  More than one activity (Sections A through Z) may be taken in the same term.'),
 (4329,104,109,'Team Sport Activities',3797,'Skills and knowledge essential for leisure-time activities which are classified as team sport activities. Prerequisites for each team sport activity are provided in the Class Schedule. More than one activity (Sections A through Z) may be taken in the same term.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4330,104,110,'Gymnastic Activities',3798,'Skills and knowledge essential for leisure-time activities which are classified as gymnastic activities. Sections C and Z may be taken in the same term.'),
 (4331,104,111,'Prescribed Exercise',3799,'Prescribed exercises adapted to individual needs, capacities, and interests; open to persons with paraplegia, permanently disabled, and individuals with significant temporary disabilities who will require long term rehabilitation. Students must be registered or eligible to register with DRES.'),
 (4332,104,120,'Injuries in Sport',3800,'Emphasizes injury mechanisms, means of injury prevention, and emergency care applied to various types of sport injuries; laboratory sessions emphasize preventive and therapeutic taping and emergency first aid.'),
 (4333,104,121,'Survey of Sports Medicine',3801,'Introduction to sports medicine for non-kinesiology majors; includes discussion of training, conditioning, preparation for sports, injury aspects of sports, and rehabilitation.'),
 (4334,104,122,'Physical Activity and Health',3802,'Provides the scientific evidence of physical activity in preventing disease and optimizing quality of life.  Teaches behavioral change strategies to achieve an active lifestyle. Designed for non-majors.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4335,104,125,'Introduction to Kinesiology',3803,'Course serves as an introduction to Kinesiology and will provide an overview of the Kinesiology curriculum, areas of study, and opportunities available for a career in the field. Approved for S/U grading only.'),
 (4336,104,130,'Analysis of Basic Movement',3804,'Introduction to human movement through development of skills and knowledge relative to structure and function of the human body in selected physical activities including: basic postural and locomotion patterns and fundamental throwing patterns; also studies developmental aspects of typical and atypical movement skills. Emphasizes performance and qualitative analysis of movement skills.'),
 (4337,104,131,'Movement Skills-Fitness',3805,'Development of and participation in a physical fitness program including physical fitness assessment.'),
 (4338,104,132,'Movement Skills-Swimming',3806,'Development of an understanding of basic swimming skills; emphasizes performance and qualitative analysis of personal aquatic skills, developmental aspects of aquatic skills, and analysis of atypical movement patterns in an aquatic environment. Prior to enrolling in this course, students must have the ability to execute a minimum of one of five basic strokes in deep water, perform a standing dive, and tread in deep water.  Prerequisite: KIN 130 or concurrent enrollment or consent of instructor.'),
 (4339,104,133,'Movement Skills-Dance',3807,'Development of an understanding of basic dance steps, positions and sequences; emphasizes performance and qualitative analysis of personal dance skills, developmental aspects of dance and rhythm, and analysis of atypical movement patterns in a dance setting.  Prerequisite: KIN 130 or current enrollment or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4340,104,134,'Movement Skills-Gymnastics',3808,'Development of an understanding of basic gymnastic movements and sequences; emphasizes performance and qualitative analysis of personal gymnastic skills, developmental aspects of gymnastic skills, and analysis of atypical movement patterns in a gymnastic setting.  Prerequisite: KIN 130 or current enrollment or consent of instructor.'),
 (4341,104,135,'Movement Skills-Field',3809,'Development of an understanding of basic field activity skills; emphasizes performance, as well as an appreciation of commonalities, in specific activities including soccer, speedball, speedaway, field hockey and flag football.  Prerequisite: KIN 130 or concurrent enrollment or consent of instructor.'),
 (4342,104,136,'Movement Skills-Racquet',3810,'Development of an understanding of basic racquet activity skills; emphasizes performance, as well as appreciation of commonalities in specific racquet activities such as tennis, badminton, squash or racquetball.  Prerequisite: KIN 130 or concurrent enrollment or consent of instructor.'),
 (4343,104,140,'Social Sci of Human Movement',3811,'Introduction to the social scientific aspects of human movement including sport; particular emphasis on concepts derived from the social sciences (including psychology) that are appropriate to human movement.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4344,104,142,'Contemporary Issues in Sport',3812,'Examines current issues in sport relative to competition, economics, race, sex, youth, educational institutions, deviant behavior, religion, psychology, and the media.'),
 (4345,104,150,'Bioscience of Human Movement',3813,'Integrates anatomical and physiological aspects of human movement; emphasizes how the body moves, physiological responses to exercise stress, physical conditioning and physical fitness.'),
 (4346,104,167,'Teaching of Aerobics',3814,'Development of knowledge and practical experience concerned with the teaching and evaluating of aerobic exercise classes. This course will cover teaching, cuing, and performance of stretching, strengthening, and aerobic activities used in exercise classes. Awareness of injuries, criteria for certifications, format styles, equipment usage, and consumer products will be studied. Prior to enrolling in this course, students must have introductory skills and knowledge of aerobic exercise.'),
 (4347,104,168,'Lifeguard Instructor Training',3815,'Examines the development of aquatic risk management and advanced rescue techniques as well as a system of lifeguard selection and training. Intended primarily for skilled aquatic personnel with the common goal of creating and maintaining a safe aquatic environment. May lead to American Red Cross certification both Lifeguard Training and Lifeguard Instructor Training. Prior to enrolling in this course, students must have the ability to swim 500 yards continuously, swim 15 yards underwater, and recover a 10 lb. brick from a depth of 12 feet.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4348,104,169,'WSI Swim Instructor Training',3816,'Designed to prepare aquatic professionals to teach progressive levels of swimming. Students will also learn how to analyze human movement in the aquatic environment. May lead to American Red Cross certification both Lifeguard Training and Lifeguard Instructor Training. Prior to enrolling in this course, student must have American Red Cross Level VI swimming ability and Emergency Water Safety knowledge and ability.'),
 (4349,104,181,'Athl Training Directed Observ',3817,'Directed observation and acquisition of athletic training skills for selection into the nationally accredited Commission on Accreditation of Allied Health Education Programs (CAAHEP) Athletic Training Education Program. Emphasis is on acquisition of athletic training skills and the UIUC\'s athletic training educational program policies and procedures. May be repeated to a maximum of 4 hours. Prerequisite: KIN 120 or concurrent enrollment, or consent of Kinesiology advisor.'),
 (4350,104,182,'Clin Progressions in AT I',3818,'Supervised practicum in the athletic training setting. Emphasis will be placed on student progression in the athletic training competencies. Offered to those students admitted into the Commission on Accreditation of Allied Health Education Programs  Prerequisite: KIN 181 and admission to the Athletic Training program.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4351,104,199,'Undergraduate Open Seminar',3819,'May be repeated. Approved for both letter and S/U grading.'),
 (4352,104,220,'Fund of Athletic Training',3820,'Discussion of the role of the athletic trainer; legalities, facilities, advanced emergency procedures, injury prevention and organization and administration of athletic health care programs. Understanding the process of injury and healing as a basis for prevention and treatment of athletic injuries is emphasized including general medical terminology.  Prerequisite: KIN 120, or consent of instructor.'),
 (4353,104,221,'Therapeutic Modalities in AT',3821,'Emphasis on instrumentation and application of therapeutic modalities in the laboratory setting including therapeutic heat, therapeutic cold, electrotherapy, traction, massage, hydrotherapy, pain control and postural alignment.   Prerequisite: Credit or concurrent enrollment in KIN 220, or consent of instructor.'),
 (4354,104,222,'Base for Prescrip of Therap Ex',3822,'Functional anatomy and injury constraints as a basis for prescription of therapeutic exercises for musculoskeletal conditions; laboratory sessions stress clinical evaluation of muscle and joint function and familiarization with therapeutic exercises.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4355,104,230,'Leisure Services and Diversity',3823,'Same as RST 230.  See RST 230.'),
 (4356,104,239,'Coaching Strategies',3824,'Examination of philosophy, ethics, strategies, motivational techniques, performance analysis, program organization, contest administration, and equipment and facility management related to coaching.'),
 (4357,104,240,'Soc & Psych of Phys Activity',3825,'Discusses how social and psychological processes and constraints affect human action in physical activity environments. Attention is given to socialization, personal dynamics, stratification, and ideological and economic constraints upon the manifestations of physical activity.  Prerequisite: KIN 140 or consent of instructor and completion of the Campus Composition I general education requirement.'),
 (4358,104,247,'Intro to Sport Psychology',3826,'Analysis of the competitive sport process, with study of how personality and situational variables affect motivation, anxiety, and aggression in sport. Attention is given to the psychological skills needed by coaches and athletes for successful and enjoyable sports participation.'),
 (4359,104,249,'Sport & Modern Society',3827,'The sociological analysis of sport in modern societies with regard to social class, politics, community, education, and collective behavior. Same as SOC 249.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4360,104,257,'Coordination, Control & Skill',3828,'Introduction to the concepts and principles of the coordination and control of movement and the development of skilled action. The course will focus on such topics as fundamental movement activities; movement control processes; acquisition, retention and transfer of skill; and the role of constraints to action. These topics have implications for understanding skilled performance, motor development and human performance in general.  Prerequisite: KIN 140 and KIN 150 or consent of instructor.'),
 (4361,104,262,'Motor Develop, Growth & Form',3322,'Examination of the concepts of motor development, physical growth, and body form throughout the lifespan. Major emphasis is on the period of birth through adolescence.  Same as HDFS 262.'),
 (4362,104,268,'Children\'s Movement',3829,'Introduction and overview of kinesiology principles and physical activity related to children. Laboratory portion of class focuses on the application of information to teaching physical activity to elementary school children. For non-kinesiology majors.'),
 (4363,104,281,'Clinical Progression AT 2',3830,'Progression in athletic training clinical skills for the Athletic Training Education Program. Emphasis is on mastery of entry-level athletic training skills for first aid, injury management, and knowledge of initial screening and record keeping methods of the athletic training profession.  Prerequisite: KIN 182 and consent of academic advisor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4364,104,282,'Clinical Progression AT 3',3831,'Progression in athletic training clinical skills for the Athletic Training Education Program. Emphasis is on mastery of entry-level athletic training skills for therapeutic exercise injury management, and knowledge of athletic injury triage for the athletic training profession. Prerequisite: KIN 281 and consent of academic advisor.'),
 (4365,104,320,'Adv Assess of Athl Injuries UE',3832,'Analyzes injury patterns and mechanisms for the various joints and body segments; emphasizes the nature of the injuries, clinical evaluation and therapeutic principles, the physiology of the healing process, and functional anatomy. Prerequisite: KIN 220, or consent of instructor.'),
 (4366,104,325,'Adv Assess of Athl Inj - LE',3833,'Provides knowledge of low back and lower extremity. Emphasis is on acquisition of athletic training policies and procedures including content areas of assessment, evaluation, general medical conditions and temporary disabilities of the low back and lower extremity. Prerequisite: KIN 320, or consent of instructor.'),
 (4367,104,344,'Anthropology of Play',601,'Overview of the general field, research and literature comprising the anthropological study of human play. Emphasis is on the study of definitions and critiques of human play from various cultural perspectives. Provides extensive practice in writing within the genre of cultural anthropology. Same as ANTH 344. Prerequisite: One course in Socio-Cultural Anthropology or consent of instructor and a Composition I course.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4368,104,352,'Bioenergetics of Movement',3834,'Study of the nature of energy transfer during physical activity; mechanisms of metabolic control, force production, cardiorespiratory support and adaptation relative to physical activity.  Prerequisite: MCB 103.'),
 (4369,104,355,'Biomechanics of Human Movement',3835,'Studies the biological and mechanical principles of human motor performance; analyzes selected movement skills in depth.  Prerequisite: CSB 334, MATH 012 or above, or consent of instructor.'),
 (4370,104,360,'Adapted Physical Education',3836,'Organization, administration, and conduct of physical education programs for the most prevalent types of medical conditions found in school settings; emphasis on analyzing motoric needs and prescribing programs of motor activity for special populations, including individuals with mental retardation and learning disabilities  Prerequisite: Junior standing or above and enrollment in the Teacher Certification program or consent of instructor.'),
 (4371,104,361,'Curriculum in Grades K-6',3837,'Examines the theoretical and philosophic curricular principles necessary to the development of a sound, professionally grounded, and research-based curriculum for children in grades K-6. Requires planning a variety of developmentally appropriate learning activities that are taught to children during micro-teaching experiences in the field. Prerequisite: Junior standing or above and enrollment in the Teacher Certification program or consent of the instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4372,104,362,'Curriculum in Grades 7-12',3838,'Provides students with theoretical knowledge and professional practice in secondary physical education curriculum and instruction. This research-based course emphasizes effective teaching, development of content, and analysis of curricular models in grades 7-12. Prerequisite: Junior standing or above and enrollment in the Teacher Certification program or consent of instructor.'),
 (4373,104,363,'Instructional Strategies in PE',3839,'Analyzes the teaching-learning process, emphasizing the identification of instructional strategies specific to the development of skilled performance in movement activities.  Prerequisite: Junior standing or above and enrollment in the Teacher Certification program.'),
 (4374,104,364,'Exper in the Common School',3840,'Supervised practice in observing, assisting, and teaching children in elementary, junior high school, and senior high school. Emphasis is on understanding motor behavior, teacher-learner behavior, and interrelatedness with other aspects of the learning environment. May be repeated to a maximum of 6 hours. Prerequisite: Junior standing or above and enrollment in the Teacher Certification program or consent of the instructor.'),
 (4375,104,381,'Clinical Progression AT 4',3841,'Progression in athletic training clinical skills for the Athletic Training Education Program. Emphasis is on mastery of entry-level athletic training skills for therapeutic modalities and knowledge of athletic injuries for the athletic training profession to real problems in the context of patient, subject and athlete outcome objectives. Prerequisite: KIN 282 and consent of academic advisor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4376,104,382,'Clinical Progression AT 5',3842,'Progression in athletic training clinical skills for the Athletic Training Education Program. Emphasis is on mastery of entry-level athletic training skills on athlete evaluation and physical assessment skills of athletic injuries for the athletic training profession to real problems in the context of patient, subject and athlete outcome objectives. Prerequisite: KIN 381 and consent of academic advisor.'),
 (4377,104,384,'Capstone Proficiency AT',3843,'Capstone clinical proficiency for the Athletic Training Education Program. Emphasis is on independent clinical proficiency of athletic training skills. Students may choose any 384 section for their capstone clinical experience: High School, Rehabilitation, SportWell and Athletic Training Room sections. May be repeated to a maximum of 8 hours. Prerequisite: Concurrent enrollment or credit in KIN 381, or consent of academic advisor.'),
 (4378,104,385,'Exper in Kinesiology Research',3844,'Supervised laboratory experiences in kinesiology research; individual work under the supervision of members of the faculty in their respective fields. The student assists with data collection, processing, and analysis for research in progress. May be repeated to a maximum of 12 hours.  Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4379,104,386,'Exercise Instruction & Elderly',3845,'This course is designed to offer practical experience opportunities to undergraduate Kinesiology students aspiring to work in applied exercise fields with a diverse aged population.  It will entail extensive \"on the job\" training through the Lifetime Fitness Program, an older adult service program of the Department of Kinesiology.  Additionally, students will gain training in current program management practicies.   May be repeated to a maximum of 6 hours.  Prerequisite: Kinesiology 352 (Bioenergetics of Movement) or consent of instructor is required.  A current CPR is required at the beginning of the term and certification must remain current.'),
 (4380,104,387,'Exper in the Agency Setting',3846,'Supervised practical experience in leadership roles in nonschool agency settings; emphasis on observing, planning, and conducting physical activity programs for children and/or adults in preschool, recreation, or other social agencies. May be repeated for a maximum of 6 hours.'),
 (4381,104,390,'Honors',1642,'Lectures and discussion dealing with issues in kinesiology, dance, health education, recreation education, and related fields. Same as CHLH 390, and LEIS 390. May be repeated to a maximum of 6 hours.  Prerequisite: James Scholar standing or grade-point average of 3.0.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4382,104,391,'Special Project-Problems',3847,'Special projects in research and independent investigation in any phase of health, kinesiology, physical education, and related areas selected by the students. May be repeated to a maximum of 6 hours.  Prerequisite: Junior or senior standing; grade-point average of 2.5; consent of instructor.'),
 (4383,104,393,'Honors Thesis',3848,'Planning, researching and writing of an honors thesis, under supervision of a faculty member, on a problem of appropriate scope and character. Paper will be presented at a suitable meeting and/or seminar.  May be repeated to a maximum of 6 hours.  Prerequisite: Senior standing when enrolling; minimum grade point average (total, University and Kinesiology prefix courses) of 3.25; a minimum of one full year (2 semesters) remaining at the University of Illinois, Urbana-Champaign campus; and submission of a written proposal.'),
 (4384,104,401,'Measure & Eval in Kinesiology',3849,'Examines the concepts of observation, measurement, and evaluation of human motor performance and functioning in physical activity contexts.  3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 140 and KIN 150, or graduate standing, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4385,104,407,'Disability, Culture & Society',618,'Same as ANTH 404, CHLH 407, and REHB 407. See CHLH 407.'),
 (4386,104,429,'Structural Basis of Movement',3850,'Advanced study of the structural basis of human movement, with special emphasis on the functional anatomy which underlies clinical Kinesiology. 3 undergraduate hours. 4 graduate hours.  Prerequisite: MCB 334 or graduate standing or consent of instructor.'),
 (4387,104,441,'Games in Culture',3851,'Examines game phenomena as cultural action systems with special emphasis on the biosocial behavior expressed in varying societies; topics include game components, cultural contexts, ecological strategies, enculturation, acculturation, symbolism, change process, and maladaptive behavior. 3 undergraduate hours. 4 graduate hours. Prerequisite: Junior, Senior or graduate standing or consent of instructor.'),
 (4388,104,442,'Body, Culture & Society',3291,'Analysis of the significant social aspects of the human body including anthropological, historical, psychological and sociological perspectives. Places emphasis on cross-culture and cross-national studies of bodily behavior with particular stress on exercise, health and sport practices. Same as GWS 442. 3 undergraduate hours. 4 graduate hours. Prerequisite: KIN 249 or SOC 249, or graduate standing; or consent of instructor.'),
 (4389,104,443,'Psychophysiology in Ex & Sport',3852,'Designed to give the student an understanding of the interaction between psychological processes and physiological parameters in exercise and sport. Examines psychophysiological exercise and sport research with particular attention to relevant models and theories. Same as PSYC 443. 3 undergraduate hours. 4 graduate hours. Prerequisite: Junior or senior standing, KIN 240, or graduate standing, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4390,104,446,'Gender & Physical Activity',3293,'Examination of the gendered nature of physical activity in such contexts as physical education, sport, play, games, and leisure. Analyzes theoretical and methodological assumptions pertaining to gender and physical activity/sport contexts with particular attention on concepts of masculinity, femininity, role conflict, socialization influences and media representations of gender. The feminist critique of socio-cultural sport studies is also considered.  Same as GWS 446.  3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 240, or graduate standing, or consent of instructor.'),
 (4391,104,447,'Psych of Sport Performance',3853,'Outlines the social psychological parameters which influence behavior and performance in sport; emphasizes the impact of social influences upon the individual within the sport context, including such factors as achievement motivation, competition, anxiety, aggression, and personality.  Same as PSYC 447. 3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 140, KIN 247, or PSYC 201, or graduate standing, or consent of instructor.'),
 (4392,104,448,'Exercise & Health Psychology',1649,'Examines the psychological determinants and consequences of exercise and physical activity as a health promoting behavioral process.  Same as CHLH 448. 3 undergraduate hours. 4 graduate hours.  Prerequisite: Junior standing or above, or graduate standing, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4393,104,449,'Sociology of Sport',3854,'Sociological analysis of sport as a sociocultural system which progresses from the micro to the macro level; focuses on theoretical and conceptual issues in sociology of sport.  Same as SOC 479. 3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 249, or SOC 249, or graduate standing, or consent of instructor.'),
 (4394,104,450,'Biochemistry of Exercise',3855,'Introduces the metabolic and biochemical adaptation of the body in response to acute and chronic physical activity. Primary focus is given to the subcellular and enzymatic regulation and integration during exercise. Substrate metabolism, bioenergetics, hormonal action and nutritional influences as related to exercise are emphasized.  3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 352 or MCB 350 or consent of instructor.'),
 (4395,104,452,'Clin & Applied Ex Physiology',3856,'Physical fitness appraisal and guidance in clinical and applied settings with emphasis on medical clearance, risk factor assessment, physical fitness assessment and exercise prescription. 3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 352, or graduate standing, or consent of instructor.'),
 (4396,104,453,'Body Composition',3857,'Examines the theoretical and technical aspects of measuring human body composition; introduces relevant techniques and instrumentation currently in use; reviews research methodology and findings pertaining to the effects of exercise conditioning and nutritional modification on body composition. 3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 352 or graduate standing, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4397,104,454,'Growth & Physical Development',3342,'Study of the growth and physical development of children through adolescence with emphasis on those systems and body composition changes related to motor performance and exercise stress. Same as HDFS 454. 3 undergraduate hours. 4 graduate hours.  Prerequisite: MCB 103 and MCB 334 or graduate standing or consent of instructor.'),
 (4398,104,455,'Quant Analysis of Human Motion',3858,'Quantitative mechanical analysis of human motion using film-based, video-based, and optical-electronic motion measurement systems. Basic mechanical concepts are presented using a vector algebra approach. The focus is on two- dimensional motion analysis with an introduction to three-dimensional techniques. 3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 355 or graduate standing or consent of instructor.'),
 (4399,104,456,'Electromyographic Kinesiology',3859,'Focuses upon the biological components of volitional and reflexive movement in humans; theory and technology of electromyography are utilized to describe and quantify the neuromuscular input to the mechanical output. 3 undergraduate hours. 4 graduate hours.  Prerequisite: MCB 103, MCB 334, or graduate standing or consent of instructor.'),
 (4400,104,457,'Motor Learning & Control',3860,'Discussion and analysis of scientific principles related to the learning and control of motor skills; review of related literature and research in motor learning and control. The focus of the course is on mechanisms for the control of movement and recent theories of how movements are acquired and performed. Course iformation: 3 undergraduate hours. 4 graduate hours. Prerequisite: KIN 257 or graduate standing or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4401,104,458,'Neurobio of Aging',3861,'Same as PSYC 451 and NEUR 451. See PSYC 451.'),
 (4402,104,459,'Physical Activity & Aging',3862,'Examines aging and age-related changes in the cells, tissues, organs, and systems of the human body; emphasizes the role of physical activity and other lifestyle choices in modifying the aging process and in influencing the onset and progression of the chronic diseases which accompany aging. 3 undergraduate hours. 4 graduate hours.  Prerequisite: Junior, Senior, or graduate standing or consent of instructor.'),
 (4403,104,460,'Technology & Pedagogical KINES',3863,'Promotes mastery of technology skills and complex computer applications through the analysis of research and critical issues related to technology in Kinesiology. The completion of technology modules, requiring problem solving and the collection and analysis of assessment data, will culminate in an interactive, multimedia project. 3 undergraduate hours. 4 graduate hours.  Prerequisite: Junior standing.'),
 (4404,104,462,'Research on Teacher Education',3864,'Critically examines theories, trends, problems, and implications of research on teacher education in Kinesiology. Students will complete a series of written assignments that are grounded in theory, illustrate critical thinking skills, and demonstrate knowledge of the literature. 3 undergraduate hours. 4 graduate hours.  Prerequisite: Junior, senior, or graduate standing.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4405,104,473,'Skill Acquisition Strategies',3865,'Examines theory and practice related to structuring practice conditions to maximize the acquisition and performance of motor skills. The nature of skill, activities, and strategies for enhancing skill are discussed with particular emphasis placed on strategies that instructors, teachers, and/or coaches can use to enhance skill acquisition and performance. 3 undergraduate hours. 4 graduate hours.  Prerequisite: KIN 257 or graduate standing or consent of instructor.'),
 (4406,104,481,'Sport Med I: Patho & Inj',3866,'Pathology of athletic injury and illness. Emphasis on acquisition of athletic training knowledge and understanding of the physiological response of human growth and development and the progression of injuries, illness and disease processes in athletes. Students complete 30 hours of observation with a consulting physician in sports injury triage. 2 undergraduate hours. 2 graduate hours.  Prerequisite: KIN 320, or consent of instructor.'),
 (4407,104,482,'Sport Med II: Pharm & Inj',3867,'Knowledge of pharmacology in athletic training including the applications, indications, contraindications, precautions, and interactions of medications as well as governing regulations with emphasis on acquisition of athletic training values and athletic training policies and procedures. Students complete 30 hours of observation with a consulting physician.  Prerequisite: KIN 320, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4408,104,483,'Sport Med III: Med Sup: CI',3868,'Fundamentals of medical supervision with the emphasis on acquisition of athletic training skills to recognize, treat, and refer, when appropriate, the general medical conditions and disabilities of athletes. This includes 30 hours of clinical practice with a consulting physician, including selected medical criterion for return to sport participation. 2 undergraduate hours. 2 graduate hours. May be repeated to a maximum of 4 hours.  Prerequisite: KIN 320, or consent of instructor.'),
 (4409,104,484,'Clin Resrch in Sports Med & AT',3869,'Current topics course including lectures, discussions, invited speakers, faculty presentation, and student presentation in the study and analysis of research topics in Sports Medicine and Athletic Training. The topics will be based on the National Athletic Trainers\' Association\'s Educational Competencies and the U.S. Joint Review Committee on Athletic Training.  May be repeated to a maximum of 4 hours.  Prerequisite: KIN 320 or consent of instructor.'),
 (4410,104,485,'Clin Exper in Sports Medicine',3870,'Clinical experiences in medical supervision of sports programs, in the areas of therapeutic exercises, fitness programming, and cardiac rehabilitation.  May be repeated to a maximum of 8 hours.  Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4411,104,494,'Special Topics',3871,'Lecture course on topics of current interest; specific topics announced in the Schedule. May be repeated.'),
 (4412,104,501,'Kinesiology Research Methods',3872,'Review and appraisal of common research procedures; application of statistical procedures, library methods, evaluation procedures, and experimental methods.'),
 (4413,104,520,'Issues in Sports Medicine',3873,'Addresses current issues in the medical aspects of sports; examples of these issues are epidemiology of injuries and treatment forms, use of sports equipment, questionable sports practices, and preventive techniques.'),
 (4414,104,522,'Ortho-Kinesiology',3874,'Analyzes pathomechanics underlying injury and orthopedic problems; also analyzes rehabilitation methods for orthopedic and neurological dysfunctions.'),
 (4415,104,547,'Sport Psychology',3875,'Analysis of psychological factors and principles with special reference to motor performance, learning motor skills, perception, and emotion in sports situations; review of literature; and independent projects.  Prerequisite: KIN 447 or consent of instructor.'),
 (4416,104,551,'Sci Basis of Phys Performance',3876,'Contemporary trends in the study of human performance and exercise stress; analysis of the research literature, experimental strategies, and research instrumentation. Lecture-discussion and laboratory.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4417,104,552,'Neuromuscular Physiology',3877,'In-depth study of the neuromuscular aspects of human activity; focus on selected topics related to growth, physical development, exercise prescriptions, athletic conditioning, and fitness.'),
 (4418,104,553,'Circulorespiratory Physiology',3878,'Aerobic performance responses to short-term, intermittent, and prolonged physical activity; special consideration given to endurance training methods and assessment techniques, ergogenic aids, and problems associated with growth, environmental influences, and competitive sport.  Prerequisite: KIN 551 or consent of instructor.'),
 (4419,104,555,'Experimental Kinesiology',3879,'Mechanical and neuromuscular approach to human movement; analysis and experimental research.  Prerequisite: KIN 455 or consent of instructor.'),
 (4420,104,557,'Stress Immunology',3880,'This course will examine the role of stress in modulating immune function and the pathobiological mechanisms resulting in disease.  An emphasis will be placed upon the reciprocal communication pathways that exist between the central nervous, endocrine and immune systems.   Prerequisite: Consent of the instructor.  It will be assumed that students will have introductory knowledge in biochemistry, endocrinology, and immunology.'),
 (4421,104,569,'Administration of PE & Sport',3881,'Analysis of completed research relating to theory and practice of administration in physical education and sport; the development of policy statements and procedures manuals for the various educational levels; and experience in the use of the case plan of instruction as a teaching technique for the development of competence and knowledge relating to human relations and administration in this specialized field.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4422,104,575,'Leisure and Culture',716,'Same as ANTH 575, and RST 575. See RST 575.'),
 (4423,104,590,'Independent Study',3882,'Independent research on special projects. May be repeated.'),
 (4424,104,591,'Seminar',3883,'Lectures, discussions, and critiques on kinesiology and related subjects by faculty members and visiting professional leaders; presentation and criticism of student research.  May be repeated in the same or subsequent terms as topics vary. Approved for S/U grading only.'),
 (4425,104,594,'Special Topics',3884,'Lecture course in topics of current interest; specific subject matter announced in the Schedule. May be repeated.'),
 (4426,104,599,'Thesis Research',3885,'Preparation of theses in kinesiology. May be repeated. Approved for S/U grading only.'),
 (4427,105,201,'Elementary Korean I',3886,'Introduction to Korean, including conversation with a native Korean-speaking tutor under the direction of a linguist-instructor, and a minimum of formal grammar and writing. Credit is not given for both KOR 201 and KOR 221.'),
 (4428,105,202,'Elementary Korean II',3887,'Second term of spoken Korean, including conversation with a native Korean-speaking tutor under the direction of linguist instructor; studies formal grammar based on conversational materials; and includes some work on written Korean. Credit is not given for both KOR 202 and KOR 222. Prerequisite: KOR 201.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4429,105,203,'Intermediate Korean I',3888,'First term of second year of the Korean language, including drill for advanced conversational fluency; introduces a variety of styles and levels of discourse and usage; and increases study of the written language and formal grammar. Credit is not given for both KOR 203 and KOR 222. Prerequisite: KOR 202.'),
 (4430,105,204,'Intermediate Korean II',3889,'Second term of second year of the Korean language including drill for more advanced conversational fluency; more variety of styles and levels of discourse and usage; more formal grammar and an introduction of basic Chinese characters. Credit is not given for both KOR 204 and KOR 241. Prerequisite: KOR 203.'),
 (4431,105,221,'Korean Reading and Writing I',3890,'Introduction to Korean orthography, syntax, and vocabulary for students who have the ability to speak Korean. Credit is not given forboth KOR 221 and/or KOR 201 - KOR 204. Prerequisite: Ability to speak Korean as determined by a placement test.'),
 (4432,105,222,'Korean Reading and Writing II',3891,'Continuation of KOR 221. Credit is not given for both KOR 222 and either KOR 202 or KOR 203. Prerequisite: KOR 221.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4433,105,241,'Korean Reading and Writing III',3892,'Continuation of KOR 222. Credit is not given for both KOR 241 and either KOR 204 or KOR 305. Prerequisite: KOR 222.'),
 (4434,105,242,'Korean Reading and Writing IV',3893,'Continuation of KOR 241. Credit is not given for KOR 242 and KOR 306. Prerequisite: KOR 241.'),
 (4435,105,305,'Advanced Korean I',3894,'Concentrates on the ability to engage in fluent discourse, on comprehensive grammatical knowledge, and on the ability to read ordinary texts in Korean, including some Chinese characters. Credit is not given for KOR 305 and KOR 241. Prerequisite: KOR 204.'),
 (4436,105,306,'Advanced Korean II',3895,'Continuation of KOR 305; emphasizes rapid reading, fluent conversation, learned vocabulary and idiom acquisition, and reading of newspapers. Credit is not given for KOR 306 and KOR 242. Prerequisite:  KOR 305.'),
 (4437,105,440,'Fourth Year Korean I',3896,'Develop skills in upper advanced level Korean language use including formal writings and readings in materials on culture and society, sophisticated appropriate speaking skills.  Basic Chinese characters are systematically introduced.  Prerequisite: KOR 242 or KOR 306.'),
 (4438,105,441,'Fourth Year Korean II',3897,'Continuation of KOR 440.  Prerequisite: KOR 440 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4439,105,490,'Readings in Korean Lit',3898,'Guided reading in Korean literature in the vernacular. 3 undergraduate hours. 4 graduate hours. May be repeated to a maximum of 6 undergraduate hours, or 8 graduate hours. Prerequisite: Reading knowledge of Korean and consent of instructor.'),
 (4440,106,101,'Landscape Architecture Intro',3899,'Survey of the discipline, practice, and philosophy of landscape architecture'),
 (4441,106,199,'Undergraduate Open Seminar',3900,'May be repeated.'),
 (4442,106,212,'Water and Society',3901,'A comparative investigation of built landscapes and hydraulic resources through history.  Examines problems of water scarcity, abundance and changes in ecology, human social organization, economy, law, and cultural values related to natural water conditions and human management.  Comparative case studies include the ancient Near East and modern Middle East, ancient and modern Egypt, the Roman empire, Peru, the Netherlands, South Asia, Illinois River basin, and the American West.'),
 (4443,106,215,'Buildings, Land and Culture',736,'Introduction to the study of the ordinary landscape and its representation in the common media. Investigates ordinary places as indicators of cultural values and beliefs, and why they look and function in certain ways. Topics approached through historical accounts and thematic analysis. Same as ARCH 215.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4444,106,218,'S. Asian Cultural Landscapes',1058,'Survey of Hindu, Buddist, and Islamic landscapes of South Asia. Examines urban structures, building typologies, and open space types through history as influenced by concepts of the natural, sacred, political, and social. Same as ASST 218.'),
 (4445,106,220,'African Arch & Urbanism',570,'Examines the buildings, landscapes, and societies of pre-colonial, sub-Saharan African cities from the third century BCE until the nineteenth century CE.  Same as ANTH 223.'),
 (4446,106,222,'Islamic Gardens & Architecture',3902,'Study of the formation, history, and meaning of the landscape and architecture of the Islamic world.'),
 (4447,106,233,'Foundation Design Studio',3903,'Introduction to the fundamentals of design, including studies in two- and three-dimensional abstract and applied problems, basic elements and procedures of design, and principles of landscape composition. Open to Landscape Architecture majors only. Prerequisite: Credit or concurrent registration in LA 280, or consent of instructor'),
 (4448,106,234,'Site Design Studio',3904,'Site as the fundamental unit of landscape design. Involves ecological, cultural and experiential understanding of sites, and the creation of place-specific designs. Field trip required; see Class Schedule for current fees.  Prerequisite: LA 233 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4449,106,241,'Landform Design & Construction',3905,'Introduction to landform design, drainage, stormwater management, surveying, and materials.  Prerequisite: MATH 014 or 016'),
 (4450,106,242,'Nature and American Culture',3406,'Same as HIST 282, RST 242, and NRES 242. See RST 242.'),
 (4451,106,244,'Sci-Values of Envt Decisions',3906,'Same as RST 244, and NRES 244. See RST 244.'),
 (4452,106,250,'Environmental Site Analysis',3907,'Principles and practices of identifying, analyzing, and recording landscape resources. Field trip required; see Class Schedule for current fees.  Prerequisite: GEOL 101or GEOG 103 or consent of instructor.'),
 (4453,106,270,'Behavioral Factors in Design',3908,'Introduces the impacts of cultural and social factors, such as age, gender, physical ability, economic status, ethnicity and how people interact with the environment. Reading assignments, short exercises, field trips, and evaluation of space will enable students to evaluate  and potentially design more socially and ecologically responsive environments.'),
 (4454,106,280,'Design Communications I',3909,'Fundamentals  of visual communication in the design process and presentation for landscape architecture.  Includes freehand and constructed drawing, color, media, and models. Open to Landscape Architecture majors only. Prerequisite: Concurrent registration in LA 233');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4455,106,281,'Design Communications 2',3910,'Advanced principles and techniques of visual communication in landscape architectural rendering, emphasizing computer-based techniques. Open to Landscape Architecture majors only. Prerequisite: Concurrent registration in LA 234; completion of LA 280 and completion of campus Composition I general education requirement or consent of instructor'),
 (4456,106,301,'Senior Honors',3911,'Independent guided study and research in a selected area of landscape architecture; for candidates for honors in landscape architecture. May be repeated to a maximum of 9 hours.  Prerequisite: Senior standing in landscape architecture, a university grade-point average of 3.0, and consent of head of department.'),
 (4457,106,314,'History of World Landscapes',741,'Analysis of the development of landscape architecture as a result of environmental and cultural influences.  Same as ARCH 314.'),
 (4458,106,315,'History of Modern Lndscpe Arch',3912,'A selected overview of developments in landscape architecture in the western world from 1900 to the present. Prerequisite: LA 314.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4459,106,335,'Community & Open Space Studio',3913,'Development of design solutions at site and master plan scale relative to community, urban and open space problems; emphasizes development of analysis and design techniques to integrate physical context of place with social context. Field trip required; see Class Schedule for current fees.  Prerequisite: LA 234 or consent of instructor.'),
 (4460,106,336,'Design Workshop Studio 1',3914,'Project design at various scales utilizing problems of a wide range of complexity and subject matter; rural, community, and urban problems, housing, recreation, and natural areas; emphasizes problem analysis and generation of innovative design alternatives. Students select from several sections depending on specific interests.  Prerequisite: LA 335 or consent of instructor'),
 (4461,106,342,'Site Engineering',3915,'Principles of site engineering including landform design, stormwater management, site surveying, circulation systems and site utility planning  Prerequisite:  LA 241 and college trigonometry; or consent of instructor.'),
 (4462,106,343,'Landscape Construction',3916,'Construction methods, materials, and procedures related to the design of landscape structures; development of design details and cost estimating.  Prerequisite: LA 342 or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4463,106,345,'Professional Internship',3917,'Professionally supervised field experience in private firms and public agencies designed to introduce students to professional practice. Students work in a department-approved firm or agency of their choice either during a regular or summer session. Approved for S/U grading only.  May be repeated to a maximum of 3 hours.  Prerequisite: Junior standing and consent of instructor.'),
 (4464,106,346,'Professonal Practice',3918,'Study of the profession of landscape architecture including an introduction to modes of practice, career evolution, organizational theory, office procedures, project management and professional ethics.  Prerequisite: Junior standing or consent of instructor'),
 (4465,106,390,'Independent Study',3919,'Supervised independent study, research, or special project in a selected area related to landscape architecture. May be repeated to a maximum of 9 hours.  Prerequisite: Junior or senior standing; consent of instructor and head of department prior to advance enrollment and registration.'),
 (4466,106,399,'Off-Campus Study',3920,'Provides campus credit for off-campus study. (Summer session, 0 to 6 undergraduate hours).  Final determination of appropriate credit is made by a faculty review committee upon completion of the student\'s work. Maximum credit, 15 hours (summer session, 6 hours), all of which must be earned within one term. Approved for both letter and S/U grading.  Prerequisite: Junior standing; prior review and approval of the student\'s written proposal by a faculty committee and the department head.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4467,106,425,'Hist Geog Amer Land to 1880',3107,'Same as GEOG 425. See GEOG 425.'),
 (4468,106,426,'Hist Geog Amer Land Since 1880',3108,'Same as GEOG 426. See GEOG 426.'),
 (4469,106,427,'Amer Vernacular Cultural Land',3109,'Same as GEOG 427. See GEOG 427.'),
 (4470,106,437,'Regional Design Studio',3921,'Ecological design and planning studio emphasizing team approaches to design development and evaluation using current human and environmental research results. Projects require field work, analysis, problem-solving, and advanced design and presentation products. 5 undergraduate or 6 graduate hours. Prerequisite: LA 336 or consent of instructor.'),
 (4471,106,438,'Design Workshop Studio 2',3922,'Project design at various scales utilizing problems of a wide range of complexity and subject matter; rural, community, and urban problems, housing, recreation, and natural areas; and emphasizes problem analysis and generation of innovative design alternatives. The student selects from several sections depending on specific interests. 5 undergraduate hours, or 3 to 6 graduate hours Prerequisite: LA 336 or consent of instructor'),
 (4472,106,441,'Land Resource Evaluation',3923,'Examines concepts for the value of land, land resource problems and policy responses, methods for evaluating land resource development and policy alternatives, and case studies of land resource evaluation. Same as UP 441.  Prerequisite: Graduate standing or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4473,106,444,'Social Impact Assessment',2687,'Same as ENVS 444, RST 444, NRES 444, RSOC 444, and UP 444. See RST 444.'),
 (4474,106,450,'Ecology for Land Restoration',3924,'Ecological implications of alternative land use patterns; equipment, field techniques, and nomenclature in current use by environmental consultants; and elements of a baseline ecosystem study.  Prerequisite: Consent of instructor.'),
 (4475,106,452,'Natural Precedent in Planting',3925,'Biogeography; identification of native species, uses of native plants in the landscape; and restoration and planting design projects. Field trips required.  Prerequisite: HORT 302 or consent of instructor.'),
 (4476,106,453,'Cultural Precedent in Planting',3926,'Planting design issues; historic precedent and contemporary comprehensive design projects; management practices; technical documents; and plant use and identification. Field trips required.  Prerequisite: LA 452'),
 (4477,106,454,'Landscape Archaeology',651,'Same as ANTH 453.  See ANTH 453.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4478,106,460,'Heritage Management',656,'Same as ANTH 460.  See ANTH 460.'),
 (4479,106,470,'Social/Cultural Design Issues',3927,'Critical discussion of notions and theories pertaining to the reciprocal effects of landscape architectural design and human behavior.'),
 (4480,106,472,'Museum Theory and Practice',658,'Same as ANTH 462 and ARTH 462.  See ANTH 462.'),
 (4481,106,501,'Landscape Arch Theory & Prac',3928,'Seminar to introduce the discipline, profession, and practice of landscape architecture. Emphasis is on understanding the skills and knowledge base of the profession including environmental, social and historical factors in design.'),
 (4482,106,505,'Methods in Arch & LA History',3929,'Seminar on the historiography of architectural and landscape history, including an introduction to the major concepts and figures in the discipline, past and present. Students will learn of approaches historians have used for analyzing the built environment from traditional methods to newer interpretive frameworks, and examine how contemporary values determine or inform the writing of history.'),
 (4483,106,506,'Landscape and Vision',3930,'A study of the major 20th-century texts on vision, perception, and perspective as applied to architecture and landscape.  Prerequisite: doctoral students only; master\'s level students must receive permission from instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4484,106,513,'History of World Landscapes',776,'Introduction to the landscape architectural heritage of the past in its social, environmental and historical context.  Same as ARCH 510.'),
 (4485,106,515,'Hist & Thry of Modrn Land Arch',3931,'A selected overview of developments in landscape architecture in the western world from 1900 to the present.  Prerequisite: LA 513 or approval of instructor.'),
 (4486,106,517,'Land and Society',3932,'Historical and cross-cultural investigation of the use, shaping, and perception of the land-based environment; case studies, critical problems and issues, and theories of social-environmental interaction.  Prerequisite: Consent of instructor.'),
 (4487,106,537,'Landscape Plan & Design Studio',3933,'Ecological design and planning studio emphasizing design that reflects evaluation and integration of human and environmental research results. Detailed investigation of design options.  Prerequisite: LA 441 and LA 450, or consent of instructor.'),
 (4488,106,540,'Public Involvement in Res Mgmt',2693,'Same as ENVS 540, RST 540, NRES 540, RSOC 540, and UP 540. See NRES 540.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4489,106,542,'Landscape Modeling',3934,'Computational representations and modeling of landscape processes and solution methods for problems involving the special arrangement of land use activities: estimation, simulation and optimization methods, their effectiveness, efficiency; and application. Same as UP 542.  Prerequisite: LA 441 or consent of instructor'),
 (4490,106,550,'Environ. Impact Assessment',3935,'Requirements of the National Environmental Policy Act and Guidelines from the Council on Environmental Quality for preparing and writing environmental impact statements; includes interdisciplinary team efforts and impact assessment techniques.  Prerequisite: Graduate or law school standing, or consent of instructor.'),
 (4491,106,562,'Social Construction of Space',710,'Same as ANTH 557. See ANTH 557.'),
 (4492,106,563,'Soc/Beh Research Designed Env',802,'Same as ARCH 563. See ARCH 563.'),
 (4493,106,565,'Design/Behavior Studio',804,'Development of site or project scale design emphasizing the integration of user needs and behavioral factors. Same as ARCH 565. May be repeated to a maximum of 12 hours.  Prerequisite: LA 564, or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4494,106,583,'Environ History Cities&Regions',3135,'Same as GEOG 583, and UP 583. See UP 583.'),
 (4495,106,587,'Graduate Seminar',3936,'Preparation, presentation, and discussion of research papers on current and future areas of landscape architectural application. May be repeated.  Prerequisite: Consent of instructor'),
 (4496,106,590,'Directed Research',3937,'Nature and scope of projects to be determined by consultation between student and faculty adviser; open to landscape architecture majors as well as those from other disciplines who wish to engage in interdisciplinary work. Approved for both letter and S/U grading.  May be repeated.  Prerequisite: Consent of instructor.'),
 (4497,106,593,'Islamic & S Asian Landscapes',3938,'Topics in Islamic and South Asian cultural landscape history, including historiography, methodology and recent scholarship.  An advanced course that requires disciplinary familiarity with research on the built environment, material culture and visual culture.  May be repeated up to 8 hours per semester to a maximum of 12 total hours.'),
 (4498,106,594,'Cultural Heritage Landscapes',3939,'Topics in cultural landscape heritage, conservation planning and design.  Investigates theories of landscape, heritage, and their intersections, with readings drawn from anthropology, geography, and landscape studies, as well as applied work on historical landscape conservation, preservation and management.   May be repeated up to 10 hours per semester to a maximum of 16 total hours.  Prerequisite: Concurrent enrollment in LA 438 Design Workshop II may be required in the spring semester; check Class Schedule.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4499,106,598,'Master\'s Project',3940,'Major independent or small-group project synthesizing knowledge from previous coursework.  Approved for both letter and S/U grading.  Prerequisite: Consent of instructor and program adviser.'),
 (4500,106,599,'Thesis Research',3941,'May be repeated. Approved for S/U grading only. Prerequisite: Graduate standing in landscape architecture'),
 (4501,107,100,'Intro Collaborative Learning',3942,'Weekly meetings for students enrolled in the College of LAS Learning Communities program to prepare freshmen for collaborative learning environments through campus orientation, study skills, study groups, and project-based assignments related to their linked courses. Approved for S/U grading only. Prerequisite: Students must be enrolled in a freshman LAS Learning Community set of linked courses.'),
 (4502,107,101,'Global Awareness I',3943,'Weekly meetings for students enrolled in the College of LAS Global Studies Initiative program to prepare freshmen for collaborative learning environments through campus orientation, study skills, study groups, and project-based assignments related to their global studies course.  Prerequisite: Students must be enrolled in a freshman LAS Global Studies Initiative Course.'),
 (4503,107,102,'Global Awareness II',3944,'Weekly meetings for students enrolled in the College of LAS Global Studies Initiative program to prepare freshmen for collaborative learning environments through campus orientation, study skills, study groups, and project-based assignments related to their global studies course.  Spring Semester Course.   Prerequisite: Students must be enrolled in a freshmen LAS Global Studies Initiative Course.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4504,107,110,'Workshop-Tutorial',3945,'Independent study and experimental seminars open to Unit One students and to others; specific offerings vary each term. Credit toward college or departmental requirements is contingent upon approval by the appropriate unit. A total of 12 hours of LAS 110 credit may be applied toward graduation in the College of Liberal Arts and Sciences. Approved for both letter and S/U grading. Prerequisite: Unit One students or consent of Unit One Director.'),
 (4505,107,199,'Undergraduate Open Seminar',3946,'May be repeated.'),
 (4506,107,298,'Study Abroad Predepart Orient',3947,'Eight-week course designed for students planning to study abroad. Examines the effects of different cultural orientations upon interaction and adjustment; explores the issues surrounding the experience of entering, interacting within, and returning from a foreign culture; increases awareness of verbal, non-verbal, and cultural factors affecting information processing. Course combines the theoretical with a strong experiential component and hands-on training. Essential for any country of destination and length of sojourn.  Prerequisite: Study Abroad Office approval.'),
 (4507,107,299,'LAS Study Abroad',3948,'Provides credit toward the undergraduate degree for study at accredited foreign institutions or approved overseas programs. Final determination of credit is made upon the student\'s completion of the work. (Summer session, 0 to 8 hours). May be repeated to a maximum of 36 term hours per academic year or to a total of 44 term hours, all of which must be earned within one calendar year. Approved for both letter and S/U grading.  Prerequisite: One year of residence at UIUC, good academic standing, and prior approval of the major department and the College of Liberal Arts and Sciences.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4508,107,494,'Senior Project',3949,'For students seeking graduation with distinction in IPS. 2 or 4 undergraduate hours.  No graduate credit.  May be repeated to a maximum of 4 undergraduate hours. Prerequisite: Consent of instructor and IPS Advisory Committee; open only to students whose major is IPS and who have a cumulative grade point average of at least 3.25.'),
 (4509,107,495,'Interdiscipl Honors Seminar',3950,'Seminar on interdisciplinary topics in the natural sciences, social sciences, humanities, and arts. 3 undergraduate hours. No graduate credit. May be repeated to a maximum of 6 hours.  Prerequisite: Consent of instructor.'),
 (4510,108,170,'Introduction to Latin America',3951,'Interdisciplinary introduction to the ways of life of Latin American peoples, their origins and current expressions; discusses social, economic issues, and domestic and international policies related to them in the context of other societies in developing countries.'),
 (4511,108,199,'Undergraduate Open Seminar',3952,'May be repeated.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4512,108,240,'Constr Afr and Carib Identity',357,'Same as AFST 209, CWL 225, and FR 240. See FR 240.'),
 (4513,108,325,'Cultural Prod in Dictatorships',3953,'Cultural production (visual and literary) of three Latin American dictatorships: Chile (1973-1990), Uruguay (1973-1985) and Argentina (1976-1983).  Provides a comparative study of narrative- both short stories and novels- and films covering the dictatorship years and the process of redemocratization.  The guiding questions are: how and through which means can a traumatic past be represented?  Do texts and films share topics and strategies to depict authoritarianism?   Prerequisite: LAST 170 or consent of instructor.'),
 (4514,108,395,'Special Topics',3954,'Topical survey of cultural, social, economic, and political factors in Latin American life. Each term a particular topic is considered.  Prerequisite: A basic course in a humanities or social science discipline.'),
 (4515,108,401,'Latin American Ethnobotany',615,'Background in the major issues involving plant sciences, culture, and history, focused on indigenous knowledge and practices in Latin America. Same as ANTH 401, HIST 409, and IB 454. 3 undergraduate hours. 4 graduate hours.  Prerequisite: Students should have some background in biology, ecology, history, or anthropology, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4516,108,425,'Depictions of Violence',3955,'A comparative study of themes, forms and representations of violence in twentieth-century Latin American literature and cinema.  Examines the treatment of a variety of issues: perpetrators, victims, types of violence, concepts of community, etc in readings and films from Argentina, Colombia, Cuba, Brazil and Mexico.  The readings and films will be related to specific historical periods.  3 undergraduate hours.  4 graduate hours.  Prerequisite: LAST 170 or consent of instructor.'),
 (4517,108,442,'Arts of Colonial Latin America',954,'Same as ARTH 442. See ARTH 442.'),
 (4518,108,445,'Native Latin Amer Languages',3956,'Upon the consent of the Director of the Center for Latin American and Caribbean Studies, tutorials are available in special native Latin American languages not regularly offered by the University (ie. Quechua, Kagchikel Mayan). Tutorials at the elementary, intermediate, and advanced levels may be arranged. Students registering for unit credit for the first two terms must first present satisfactory evidence of knowledge of the language at the elementary level, either in the form of credit earned at another institution or by passing a proficiency examination. May be repeated up to 6 terms successively to a maximum of 16 hours. Graduate credit is given only for work beyond the elementary level. Prerequisite: Consent of instructor.'),
 (4519,108,476,'Four Lat Am Political Ideas',3447,'Same as HIST 408. See HIST 408.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4520,108,490,'Individual Study',3957,'Major tutorial normally taken in the senior year. Students read the works from list devised in consultation with a faculty tutor and write a term paper.  May be repeated as topics vary to a maximum of 6 hours. Prerequisite: LAST 170; a declared major in Latin American and Caribbean Studies; consent of instructor.'),
 (4521,108,550,'Interdisc Seminar Latin Am St',3958,'Examines the interconnections among research approaches and problems in the field of Latin American and CaribbeanStudies. May be repeated to a maximum of 8 hours if topics vary.  Prerequisite: M.A. standing in Latin American and Caribbean Studies, or consent of instructor.'),
 (4522,108,599,'Thesis Research',3959,'Preparation of M.A. thesis. May be repeated to a maximum of 8 hours with approval. Students may register in more than one section per term. Approved for S/U grading only. Prerequisite: M. A. standing in Latin American and Caribbean Studies and consent of instructor.'),
 (4523,109,101,'Elementary Latin I',3960,'Grammar and reading for students who have had no work in Latin.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4524,109,102,'Elementary Latin II',3961,'Grammar and reading of easy prose.  Prerequisite: LAT 101 or one year of high school Latin.'),
 (4525,109,103,'Intermediate Latin',3962,'Review of grammar; reading of easy narrative prose.  Prerequisite: LAT 102 or two years of high school Latin.'),
 (4526,109,104,'Intro to Latin Literature',3963,'Continuation of LAT 103, with readings chiefly in Latin poetic literature.'),
 (4527,109,105,'Intensive Elementary Latin',3964,'Equivalent to LAT 101 and LAT 102. Introduction to basic grammar and syntax for students who have had no previous Latin and want to learn at a rapid rate; use of computer-assisted individual mastery lessons.'),
 (4528,109,106,'Intensive Intermediate Latin',3965,'Equivalent to LAT 103 and LAT 104. Review of grammar and syntax and reading of easy prose and poetry for students who have attained 102 proficiency and wish to advance more rapidly; use of computer-assisted program materials.  Prerequisite: LAT 102 or LAT 105, or a placement score showing high school achievement equivalent to LAT 102.'),
 (4529,109,199,'Undergraduate Open Seminar',3966,'May be repeated.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4530,109,301,'Survey of Latin Literature I',3967,'The republican period.  Prerequisite: LAT 104 or four years of high school Latin.'),
 (4531,109,302,'Survey of Latin Literature II',3968,'The imperial period.  Prerequisite: LAT 104 or four years of high school Latin.'),
 (4532,109,410,'Intro to Indo-European Ling',3258,'Same as GRK 410, and LING 409. See LING 409.'),
 (4533,109,411,'Intermediate Prose Composition',3969,'Practice in the writing of Latin prose.  Prerequisite: LAT 104 or the equivalent.'),
 (4534,109,460,'Medieval Latin',3970,'Literary and historical texts in prose and poetry will be read in the original; the course will also cover patristic writings. Same as MDVL 460.  Prerequisite: Two years of college Latin, or consent of the instructor.'),
 (4535,109,471,'Intro Second Lang Learn Tchg',1612,'Same as CHIN 471, FR 471, GER 469, HUM 471, JAPN 471, RUSS 471, and SPAN 471. See SPAN 471.'),
 (4536,109,475,'Intro to Comm Lang Tchg',1613,'Same as CHIN 475, FR 475, GER 475, JAPN 475, RUSS 475, and SPAN 475. See SPAN 475.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4537,109,478,'Topics Secondary Lang Tchg',1615,'Same as CHIN 478, FR 478, GER 478, JAPN 471, RUSS 478, and SPAN 478. See SPAN 478.'),
 (4538,109,491,'Readings in Latin Literature',3971,'Readings in authors or special topics chosen by the instructor from the entire extant literature in Latin. 3 undergraduate hours. 3 or 4 graduate hours. May be repeated.  Prerequisite: Three years of college Latin or equivalent; consent of instructor.'),
 (4539,109,492,'Senior Thesis',3972,'Thesis and honors. For candidates for honors in Latin and for other seniors. No graduate credit. Prerequisite: Senior standing, and consent of Classics Honors Program.'),
 (4540,109,493,'Independent Reading',3973,'May be repeated to a maximum of 8 undergraduate hours, or 12 graduate hours. Prerequisite: LAT 302 and consent of the instructor.'),
 (4541,109,498,'Senior Survey',3974,'For candidates for honors in Latin and for other seniors. No graduate credit. Prerequisite: Senior standing, and consent of Classics Honors Program.'),
 (4542,109,500,'Latin for Grad Students I',3975,'Basic grammar, syntax, and vocabulary; reading practice. Designed for graduate students who need to use Latin in their research. Credit may not be used toward a graduate degree.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4543,109,501,'Latin for Grad Students II',3976,'Directed readings, largely in medieval and modern Latin. Designed for graduate students who need to use Latin in their research. Credit may not be used toward a graduate degree. Prerequisite: LAT 500 or two years of high school Latin, or equivalent.'),
 (4544,109,511,'Advanced Prose Composition',3977,'Practice in writing Latin prose, with special attention to stylistic questions.  Prerequisite: LAT 411 or equivalent.'),
 (4545,109,520,'Proseminar',3978,'Alternating poetry and prose, concentrates on a major author from one of the following areas: epic, oratory, lyric and elegiac poetry, history, drama, philosophy, satire, or epistolography. Areas normally follow this sequence in successive years. May be repeated to a maximum of 20 hours if topics vary.  Prerequisite: LAT 491 or equivalent.'),
 (4546,109,531,'Special Disciplines',3266,'Same as GRK 531. See GRK 531.'),
 (4547,109,580,'Latin Seminar',3979,'Research on special problems of Latin literature; required of all majors in classical philology.  Prerequisite: A Latin proseminar.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4548,109,595,'Intro to Classical Studies',3268,'Same as GRK 595. See GRK 595.'),
 (4549,109,599,'Thesis Research',3980,'Guidance in writing theses for advanced degrees. May be repeated. Approved for S/U grading only.'),
 (4550,110,199,'Undergraduate Open Seminar',3981,'Approved for both letter and S/U grading.'),
 (4551,110,499,'LAW Study Abroad',3982,'Provides campus credit for study at accredited foriegn institutions or approved overseas programs.  Final determination of credit granted is made after the student\'s successful completion of work.  Approved for both letter and S/U grading.  Prerequisite: Full academic standing in the College of Law or Graduate College and consent of major department.  Law students, successful completion of the year requirements.'),
 (4552,110,501,'Intro to United States Law',3983,'Intensive introduction to the American legal system for graduate law students with prior professional training in non-common law legal systems; stresses the functioning of basic U.S. legal institutions and the techniques of American legal research. 3 professional hours. 4 graduate hours.'),
 (4553,110,597,'Interdisciplinary Legal Res.',3984,'For students pursuing an interdisciplinary research project completed under the joint supervision of a professor from the College of Law and a professor from elsewhere on campus. Prerequisite: Consent of the Associate Dean of the College of Law.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4554,110,599,'Thesis Research',3985,'Approved for S/U grading only.'),
 (4555,110,600,'Pro Bono Service',3986,'Course carries no academic credit, but recognizes law students who provide at least sixty hours of pro bono legal service to the community. The sixty hours of service may be performed at any time during the student\'s three years of law school, and must be documented through reports to the Associate Dean for Academic Affairs. 0 credit. Students may enroll only with permission of the Associate Dean for Academic Affairs. Approved for S/U grading only. Prerequisite: Enrollment in the J.D. program at the College of Law.'),
 (4556,110,601,'Contracts',3987,'Enforceability of promises including unjust enrichment and reliance, offer and acceptance, mistake, unfairness and overreaching, unconscionability, Statute of Frauds, interpretation of contract language, conditions, and third party beneficiaries.'),
 (4557,110,602,'Property',3988,'Basic first-year course in property law, required of all students. Provides an overview of law of the land, with incidental coverage of personal property; includes the concept of property, acquisition of private property, recognized property interests, and gratuitous transfer of property interests.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4558,110,603,'Torts',3989,'Basic course in civil wrongs, including intentional torts (such as assault and battery), negligence (duty, unreasonable risk analysis, actual and proximate cause), and strict liability. 3 professional hours. 4 graduate hours. Law students only.'),
 (4559,110,604,'Criminal Law',3990,'Sources and purposes of the criminal law; the meaning of criminal responsibility; and the characteristics of particular crimes. 3 professional hours. 4 graduate hours. Law students only.'),
 (4560,110,605,'Criminal Procedure',3991,'Problems in the administration of criminal justice with emphasis on right to counsel, arrest, search, interrogation, lineups, and the scope and administration of exclusionary rules 3 professional hours. 4 graduate hours.'),
 (4561,110,606,'Constitutional Law I',3992,'Spring semester first-year course provides an introduction to constitutional law, including the origins of judicial review, basic Article III limits on federal court jurisdiction, the nature and scope of federal legislative power, the Commerce Clause, and the relationship of the federal government to the states. 3 professional hours. 4 graduate hours.'),
 (4562,110,607,'Civil Procedure',3993,'Role and importance of procedure in litigation, including jurisdiction, pleadings and parties, pretrial motions and discovery, trial practice (except evidence), relationship between judge and jury, the effect of a decision in one case on subsequent litigation between the same or different parties (res judicata), verdicts and judgements, and appelate review.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4563,110,608,'Statutory Interpretation',3994,'Introduces students to the legislative process, as well as to basic methods of statutory interpretation. Students select from several sections, each focusing on a different statutory scheme. Within that statutory context, students study basic canons of statutory interpretation, the uses of legislative history, and other issues arising in the application and interpretation of statutes. 3 professional hours. 4 graduate hours.  Prerequisite: Enrollment in the College of Law.'),
 (4564,110,609,'Legal Writing & Analysis',3995,'Emphasis on development and improvement of skills in legal writing, and training in legal bibliography. Assignments may include brief writing and preparation of legal memoranda and opinions Approved for S/U grading only.'),
 (4565,110,610,'Introduction to Advocacy',3996,'Continuation of LAW 609. Introduction to Advocacy is required in the second semester of the first year for further development of legal research skills persuasive writing and oral advocacy. Each student will work on the preparation of a summary judgment motion and an appellate brief relating to their first semester assignment, then argue their assigned case before a panel of local attorneys and faculty. 3 professional hours. 2 graduate hours. Approved for S/U grading only.'),
 (4566,110,611,'Constitutional Law II',3997,'This elective for second- and third-year law students studies constitutional notions of state action; substantive due process and equal protection; procedural due process; and the congressional power to enforce civil rights. Study of the rights guaranteed by the First Amendment is not part of this course (see LAW 612). 3 professional hours. 4 graduate hours.  Prerequisite: LAW 606.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4567,110,612,'Constitutional Law, III',3998,'This elective for second-and third-year law students is an intensive study of the First Amendment to the Constitution and its application to the states through the Fourteenth Amendment. Examines decisions of the U.S. Supreme Court in areas concerning freedom of speech, religion, and the press. Specific topics include punishment of criminal advocacy; regulation of picketing and public demonstrations; obscenity; commercial speech; regulation of news media; and religious exemptions from government regulation. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 606.'),
 (4568,110,613,'Constitutional Litigation',3999,'Study of the federal statutes that authorize civil suits against public officials and governmental entities responsible for the deprivation of constitutional rights; immunities and defenses; potential remedies; federalism issues. 2 to 3 professional hours. 2 to 4 graduate hours.'),
 (4569,110,614,'Legislation',4000,'Constitutional and statutory issues in legislative procedure, including issues relating to separation of power between Congress and the President; particular legislative-executive issues raised by the Illinois Constitution; and techniques of legislative drafting and the interpretation of statutes. 2 to 3 professional hours. 2 to 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4570,110,615,'Administrative Law',4001,'Functions of administrative tribunals in federal, state, and municipal government; the procedure before such administrative tribunals; and judicial relief from administrative decisions. 3 professional hours. 4 graduate hours.'),
 (4571,110,616,'Environmental Law and Pol, I',4002,'Course is the basic introduction to Environment Law; it considers the principal legal approaches used to deal with environmental problems, including common-law, statutory, regulatory, and economic-incentive systems. 3 professional hours. 4 graduate hours.'),
 (4572,110,617,'Environmental Law and Pol, II',4003,'Course will examine closely the major federal statutes that control pollution and protect natural areas, including the Clean Water Act, the Clean Air Act, and the various statutes dealing with hazardous substances. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 616.'),
 (4573,110,618,'Natural Resources',4004,'Legal problems associated with the ownership and use of land, water, and mineral resources. 3 professional hours. 2 or 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4574,110,619,'Wildlife Law',4005,'Covers a variety of legal issues relating to the status and treatment of wildlife and the management of natural areas for the conservation of biodiversity. 3 professional hours. 4 graduate hours.'),
 (4575,110,620,'Health Law Policy',4006,'This course focuses on the profound legal and policy issues raised by changes in health law and the U.S. health care delivery system including: access to health law and the U.S. health care delivery system including: access to health services; the financing and organization of the health care system; development of legal standards to ensure quality of care; and issues of long-term care.  In addition, we will focus on the process of making laws and polices; what entities, institutions, and individuals control decisions about the quality and cost of health care.  We will also explore the need and basis for reform.'),
 (4576,110,621,'Read in Conservation Theory',4007,'Considers selected topics in conservation  thought in twentieth-century United States, from the conservation-preservation debate of the Progressive Era through the ethical and ecological debates of the present. 3 professional hours. 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4577,110,622,'Land Use Planning',4008,'Examination of the legal and administrative aspects of land development and regulation in an urban society, including the techniques and problems of planning; the tools of plan effectuation, such as zoning, subdivision regulation, renewal and redevelopment, and housing programs; and the allocation of decision-making among various levels of government. 2 to 3 professional hours. 2 to 4 graduate hours.'),
 (4578,110,623,'Real Estate Transactions',4009,'Elective for second- and third-year law students introduces various issues relating to real property, transfers, including real estate contracts, conveyancing and title protection 3 professional hours. 4 graduate hours.  Prerequisite: LAW 602.'),
 (4579,110,624,'Real Estate Finance',4010,'Methods of financing land acquisition and residential and commercial development, including publicly owned and subsidized housing. 3 professional hours. 4 graduate hours.'),
 (4580,110,625,'State and Local Government',4011,'The law governing the structure, powers, and operation of local governments in urban and suburban areas with analysis of political, economic, and social implications. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 606.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4581,110,626,'American Indian Law',4012,'Historical survey of congressional policy toward Indian affairs; studies relevant legislation delineating the relationship between tribes and the federal government; considers the unique jurisdictional problems that arise with conflicting claims of state, federal, and tribal sovereignty and regulatory authority; focus on individual rights and benefits conferred by federal law to American Indians, including Indian welfare laws, employment preference systems, and religious freedom legislation. 3 professional hours. 4 graduate hours.'),
 (4582,110,627,'Legal Research',4013,'In this course, students will be introduced to the basic tools and methodology used in conducting legal research and will develop the skills necessary to identify and locate relevant, complete and current legal information in both print and digital formats.  Weekly problem-based research exercises will be assigned.  The course will meet twice weekly for the first seven weeks of the semester.  Required in the first year, fall.'),
 (4583,110,628,'Sales',4014,'Elective for second- and third-year law students is a foundational course for commercial law. Subjects covered include the nature and operation of the Uniform Commercial Code, issues regarding formation and performance of sales contracts, warranty, remedies for breach, documentary transactions and international sales. 3 professional hours. 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4584,110,629,'Bankruptcy',4015,'Study of the regulation of the relationship between debtors and creditors under the federal Bankruptcy Code. 3 professional hours. 4 graduate hours.'),
 (4585,110,630,'Bankruptcy Reorganizations',4016,'Detailed study of business bankruptcy reorganizations under Chapter 11 of the Bankruptcy Code. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 629.'),
 (4586,110,631,'Secured Transactions',4017,'Study of secured transactions under Article 9 of the Uniform Commercial Code 2 to 3 professional hours. 2 to 4 graduate hours.'),
 (4587,110,632,'Commercial Law',4018,'A survey course of the Uniform Commercial Code.  There is substantial coverage of Article 2 (Sales), Article 3 (Negotiable Instruments) and Article 9 (Secured Transactions).'),
 (4588,110,633,'Business Associations I',4019,'Examines the basic legal consequences for individuals, organizations, and society of the formation, control, and financing of organizations. Surveys agency relationships, partnerships, and close and public corporations. 3 or 4 professional hours or 4 graduate hours.'),
 (4589,110,634,'Securities Regulation',4020,'Explores the federal securities laws governing issuance of securities in the primary markets. Emphasis on regulatory requirements governing corporate financing 3 professional hours. 4 graduate hours.  Prerequisite: LAW 633.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4590,110,635,'Securities Litigation',4021,'This course focuses in detail on the substantive law and strategic considerations that are important in securities litigation, whether private suits by individual investors, private class actions under federal securities laws, or federal and state government enforcement proceedings.  Topics include: 10(b) fraud suits under the 1934 Act, 11 and 12(a)(2) suits under the 1933 Act, insider trader liability, procedural issues in class actions, and litigation under federal proxy solicitation and tender offer regulations.   Prerequisite: LAW 633, Business Associations I.'),
 (4591,110,636,'Business Associations II',4022,'This is the second course in the sequence (offered during the Spring semester).  It covers derivative suits, corporate finance, introduction to securities regulation, insider trading and mergers and acquisitions.   Prerequisite: Business Associations I.'),
 (4592,110,637,'Unincorporated Business Assoc',4023,'This course provides in-depth coverage of partnerships, limited liability companies and other unincorporated business entities, emphasizing planning and drafting considerations.   Prerequisite: None though LAW 633, Business Associations I, is encouraged.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4593,110,638,'White Collar Crime',4024,'This course will focus on the federal statutes commonly invoked in corporate and white collar prosecutions, including those used in prosecutions for conspiracy, mail and wire fraud, RICO, extortion, bribery, tax offenses, obstruction of justice, and false statements.  The class will investigate the theoretical and policy framework for individual and institutional responsibility in our criminal justice system and will also explore emerging theories of corporate criminal liability and the principles undergirding the sanctions imposed for white collar crime.  Prerequisite: This course is appropriate for law students who have completed introductory courses in criminal law and procedure.  Some students have found it helpful to complete the course in LAW 633, Business Association I, before taking this course, but it is not a prerequisite.'),
 (4594,110,639,'Corporate Finance',4025,'Analysis of corporate and securities law problems using the tools of modern financial theory. Emphases will typically include valuation, capital structure, and fundamental changes of public corporations. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 633.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4595,110,640,'Business Planning',4026,'Examination of planning situations wherein tax, corporations, corporate finance, securities regulation, and accounting materials are interrelated; organization of close corporations and public companies, corporate distributions and recapitalizations, sale of corporate businesses, corporate acquisitions and mergers, and corporate separations; and problems requiring written opinions and solutions. 2 to 3 professional hours. 2 to 4 graduate hours.  Prerequisite: LAW 633 and LAW 647.'),
 (4596,110,641,'Quantitative Methods',4027,'Directed at equipping law students with quantitative skills that will assist them in the practice of law. Topics that will be covered include the following: business concepts in the law (e.g., reading a balance sheet, the importance of and methods of computing net, internal, and real rates of return); statistical and other quantitative methods in the law (e.g., using statistical evidence and employing decision theory as a guide to litigation strategy); the theory and methods of computing compensatory damages; and methods of finding and using expert witnesses on economic issues. 3 professional hours. 4 graduate hours.'),
 (4597,110,642,'Antitrust Law',4028,'The limitations imposed by the Sherman Act, Clayton Act, and Federal Trade Commission Act on anticompetitive practices by business firms; emphasizes price fixing and other agreements among competitors, monopolization, mergers, exclusive dealing, tying arrangements.  Considers applicability of traditional rules to intellectual property and new technologies. 3 professional hours. 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4598,110,643,'Trademark Law',4029,'Course introduces basic legal concepts relating to statutory and common-law trademark, interference with contractual relations and trade libel, the federalization of unfair competition law, and the role of the Federal Trade Commission in consumer protection activities. 3 professional hours. 4 graduate hours.'),
 (4599,110,644,'Copyright Law',4030,'Offers an in-depth look at the legal aspects of copyright with special emphasis on the application of traditional copyright principles to new technologies and media of expression 3 professional hours. 4 graduate hours. First-year law students who have taken the Copyright Act section of LAW 608 are not permitted to enroll in this course due to overlapping coverage.'),
 (4600,110,645,'Patent Law',4031,'Historical development of protection of ideas, inventions, and discoveries; patentability; securing the patent; amendment and correction of patents; and infringement remedies, defenses, and procedure 2 to 3 professional hours. 2 to 4 graduate hours.'),
 (4601,110,646,'Cyberspace Law',4032,'The course addresses the architecture of cyberspace, applicability and problems of mapping existing legal concepts to the domain, modification of familiar legal concepts for application in cyberspace, and the development of new legal concepts to address emerging problems and disputes in cyberspace.  We will examine trademarks and the domain name system; copyright and patent liability; speech in cyberspace; personal jurisdiction; choice of law; governance and sovereignty of cyberspace; and cybercommunities; the individual and privacy in cyberspace.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4602,110,647,'Income Taxation',4033,'The fundamental course in federal income taxation. Includes materials relating to income taxation of individuals and an introduction to taxation of corporations and shareholders. 3 or 4 professional hours. 4 graduate hours.'),
 (4603,110,648,'Corporate Taxation',4034,'In-depth study of federal income tax law related to taxation of corporations, shareholders, partnerships, and partners. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 647.'),
 (4604,110,649,'Partnership Taxation',4035,'Involves the study of Subchapter K of the Internal Revenue Code, including partnership formation, allocations, distributions, and liquidations. Also examines the tax treatment of Subchapter S corporations. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 647.'),
 (4605,110,650,'Estate and Gift Taxation',4036,'Comprehensive treatment of federal transfer (estate and gift) taxes. 3 professional hours. 4 graduate hours.  Prerequisite: LAW 647.'),
 (4606,110,651,'Tax Exempt Organizations',4037,'This course will cover the rationale and technical tax requirements for exempting charities from federal and state taxes.  Subjects covered will include the rationale for exemption (especially with respect to churches, schools, and hospitals), qualification rules under I.R.C. Section 5 (c)(3), the Unrelated Business Income Tax, and if time permits, the charitable contributions deduction.   Prerequisite: Income Taxation (LAW 647) is a prerequisite, though it may be waived in appropriate cases.  The course is otherwise open to second and third year students.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4607,110,652,'International Taxation',4038,'Survey of the problems in U.S. taxation of foreign persons and foreign income, with special emphasis upon foreign business transactions of U.S. corporation 3 professional hours. 4 graduate hours.  Prerequisite: LAW 647.'),
 (4608,110,653,'International Business Trans.',4039,'Doing business abroad: export-import regulations, use of foreign commission merchants, licensing of patents and know-how, investment and exchange problems, establishing a foreign operation (including forms of business organization available abroad), and application of United States and foreign antitrust law to the business operation. 3 professional hours. 4 graduate hours.'),
 (4609,110,654,'International Trade Policy',4040,'Analysis of the regulation of trade between nations by international agreement (e.g., the GATT), by multinational organizations (e.g., the European Communities), and by individual countries; emphasizes U.S. import restraints, export controls, and related laws. 3 professional hours. 4 graduate hours.'),
 (4610,110,655,'European Union Law',4041,'Intensive study of the European Common Market, particularly of its laws relating to trade barriers, establishment of companies, and antitrust; and United States legislation in the field of international trade. 2 to 3 professional hours. 2 to 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4611,110,656,'International Law',4042,'The nature, sources, and subjects of international law and its place in the control of international society; includes an examination of the law of jurisdiction, territory, recognition and succession of states, rights and immunities of states in foreign courts, diplomatic immunities, treaties, protection of citizens abroad, settlement of international disputes, war and neutrality, the United Nations, and the International Court of Justice. 3 professional hours. 4 graduate hours.'),
 (4612,110,657,'International Human Rights Law',4043,'Studies established and developing legal rules and procedures governing the protection of international human rights, including Marxist and Third World, as well as Western, conceptions of those rights 3 professional hours. 4 graduate hours.'),
 (4613,110,658,'Comparative Legal Institutions',4044,'This course is designed to examine a range of foreign legal institutions from an interdisciplinary perspective.  The emphasis is not on particular rules of substantive law but on the structure of different legal systems and the consequences of those structural differences for law and society.  Examples will be drawn primarily from Europe and Asia, with special emphasis on understanding the contrasts between foreign legal systems and that of the United States.  Topics will include judicial review, corporate governance, legal culture, and the question of whether legal systems are converging with globalization.  Students will be expected to write a paper.'),
 (4614,110,659,'Comparative Constitutional Law',4045,'This course compares U.S. and Canadian Constitutional Law.  The class will be taught via teleconferencing to students here at Illinois and students at the University of British Columbia.  Class attendance and a paper (which can be used to satisfy the writing requirement) are required.  There are no prerequisites -- although constitutional decisions that are studied in Con Law II are discussed, none of the Canadian students have had these classes either.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4615,110,660,'Individual Employee Rights',4046,'This course investigates the legal rights and responsibilities of employees in the non-union workplace.  The course will emphasize particularly the role of law in adjusting the balance of power between individual employees and employers.  It will study the regulation of contract, tort, and statute of such areas as hiring, discharge, compensation, employee privacy and dignity and the like.'),
 (4616,110,661,'Introduction to Civil Law',4047,'More individuals in the world solve their legal problems in the framework of what is called the civil law system than in the Anglo-Saxon case law system. This course introduces students to the civil law system, roughly defined as an ensemble of legal thought formations and practices derived from Roman law. Understanding the particularities of the civil law systems furthers a partnership with lawyers working on other continents in different legal traditions. Comparisons of legal frameworks and positions are included in the course approach. 3 professional hours. 4 graduate hours.'),
 (4617,110,662,'Labor Law I',4048,'Study of the National Labor Relations Act as amended, the preact history of the labor movement, and the judiciary\'s response thereto, with emphasis on understanding the problems, experiments, and forces leading to the enactment; includes the negotiation and administration of the collective bargaining agreement, especially the grievance arbitration procedure, its operation and place in national labor policy; and explores the relationship of the individual and the union. Same as LIR 547. 3 or 4 professional hours or 4 graduate hours. Prerequisite: Graduate standing or completion of first year of law curriculum.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4618,110,663,'Labor Law and Public Policy',4049,'Analyzes current major policy issues in labor relations and employment law through the concepts and techniques of the lawyer and the labor relations specialist. Same as LIR 551. 3 professional hours. 4 graduate hours.  Prerequisite: For law students, LAW 662 or consent of instructor; for Institute of Labor and Industrial Relations and other graduate students, one semester of labor and industrial relations course work or consent of instructor.'),
 (4619,110,664,'Employment Discrimination',4050,'Problems arising under federal statutory prohibitions of discrimination in employment, with particular emphasis on evidentiary problems and the use of statistical proofs; defining relevant labor pools, using statistical analyses of data, and establishing proof of test validation. 2 to 3 professional hours. 2 or 4 graduate hours. (summer session, 3 professional hours or 4 graduate hours. ). Prerequisite: All first year law courses.'),
 (4620,110,665,'Workplace Dispute Resolution',2504,'Same as ECON 543, and LIR 543. See LIR 543.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4621,110,666,'Compar Employmt Relations Sys',4051,'Same as LIR 554. See LIR 554.'),
 (4622,110,667,'Family Law',4052,'The creation and dissolution of the family, and legal relationships established by marriage, cohabitation and procreation. Covers the law of marriage, divorce, annulment, separation, unmarried cohabitation, illegitimacy, adoption and rights of child custody, parental property on divorce, inheritance, and related rights. Legal rules are placed into the social setting in which they operate, and emphasis is given to family policy as reflected in current developments in family law reform, including constitutional law. 3 professional hours. 4 graduate hours.'),
 (4623,110,668,'Decendent\'s Estates and Trusts',4053,'Studies the means of transferring wealth, with primary emphasis on gratuitous transfers; the means available for making gratuitous transfers, including the validity and effect of testamentary instruments and trust deeds; and problems concerning the dispositive provisions of any type of instrument which transfers wealth. 3 professional hours. 4 graduate hours.'),
 (4624,110,669,'Problems in Estate Planning',4054,'Selected problems in the planning of estates which will serve to integrate the basic materials in property, trusts, wills, and income, estate, and gift taxation 2 to 3 professional hours. 4 graduate hours.  Prerequisite: LAW 647, LAW 650, and LAW 668.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4625,110,670,'Elder Law',4055,'Examines the various legal implications of people living longer, with special emphasis on public policies and programs affecting the financing of medical care, housing arrangements, and income maintenance of persons aged 60 years and older. 3 professional hours. 4 graduate hours.'),
 (4626,110,671,'Children and the Law',4056,'The course will examine how the law treats children and childhood in civil and criminal legal disputes.  It will consider the status, rights and obligations of children when they appear either as actors or subjects in various social and public institutions (e.g. courts, schools, prisons, families, and the workplace).  We will explore these issues while considering the fundamental debate of juvenile law: how the legal system should strike the balance between trying to protect children and recognizing children as autonomous and fully accountable persons.'),
 (4627,110,672,'Feminism and the Law',4057,'This course focuses on contemporary issues surrounding law and feminism.  The course begins with an overview of different strands of feminist theory and then turns to specific issues affecting women.  The topics covered in the course include violence against women, reproductive rights, marriage and divorce, motherhood, sexual harassment, critical race feminism, comparable worth, sexual orientation, prostitution, pornography, and women in the legal profession.  The readings for the course include not only court opinions, statutes, and law review articles, but also a significant amount of social-science literature.  Students are asked to write a 15-page paper and to help lead the discussion once during the semester.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4628,110,673,'Workers Compensation',4058,'This course will be a general survey class on rules relating to workers compensation claims and litigation.  The course will begin with an overview of the historical development of workers compensation laws, then survey the general principles applicable to such laws, with particular emphasis on the Illinois Workers Compensation Act.  Guest speakers will include an arbitrator, a petitioner\'s attorney and a claims manager.'),
 (4629,110,674,'Advanced Torts',4059,'Examines a variety of advanced tort topics, such as defamation, privacy, misrepresentation, special duties, prima facie tort, alternative compensation schemes, and proposals for tort reform. 3 professional hours. 4 graduate hours.'),
 (4630,110,675,'Products Liability',4060,'Substantive theories of products liability: negligence, breach of warranty, strict liability, and tortious misrepresentation; procedural and remedial problems with, and defenses to, each substantive theory. 2 to 3 professional hours. 2 to 4 graduate hours.'),
 (4631,110,676,'Insurance Law',4061,'Covers principles generally applicable to insurance law and includes distinctive rules governing certain types of insurance coverage; objectives are to examine the nature of the insurance contract, marketing of insurance, principles of indemnity, individuals and entities protected by insurance rules, and risks that are shifted by insurance coverage. 3 professional hours. 4 graduate hours.  Prerequisite: First-year curriculum.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4632,110,677,'Sports Law',4062,'Examines specialized aspects of the sports industry; emphasis given to antitrust, labor, and tax issues as applied to professional sports, and antitrust and constitutional issues that have allowed courts to intervene in intercollegiate athletics. Does not consider recurring legal problems for which general principles of law are applicable. 2 to 3 professional hours. 2 or 4 graduate hours.'),
 (4633,110,678,'Anthropology and Law',713,'Same as ANTH 560.  See ANTH 560.'),
 (4634,110,679,'Advanced Criminal Procedure',4063,'Problems in the administration of criminal justice, with emphasis upon the commencement of formal proceedings (bail, decision to prosecute, grand jury, preliminary hearing, location of prosecution, scope of prosecution, speedy trial); the adversary system (pleas, discovery, jury trials, prejudicial publicity, ethical problems, double jeopardy); and post-conviction review (post-trial motions, appeals, habeas corpus, related post-conviction remedies). 3 professional hours. 4 graduate hours.  Prerequisite: LAW 604 and LAW 605.'),
 (4635,110,680,'Professional Responsibility',4064,'Problem course analyzing ethical issues that arise in the practice of law and considering the approaches to such issues taken by the American Bar Association\'s Code of Professional Responsibility, Model Rules of Professional Conduct, and Code of Judicial Conduct. 2 to 3 professional hours. 2 to 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4636,110,681,'Civil Procedure II',4065,'Elective for second- and third- year law students introduces issues of jurisdiction and choice-of-law, such as subject matter jurisdiction, removal, Erie doctrine and federal common law 3 professional hours. 4 graduate hours.  Prerequisite: LAW 607.'),
 (4637,110,682,'Evidence',4066,'Law governing the proof of disputed issues of fact; function of the court and jury; competence and examination of witnesses; standards of relevancy; privileged communications; illegal evidence; hearsay rule; best evidence rule; presumptions; and judicial notice. 3 or 4 professional hours. 4 graduate hours.'),
 (4638,110,683,'Complex Litigation',4067,'Legal and practical issues in \"complex\" cases: problems of joinder in multi-party cases, consolidation of cases brought independently (including the activities of the Judicial Panel of Multidistrict Litigation), class actions, discovery issues including the assertion and waiver of evidentiary privileges and use of computers, consequences of active judicial \"management\" of litigation at the pretrial stage, settlement of complex cases, and res judicata problems. 3 professional hours. 4 graduate hours.'),
 (4639,110,684,'Federal Courts',4068,'Examination of the relationship of federal courts to other organs of federal government and to the states, including an analysis of cases dealing with congressional control over jurisdiction, federal review of state court decisions (including the relationship between state and federal substantive and procedural law), and application of law to fact; the scope of the federal question of jurisdiction in federal courts; abstention; federal injunctions of state criminal proceedings; and problems of justiciability, advisory opinions, and mootness. 3 professional hours. 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4640,110,685,'Dispute Resolution',4069,'Examination of the limitations, consequences, and costs, as well as the indispensability of some aspects of modern litigation; the possibilities, requirements, and legal problems of consensual and of court-annexed dispute resolution processes alternative to final judicial adjudication, including legal counseling, negotiation, mediation, arbitration, mini-trials, summary trials, summary jury trials, early neutral evaluation, private resolution providers, and settlement processes; current disputes used for illustration. 2 to 3 professional hours. 2 to 4 graduate hours.'),
 (4641,110,686,'Remedies',4070,'Survey of legal and equitable remedies for the protection of personal and property rights. Procedural and substantive aspects of injunctions; restitution of unjust enrichment in the context of the receipt of unsolicited benefits, benefits derived from the commission of tortious acts, and the mistaken acquisition of benefits; alternative remedies arising from bargain transactions; and remedies for violations of civil rights. 2 to 3 professional hours. 2 or 4 graduate hours.'),
 (4642,110,687,'Jurisprudence',4071,'The place of law in society; the nature, goals, and methods of law; and the relation of law and social science. 3 professional hours. 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4643,110,688,'American Legal History',3487,'Studies selected topics in the development of law and legal institutions in the United States with particular emphasis on the history of the legal profession, legal education, and the role of lawyers and courts in U.S. society. Same as HIST 484.  3 professional hours. 4 graduate hours.  Prerequisite: Some prior study of U.S. history, particularly social and intellectual, is helpful but not required.'),
 (4644,110,689,'Law and Economics',4072,'Introduction to the economic analysis of law, including property, contracts, torts, criminal law, and related topics. 3 professional hours. 4 graduate hours.'),
 (4645,110,690,'Advanced Law and Economics',4073,'This course will examine new literature on topics covered in the basic course on law and economics (such as intellectual property law, moral theory in the law, property and liability rules, and criminal law) and scholarly literature on new issues in the field (such as game theory in the law, bankruptcy, law in assisting the transition and developing economies, econometric studies of legal topics, and behavioral law and economics).  Students must lead a discussion, participate in the other seminar discussions, present their paper topics to the group, and submit a substantial research paper of no less than 20 pages.   Prerequisite: Completion of LAW 689, Law and Economics.'),
 (4646,110,691,'Philosophy of Law',4074,'The course works through two topics: the nature of law and legal reasoning; and the nature of both liberty and justice, both by themselves and as limits on what should be the content of law.  Under the first topic, we examine both ancient and modern law and natural rights views; legal positivist and skeptical views, divorcing law from any connection to morality; \"interpretivist\" views that liken law to literature and other hermeneutic disciplines such as theology and psychoanalysis; and the alleged obligation to obey the law of both citizens and judges.  Under the second topic we examine what content can be given to any supposed general right to liberty; how such a right, if it exists, should limit legislation on such topics as abortion, homosexual behavior, birth control, recreational drug use, and the like; what content can be given to three kinds of justice (distributive justice, corrective justice, and retributive justice); whether distributive justice requires, or is even compatible with race-based or gender-based affirmative action plans in employment and education; whether reparations for long past injuries to groups are required by corrective justice; whether the death penalty is required by, or is even consistent with, retributive justice.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4647,110,692,'Critical Race Theory',4075,'This seminar will introduce students to the historical development and basic theoretical principles of Critical Race Theory, an intellectual movement in critical scholarship that (1) rejects the possibility of race-neutral practices and institutions; and (2) is committed to the possibility of an affirmative program for racial emancipation.  Students will read a variety of writings by critical race theorists and their critics; a paper will be required.'),
 (4648,110,693,'Clinical Training',4076,'Several clinics offer practical legal education, through field work in public agencies (externships) or through a variety of in-house clinics. The clinics focus on specific lawyering skills that are relevant to a particular area of practice (e.g., litigation or transactional), and have a classroom component. Students engage in legal work under the supervision of experienced attorneys; the work may include conducting client interviews, doing legal research and fact investigation, preparing legal documents, negotiating, and in some cases, engaging in real trials. Approved for both letter and S/U grading. Prerequisite: Completed or enrolled concurrently with LAW 680.'),
 (4649,110,694,'Trial Advocacy',4077,'Examination of the problems of advocacy and tactics at the trial level. Students engage in all aspects of actual trial work, including witness preparation, opening and closing statements, direct and cross examination, and jury instructions; culminates in student conduct of a full jury trial in late spring; demonstrations are conducted by staff and visiting judges and practitioners. Can be repeated to a total of 4 hours. Approved for both letter and S/U grading.  Prerequisite: Completed or enrolled concurrently with LAW 682.  JD students only.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4650,110,695,'Fundamentals of Trial Practice',4078,'Explores the theory and reality of trial practice, from developing a theory of the case through submission of jury instructions; topics include fact gathering, jury selection, opening statements, direct and cross-examination, exhibits, expert witnesses, and closing arguments. 3 professional hours. 4 graduate hours. Approved for both letter and S/U grading. Prerequisite: LAW 694 and completed or enrolled concurrently with LAW 682.  JD students only.'),
 (4651,110,696,'Legal Problems',4079,'Preparation of comments on current legal developments for publication in the University of Illinois Law Review or the Illinois Bar Journal.  May be repeated up to 4 time(s). No graduate credit. Approved for S/U grading only.'),
 (4652,110,697,'Moot Court Board',4080,'Preparation of an appellate brief; presentation of an appellate oral argument; participation in intramural, state, national, or international moot court competition. May be repeated to a maximum of 4 hours. No graduate credit. Approved for S/U grading only.'),
 (4653,110,698,'Negotiations',4081,'This course provides an overview of the theoretical issues negotiators must confront and an opportunity for students to hone their negotiating skills.  Attention will be paid to cooperative and competitive negotiations, strategic and psychological barriers to successful negotiations, the personal styles of negotiators, and differences between two-party and multi-party negotiating situations.  There will be numerous in-class exercises and writing assignments.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4654,110,699,'Independent Study',4082,'Individual research on a special problem selected in consultation with the instructor. May be repeated to a maximum of 4 hours. 0 to 4 professional hours or 2 to 4 graduate hours. Approved for both letter and S/U grading.'),
 (4655,110,792,'Current Legal Problems',4083,'This is an umbrella course listing for specialty topics of current legal issues of interest. May be repeated.'),
 (4656,110,793,'Advanced Litigation Topics',4084,'This is an umbrella course listing for specialty topics of current interest in litigation.   May be repeated if different underlying section.'),
 (4657,110,794,'Adv Topics in Business Law',4085,'This is an umbrella course listing in business law for specialty topics of current interest.  May be repeated if different underlying section.'),
 (4658,110,795,'Adv Topics in Criminal Law',4086,'This is an umbrella course listing in criminal law for specialty topics of current interest.   May be repeated if different underlying section.'),
 (4659,110,796,'Comparative Law Topics',4087,'This is an umbrella course listing in comparative law for specialty topics of current interest.   May be repeated if different underlying section.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4660,110,797,'Intellectual Property Topics',4088,'This is an umbrella course listing in intellectual property law for specialty topics of current interest.  May be repeated if different underlying section.'),
 (4661,110,798,'Seminars',4089,'This is an umbrella course listing for specialty topics of special interest.   May be repeated.'),
 (4662,111,201,'Elementary Lingala I',359,'Introduction to Lingala; emphasizes grammar, pronunciation, reading and conversation in standard Lingala. Participation in language laboratory required.  Same as AFST 211.'),
 (4663,111,202,'Elementary Lingala II',360,'Continuation of elementary Lingala, with introduction of more advanced grammar; emphasizes more fluency in speaking, reading, and writing simple sentences in standard Lingala. Participation in language laboratory required.  Same as AFST 212.  Prerequisite: LGLA 201'),
 (4664,111,403,'Intermediate Lingala I',380,'Survey of more advanced grammar, with emphasis on increasing conversational fluency, composition skills, study of written texts in the standard and spoken Lingala dialects, and discussion of grammatical variations.  Participation in language laboratory required.  Same as AFST 413. 5 undergraduate hours. 4 graduate hours.  Prerequisite: LGLA 202.'),
 (4665,111,404,'Intermediate Lingala II',381,'Continuation of LGLA 403. Emphasizes ability to engage in reasonably fluent discourse in Lingala, comprehensive knowledge of formal grammar, and ability to read ordinary texts in various Lingala dialects. Participation in language laboratory required.  Same as AFST 414. 5 undergraduate hours. 4 graduate hours.  Prerequisite: LGLA 403.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4666,111,405,'Advanced Lingala I',382,'Third-year Lingala with emphasis on conversational fluency and on increased ability in reading and comprehending texts, including newspaper prose and Central African cultural materials, in at least two Lingala varieties. Course will also deal with the advanced level grammar found in such texts.  Same as AFST 415.  Prerequisite: LGLA 404 or equivalent'),
 (4667,111,406,'Advanced Lingala II',383,'Continuation of LGLA 405 with increased emphasis on conversational fluency and comprehension of advanced level grammar in the reading of a variety of prose texts on current cultural issues. Same as AFST 416.  Prerequisite: LGLA 405 or equivalent'),
 (4668,111,407,'Topics Lingala Lang & Lit I',384,'Selected readings from modern Lingala authors and composers, with a focus on novels, plays, music, and basic poetry illustrative of Central African cultural issues and advanced level Lingala grammar, as well as development of expository writing skills. Same as AFST 417.  Prerequisite: LGLA 406.'),
 (4669,111,408,'Topics Lingala Lang & Lit II',385,'Continuation of LGLA 407 with increased emphasis on the reading and comprehension of literary texts exemplified in advanced level novels, plays, and poetry, as well as on advanced mastery of expository writing skills.  Same as AFST 418.  Prerequisite: LGLA 407.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4670,112,100,'Intro to Language Science',4090,'Introduction to the theory and methodology of general linguistics; includes the various branches and applications of linguistics.'),
 (4671,112,104,'Talking Culture',545,'Same as ANTH 104. See ANTH 104.'),
 (4672,112,105,'Language in Daily Life',4091,'Analysis of what constitutes knowledge of language, how it is used in daily life, and how speakers are perceived by others. Emphasis on discovering what makes language function as it does through an examination of its forms and functions in real life.'),
 (4673,112,111,'Language in Globalization',4092,'Introduction to the role of language in globalization by examining communication issues concerning language use across cultural, political and geographic boundaries.  Explores the interaction of language and other cultural forms in the global context.  Among the topics discussed are issues of identity, spread of English and its acculturation to local contexts of use, creativity in language mixing, language in global pop cultures, language in cyberspace, as well as minority language experiences, and loss of indigenous languages.  This course can be used to fulfill either Western or Nonwestern general education categories, but not both.'),
 (4674,112,115,'Language and Culture in India',4093,'Examines the relationship between language and culture in the multilingual and multicultural context of India. Special topics of focus are: linguistic and cultural diversity in India, impact of the language and culture contact on the structure and function of languages (convergence, diglossia, code-mixing, pidgins and creoles), language and identity, language of religion, language and gender, language in the media, literature and culture, language and power, language and globalization. Same as RLST 115.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4675,112,121,'American Sign Language I',4094,'Same as SHS 121.  See SHS 121.'),
 (4676,112,191,'Freshman Honors Tutorial',4095,'Study of selected topics on an individually arranged basis. Open only to honors majors or to Cohn Scholars. May be repeated once. Prerequisite: Consent of departmental honors advisor.'),
 (4677,112,199,'Undergraduate Open Seminar',4096,'May be repeated.'),
 (4678,112,210,'Language History',4097,'Addresses the question \"Why does language change?\" Specific topics include: the history and origin of writing; why pronunciation changes; change in vocabulary and what it tells us about change in culture and society; the relation between \"language\" and \"dialect\"; multilingualism and its consequences, including Pidgins and Creoles; genetic relationship between languages, with focus on the \"Indo-European\" family (English, German, French, Russia, Latin, Greek, and Sanskrit, etc.) and the relationships between human languages.  Prerequisite: Fulfillment of the foreign language requirement of the College of Liberal Arts and Sciences.'),
 (4679,112,221,'American Sign Language 2',4098,'Same as SHS 221.  See SHS 221.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4680,112,225,'Elements of Psycholinguistics',4099,'Introduction to the theory and methodology of psycholinguistics with emphasis on language acquisition and linguistic behavior.'),
 (4681,112,240,'Language in Human History',4100,'Role of language in the life of nations as a tool of communication, as a symbol of identity, and as a means of power. Scripts and orthographies, language planning, culture and language glossopolitics.   Prerequisite: Three years of high school foreign language study or fulfillment of the foreign language requirement of Liberal Arts and Sciences.'),
 (4682,112,250,'Language Diversity in the USA',4101,'Investigation of the uses and users of different language varieties- English and non-English- as well as issues of language discrimination, gender/race/class, youth culture, and new communication technologies.'),
 (4683,112,260,'Language and the Law',4102,'Explores the various ways in which issues of language intersect with issues of law.  Through the analysis of specific pieces of legislation and court cases, we study the linguistic aspects of fundamental questions of the humanities and social sciences - individual freedom and group membership, the role of the state, language and culture. Focus on the United States, but other national situations will be included as necessary.'),
 (4684,112,270,'Language, Technology & Society',4103,'What technologies have humans developed to augment the quintessential human ability: language?  We start with the development of writing, the first technology that was specifically designed for language, and trace its history through the invention of printing, and into the digital age.  With the advent of computers the relevance of language for technology has broadened significantly.  We review technologies such as automatic speech recognition, speech synthesis and automatic translation, and discuss their implications for present and future human-machine interaction.  Prerequisite: LING 100 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4685,112,290,'Individual Study',4104,'Individual readings and research reports on special topics dealing with the theoretical or applied aspects of the linguistic sciences.  May be repeated to a maximum of 8 hours.  Prerequisite: Written consent of instructor.'),
 (4686,112,300,'Anat & Physiol Spch Mechanism',4105,'Same as SHS 300. See SHS 300.'),
 (4687,112,301,'Elements of Syntax',4106,'Introduction to concepts and techniques essential for syntactic analysis and description, with special attention to testing analyses and justifying them.  Prerequisite: LING 100 or consent of instructor.'),
 (4688,112,302,'Elements of Phonology',4107,'Introduces elements of phonological theory and data analysis. Emphasis is placed on both Structuralist and Generative theories, introducing students to the principles of phonological contrast, allophony, neutralization, and markedness. Formal phonological models are considered, including both distinctive feature theory and prosodic theory. Equal emphasis is placed on linguistics data analysis.   Prerequisite: LING 100 or consent of instructor.'),
 (4689,112,303,'General Speech Science',4108,'Same as SHS 301.  See SHS 301.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4690,112,307,'Elmnts Semantics & Pragmatics',4109,'Introduction to the theory of meaning for natural language, including techniques for the description of lexical meaning, compositional determination of phrase and sentence meaning, and pragmatic effects on interpretation in context.  Prerequisite: LING 100 or consent of instructor.'),
 (4691,112,321,'American Sign Language 3',4110,'Same as SHS 321.  See SHS 321.'),
 (4692,112,391,'Honors Individual Study',4111,'Study and research for honors thesis; open only to seniors in the linguistics major who are eligible for departmental distinction.  May be repeated to a maximum of 8 hours.  Prerequisite: Written consent of instructor and linguistics course average of 3.4.'),
 (4693,112,400,'Intro to Linguistic Structure',614,'Introduction to the theory and methodology of the science of linguistics with special reference to phonology and syntax. Same as ANTH 400. 3 undergraduate hours. 2 graduate hours.'),
 (4694,112,401,'Intro to General Phonetics',4112,'Introduction to the main branches of general phonetics and phonological theory; emphasis on analysis of non-Western languages and research techniques. 3 undergraduate hours. 2 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4695,112,402,'Tools & Tech Spch & Lang Proc',4113,'Introduction to aspects of the tools and methods of studies in speech and natural language processing (NLP), with a focus on programming for NLP and speech applications, statistical methods for data analysis, and tools for displaying and manipulating speech data.   Prerequisite:  LING 400 or equivalent general introduction to linguistics.'),
 (4696,112,404,'Tutorials in Non-Western Lang',4114,'Advanced or intensive language instruction in a selected non-Western language; excludes instruction in East or Southeast Asian languages. May be repeated with approval. 1 to 5 undergraduate hours, or 2 to 4 graduate hours. Prerequisite: Consent of instructor.'),
 (4697,112,405,'Intro to Applied Linguistics',2538,'Introduction to the applications of general linguistic theory to the specific fields of stylistics, theory of translation, contrastive analyses, and the teaching and learning of foreign and second languages; practical assignment work.  Same as EIL 405. 3 undergraduate hours. 2 or 4 graduate hours.  Prerequisite: Consent of instructor.'),
 (4698,112,406,'Intro to Computational Ling',4115,'Introduces the field of natural language processing and computational linguistics. Topics include finite-state methods, parsing, probabilistic methods, machine learning in NLP, computational semantics and applications of NLP technology. The course is mostly about concepts rather than programming, though some programming assignments will be given. 3 undergraduate hours. 4 graduate hours.  Prerequisite: LING 400 or equivalent; and LING 402 or a C S 100-level programming course, or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4699,112,407,'Intro to Mathematical Ling',619,'Principles of set theory, logic and formal systems, and automata theory; introduction to the formal theory of grammars. Same as ANTH 406. 3 undergraduate hours. 4 graduate hours.  Prerequisite: LING 400.'),
 (4700,112,408,'Writing Systems',4116,'Introductory survey of writing systems, with emphasis on what kind of linguistic information is encoded; historical development and adaptation of writing systems to new languages; computational models of writing systems; decipherment; the effect of properties of writing systems on human reading, and linguistic awareness; implications of writing systems for linguistic theory.  Prerequisite: LING 400 or equivalent general introduction to linguistics, or LING 302 or equivalent introduction to phonology.  3 Undergraduate Hours.  4 Graduate Hours.'),
 (4701,112,409,'Intro to Indo-European Ling',3258,'Introductory survey of Indo-European languages and their mutual relations; exemplification of methods of reconstruction; principles of comparative phonology and introductory survey of morphology; and discussion of theories about the original home, culture, and society of the Indo-Europeans. Same as GRK 410, and LAT 410. 3 undergraduate hours. 4 graduate hours.  Prerequisite: Fulfillment of the language requirement of the College of Liberal Arts and Sciences.'),
 (4702,112,410,'Historical Linguistics',4117,'Introduction to historical and comparative linguistics with particular attention to theoretical issues. 3 undergraduate hours. 2 or 4 graduate hours.  Prerequisite: LING 400 and LING 401 or concurrent registration.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4703,112,412,'Lang in African Culture & Soc',379,'Introductory survey of the role of language in African cultures and societies, with particular emphasis on the study of indigenous African linguae francae  in multilingual settings, their spread, and use as media of communication in various domains, and as tools of development. Same as AFST 412. 3 undergraduate hours. 4 graduate hours.  Prerequisite: AFST 222 or consent of instructor.'),
 (4704,112,414,'Introductory Coptic I',1871,'Same as COP 401, and RLST 401. See COP 401.'),
 (4705,112,415,'Introductory Coptic II',1872,'Same as COP 402, and RLST 402. See COP 402.'),
 (4706,112,416,'Structure of French Language',2941,'Same as FR 416. See FR 416.'),
 (4707,112,418,'Language&Minorities in Europe',2943,'Same as FR 418, GER 418, PS 418, and SLAV 418.  See FR 418.'),
 (4708,112,420,'Intro to African Linguistics',4118,'Introduction to the genetic and typological classification of the main language families of Africa; concentration on grammatical and phonological characteristics. 3 undergraduate hours. 4 graduate hours.  Prerequisite: LING 100 or LING 400; consent of instructor.'),
 (4709,112,423,'Language Acquisition',1843,'Same as COMM 423, and PSYC 423. See PSYC 423.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4710,112,425,'Intro to Psycholinguistics',1844,'Introductory survey of psychological and linguistic approaches to the study of communication. Same as COMM 425. 3 undergraduate hours. 4 graduate hours. Credit is not given for both LING 425 and PSYC 425. Prerequisite: An introductory course in linguistics or psychology'),
 (4711,112,427,'Language and the Brain',4119,'Same as PSYC 427. See PSYC 427.'),
 (4712,112,429,'Language of Religion',4120,'Same as RLST 429. See RLST 429.'),
 (4713,112,430,'Intro to East Asian Ling',2294,'Introduction to the genetic relation of the Far Eastern languages with other languages; concentration on synchronic analysis of phonology and syntax. Same as EALC 430. 3 undergraduate hours. 4 graduate hours.  Prerequisite: LING 400; consent of instructor.'),
 (4714,112,432,'Gender and Language',3290,'Same as GWS 432, and SPCM 432. See SPCM 432.'),
 (4715,112,435,'Neuroling of Bilingualism',2541,'Study of the structure and function of the brain in relation to language processing in bi- and multi-linguals, with a focus on how two or more languages are organized, produced, and perceived in one brain. Same as EIL 435, and SLS 435. 3 undergraduate hours. 4 graduate hours. Graduate students are required to write a term paper. Prerequisite: LING 225, LING 405, LING 425, or consent of instructor.'),
 (4716,112,438,'Philosophy of Language',4121,'Same as PHIL 438. See PHIL 438.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4717,112,440,'History of Linguistics',4122,'Survey of linguistic theories from ancient to modern times; special emphasis on comparative grammar and the development of structural linguistics; and extended discussion of at least one other period. 3 undergraduate hours. 4 graduate hours.'),
 (4718,112,450,'Sociolinguistics I',2543,'Introduction to the fundamental concepts, philosophy, and research methods of the study of language in its social contexts. Special attention to language spread, and language variation; language attitudes; language diversity; code-switching; language standardization; and language identity and loyalty. Same as EIL 450. 3 undergraduate hours. 2 or 4 graduate hours.  Prerequisite: LING 405 or equivalent.'),
 (4719,112,462,'Intro Romance Ling',4123,'Same as SPAN 435, FR 462, ITAL 435, PORT 435, and RMLG 435.  See SPAN 435.'),
 (4720,112,465,'Introduction to Bantu Syntax',4124,'Introduction to the study of the syntax of Bantu languages, with particular attention to the morphological, argument, and syntactic structures that characterize these languages. 3 undergraduate hours. 4 graduate hours.  Prerequisite: LING 400 or equivalent.'),
 (4721,112,469,'Structure of Semitic Languages',401,'In-depth survey of comparative issues in Semitic Linguistics, with particular emphasis on morphology, syntax, phonology and language change from the perspectives of current linguistic theories. Same as AFST 469. 3 undergraduate hours. 4 graduate hours.  Prerequisite: LING 100, LING 400, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4722,112,470,'Mind, Culture and Society',665,'Same as ANTH 470 and COMM 470. See ANTH 470.'),
 (4723,112,480,'Intro to Slavic Linguistics',4125,'Same as SLAV 480. See SLAV 480.'),
 (4724,112,486,'Computer Foreign Lang Tchg',1813,'Same as CLCV 482, EIL 482, FR 482, GER 482, HUM 482, ITAL 482, PORT 482, SLAV 482, and SPAN 482. See HUM 482.'),
 (4725,112,488,'English Phon & Morph for TESL',2547,'Same as EIL 488. See EIL 488.'),
 (4726,112,489,'Theoretical Foundations of SLA',2548,'Same as EIL 489, FR 481, GER 489, ITAL 489, PORT 489, SLS 489, and SPAN 489. See EIL 489.'),
 (4727,112,490,'Special Topics in Linguistics',4126,'Course provides an opportunity to focus on various subfields of the linguistic sciences, depending on the interests of the faculty and student. 3 undergraduate hours. 4 graduate hours. May be repeated as topic varies to a maximum of 9 undergraduate hours, or 12 graduate hours. Students may register for up to two sections in the same term. Prerequisite: LING 100, LING 400, or consent of instructor.'),
 (4728,112,500,'Intro to General Linguistics',689,'Introduction to the linguistic sciences; linguistic theory and methodology; and branches of linguistics and their application. Same as ANTH 500, and EIL 520. Credit may not be applied toward a graduate degree in linguistics.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4729,112,501,'Syntax I',4127,'Introduction to the fundamental concepts, philosophy, and methods of syntactic theory.  Prerequisite: LING 400 or equivalent.'),
 (4730,112,502,'Phonology I',4128,'Examination of language-specific phonological problems with a view toward formulating a language-independent theory of phonology.  Prerequisite: LING 401 or consent of instructor.'),
 (4731,112,504,'Practicum',4129,'Supervised practical experience in extended linguistic research on individual topics of the student\'s choice.  Prerequisite: LING 501 and LING 502'),
 (4732,112,506,'Topics in Computational Ling',4130,'Provides an introduction to practical problems in computational linguistics in a laboratory setting.  At the beginning of the semester, a substantial project will be assigned to the class, and the class will work as a team towards implementing a solution, and evaluating the final product against a test corpus, which will also be developed during the class.  Topical readings will also be assigned and will be discussed.  May be repeated if topics vary.  Students may register in more than one section per term to a maximum of 8 hours and may be repeated in subsequent terms to a maximum of 12 hours.  Approved for letter or S/U grading.  Prerequisite: LING 406, and an introductory level Computer Science programming course, or consent of instructor.'),
 (4733,112,507,'Formal Semantics I',4131,'Introduction to formal semantic theory for natural language, with attention to quantification, anaphora, tense, intensionality, and related topics.   Prerequisite: LING 407 or consent of the instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4734,112,509,'Cognitive Semantics',4132,'Course analyzes the nature of linguistic semantic categories and their implications for theories of grammar; examining the issues and controversies surrounding frame semantics, decompositional semantics, prototype theory, and conceptual metaphor. Approved for both letter and S/U grading.'),
 (4735,112,510,'Topics in African Linguistics',4133,'Discussion of advanced selected topics in African linguistics; concentration on morphology, tonology, phonology, sociolinguistics, language acquisition, and syntax. May be repeated if topics vary. Students may register in more than one section per term to a maximum of 8 hours and may be repeated in subsequent terms to a maximum of 12 hours. Prerequisite: LING 501 and LING 502; or consent of instructor.'),
 (4736,112,512,'Language and Culture',695,'Same as ANTH 512. See ANTH 512.'),
 (4737,112,513,'Pedagogical Grammar',2550,'Same as EIL 522. See EIL 522.'),
 (4738,112,514,'Design & Stats in Lang Study',2554,'Quantitatively oriented approach to research design and data analysis in language study, with emphasis on principles of probability theory, descriptive and inferential statistics (including ANOVAs, correlation, and regression and analysis), parametric and non-parametric statistics, and the construction of appropriate research designs for the study of language.  Term paper required.  Same as EIL 574.  Prerequisite: LING 400 or equivalent; LING 425, or EIL 489 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4739,112,515,'Topics in Applied Linguistics',2555,'Advanced seminar to critically evaluate linguistic theories by discussing their applications to language-related areas such as contrastive linguistics, corpus linguistics, language acquisition, language and literacy, language policy, language standardization, text linguistics, and translation. Same as EIL 575. May be repeated in the same or subsequent terms to a maximum of 12 hours, as topics vary. Prerequisite: LING 400, LING 405, LING 450, or LING 500, or consent of instructor.'),
 (4740,112,516,'Field Methods',4134,'Analysis of the phonetic, phonological, morphological, and syntactic structure of an undescribed language through the elicitation of data from a native language consultant. The class develops a linguistic sketch of the language, including a computerized lexicon.  Prerequisite: LING 501 and LING 502.'),
 (4741,112,518,'Sociolinguistic Methodology',4135,'Advanced research-oriented seminar emphasizing the review and critical evaluation of theoretical and methodological approaches to sociolinguistic variation. Topics include dialectology, diachronic language change, code-mixing, pidgins and Creoles, styles, registers, and genres.  Prerequisite: Consent of instructor.'),
 (4742,112,520,'Acoustic Phonetics',4136,'Explores advanced issues in acoustic theory and digital signal processing in the context of linguistic phonetics and phonological research. Emphasis is placed on the spectral properties of speech sounds and their instrumental documentation. A significant portion of the course will utilize the phonetics laboratory.   Prerequisite: LING 401 and LING 502.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4743,112,522,'Articulatory Phonetics',4137,'Explores advanced issues in sound production in the context of linguistic phonetics and phonological research. Three main areas of focus include an overview of vocal tract physiology and anatomy, laboratory/instrumental methodology, and linguistic patterns such as assimilations and coarticulations.   Prerequisite: LING 401 or equivalent.'),
 (4744,112,524,'Dev Psycholinguistics',1853,'Same as COMM 524, and PSYC 524. See PSYC 524.'),
 (4745,112,525,'Psycholinguistics',1854,'Same as COMM 525, and PSYC 525. See PSYC 525.'),
 (4746,112,529,'Second Lang Acq & Bilingualism',4138,'Research seminar: students will design and execute a research project on second language acquisition and/or bilingualism.  Same as PSYC 529.  Prerequisite: Consent of instructor.'),
 (4747,112,535,'Sem Neuroling of Bilingualism',2551,'Seminar on the neurolinguistic aspects of bilingualism and second-language acquisition, with particular attention to the development of a critical understanding of the manner in which dual- and multi-language systems co-exist in one brain. Same as EIL 535, and SLS 535.  Prerequisite: LING 435 or consent of instructor.'),
 (4748,112,541,'Syntax II',4139,'Issues in the theory and practice of syntactic description, with special attention to implications for universal grammar.   Prerequisite: LING 501 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4749,112,542,'Phonology II',4140,'Continuation of LING 502  Prerequisite: LING 502'),
 (4750,112,547,'Formal Semantics II',4141,'A continuation of LING 507 covering advanced topics in formal semantic theory.   Prerequisite: LING 507 or consent of instructor.'),
 (4751,112,550,'Sociolinguistics II',4142,'Focus on a critical examination of issues in the theory and practice of sociolinguistics concerning the study of language variation from a cross-linguistic perspective, language diversity, multilingualism, language ideology and power.  Prerequisite: LING 450 or equivalent.'),
 (4752,112,551,'Pragmatics',4143,'Examination and development of theories of language use, addressing the role of pragmatics in linguistics and in linguistic theory, with special attention to the major research questions concerning natural language processing.   Prerequisite: LING 501 and LING 507, or consent of instructor.'),
 (4753,112,555,'Socioling of World Englishes',4144,'In-depth profile of the sociolinguistics of English as an international language, including study of the processes of nativization and acculturation, the development of new culture-specific discourse types and literatures, attitudes of native and non-native speakers toward the power and domination of English, and approaches to teaching English in international contexts.  Prerequisite: LING 450 or equivalent, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4754,112,559,'Sem Romance Ling',2971,'Same as FR 559, ITAL 559, PORT 559, RMLG 559, and SPAN 557. See SPAN 557.'),
 (4755,112,560,'Seminar in Bilingualism',4145,'Research-oriented seminar on theoretical and applied aspects of bilingualism; critical evaluation of linguistic, neurolinguistic, sociolinguistic, and psycholinguistic approaches to bilingualism; and concentration on selected case studies from Western and non-Western societies, especially Asia and Africa.  Prerequisite:  LING 450 or an introductory course in linguistics.'),
 (4756,112,570,'Seminar in Cognitive Science',4146,'Same as PSYC 514, ANTH 514, CS 549, EPSY 551, and PHIL 514. See PSYC 514.'),
 (4757,112,575,'Exper Phon I Spch Physiol',4147,'Same as SHS 500. See SHS 500.'),
 (4758,112,576,'Exper Phon II Spch Acous Perc',4148,'Same as SHS 501. See SHS 501.'),
 (4759,112,581,'Topics in Syntactic Theory',4149,'Investigation of syntactic universals; recent developments in the theory of syntax.  May be repeated if topics vary.  Prerequisite: LING 541 or consent of instructor.'),
 (4760,112,582,'Topics in Phonological Theory',4150,'Recent developments in the theory of phonology.  May be repeated if topics vary.  Prerequisite: LING 542 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4761,112,583,'Topics Historical Linguistics',4151,'Advanced course on methods and current issues in historical linguistics including diachronic phonology, diachronic syntax, and language contact. 4 graduate hours. May be repeated to a maximum of 8 hours. Students may register in more than one section per term.  May be repeated in subsequent terms to a maximum of 16 hours. Prerequisite: Graduate concentration in historical linguistics, or consent of instructor.'),
 (4762,112,584,'Theories in SLA',1756,'Same as CI 584, EALC 584, EIL 584, EPSY 563, FR 584, GER 584, ITAL 584, PORT 584, SLS 584, and SPAN 584. See SPAN 584.'),
 (4763,112,585,'Topics in Semantic Theory',4152,'Advanced topics in theoretical semantics. 4 graduate hours. May be repeated to a maximum of 8 hours. Students may register in more than one section per term.  May be repeated in subsequent terms to a maximum of 12 hours. Prerequisite: Consent of instructor.'),
 (4764,112,587,'Topics in Sociolinguistics',4153,'Discussion of current topics in sociolinguistics that have relevance to contemporary societies. May be repeated in the same term to a maximum of 8 hours. May be repeated in separate terms to a maximum of 12 hours. Approved for both letter and S/U grading. Prerequisite: LING 450.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4765,112,588,'Sem Second Lang Learn',2312,'Same as EALC 588, EIL 590, FR 588, GER 588, ITAL 588, PORT 588, SLS 588, and SPAN 588. See SPAN 588.'),
 (4766,112,590,'Special Topics in Linguistics',4154,'Individual studies in the areas of linguistics not covered by regular course offerings'),
 (4767,112,591,'Seminar in Linguistic Analysis',4155,'Discussion of advanced topics of current interest.  May be repeated with approval.  Prerequisite: LING 501 and LING 502.'),
 (4768,112,594,'Math Models of Language',2435,'Same as ECE 594. See ECE 594.'),
 (4769,112,599,'Thesis Research',4156,'May be repeated. Approved for S/U grading only.'),
 (4770,113,199,'Undergraduate Open Seminar',4157,'May be repeated.'),
 (4771,113,412,'Economics of Poverty',2462,'Same as ECON 412. See ECON 412.'),
 (4772,113,434,'Employee Benefit Plans',2880,'Same as FIN 434. See FIN 434.'),
 (4773,113,440,'Economics of Labor Markets',2468,'Same as ECON 440. See ECON 440.'),
 (4774,113,441,'Economics of Human Resources',2469,'Same as ECON 441. See ECON 441.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4775,113,450,'European Working Class History',3466,'Same as HIST 450, and SOC 422. See HIST 450.'),
 (4776,113,480,'US Work Class Hist Since 1780',3485,'Same as HIST 480. See HIST 480.'),
 (4777,113,522,'Government Regulation',4158,'Focuses on federal and state legislation, court and agency rulings, and executive orders that regulate a wide range of private and public employment practices including: Title VII and Affirmative Action Compliance; American with Disabilities Act; drug-, HIV-, and genetic testing; Fair Labor Standards Act; Civil Service procedures; Equal Pay Act, Family and Medical Leave Act, and employment-at-will; constitutional protection for employees, job-applicants, and others.  Prerequisite: LIR 547 or LIR 591, or consent of instructor.'),
 (4778,113,530,'Found of Ind Org Psych',4159,'Same as PSYC 530. See PSYC 530.'),
 (4779,113,535,'Motivation and Morale in Indus',4160,'Same as PSYC 535. See PSYC 535.'),
 (4780,113,540,'Labor Economics I',2501,'Same as ECON 540. See ECON 540.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4781,113,541,'Labor Economics II',2502,'Same as ECON 541. See ECON 541.'),
 (4782,113,542,'Collective Bargaining',2503,'Examination of: social values and social science concepts to develop a framework for explaining the basis and shape of collective bargaining as it has been practiced in the United States; government and law, unions, and employers as part of the development of this framework; the environment of collective bargaining with respect to the role of economics and bargaining structure; the negotiating process as the interactive basis for union-management relations; conflict and conflict resolution as part of the negotiating process; wage and other effects of collective bargaining as bargaining outcomes; contemporary changes in union management relations. Case materials and exercises may be used to supplement course materials. Same as ECON 542. Graduate credit is not given for both ECON 343 and LIR 542. Prerequisite: Consent of instructor.'),
 (4783,113,543,'Workplace Dispute Resolution',2504,'Examination of the use of procedures to resolve employment disputes in both union and nonunion workplaces; comparative analysis of grievance arbitration, interest arbitration, mediation, fact-finding, and combinations of these procedures; special emphasis given to the role of third party intervention. Same as ECON 543, and LAW 665. 3 professional hours. 4 graduate hours. Professional credit only applicable to LAW 665.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4784,113,545,'Economics of Human Resources',3593,'Study of the economics of personnel with the modern corporation. Topics include hiring, promotion, evaluation, discrimination, raiding, job definition, pay schemes, benefits, and design of work. Same as HRE 534.  Prerequisite: LIR 593 or equivalent, or consent of instructor.'),
 (4785,113,547,'Labor Law I',4048,'Same as LAW 662. See LAW 662.'),
 (4786,113,548,'Topics in Personnel Mgmt',1215,'Same as BADM 511. See BADM 511.'),
 (4787,113,551,'Labor Law and Public Policy',4049,'Same as LAW 663. See LAW 663.'),
 (4788,113,554,'Compar Employmt Relations Sys',4051,'Examines employment systems in selected developed, newly industrialized, and developing economies. Explores employment systems in the context of regional and political integration.Topics include the organization and policies of unions and employers, as well as management-labor relations, and the roles of firms, national governments, and international organizations in shaping employment systems. Emphasis will be placed on the analytical tools needed to make multi-country comparisons, to link theory and practice, and to understand the reasons for major changes in the nature of the employment relations. Same as LAW 666. 3 professional hours. 4 graduate hours. Professional credit only applicable to LAW 666.'),
 (4789,113,555,'Labor in Less Developed Count',4161,'Role and place of LDCs in the world; colonialism, independence, and nation-building; economics, power, and stratification; development of labor markets and labor movements; economic, political, and social consequences of international trade, finance, and investment; international diffusion of technology and ideology; nation-states, multi-national corporations and world community; and international and regional organizations and their impact on labor in LDCs.  Prerequisite: ECON 101 or ECON 110, or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4790,113,556,'Industrial Relations Theory',4162,'Integrated analysis of the principles of industrial relations through the study of the works of the major theorists and their critics.  Prerequisite: Consent of instructor.'),
 (4791,113,557,'Human Resources Theory',4163,'Continuation of LIR 556. Focuses on contemporary research in human resource management and related fields.'),
 (4792,113,558,'Faculty-Student Workshop',4164,'Training and experience for Ph.D. students in the application of social science and industrial relations theory and research methodology to contemporary industrial relations problems through presentation and discussion of faculty and student research. Ph.D. students are required to make presentations and to participate in workshop discussions during the entire period of their campus residency. Approved for both letter and S/U grading.'),
 (4793,113,561,'Compensation Systems',4165,'Compensation theory and practice. Course addresses the theoretical and practical issues associated with the design of effective compensation systems. The design phases include establishing internal equity, external equity, and individual equity. Budgeting and administration are also addressed. Case analyses and computer simulations may be used to supplement course materials.'),
 (4794,113,562,'HR Planning and Staffing',4166,'Examines conceptual issues, policies, and practices relating to the attraction, selection, development, and planning for the most effective utilization of human resources.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4795,113,563,'HR Info Sys & Comput App in IR',4167,'Design, implementation, and evaluation of human resource information systems (HRIS). Topics to be covered include fundamental database characteristics, information systems and management processes, systems analysis and needs assessment in Human Resources and Industrial Relations departments, implementing HRI systems, the use of HRI systems to solve organizational problems, information systems and labor relations. A series of cases and computer exercises which will play a major role in determining the course grade will be used. Regular seminars and some laboratory sessions will be scheduled throughout the term.  Prerequisite: Graduate standing in Labor and Industrial Relations or consent of instructor.'),
 (4796,113,564,'HR Training and Development',4168,'Examination of: theories of behavioral change; application of these theories to training and human resource development; assessing training needs, especially with reference to the internationalization of business, changes in labor demand, demographic trends in the United States, and increasing work force diversification; advantages and disadvantages of the various training and development techniques; relation of training to organizational strategies; methods of training evaluation. Special attention is given to the need for and methods of cross-cultural training. Students develop training exercises for class presentation and participation.'),
 (4797,113,565,'HR Management and Strategy',1216,'Designed to provide integration across the specific functional areas of the human resources management (HRM) field, while at the same time demonstrating the linkages horizontally within HRM and vertically with strategic management of the firm. This case-focused course places emphasis on human resources issues of strategic importance to the organization. Same as BADM 512.  Prerequisite: One prior course from the Organizational Behavior and Personnel Management distribution subject area list (in the MHRIR degree requirements for the graduate degree in Labor and Industrial Relations).');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4798,113,566,'International HR Management',4169,'Human resource management issues examined from the perspective of the multinational firm. Topics include globalization and human resource strategy, management and the structure of multinational firms, dealing with intercultural differences, selecting employees for foreign assignments, training and developing expatriate employees, evaluation and compensation of employees in international assignments. Individual and group projects.  Prerequisite: Graduate standing.'),
 (4799,113,567,'Negotiation in HR Decisions',4170,'General survey course concerning the strategies and tactics of bargaining and negotiation, with special emphasis on applications in human resource management contexts. Topics covered will include: the structure of negotiated outcomes; integrative bargaining tactics; distributive bargaining tactics; negotiation planning; power, persuasion and influence; communication; negotiating in teams and groups; negotiating using 3rd parties (arbitrators, mediators, agents); cross-cultural negotiations. Students will discuss negotiation issues and build negotiation skills through a series of experiential exercises and cases. Credit is not given for both this course and MBA 505 (Sections W1 and W2: Managerial Negotiations). Prerequisite: Graduate standing. An introductory course in social psychology or organizational behavior is preferred but not required.'),
 (4800,113,568,'Firm Performance and HR',4171,'The purpose of this course is to enable students to understand some basic ideas about and measures of firm performance with heavy emphasis on the role of human resource managers. Students will gain an understanding of how human resource professionals fit into the organization, structure, and function of business firms. Many basic ideas from the field of finance will be studied. The course covers theoretical ideas and has many empirical, policy, and practitioner-relevant applications, all with the goal of providing human resource managers fundamental financial analysis tools to enable them to function effectively in their post-graduate corporate workplaces.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4801,113,590,'Individual Topics',4172,'Students in labor and industrial relations may register for this unit with the consent of the curriculum adviser and the adviser under whom the student will perform individual study or research. Such individual work may include special study in a subject matter for which no course is available or an individual research project, including on-the-job research in industry, which is not being undertaken for a thesis.  Approved for both letter and S/U grading.'),
 (4802,113,591,'Employment Relations Systems',4173,'General framework for the analysis of employment relationships. Topics include industrial relations theory, the American system of collective bargaining, intercountry system differences, and human resource management strategies and practices.  Prerequisite: Graduate standing.'),
 (4803,113,592,'Research Methods in LIR',4174,'Systematic analysis of theories and procedures of the various social and physical sciences bearing on research in labor and industrial relations; primary emphasis on the process of integrating the approaches and techniques of the various social sciences with respect to the study of problems in labor and industrial relations as met in practice in management, the union, and government service, as well as in teaching and research in the field.  Prerequisite: Major in social sciences or consent of instructor.'),
 (4804,113,593,'Quanitative Methods in LIR',4175,'Application of statistical methods to problems in human resources and industrial relations. Analysis and presentation of results using computer software. Covers statistical techniques through analysis of variance and multiple regression.  Prerequisite: Any elementary statistics course.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4805,113,594,'Tutorial Seminar',4176,'Research experience for Master\'s students in carrying out a problem solving project from formulation to written report in a chosen area of labor and industrial relations. Each student selects an individual topic with the approval and guidance of a faculty member and participates in a Tutorial Workshop.  Approved for both letter and S/U grading.  Prerequisite: Completion of no fewer than 24 graduate hours of LIR course work.'),
 (4806,113,598,'Des High-Involvemt Workplaces',4177,'Intensive analysis of strategies for enhancing the involvement and commitment of employees in work organizations. Focus is on the design of jobs, work teams, feedback programs, and reward systems that contribute to employee well-being and to organizational effectiveness.'),
 (4807,113,599,'Thesis Seminar',4178,'For all students writing theses in LIR at the MHRIR and Ph.D. levels. May be repeated. Approved for S/U grading only.'),
 (4808,114,199,'Undergraduate Open Seminar',4179,'May be repeated.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4809,114,201,'Info Technology and Orgs',1823,'Explores the way in which organizations collect, process, and exchange information, the technologies they use to handle information, and the organizational, technological and societal factors that affect information processing goals. Same as COMM 201.  Prerequisite: Sophomore standing.'),
 (4810,114,202,'Social Aspects Info Systems',1824,'Explores the way in which information technologies have and are transforming society and how these affect a range of social, political and economic issues from the individual to societal levels Same as COMM 202.  Prerequisite: Sophomore standing.'),
 (4811,114,310,'Computing in the Humanities',4180,'Explores use and application of technology to scholarly activity in the humanities, including projects that put classic texts on the web or create multimedia application on humanities topics.  Prerequisite: Sophomore standing.'),
 (4812,114,351,'Design Info Interfaces',4181,'Examines issues of Human Computer Interaction and the design of better computer interfaces  Prerequisite: Sophomore standing.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4813,114,352,'Cognitive Psych Info Systems',4182,'Introduction to research and theory in human cognition with emphasis on its relationship to computer models of these processes and implications for building information systems.  Prerequisite: Sophomore standing.'),
 (4814,114,390,'Special Topics Info Studies',4183,'Directed and supervised investigation of selected topics in information studies that may include among others computers and culture; information policy; community information systems; production, retrieval and evaluation of scientific or social science knowledge; computer-mediated communication; and computer-supported cooperative work. May be repeated.  Prerequisite: Sophomore standing.'),
 (4815,114,403,'Lit and Resources Children',4184,'Evaluation, selection and use of books and other resources for children (ages 0-14) in public libraries and school media centers; explores standard selection criteria for print and nonprint materials in all formats and develops the ability to evaluate and promote materials according to their various uses (personal and curricular) and according to children\'s various needs (intellectual, emotional, social and physical). 3 undergraduate hours. 2 or 4 graduate hours.  Prerequisite: For Undergraduates, Junior standing, and consent of instructor.'),
 (4816,114,404,'Lit and Resources Young Adults',4185,'Evaluation, selection and use of books and other resources for young adults (ages 12-18) in public libraries and school media centers; explores standard selection criteria for print and nonprint materials in all formats and develops the ability to evaluate and promote materials according to their various uses (personal and curricular) and according to young adults\' various needs (intellectual, emotional, social and physical). 3 undergraduate hours. 2 or 4 graduate hours.  Prerequisite: Junior standing, and consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4817,114,409,'Storytelling',4186,'Fundamental principles of the art of storytelling including techniques of adaptation and presentation; content and sources of materials; story cycles; methods of learning; practice in storytelling; and planning the story hour for school and public libraries, recreational centers, the radio, and television. 3 undergraduate hours. 2 or 4 graduate hours.  Prerequisite: Junior standing, and consent of instructor.'),
 (4818,114,410,'Intro to Text Info Systems',1955,'Same as CS 410.  See CS 410.'),
 (4819,114,451,'Intro to Network Systems',4187,'Hands-on introduction to technology systems for use in information environments. The course steps students through choosing, installing, and managing computer hardware and operating systems, as well as networking hardware and software. Students will have an opportunity to design and create a working network environment as part of the course work.'),
 (4820,114,452,'Foundations Info Proc in LIS',4188,'Covers the common data processing constructs and programming concepts used in library and information science. The history, strengths and weaknesses of the techniques are evaluated in the context of our discipline. These constructs and techniques form the basis of applications in areas such as bibliographic records management, full text management and multimedia. 4 undergraduate hours. 2 or 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4821,114,453,'Systems Analysis and Mgt',4189,'Covers how to evaluate, select and manage information systems that will be used in the daily operation of libraries and information centers. Includes the systems used by technical staff and the information consumers. Course will focus on information as a product. Attention is given to the operation of an organization as a whole and the impact of change on the integration of resources, work flow and usability. Formal methods for modeling systems, and industry practice techniques of analysis are used to address these problems and opportunities 3 undergraduate hours. 4 graduate hours.'),
 (4822,114,454,'Network Systems Administration',4190,'Issues and tools for remote technology- based communication and information systems. Current and historical trends in methods for electronic information dissemination and communication, and their impact on society, organizations and individuals are discussed. Topics include systems, issues and changes in: interpersonal, group, and mass communication; publishing; information access; education; and other areas. Hands-on use and evaluation of currently available network-based communication and retrieval systems. 3 undergraduate hours. 2 or 4 graduate hours.  Prerequisite: LIS 451 or consent of instructor.'),
 (4823,114,456,'Info Storage and Retrieval',4191,'Introduces problems of document representation, information need specification, and query processing. Describes the theories, models, and current research aimed at solving those problems. Primary focus is on bibliographic, text, and multimedia records. 3 undergraduate hours. 4 graduate hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4824,114,458,'Instruction and Assistance Sys',4192,'Provides an introduction to instruction and assistance methods used in a variety of information systems including libraries, archives, museums, and electronic environments. Includes an overview of theoretical and applied research and discusses relevant issues and concepts. Students will have an opportunity to design and present an instruction or assistance program 3 undergraduate hours. 2 or 4 graduate hours.'),
 (4825,114,465,'Princ of User Interface Design',1988,'Same as CS 465.  See CS 465.'),
 (4826,114,482,'Writing Technologies',2649,'Same as ENGL 482. See ENGL 482.'),
 (4827,114,490,'Advanced Topics Info Studies',4193,'Directed and supervised investigation of selected topics in information studies that may include among others the social, political, and historical contexts of information creation and dissemination; computers and culture; information policy; community information systems; production, retrieval and evaluation of knowledge; computer-mediated communication. May be repeated.  Prerequisite: Junior standing and LIS 201 or LIS 202, or consent of instructor.'),
 (4828,114,491,'Literacy in the Info Age',1850,'A capstone course in the Information Technology Studies minor that draws on students\' experience throughout their undergraduate program to discuss a series of themes such as community, the political sphere and education which have been impacted by the new information technologies.  Same as COMM 491. 3 undergraduate hours. 4 graduate hours.  Prerequisite: LIS 201 or LIS 202.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4829,114,501,'Info Org and Access',4194,'Emphasizes information organization and access in settings and systems of different kinds. Traces the information transfer process from the generation of knowledge through its storage and use in both print and non-print formats. Consideration will be given to the creation of information systems: the principles and practice of selection and preservation, methods of organizing information for retrieval and display, the operation of organizations that provide information services, and the information service needs of various user communities Required M.S. degree core course. All students must attend the lecture section and one discussion section.'),
 (4830,114,502,'Libraries Info and Society',4195,'Explores major issues in the library and information science professions as they involve their communities of users and sponsors. Analyzes specific situations that reflect the professional agenda of these fields, including intellectual freedom, community service, professional ethics, social responsibilities, intellectual property, literacy, historical and international models, the socio-cultural role of libraries and information agencies and professionalism in general, focusing in particular on the interrelationships among these issues Required M.S. degree core course.'),
 (4831,114,503,'Use and Users of Info',4196,'Explores information needs and uses at a general level, addressing formal and informal information channels, barriers to information, issues of value, and impacts of technology. Examines information seeking practices of particular communities and within various environments, introducing recent approaches to user-centered system design and digital library development. Provides an overview of methods that can be used to study information needs, information seeking behavior, and related phenomena.  Prerequisite: LIS 501.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4832,114,504,'Reference and Info Services',4197,'Explores reference and information services in a variety of settings, introduces widely used print and online sources, and develops question negotiation skills and search strategies.'),
 (4833,114,505,'Adm Mgt of Libs Info Centers',4198,'Designed to explore the principles that govern how organizations and institutions work, this course provides a foundation for and introduction to the theories, practices and procedures involved in the management and administration of libraries and information centers.'),
 (4834,114,506,'Youth Services Librarianship',4199,'Theory and techniques in planning, implementing and evaluating library programs/services for youth (age 0-18) in public and school libraries/media centers; the knowledge base, skills, and competencies needed by the library media professional in the development of all aspects of young people\'s reading/viewing/listening and information literacy skills.'),
 (4835,114,507,'Cataloging and Classif I',4200,'Theory and application of basic principles and concepts of descriptive and subject cataloging; emphasis on interpreting catalog entries and making a catalog responsive to the needs of users; provides beginning-level experience with choice of entries, construction of headings, description of monographs (and, to a lesser extent,of serial publications and nonprint media), filing codes, Dewey and Library of Congress classification systems, and Library of Congress subject headings.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4836,114,510,'Adult Public Services',4201,'The literature, history, and problems of providing library service to the general adult user; investigation of user characteristics and needs, and the effectiveness of various types of adult services.'),
 (4837,114,511,'Bibliography',4202,'Covers enumerative bibliography, the practices of compiling lists; analytical bibliography, the design, production, and handling of books as physical objects; and historical bibliography, the history of books and other library materials, from the invention of printing to the present.  2 or 4 graduate hours. Prerequisite: Consent of instructor.'),
 (4838,114,512,'History of Libraries',1851,'The origins, development, and evolution of libraries and related institutions, from antiquity to the twentieth century, as a reflection of literacy, recognition of archival responsibility, humanistic achievement, scientific information needs, and service to society Same as COMM 512.'),
 (4839,114,514,'History of Children\'s Lit',4203,'Interpretation of children\'s literature from the earliest times, including the impact of changing social and cultural patterns on books for children; attention to early printers and publishers of children\'s books and to magazines for children.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4840,114,522,'Info Sources and Svcs Sciences',4204,'Overview of the information needs and practices of researchers, practitioners, and the general public. Detailed consideration of disciplinary literatures and print and electronic reference materials. Advanced training in addressing reference questions and research problems in the sciences.  Prerequisite: LIS 504, or consent of instructor.'),
 (4841,114,523,'Info Sources and Svcs Soc Sci',4205,'Overview of the information needs and practices of researchers, practitioners, and the general public. Detailed consideration of disciplinary literatures and print and electronic reference materials. Advanced training in addressing reference questions and research problems in the social sciences.   Prerequisite: LIS 504, or consent of instructor.'),
 (4842,114,524,'Info Sources and Svcs Arts Hum',4206,'Overview of the information needs and practices of researchers, practitioners, and the general public. Detailed consideration of disciplinary literatures and print and electronic reference materials. Advanced training in addressing reference questions and research problems in the arts and humanities.  Prerequisite: LIS 504, or consent of instructor.'),
 (4843,114,525,'Government Information',4207,'Aims to acquaint students with government publications, their variety, interest, value, acquisition, and bibliographic control, and to develop proficiency in their reference and research use; considers publications of all types and all governments (local, national, international) with special emphasis on U. S., state and federal governments, and on the United Nations and its related specialized agencies.  Prerequisite: LIS 504, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4844,114,526,'Searching Online Info Systems',4208,'Explores the state-of-the-art in online information retrieval systems, with particular emphasis on their use as part of reference service in libraries; acquaints students with the characteristics of both bibliographic and nonbibliographic databases; and trains students in the use of at least one currently available online retrieval system.  Prerequisite: LIS 504 or consent of instructor.'),
 (4845,114,530,'Info Needs of Part Communities',4209,'Special topics sections for in-depth study of the characteristics and information needs of specialist users of libraries; goals and objectives, policies, and services; reference and bibliographical aids; and effective services that satisfy these special needs.  Prerequisite: LIS 504, or consent of instructor.'),
 (4846,114,544,'Library Cooperation & Networks',4210,'Development of library systems, with special reference to public libraries as a norm for the development of library services; detailed treatment of library standards, the growth and development of county and regional libraries, and the role of the state library and of federal legislation  Prerequisite: LIS 505 or consent of  instructor.'),
 (4847,114,548,'Library Buildings',4211,'Studies the library\'s physical plant in the light of changing concepts and patterns of library service; analyzes present-day library buildings (both new and remodeled), and their comparison with each other as well as with buildings of the past; examines the interrelationship of staff, collections, users, and physical plant; discussion supplemented by visits to new libraries and conference with their staffs. A two-day field trip is required.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4848,114,549,'Economics of Info',4212,'The various definitions of information in economic and social terms as discussed in library and information science as well as other literatures are related to government public policies and social policies. Issues such as information as a commodity and as a public good are explored. The impact of the economics of information and related public policies on libraries and information centers is discussed from a national and international perspective.'),
 (4849,114,556,'Implement Info Stor and Retr',4213,'Types of systems for storage and retrieval of documents and references; their characteristics, evaluation, factors affecting their performance, and the mathematical models on which their operations are based are covered. Primary focus is on modern computer-based systems and their implementation. Students will use programming tools to build demonstration systems and install retrieval packages as part of a case study.  Concurrent or prior registration in LIS 456 is recommended. Prerequisite: LIS 452 or proficiency in any programming language and consent of instructor.'),
 (4850,114,566,'Arch Net Info Sys',4214,'The principles and practices of designing systems, particularly network information systems. Critical evaluation of current Internet infrastructure plus evolution of research architectures. Historical survey of functionality of system components. Design project required. Familiarity with commercial on-line services assumed.   Prerequisite: CS 411 and LIS 456, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4851,114,577,'Cataloging and Classif II',4215,'More complex problems in making and evaluating the changing, modern library catalog; practical and administrative problems in cataloging serial publications, analytics, ephemeral materials, and microforms; deals with various nonprint media, rare books and manuscripts, foreign language materials, and materials in special subject areas.  Prerequisite: LIS 507, or consent of instructor.'),
 (4852,114,578,'Technical Services Functions',4216,'Seminar on the principles, problems, trends, and issues of acquiring, identifying, recording, and conserving/preserving materials in all types of libraries and information centers; includes the special problems of serials management; emphasizes service aspects.'),
 (4853,114,581,'Adm and Use Archival Materials',4217,'Administration of archives and manuscript collections in various types of institutions. Theoretical principles and archival practices of appraisal, acquisition, accessioning, arrangement, description, preservation, and reference services. Topics will include: records management programs, collecting archives programs/special collections, legal and ethical issues, public programming and advocacy, and the impact of new information technologies for preservation and access. Lectures, discussion, internet demonstration, and field trips to the Special Collections Department and University Archives.'),
 (4854,114,582,'Preserving Info Resources',4218,'Covers the broad range of library preservation and conservation for book and nonbook materials relating these efforts to the total library environment; emphasizes how the preservation of collections affects collection management and development, technical services, access to materials and service to users.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4855,114,590,'Advanced Problems in LIS',4219,'Variety of newly developed and special courses on selected problems in the four curriculum domains of Design and Evaluation of Information Systems and Services, Information Organization and Analysis, Management and Consulting for Information Systems and Services, and Access - People and Collections, offered as sections of LIS 590.  May be repeated.'),
 (4856,114,591,'Practicum',4220,'Supervised field experience of professional-level duties in an approved library or information center. A maximum of 2 hours may be applied toward a degree program. Approved for S/U grading only. Prerequisite: Completion of 14 graduate hours of library and information science courses; submission of Practicum forms.'),
 (4857,114,592,'Independent Study',4221,'Permits the intermediate or advanced student opportunity to undertake the study of a topic not otherwise offered in the curriculum or to pursue a topic beyond or in greater depth than is possible within the context of a regular course.  May be repeated by M.S. students to a maximum of 4 graduate hours; CAS students, a maximum of 8 graduate hours; Ph.D. students, a maximum of 16 graduate hours. Prerequisite: Submission of  \"Request to Enroll in LIS 592\" form.'),
 (4858,114,593,'CAS Project',4222,'Individual study of a problem in library or information science; forms the culmination of the Certificate of Advanced Study program. May be repeated. Only eight hours will apply to the Certificate of Advanced Study.  Approved for   S/U grading only. Prerequisite: Admission to Certificate of Advanced Study program in library and information science; submision of  \"Request to Enroll in LIS 593 - CAS Project\"  form.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4859,114,599,'Thesis Research',4223,'Individual study and research.  M.S. candidates, 0 to 8 graduate hours.  Doctoral candidates 0 to 16 graduate hours. May be repeated. Approved for S/U grading only.  MS students must submit a \"Request to Enroll in LIS 599 - Master\'s Thesis\" form.'),
 (4860,115,100,'Intro Latina/Latino Studies',4224,'Interdisciplinary introduction to the basis for a Latina/Latino ethnicity in the United States. Topics include immigration and acculturation experiences and their commonalities and differences, comparison of Latina/Latino experiences to those of other racial, ethnic and immigrant groups, and the potential for a pan-ethnic identity.'),
 (4861,115,199,'Undergraduate Open Seminar',4225,'May be repeated.'),
 (4862,115,201,'US Racial & Ethnic Politics',305,'Same as AFRO 201, and PS 201. See PS 201.'),
 (4863,115,220,'Latin Am & Latino Migration',4226,'General overview of international migration to the United States, using Latin American migration to the U.S., especially the Midwest, as the focal point.  Topics discussed include the history of international migration to the United States, the relationship between the history and contemporary context, the development of U.S. immigration policy, the incorporation of Latino immigrants in U.S. society, and immigrant and community responses to migration.   Same as SOC 221.   Prerequisite:  LLS 100 or SOC 100.'),
 (4864,115,227,'Latina/Latinos in Contemp US',4227,'Same as SOC 227. See SOC 227.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4865,115,240,'The Chicano Experience',4228,'Course Infromation:  Same as SPAN 240.  See SPAN 240.'),
 (4866,115,242,'US Latina & Latino Culture',4229,'Same as SPAN 242.  See SPAN 242.'),
 (4867,115,246,'Gender&Sexuality Latina/o Lit',4230,'Same as SPAN 246.  See SPAN 246.'),
 (4868,115,250,'Latina/os on the Bronze Screen',1825,'Critical, historical and theoretical exploration of Latinos representations in U.S. film from the 1900s to the present.  Examination of cinematic representations as well as the social, political, and cultural context in which those representations are produced.  The focus is on Mexican American and Puerto Rican images, but Hollywood\' s treatment of other Latinos communities and ethnic groups will be discussed.  Students will be required to attend weekly movie screenings.  Same as COMM 250.'),
 (4869,115,259,'Latina/o Cultures',581,'Same as ANTH 259. See ANTH 259.'),
 (4870,115,279,'Mexican-American History',3404,'Same as HIST 279. See HIST 279.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4871,115,280,'Caribbean Latina/o Migration',3405,'Same as HIST 280. See HIST 280.'),
 (4872,115,281,'Constructing Race in America',7,'Same as AAS 281, AFRO 281, and HIST 281. See HIST 281.'),
 (4873,115,296,'Topics Latina/o Studies',4231,'Course examines specific topics in Latina/Latino Studies not addressed in regularly offered courses. Examples include theories of ethnic identity, historical foundations, cultural expression, and relevant topics in public policy studies of Latina/Latino communities. May be repeated in same or separate terms to a maximum of 6 hours.'),
 (4874,115,300,'19thC US Latina/o Lit',4232,'Focuses on the fiction (historical novels and poetry) as well as the critical essays of the 1848 Mexican-American War and the 1898 Spanish-American War, the two key 19th century events that determined the status of the people of Caribbean and Mexican descent in the United States. Students may not register for LLS 300 and LLS 301 simultaneously.'),
 (4875,115,301,'19thC US Latina/o Lit-ACP',4233,'Course is identical to LLS 300 except for the additional writing component. Credit is not given for both LLS 300 and LLS 301. Prerequisite: Completion of campus Composition I general education requirement.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4876,115,310,'Race and Cultural Diversity',13,'Same as AAS 310, AFRO 310, and EPS 310. See EPS 310.'),
 (4877,115,316,'Latina/Latino Politics',4234,'Same as PS 316. See PS 316.'),
 (4878,115,320,'Gender & Latina/o Migration',3280,'Study of the gendered social process of international immigration, focusing on Latin American migration to the United States.  Established theories of migration, the history of international immigration to the U.S., and historical and contemporary Mexico, Caribbean and Central American migration flows will be discussed in great detail.  Primary focus on how gender shapes the migration experiences of immigrants and the gendered impact of migration on the economic, political, and social status of individuals.   Same as SOC 321 and GWS 320.  Prerequisite: LLS 100 or SOC 100.'),
 (4879,115,359,'Adv Topics in Latina/o US',604,'Same as ANTH 359. See ANTH 359.'),
 (4880,115,360,'20thC US Latina/o Lit',4235,'Focuses on the major U.S. Latina/Latino writers and texts and their depictions of the events that have shaped 20th-Century U.S. Latina/Latino cultures.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4881,115,375,'Latina/o Media in the US',1836,'Same as COMM 375. See COMM 375.'),
 (4882,115,378,'La Latina',3441,'Same as HIST 378.  See HIST 378.'),
 (4883,115,379,'Latina/os and the City',3442,'Examination of the migration and settlement of Latina/o populations (Mexicans, Puerto Ricans, Cubans, Dominicans, and Central and South Americans) in U.S. cities.  Focus on the historic, economic, social and political factors that influenced these migrations and the choices migrants made to come to the United States and to urban areas in particular.  Study of the regional variation among Latina/o groups, and coalition building and collaborative ventures between Latina/os and other communities of color in urban areas.   Same as HIST 379.'),
 (4884,115,390,'Independent Study',4236,'Special topics not treated in regularly scheduled courses; designed especially for advanced Undergraduates. May be repeated in the same or subsequent terms as topics vary to a maximum of 6 hours. Approved for both letter and S/U grading.  Prerequisite: One course in Latina/Latino Studies and consent of instructor.'),
 (4885,115,391,'Latina/o Seminar in Spanish',4237,'Examination of specific topics in Latina/Latino Studies not addressed in regularly offered courses. Examples include theories of ethnic identity, historical foundations, cultural expression, and relevant topics in public policy studies of Latina/Latino communities. Course will be taught primarily in Spanish and will require a reading, writing, and speaking knowledge of Spanish. May be repeated to a maximum of 6 hours.  Prerequisite: Completion of SPAN 140, SPAN 141, SPAN 142, SPAN 143 or the equivalent, or test based on competency in Spanish, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4886,115,412,'Hispanics in the U.S.',4238,'Same as SOCW 412. See SOCW 412.'),
 (4887,115,422,'US Latina and Latino Families',3338,'Same as HDFS 422. See HDFS 422.'),
 (4888,115,433,'Found of Bilingual Educ',1700,'Same as CI 433. See CI 433.'),
 (4889,115,435,'Commodifying Difference',339,'An interdisciplinary examination of how racial, ethnic and gender difference is negotiated through media and popular culture, and how racial, ethnic and gendered communities use cultural forms to express identity and difference. Among the theoretical questions explored are the politics of representation, ethnic/racial authenticity, cultural commodification and transnational popular culture. Some of the cultural forms examined are cultural festivals/parades, ethnic/race-based beauty pageants, cinematic and televisual texts and musical forms, such as Hip-Hop and Salsa. Same as AFRO 435, AAS 435, COMM 432 and GWS 435. 3 undergraduate hours. 4 graduate hours.  Prerequisite: Any combination of 6 hours from Latina/o Studies, Asian American Studies, Afro-American Studies, Gender and Women Studies or Media Studies; graduate standing, or consent of instructor.'),
 (4890,115,438,'Latina/o Social Movements',4239,'Determinants, dynamics and consequences of Latina/o social movements and public actions in the U.S..  Theories and history of social movements, and globalization and the new immigration, and transportational community formation.  Case studies include the UFW, La Raza Party, and mobilization of Latina/o communities to address specific local issues.  Prerequisite: LLS 100 or advisor\' s permission.  3 Undergraduate Hours.  4 Graduate Hours.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4891,115,442,'US Latina Lit and Iconography',3292,'Systematically addresses contemporary Latina feminism, its contexts, and its origins through the study of influential female cultural icons from the 16th century to the present. This critical approach allows contemporary Latina feminism to construct historical and cultural narratives based on women\'s contributions to culture. Students will also learn how contemporary theoretical approaches Postcoloniality, Gender Studies, Nationalism, etc. influence the study of Latina identity. Same as GWS 445 and SPAN 442. 3 undergraduate hours. 4 graduate hours. Prerequisite: At least one previous course in U.S. Latina/Latino Studies or Gender and Women\'s Studies, or consent of instructor.'),
 (4892,115,449,'Issues in Latina/o Educ',1708,'Same as CI 449. See CI 449.'),
 (4893,115,465,'Race, Sex, and Deviance',21,'Explores how racial stereotypes rely on sexual stereotypes by examining the intersections of ethnic studies, gender and women\'s studies, and queer studies. Interdisciplinary course that draws from critial legal studies, sociology, anthropology, literary criticism, and history. Same as AAS 465, AFRO 465, and GWS 465. 3 undergraduate hours. 4 graduate hours. Prerequisite: Any lower division course in LLS, AAS, AFRO, or GWS.'),
 (4894,115,472,'Border Latina, Latino Cultures',667,'Same as ANTH 472. See ANTH 472.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4895,115,475,'History of the American West',3483,'Same as HIST 476. See HIST 476.'),
 (4896,115,496,'Seminar in Latina/o Studies',4240,'3 Undergraduate hours.  4 Graduate hours.  May be repeated up to 6 undergraduate hours or 12 graduate hours.'),
 (4897,115,590,'Independent Study',4241,'Independent study on special topics not treated in regularly scheduled courses.  Approved for both letter and S/U grading.  May be repeated to a maximum of 8 hours.'),
 (4898,115,596,'Graduate Seminar in LLS',4242,'Examination of specific topics in Latina/Latino Studies. Topics vary.  May be repeated in the same or subsequent semesters to a maximum of 12 hours.'),
 (4899,116,2,'Introductory Algebra',4243,'Methods of elementary algebra, including simplification of algebraic expressions, solving linear and quadratic equations, equations of lines, systems of linear equations, and radicals. Enrollment is restricted. Credit may not be used toward graduation in the College of LAS. Prerequisite: Score on appropriate placement test, or consent of Mathematics Department.'),
 (4900,116,12,'Algebra',4244,'Rapid review of basic techniques of factoring, rational expressions, equations and inequalities; functions and graphs; exponential and logarithm functions; systems of equations; matrices and determinants; polynomials; and the binomial theorem. Students who need both algebra and trigonometry should enroll in MATH 016.  Credit is not given for both MATH 012 and MATH 016. Credit not applicable toward graduation in certain curricula.  Prerequisite: 1.5 units of high school algebra, and 1 unit of high school geometry.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4901,116,16,'Algebra and Trigonometry',4245,'Unified treatment of algebra and trigonometry.  Credit is not given for both MATH 016 and MATH 012. Credit not applicable toward graduation in certain curricula.  Prerequisite: 1.5 units of high school algebra; 1 unit of high school geometry.'),
 (4902,116,103,'Theory of Arithmetic',4246,'Analyses of the mathematical issues and methodology underlying elementary mathematics in grades K-5. Topics include sets, arithmetic algorithms, elementary number theory, rational and irrational numbers, measurement, and probability. There is an emphasis on problem solving. Priority registration will be given to students enrolled in teacher education programs leading to certification in elementary or childhood education.  Prerequisite: MATH 012 or equivalent.'),
 (4903,116,115,'Precalculus',4247,'Analytic geometry; exponential and logarithmic functions; trigonometric functions of real numbers; trigonometric functions of angles; analytic trigonometry; limits and a preview of calculus; and a focus on modeling. Students may not receive credit for this course and MATH 016. Students may not receive credit for MATH 115 if MATH 115 is taken after receiving credit for MATH 220 or MATH 221. Prerequisite: MATH 012 or equivalent.'),
 (4904,116,117,'Elementary Mathematics',4248,'Analyses of the mathematical issues and methodology underlying elementary mathematics in grades 6-8. Topics include the Real number system and field axioms, sequences and series, functions and math modeling with technology, Euclidean and non-Euclidean geometry, probability and statistics. Priority registration will be given to students enrolled in teacher education programs leading to certification in elementary education. Prerequisite: MATH 012 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4905,116,119,'Ideas in Geometry',4249,'General education course in mathematics, for students who do not have mathematics as a central part of their studies. The goal is to convey the spirit of mathematical thinking through topics chosen mainly from plane geometry.  Prerequisite: Two units of high school algebra; one unit of high school geometry; or equivalent.'),
 (4906,116,124,'Finite Mathematics',4250,'Introduction to finite mathematics for students in the social sciences; introduces the student to the basic ideas of logic, set theory, probability, vectors and matrices, and Markov chains. Problems are selected from social sciences and business.  Prerequisite: MATH 012, or an adequate ACT score.'),
 (4907,116,125,'Elementary Linear Algebra',4251,'Basic concepts and techniques of linear algebra; includes systems of linear equations, matrices, determinants, vectors in n-space, and eigenvectors, together with selected applications, such as Markov processes, linear programming, economic models, least squares, and population growth. Credit is not given for both MATH 125 and MATH 225. Prerequisite: MATH 012, or an adequate ACT score.'),
 (4908,116,161,'Statistics',4252,'Same as STAT 100. See STAT 100.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4909,116,181,'A Mathematical World',4253,'Introduction to selected areas of mathematical sciences through application to modeling and solution of problems involving networks, circuits, trees, linear programming, random samples, regression, probability, inference, voting systems, game theory, symmetry and tilings, geometric growth, comparison of algorithms, codes and data management.  Prerequisite: Three years of high school mathematics, including two years of algebra and one year of geometry.'),
 (4910,116,198,'Freshman Seminar',4254,'Guides the student in the study of selected topics not considered in standard courses.  Prerequisite: Enrollment in the mathematics honors program; consent of department.'),
 (4911,116,199,'Undergraduate Open Seminar',4255,'Approved for both letter and S/U grading.  May be repeated.'),
 (4912,116,210,'Theory of Interest',4256,'Study of compound interest and annuities; applications to problems in finance.  Prerequisite: MATH 231 or equivalent.'),
 (4913,116,213,'Basic Discrete Mathematics',4257,'Beginning course on discrete mathematics, including sets and relations, functions, basic counting techniques, recurrence relations, graphs and trees, and matrix algebra; emphasis throughout is on algorithms and their efficacy.  Credit is not given for both MATH 213 and CS 173.  Prerequisite: MATH 220 or MATH 221, or equivalent');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4914,116,220,'Calculus',4258,'First course in calculus and analytic geometry; basic techniques of differentiation and integration with applications including curve sketching; antidifferentation, the Riemann integral, fundamental theorem, exponential and trigonometric functions. Credit is not given for both MATH 220 and either MATH 221 or MATH 234. Prerequisite: MATH 016 or equivalent; or an adequate ACT score.'),
 (4915,116,221,'Calculus I',4259,'First course in calculus and analytic geometry for students with some calculus background; basic techniques of differentiation and integration with applications including curve sketching; antidifferentation, the Riemann integral, fundamental theorem, exponential and trigonometric functions. Credit is not given for both MATH 221 and either MATH 220 or MATH 234. Prerequisite: One year of high school calculus or a minimum score of 2 on the AB Calculus AP exam.'),
 (4916,116,225,'Introductory Matrix Theory',4260,'Systems of linear equations, matrices and inverses, determinants, and a glimpse at vector spaces, eigenvalues and eigenvectors. Credit is not given for both MATH 225 and either MATH 125 or MATH 415. Prerequisite: MATH 220 or MATH 221; or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4917,116,231,'Calculus II',4261,'Second course in calculus and analytic geometry: techniques of integration, conic sections, polar coordinates, and infinite series.  Credit is not given for both MATH 231 and MATH 230.  Prerequisite: MATH 220 or MATH 221.'),
 (4918,116,234,'Calculus for Business I',4262,'Introduction to the concept of functions and the basic ideas of the calculus. Credit is not given for both MATH 234 and either MATH 220 or MATH 221. Prerequisite: MATH 012.'),
 (4919,116,241,'Calculus III',4263,'Third course in calculus and analytic geometry including vector analysis: Euclidean space, partial differentiation, multiple integrals, line integrals and surface integrals, the integral theorems of vector calculus.  Credit is not given for both MATH 241 and any of MATH 242, MATH 243, MATH 244, MATH 380.  Prerequisite: MATH 231 (formerly MATH 230).'),
 (4920,116,244,'Calculus for Business II',4264,'Continuation of MATH 234. Calculus of the trigonometric functions, Taylor polynomials, and infinite series; analytic geometry in n dimensions, vector calculus, classical extremum problems in n variables, and Lagrange multipliers; and multiple integrals. Credit is not given for both MATH 244 and any of MATH 241 (formerly MATH 243) or MATH 242. Prerequisite: MATH 234 or consent of instructor.'),
 (4921,116,249,'Honors Supplement',4265,'Supplemental credit hour for honors courses with additional material or special projects.   Prerequisite: Concurrent registration in a specially designated honors section and consent of department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4922,116,257,'Numerical Methods',1949,'Same as CS 257. See CS 257.'),
 (4923,116,267,'Math Issues National Security',3239,'Applications of mathematics to aspects of physical, biological, and social sciences that affect national security. Same as GLBL 267.  Prerequisite:  Completion of the Quantitative Reasoning I requirement, or consent of instructor.'),
 (4924,116,284,'Intro Differential Systems',4266,'First order differential equations; mathematical models and numerical methods; linear systems and matrices; higher-order linear differential equations; eigenvalues and eigenvectors; linear systems of differential equations; Laplace transform methods. Students may not receive credit for MATH 284 and MATH 385 or MATH 386. Prerequisite: MATH 231 or equivalent.'),
 (4925,116,290,'Symbolic Computation Lab',4267,'Laboratory component to courses using a symbolic programming package.  Prerequisite: Consent of department; concurrent registration in a designated section of a mathematics course with symbolic computation component. May be taken only once for credit.'),
 (4926,116,299,'Topics in Mathematics',4268,'Topics course; see Class Schedule or department office for current topics.  May be repeated in the same or subsequent semesters to a maximum of 8 hours.  Prerequisite: MATH 220 or MATH 221; Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4927,116,347,'Fundamental Mathematics',4269,'Fundamental ideas used in many areas of mathematics. Topics will include: techniques of proof, mathematical induction, binomial coefficients, rational and irrational numbers, the least upper bound axiom for real numbers, and a rigorous treatment of convergence of sequences and series. This will be supplemented by the instructor from topics available in the various texts. Students will regularly write proofs emphasizing precise reasoning and clear exposition. Credit is not given for both MATH 347 and MATH 348. Prerequisite: MATH 231.'),
 (4928,116,348,'Fundamental Mathematics-ACP',4270,'Course is identical to MATH 347 except for the additional writing component. Approved for both letter and S/U grading.  Credit is not given for both MATH 348 and MATH 347. Prerequisite: MATH 231 and completion of the campus COMPOSITION I general education requirement.'),
 (4929,116,370,'Actuarial Problem Solving',4271,'Methods and techniques of solving problems in actuarial mathematics for advanced students intending to enter the actuarial profession. Approved for S/U grading only. May be repeated in the same or subsequent semesters to a maximum of 4 hours. Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4930,116,380,'Advanced Calculus',4272,'Introductory study of vector calculus and functions of several variables; topics include directional derivatives; Jacobians; change of variables in multiple integrals; maxima and minima; line and surface integrals; theorems of Gauss, Green, and Stokes; infinite series; and uniform convergence.  Credit is not given for both MATH 380 and MATH 241 (formerly MATH 243).  Prerequisite: MATH 242.'),
 (4931,116,385,'Intro Differential Equations',4273,'Intended for engineering students and others who require a working knowledge of differential equations; included are techniques and applications of ordinary differential equations and an introduction to partial differential equations. Credit is not given for both MATH 385 and either MATH 386 or MATH 441. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242, or equivalent.'),
 (4932,116,386,'Intro to Differential Eq Plus',4274,'Intended for engineering students and others who require a working knowledge of differential equations, included are techniques and applications of ordinary differential equations, linear systems of differential equations, and an introduction to partial differential equations.  Credit not given for both MATH 386 and any of MATH 284, MATH 385, MATH 441. Prerequisite: MATH 241(formerly MATH 243) or MATH 242, or equivalent.'),
 (4933,116,390,'Individual Study',4275,'Guided individual study of advanced topics not covered in other courses. May be repeated to a maximum of 8 hours.  Approved for both letter and S/U grading.  Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4934,116,402,'Non Euclidean Geometry',4276,'Historical development of geometry; includes tacit assumptions made by Euclid; the discovery of non-Euclidean geometries; geometry as a mathematical structure; and an axiomatic development of plane geometry. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242; MATH 347 or MATH 348, or equivalent; or consent of instructor.'),
 (4935,116,403,'Euclidean Geometry',4277,'Selected topics from geometry, including the nine-point circle, theorems of Cera and Menelaus, regular figures, isometries in the plane, ordered and affine geometries, and the inversive plane. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242; or consent of instructor.'),
 (4936,116,405,'Teacher\'s Course',4278,'Presents selected topics in mathematics that are related to the content of secondary school mathematics programs; provides background for enrichment topics for secondary school students. Subject matter varies with the instructor. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242; MATH 347 or MATH 348, or equivalent; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4937,116,406,'History of Calculus',4279,'Examination of the historical origins and genesis of the concepts of the calculus; includes mathematical developments from the ancient Greeks to the eighteenth century. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242, or equivalent.'),
 (4938,116,408,'Actuarial Statistics I',4280,'Same as STAT 408. See STAT 408.'),
 (4939,116,409,'Actuarial Statistics II',4281,'Same as STAT 409. See STAT 409.'),
 (4940,116,412,'Graph Theory',4282,'Examines basic concepts and applications of graph theory, where graph refers to a set of vertices and edges that join some pairs of vertices; topics include subgraphs, connectivity, trees, cycles, vertex and edge coloring, planar graphs and their colorings. Draws applications from computer science, operations research, chemistry, the social sciences, and other branches of mathematics, but emphasis is placed on theoretical aspects of graphs. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 347 or MATH 348 or equivalent experience or CS 273.'),
 (4941,116,413,'Intro to Combinatorics',1958,'Permutations and combinations, generating functions, recurrence relations, inclusion and exclusion, Polya\'s theory of counting, and block designs. Same as CS 413. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 347 or MATH 348 or equivalent experience.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4942,116,414,'Mathematical Logic',4283,'Introduction to the formalization of mathematics and the study of axiomatic systems; expressive power of logical formulas; detailed treatment of propositional logical and predicate logic; compactness theorem and Godel completeness theorem, with applications to specific mathematical theories; algorithmic aspects of logical formulas. Proofs are emphasized in this course, which can serve as an introduction to abstract mathematics and rigorous proof; some ability to do mathematical reasoning required. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 347 or MATH 348 or equivalent experience.'),
 (4943,116,415,'Linear Algebra',4284,'Introductory course emphasizing techniques of linear algebra; topics include matrix operations, determinants, linear equations, vector spaces, linear transformations, eigenvalues, and eigenvectors. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Credit not given for both MATH 225 and either MATH 125 or MATH 415.  Prerequisite: MATH 241(formerly MATH 243) or MATH 242.'),
 (4944,116,417,'Intro to Abstract Algebra',4285,'Fundamental theorem of arithmetic. Congruencies, groups and group actions, Polya counting, rings, fields, and roots of polynomials. Emphasizes proofs. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 415 and either MATH 347 or MATH 348; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4945,116,418,'Intro to Abstract Algebra II',4286,'Vector spaces, ruler and compass constructions, finite fields, with application to Steiner systems. Linear codes or Groeber bases. Emphasizes proofs. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 417 or consent of instructor'),
 (4946,116,423,'Differential Geometry',4287,'Applications of the calculus to the study of the shape and curvature of curves and surfaces; introduction to vector fields, differential forms on Euclidean spaces, and the method of moving frames for low- dimensional differential geometry. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242, or equivalent.'),
 (4947,116,424,'Honors Real Analysis',4288,'A rigorous treatment of basic real analysis via metric spaces.  Metric space topics include continuity, compactness, completeness, connectedness and uniform convergence.  Analysis topics include the theory of differentiation, Riemann-Darboux integration, sequences and series of functions, and interchange of limiting operations.  As part of the honors sequence, this course will be rigorous and abstract.  Approved for honors grading.  3 undergraduate hours.  Prerequisite: An honors section of MATH 347 and consent of the department.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4948,116,425,'Honors Advanced Analysis',4289,'A theoretical treatment of differential and integral calculus in higher dimensions.  Topics include inverse and implicit function theorems, submanifolds, the theorems of Green, Gauss and Stokes, differential forms, and applications.  As part of the honors sequence, this course will be rigorous and abstract.   Approved for honors grading.  3 undergraduate hours.  Prerequisite: MATH 424 and consent of the department.'),
 (4949,116,426,'Honors Linear Algebra',4290,'Matrix operations, vector spaces, linear transformations, bilinear forms and orthogonality.  As part of the honors sequence, this course will be rigorous and abstract.   Approved for honors grading.  2 undergraduate hours.  Students may not receive credit for this course and any of the following:  MATH 125, MATH 225 or MATH 415.  Prerequisite: Consent of department.'),
 (4950,116,427,'Honors Abstract Algebra',4291,'Group theory, counting formulae, factorization, modules with applications to Abelian groups and linear operators.  As part of the honors sequence, this course will be rigorous and abstract.  Approved for honors grading.  3 undergraduate hours.  Students may not receive credit for both this course and MATH 417.  Prerequisite: MATH 426 and consent of the department.'),
 (4951,116,428,'Honors Geometric Analysis',4292,'Functions on Euclidean spaces, linear and multilinear functions, vector fields and differential forms on Euclidean space, singular chains and Stoke\'s theorem, manifolds, integration on manifolds, classical theorems as corollaries of Stoke\'s theorem.  As part of the honors sequence, this course will be rigorous and abstract.  Approved for honors grading.  3 undergraduate hours.  Prerequisite: Consent of the department; either (option I) MATH 427 or (option II) MATH 241 (formerly MATH 243) or MATH 380; MATH 415; MATH 447.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4952,116,432,'Set Theory and Topology',4293,'Informal set theory, cardinal and ordinal numbers, and the axiom of choice; topology of metric spaces and introduction to general topological spaces.  3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 347 or MATH 348 or consent of instructor.'),
 (4953,116,439,'Philosophy of Mathematics',4294,'Same as PHIL 439. See PHIL 439.'),
 (4954,116,441,'Differential Equations',4295,'Basic course in ordinary differential equations; topics include existence and uniqueness of solutions and the general theory of linear differential equations; treatment is more rigorous than that given in MATH 385. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Credit is not given for both MATH 441 and either MATH 385 or MATH 386. Prerequisite: MATH 241(formerly MATH 243) or MATH 242, or equivalent.  Recommended: MATH 347 or MATH 348.'),
 (4955,116,442,'Intro Partial Diff Equations',4296,'Introduces students to partial differential equations, emphasizing the wave, diffusion and potential (Laplace) equations. The focus is on understanding the physical meaning and mathematical properties of solutions of partial differential equations. Methods include fundamental solutions and transform methods for problems on the line, and separation of variables using orthogonal series for problems in regions with boundary. Convergence of Fourier series is covered in detail. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 385 or MATH 386 or MATH 441.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4956,116,444,'Elementary Real Analysis',4297,'Careful treatment of the theoretical aspects of the calculus of functions of a real variable; topics include the real number system, limits, continuity, derivatives, and the Riemann integral. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Credit is not given for both MATH 444 and MATH 447. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242; MATH 347 or MATH 348, or equivalent.'),
 (4957,116,446,'Applied Complex Variables',4298,'For students who desire a working knowledge of complex variables; covers the standard topics and gives an introduction to integration by residues, the argument principle, conformal maps, and potential fields. Students desiring a systematic development of the foundations of the subject should take MATH 448. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Credit is not given for both MATH 446 and MATH 448. Prerequisite: MATH 241(formerly MATH 243) or MATH 380; or consent of instructor.'),
 (4958,116,447,'Real Variables',4299,'Careful development of elementary real analysis including such topics as completeness property of the real number system; basic topological properties of n-dimensional space; convergence of numerical sequences and series of functions; properties of continuous functions; and basic theorems concerning differentiation and Riemann integration. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Credit is not given for both MATH 447 and MATH 444. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242, or equivalent; junior standing; MATH 347 or MATH 348, or equivalent experience; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4959,116,448,'Complex Variables',4300,'For students who desire a rigorous introduction to the theory of functions of a complex variable; topics include Cauchy\'s theorem, the residue theorem, the maximum modulus theorem, Laurent series, the fundamental theorem of algebra, and the argument principle. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Credit is not given for both MATH 448 and MATH 446. Prerequisite: MATH 241 (formerly MATH 243) or MATH 380; MATH 447.'),
 (4960,116,450,'Intro to Numerical Analysis',1981,'Same as CS 450, CSE 401, and ECE 491. See CS 450.'),
 (4961,116,453,'Elementary Theory of Numbers',4301,'Topics covered include divisibility, primes, congruences, quadratic reciprocity, and Farey sequences. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242, or equivalent.'),
 (4962,116,455,'Numerical Methods for PDEs',1982,'Same as CS 455, and CSE 411. See CS 455.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4963,116,458,'Numerical Linear Algebra',1983,'Same as CS 458, and CSE 412. See CS 458.'),
 (4964,116,459,'Numerical Approx and ODEs',1984,'Same as CS 459, and CSE 413. See CS 459.'),
 (4965,116,461,'Probability Theory',4302,'Introduction to mathematical probability; includes the calculus of probability, combinatorial analysis, random variables, expectation, distribution functions, moment-generating functions, and central limit theorem. Same as STAT 451. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242, or equivalent.'),
 (4966,116,463,'Statistics and Probability I',4303,'Same as STAT 400. See STAT 400.'),
 (4967,116,464,'Statistics and Probability II',4304,'Same as STAT 410. See STAT 410.'),
 (4968,116,465,'Analysis of Variance',4305,'Same as STAT 424. See STAT 424.'),
 (4969,116,468,'Topics in Applied Statistics',4306,'Same as STAT 430. See STAT 430.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4970,116,469,'Methods of Applied Statistics',4307,'Same as STAT 420. See STAT 420.'),
 (4971,116,470,'Actuarial Numerical Analysis',4308,'Numerical methods needed in actuarial science including iterative methods of solving equations, interpolation, numerical integration and linear systems. In addition, the theory of finite differences, and applications to actuarial problems will be covered. The level will be consistent with professional examinations in the field. Credit is not given for both MATH 470 and CS 457 / MATH 257. Prerequisite: MATH 210; MATH 241 (formerly MATH 243) or MATH 242; a 100-level computer science course; or consent of instructor.'),
 (4972,116,471,'Actuarial Theory I',4309,'Distribution of the time-to-death random variable for a single life, and its implications for evaluations of insurance and annuity functions, net premiums, and reserves.  Prerequisite: MATH 408 and MATH 210.'),
 (4973,116,472,'Actuarial Theory II',4310,'Continuation of MATH 471. Emphasis is on multiple-life functions. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 471.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4974,116,473,'Algorithms',1989,'Same as CS 473, and CSE 414. See CS 473.'),
 (4975,116,475,'Formal Models of Computation',1990,'Same as CS 475. See CS 475.'),
 (4976,116,476,'Actuarial Risk Theory',4311,'Mathematical analysis of the risk to an insurer due to variations in expected claim numbers and amounts; optimal insurance systems; the probability of ruin in the long run; reinsurance; dividend formulas. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: Credit or concurrent registration in STAT 409 or STAT 410.'),
 (4977,116,477,'Survival Analysis',4312,'The statistical process of analyzing survival data, particularly for insurance applications. Parametric, semiparametric and nonparametric methods; special attention to censoring and truncation. Testing adequacy of selected models.  Prerequisite: MATH 409 or MATH 464; MATH 471 is recommended but not required.'),
 (4978,116,478,'Actuarial Modeling',4313,'Considers the specification and evaluation of various types of actuarial models. Examines severity, frequency, and compound distributions useful in modeling the insurance loss process. Credibility theory is also discussed. 3 undergraduate hours. 3 or 4 graduate hours.  Prerequisite: MATH 408, MATH 461 or MATH 463; credit or concurrent registration in MATH 409 or MATH 464.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4979,116,481,'Vector and Tensor Analysis',4314,'Vector spaces, transformation properties, covariant and contravariant tensors, and differential geometry of surfaces; applications to relativity theory. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 380, or equivalent; or consent of instructor.'),
 (4980,116,482,'Linear Programming',4315,'Rigorous introduction to a wide range of topics in optimization, including a thorough treatment of basic ideas of linear programming, with additional topics drawn from numerical considerations, linear complementarity, integer programming and networks, polyhedral methods. 3 undergraduate hours. 3 or 4 graduate hours. Four hours of credit requires approval of the instructor and completion of additional work of substance.  Prerequisite: MATH 415.'),
 (4981,116,484,'Nonlinear Programming',4316,'Iterative and analytical solutions of constrained and unconstrained problems of optimization; gradient and conjugate gradient solution methods; Newton\'s method, Lagrange multipliers, duality and the Kuhn-Tucker theorem; and quadratic, convex, and geometric programming. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 242; MATH 347 or MATH 348; or equivalent; MATH 415 or equivalent; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4982,116,487,'Advanced Engineering Math',2388,'Complex linear algebra, inner product spaces, Fourier transforms and analysis of boundary value problems, Sturm-Liouville theory. Same as ECE 493. 3 undergraduate hours. 3 or 4 graduate hours. Prerequisite: MATH 385.'),
 (4983,116,488,'Math Methods In Engineering',4317,'Matrices, determinants, bounds and approximations to eigenvalues, introduction to linear operator theory and inner product spaces, orthogonal expansions, and Fourier transforms. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 241 (formerly MATH 243) or MATH 380, or equivalent.'),
 (4984,116,489,'Differential Equations II',4318,'Continuation of MATH 385. The course treats systems of linear differential equations (and includes the necessary matrix theory), and then concentrates on nonlinear systems, studying their dynamics by means of phase plane analysis and other methods. Applications of nonlinear systems to physics and biology will be given. 3 undergraduate hours. 3 or 4 graduate hours. 4 hours of credit requires approval of the instructor and completion of additional work of substance. Prerequisite: MATH 385 or MATH 386 or MATH 441.'),
 (4985,116,490,'Advanced Topics in Mathematics',4319,'Deals with selected topics and applications of mathematics; see Class Schedule or department office for current topics.  May be repeated with approval.  Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4986,116,491,'Logic Design',1986,'Same as CS 462, and ECE 462. See ECE 462.'),
 (4987,116,493,'Statistical Computing',4320,'Same as STAT 428. See STAT 428.'),
 (4988,116,494,'Time Series Analysis',4321,'Same as STAT 429. See STAT 429.'),
 (4989,116,496,'Honors Seminar',4322,'Careful study of a selected area of mathematics, carried out either deductively from axioms or inductively through problems; subject matter varies with instructor. 3 undergraduate hours. No graduate credit. May be repeated to a maximum of 6 hours.  Prerequisite: Consent of Mathematics Honors Committee.'),
 (4990,116,499,'Introduction Graduate Research',4323,'Seminar is required of all first-year graduate students in Mathematics. It provides a general introduction to the courses and research work in all of the areas of mathematics that are represented at the University of Illinois at Urbana-Champaign. May be repeated to a maximum of 2 hours. Prerequisite: Graduate standing or consent of instructor.'),
 (4991,116,500,'Abstract Algebra I',4324,'Isomorphism theorems for groups, centers of p-groups, simplicity of A n, Jordan-Holder Theorem; Commutative Rings and Fields, PIDs, UFDs, Gauss\'s Lemma, splitting fields, Hilbert Basis Theorem, Zariski topology; Modules over Commutative Rings, structure theorem for finitely generated modules over PIDs, with applications to abelian groups and canonical forms for matrices; Zorn\'s lemma and applications, existence and uniqueness of algebraic closures; Categories and Functors, universal mapping properties, natural transformations, limits and colimits.  Prerequisite: MATH 417 and MATH 418.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4992,116,501,'Abstract Algebra II',4325,'Solvable groups, finite p-groups, semidirect products, Sylow\'s theorem; Galois Theory, transcendental extensions, separable and normal extensions, finite Galois groups, Theorem of the Primitive Element, Fundamental Theorem of Galois Theory, symmetric Function Theorem, examples, cyclotomic, cyclic and radical extentions; Modules over Arbitrary Rings, exact sequences, projective and injective modules, Tensor products, Matrix rings, Schur\'s lemma, Wedderburn\'s theorem on semisimple rings, group algebras, Maschke\'s theorem; Algebraic Geometry, varieties, morphisms of varieties, Noetherian properties, Irreducible varieties and prime ideals.  Prerequisite: MATH 500.'),
 (4993,116,502,'Commutative Algebra',4326,'Commutative rings and modules, prime ideals, localization, noetherian rings, primary decomposition, integral extensions and Noether normalization, the Nullstellensatz, dimension, flatness, Hensel\'s lemma, graded rings, Hilbert polynomial, valuations, regular rings, singularities, unique factorization, homological dimension, depth, completion. Possible further topics: smooth and etale extensions, ramification, Cohen-Macaulay modules, complete intersections.  Prerequisite: MATH 501 or consent of instructor.'),
 (4994,116,503,'Group Theory',4327,'Structure of groups, derived groups, nilpotence and solvability, and extensions and products.  Prerequisite: MATH 501 or equivalent.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (4995,116,504,'Non-commutative Rings',4328,'Structure of Artinian rings, Morita theory, radicals, Brauer groups, finiteness conditions, and other topics at the choice of the instructor.  Prerequisite: MATH 501 or consent of instructor.'),
 (4996,116,505,'Homological Algebra',4329,'Definition and properties of the functors Ext and Tor; projective, injective, and flat modules; group extensions; dimensions of rings, and Hilbert theorem on syzygies  Prerequisite: MATH 501 or equivalent'),
 (4997,116,506,'Group Representation Theory',4330,'Representation of groups by linear transformations, group algebras, character theory, and modular representations.  Prerequisite: MATH 501 or equivalent.'),
 (4998,116,508,'Computer Algebra Systems',4331,'Hands-on exploration of specialized computer algebra systems, their capabilities and applications. Each student will be required to complete an individual project containing original work.  Prerequisite: MATH 417 or an equivalent course in abstract algebra.'),
 (4999,116,510,'Riemann Surf & Algebraic Curv',4332,'An introduction to Riemann Surfaces from both the algebraic and function-theoretic points of view. Topics include projective algebraic curves, differential forms, integration, divisors of poles and zeroes, linear systems, the Riemann-Roch theorem, Serre duality, and applications. Prerequisite: MATH 500 and MATH 542.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5000,116,511,'Algebraic Geometry',4333,'Properties of affine and projective varieties defined over algebraically closed fields; rational mappings, birational geometry and divisors, especially on curves and surfaces; introduction to the language of schemes; and Riemann-Roch theorem for curves.  Prerequisite: MATH 501.'),
 (5001,116,520,'Differentiable Manifolds',4334,'Definition and properties of differentiable manifolds and maps, introducing vector fields, tangent bundles, differential forms, exterior derivatives, and foliations.  Prerequisite: MATH 423 or MATH 481, or consent of instructor.'),
 (5002,116,521,'Riemannian Geometry',4335,'Local and global properties of Riemannian manifolds.  Prerequisite: MATH 520.'),
 (5003,116,522,'Lie Groups and Lie Algebras I',4336,'A general introduction to Lie groups and algebras and their representation theory.  Theory of finite group representations, Lie groups as matrix groups, and as differentiable manifolds, Lie algebras as tangent spaces and as abstract objects, and their representations.  Examples of the classical groups.   May be repeated up to 8 hours.  Prerequisite: Undergraduate linear algebra, abstract algebra, point set topology, differentiation on manifolds.'),
 (5004,116,523,'Lie Groups and Lie Algebras II',4337,'The classification of simple Lie algebras, classification of highest weight representations, Weyl group actions, Lie group representations, homogeneous spaces, Clifford algebras, Spin groups and further applications.  May be repeated up to 8 hours.  Prerequisite: MATH 522');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5005,116,524,'Linear Analysis on Manifolds',4338,'Study of topological invariants of differentiable and complex manifolds. Prerequisite: MATH 520 and MATH 526, or consent of instructor.'),
 (5006,116,525,'Topology',4339,'Winding numbers, singular and de Rahm homology and cohomology in dimension zero and one, fixed point theorems, Jordan curve theorem, covering spaces, fundamental groups, classification of surfaces, van Kampen Theorem, singular homology, Eilenberg-Steenrod axioms, homology groups of surfaces.  Prerequisite: MATH 417 and MATH 448 or consent of instructor.'),
 (5007,116,526,'Algebraic Topology',4340,'CW-complexes, relative homeomorphism theorem, cellular homology, cohomology, Kunneth theorem, Eilenberg-Zilber theorem, cup products, Poincare duality, examples. Prerequisite: MATH 525, MATH 500; or consent of instructor.  MATH 501 is recommended but not required.'),
 (5008,116,527,'Homotopy Theory',4341,'Homotopy groups, fibrations and cofibrations, Hurewicz theorem, obstruction theory, Whitehead theorem and additional topics perhaps drawn from Postnikov towers, Freudenthal suspension theorem, Blakers-Massey theorem, spectra.  Prerequisite: MATH 526. MATH 501 is recommended but not required.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5009,116,530,'Algebraic Number Theory',4342,'Further development of the theory of fields covering topics from valuation theory, ideal theory, units in algebraic number fields, ramification, function fields, and local class field theory.  Prerequisite: MATH 500 or equivalent.'),
 (5010,116,531,'Analytic Theory of Numbers I',4343,'Problems in number theory treated by methods of analysis; arithmetic functions, Dirichlet series, Riemann zeta function, L-functions, Dirichlet\'s theorem on primes in progressions, the prime number theorem.  Prerequisite: MATH 448 and either MATH 417 or MATH 453.'),
 (5011,116,532,'Analytic Theory of Numbers II',4344,'Development of themes from MATH 531 and further topics chosen from additive number theory, asymptotic properties of multiplicative functions, circle method, diophantine approximation, lattice point problems, metric theory, modular forms, sieve theory. May be repeated.  Prerequisite: MATH 531.'),
 (5012,116,533,'Fiber Spaces and Char Classes',4345,'Continuation of MATH 527. Study of fiber bundles and their associated characteristic classes; applications to geometric problems.  Prerequisite: MATH 527.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5013,116,534,'Geometric Topology',4346,'Topics from geometric topology: complexes, cutting and pasting, and other constructions with applications to areas such as manifolds and knots.  Prerequisite: MATH 525.'),
 (5014,116,535,'General Topology',4347,'Study of topological spaces and maps, including Cartesian products, identifications, connectedness, compactness, uniform spaces, and function spaces.  Prerequisite: Consent of instructor.'),
 (5015,116,540,'Real Analysis I',4348,'Lebesgue measure on the real line; integration and differentiation of real valued functions of a real variable; and additional topics at discretion of instructor. Prerequisite: MATH 447 or equivalent'),
 (5016,116,541,'Real Analysis II',4349,'Abstract measure theory; integration on general measure spaces; and introduction to functional analysis.  Prerequisite: MATH 540.'),
 (5017,116,542,'Complex Variables I',4350,'Topics include the Cauchy theory, harmonic functions, entire and meromorphic functions, and the Riemann mapping theorem.  Prerequisite: MATH 446 and MATH 447, or MATH 448.'),
 (5018,116,543,'Complex Variables II',4351,'Continuation of MATH 542. Topics include subharmonic functions, Nevanlinna theory, analytic continuation and Riemann surfaces, and univalent functions.  Prerequisite: MATH 542.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5019,116,545,'Harmonic Analysis',4352,'Harmonic analysis on the circle, the line, and the integers, i.e., Fourier series and transforms; locally compact Abelian groups; convergence and summability; conjugate functions; Hardy spaces; uniqueness; Tauberian theorems; almost-periodic functions; commutative Banach algebras.  Prerequisite: MATH 448 and MATH 541; knowledge of Banach spaces.'),
 (5020,116,546,'Hilbert Spaces',4353,'Geometrical properties of Hilbert spaces; linear operators; and the spectral theory for self adjoint and related operators.  Prerequisite: MATH 541.'),
 (5021,116,550,'Ordinary Diff Equations',4354,'Existence, uniqueness, and continuation of solutions; topics selected from the following: the theory of linear differential operators, Sturm-Liouville theory, stability theory, and qualitative theory of differential equations.  Prerequisite: MATH 447; a first course in ordinary differential equations.'),
 (5022,116,551,'Dynamical Systems Theory',4355,'Course is an introduction to the study of dynamical systems. Students who intend to do research in nonlinear dynamics are encouraged to take this course. Specific topics will be chosen to illustrate the theory and use of techniques from global analysis and nonlinear dynamics such as (1) discrete dynamical systems, (2) global theory of ordinary differential equations, (3) Hamiltonian systems, (4) KAM theory, (5) bifurcation and stability, (6) Hopf index theory of vector fields, (7) Morse theory of gradient vector fields, (8) Lyapunov theory, (9) infinite dimensional dynamical systems, (10) structural stability.  Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5023,116,553,'Partial Differential Equations',4356,'Basic introduction to the study of partial differential equations; topics include: the Cauchy problem, power-series methods, characteristics, classification, canonical forms, well-posed problems, Riemann\'s method for hyperbolic equations, the Goursat problem, the wave equation, Sturm-Liouville problems and separation of variables, Fourier series, the heat equation, integral transforms, Laplace\'s equation, harmonic functions, potential theory, the Dirichlet and Neumann problems, and Green\'s functions.  Prerequisite: Consent of instructor.'),
 (5024,116,554,'Linear Anal & Part Diff Eq',4357,'Course will provide students with the basic background in linear analysis associated with partial differential equations. The specific topics chosen will be largely up to the instructor, but will cover such areas as linear partial differential operators, distribution theory and test functions, Fourier transforms, Sobolev spaces, pseudodifferential operators, microlocal analysis, and applications of the above topics.  Prerequisite: MATH 447, MATH 489 or consent of instructor.'),
 (5025,116,555,'Nonlinear Anal & Part Diff Eq',4358,'Course will provide students with the basic background in nonlinear analysis associated with partial differential equations. The specific topics chosen will be largely up to the instructor, but will cover such areas as existence and uniqueness techniques, nonexistence and finite time blow-up results, hyperbolic conservation laws, weak solutions, stability theory, nonlinear elliptic theory, regularity theory.  Prerequisite: MATH 554 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5026,116,556,'Methods of Math Physics I',4359,'Course covers several basic mathematical methods of wide use in physics and engineering. Topics will be selected from the following: calculus of variations, Sturm-Liouville theory and eigenvalue problems, Green\'s functions and generalized functions, Hilbert space techniques.  Prerequisite: Advanced Calculus.'),
 (5027,116,557,'Methods of Math Physics II',4360,'Course covers several basic mathematical methods of wide use in physics and engineering. Topics will be selected from the following: integral equations, spectral theory and Hilbert spaces, inverse spectral theory, soliton and waterwave theory, asymptotic methods.  Prerequisite: MATH 556 or consent of instructor.'),
 (5028,116,559,'Asymptotic Methods',4361,'Same as NPRE 559, PHYS 522, and TAM 549. See TAM 549.'),
 (5029,116,561,'Theory of Probability I',4362,'Mathematical foundations of probability and stochastic processes; probability measures, random variables, distribution functions, convergence theory, the Central Limit Theorem, conditional expectation, and martingale theory. Same as STAT 551. Prerequisite: MATH 541 or consent of instructor.'),
 (5030,116,562,'Theory of Probability II',4363,'Continuation of MATH 561 Same as STAT 552. Prerequisite: MATH 561.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5031,116,564,'Applied Stochastic Processes',4364,'Introduction to topics such as spectral analysis, filtering theory, and prediction theory of stationary processes; Markov chains and Markov processes. Same as STAT 555.  Prerequisite: MATH 446 and MATH 447.'),
 (5032,116,567,'Topics in Actuarial Theory I',4365,'Selected topics in advanced actuarial science. May be repeated to a maximum of 16 hours. Prerequisite: Consent of instructor.'),
 (5033,116,568,'Topics in Actuarial Theory II',4366,'Topics in mathematical theory of actuarial science beyond basic life contingencies, such as graduation of mortality tables, survival models, mathematics of demography. See Class Schedule or department office for current topics. A paper will generally be required. May be repeated to a maximum of 16 hours. Prerequisite: STAT 409 or STAT 410 or equivalent; credit or concurrent registration in MATH 471.'),
 (5034,116,570,'Mathematical Logic',4367,'Development of first order predicate logic; completeness theorem; formalized number theory and the Godel incompleteness theorem.  Prerequisite: MATH 417 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5035,116,571,'Model Theory',4368,'Techniques for constructing models, including compactness and Lowenheim-Skolem theorems, unions of elementary chains, and omitting types construction; categorical theories; ultraproducts; saturated models; quantifier elimination; applications to algebraically closed fields, real closed fields, and other fundamental structures of mathematics.  Prerequisite: MATH 570, or consent of instructor.'),
 (5036,116,573,'Recursive Function Theory',4369,'Various characterizations of the class of recursive (i.e., computable) functions; the Church-Turing thesis; unsolvability of the halting problem; the recursion theorem and the enumeration theorem; relative computability, the jump operation, and the arithmetical hierarchy; recursively enumerable sets; degrees of unsolvability; and the priority method.  Prerequisite: MATH 570 or consent of instructor.'),
 (5037,116,574,'Set Theory',4370,'Zermelo-Fraenkel axiomatic set theory; basic concepts in set theory such as ordinal, cardinal, rank, and definition by transfinite recursion; Godel\'s constructible universe; introduction to forcing; Boolean valued universes; large cardinals; consistency and independence of the continuum hypothesis and the axiom of choice.  Prerequisite: MATH 570 or consent of instructor');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5038,116,578,'Computational Complexity',2029,'Same as CS 579, and ECE 579. See CS 579.'),
 (5039,116,579,'Coding Theory',2027,'Same as CS 577, and ECE 556. See ECE 556.'),
 (5040,116,580,'Combinatorial Mathematics',2022,'Fundamental results on core topics of combinatorial mathematics: classical enumeration, basic graph theory, extremal problems on finite sets, probabilistic methods, design theory, discrete optimization. Same as CS 571.  Prerequisite: Consent of instructor.'),
 (5041,116,581,'Extremal Graph Theory',2023,'Extremal problems and parameters for graphs. Distance and connectivity, matching and factors, vertex and edge colorings, perfect and imperfect graphs, intersection classes and intersection parameters, Turan\'s theorem, graph Ramsey theory, graph decomposition and other extremal problems. Same as CS 572.  Prerequisite: MATH 580 or consent of instructor.'),
 (5042,116,582,'Structure of Graphs',4371,'Structure of graphs and properties of special classes of graphs. Degree sequences and reconstruction, structure of k-connected graphs, Hamiltonian cycles and circumference, planar graphs and their properties, graph minors, cycle coverings, matroidal and algebraic aspects of graphs.  Prerequisite: MATH 580 or consent of instructor.'),
 (5043,116,583,'Partial Orders and Comb Optim',4372,'Combinatorial aspects of partially ordered sets and their relation to optimization problems. Structure of posets and lattices, Dilworth\'s theorem and generalizations, linear extensions and sorting, dimension of posets, order ideals, extremal set theory, integer programming and minmax relations, matroids and their applications.  Prerequisite: MATH 580 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5044,116,584,'Methods of Combinatorics',2025,'Combinatorial methods and other mathematical methods for combinatorial problems. Enumeration by bijections and generating functions, probabilistic methods for existence proofs and asymptotic analysis, randomized algorithms, Ramsey\'s theorem and related topics, combinatorial designs and their applications, geometric problems and methods. Same as CS 575.  Prerequisite: MATH 580 or consent of instructor.'),
 (5045,116,587,'Optimization by Vector Methods',2426,'Introduction to normed, Banach, and Hilbert spaces; applications of the projection theorem and the Hahn-Banach Theorem to problems of minimum norm, least squares estimation, mathematical programming, and optimal control; the Kuhn-Tucker Theorem and Pontryagin\'s maximum principle; and introduction to iterative methods. Same as ECE 580.  Prerequisite: MATH 415 or MATH 482, and MATH 447 or consent of instructor.'),
 (5046,116,588,'Optimization in Networks',4373,'Theory and methods for optimization over directed graphs; paths, cuts, flows, and potentials; matchings; PERT and CPM; max flow, min path, out-of-kilter, Hungarian, and other algorithms; nonlinear cost functionals; painting theory; and existence and duality.  Prerequisite: MATH 241 (formerly MATH 243) or MATH 242.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5047,116,589,'Conjugate Duality and Optim',4374,'Convex analysis for constrained extremum problems; convex sets, cones, and functions; separation; Fenchel transform; duality correspondences; differential theory; nonlinear programming; sensitivity; and perturbational duality for primal, dual, and Lagrangian problems.  Prerequisite: MATH 415 and MATH 447, or consent of instructor.'),
 (5048,116,595,'Advanced Topics in Math',4375,'May be repeated in the same or separate semesters.  Prerequisite: Consent of instructor.'),
 (5049,116,597,'Reading Course',4376,'May be repeated in the same or separate terms to a maximum of 8 hours. Prerequisite: Consent of instructor'),
 (5050,116,598,'Literature Seminar in Math',4377,'Seminar on topics of current interest in mathematics. Students present seminars and discussions on various topics. See Class Schedule for current topics. Recommended for all Mathematics students.  Approved for both letter and S/U grading.  Prerequisite: Consent of instructor.'),
 (5051,116,599,'Thesis Research',4378,'May be repeated. Approved for S/U grading only. Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5052,117,500,'Issues in Business',4379,'MBA students are faced with a wide variety of issues in the work place.  This course will introduce and encourage discussions related to careers transitions, leadership, ethics, and uses of technology in the work place.  Guest lecturers and experts in their field will discuss different approaches to these issues and give students the opportunity to discuss strategies and practice skills that will prepare them for the business environment. Approved for S/U grading only. Prerequisite: Co-requisite MBA 501 and MBA 502.'),
 (5053,117,501,'Foundations of Business, I',4380,'This course focuses on understanding the business formation process and how to prepare a business plan. Specific learning objectives include: planning and measurement of firm resources; the economic theory of the firm; decision making under uncertainty; understanding customer choice; financial management; oral presentation; and computer skills. Students who receive credit for MBA 501 may not receive credit for the following courses: ACCY 501, BADM 520, BADM 544, or BADM 572, or ECON 522. Prerequisite: Admission to the Master of  Business Administration program.'),
 (5054,117,502,'Foundations of Business, II',4381,'Helps students understand how to design and manage internal processes to achieve a firm\'s objectives. Specific learning objectives include: how managers internally allocate tasks; authority and resources to achieve a firm\'s objectives; how to design and manage the process of effectively producing products and services; how to measure costs and performance of business processes; how to understand and analyze institutions and programs of modern marketing as well as communicate marketing decisions and how to manage capital resources within a firm. Students will also develop written communication skills. Students who receive credit for MBA 502 may not receive credit for the following courses: ACCY 503, BADM 509, OR BADM 567, or FIN 520.  Prerequisite: MBA 501.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5055,117,503,'Prin & Proc of Management I',4382,'Course presents information and material on how organizations interact and operate in a changing environment. Specific topics include: human resource management, organizational structures, strategic decision-making, management of technology, and technological developments. Students practice business written and oral communications skills. Students learn how to identify and manage key business resources. Specific themes focus on how managers effectively manage the human component in organizations, how to manage technology, and how to think about and respond to strategic issues. Students who receive credit for MBA 503 may not receive credit for BADM 543. Prerequisite: MBA 501 and MBA 502.'),
 (5056,117,504,'Change, Ethics, Strat & Risk',4383,'The three titled sections are specified here: Section 1. LEC, Leadership Ethics and Change: An inquiry into leadership, ethics, and change. It explores the nature of organizational leadership and discusses its relationship to ethics. Section 2. GS, Global Strategy: Teaches the fundamental economic, financial, and political factors affecting the international expansion of the firm. Section 3. DRA, Decision Risk Analysis: Introduces students to the formal analysis of managerial decision making problems that arise under conditions of uncertainty. While the analyses will be, of necessity, highly quantitative, the emphasis will be on the problem definition, identification of the appropriate tools for analysis, and on the interpretations of the solution. Concurrent registration in all three sections is required for a maximum of 6 hours. Prerequisite: MBA 501, MBA 502, and MBA 503.'),
 (5057,117,505,'Topics in Management',4384,'Course presents topics important to the study of business management. Examples of topics include: international business; strategic thinking, incentives, and information; operations management; financial reporting, financial institutions; information systems. May be repeated to a maximum of 8 hours. Students may register in more than one section per term.  Prerequisite: MBA 502.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5058,117,520,'Corporate and Global Strategy',4385,'Focuses on key issues in formulating and implementing corporate strategies with an emphasis on the international operations of firms. Issues are approached from the orientation of the general manager, whose job is to diagnose what is critical in complex business situations and find realistic solutions to strategic and organizational problems. Designed to integrate various functional areas and provide a \"total business\" perspective on issues pertaining to corporate and international strategy. Builds on learning experiences in previous modules, and acts as an integrative capstone module.  Prerequisite: Completion of the first year of the Master of Business Administration Program, including MBA 501, MBA 502, MBA 503, MBA 504, and MBA 505.'),
 (5059,117,530,'Internship',4386,'May not be repeated for credit. Approved for S/U grading only. Prerequisite: Completion of first year of Master of Business Administration program.'),
 (5060,117,531,'Special Projects',4387,'Individual projects selected by the student in consultation with a faculty member and approved by the executive officer of the program. May be repeated in the same or subsequent terms to a maximum of 12 hours. Prerequisite: Completion of first year of Master of Business Administration program.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5061,118,100,'Introductory Microbiology',4388,'Introduction to the principal activities and properties of microorganisms, including bacteria, yeasts, molds, and viruses; consideration of the role of natural processes, such as photosynthesis; and man\'s use and control of microorganisms in the production of antibodies and vaccines in industrial fermentations, in sanitation and public health, and in agriculture. Credit is not given for both MCB 100 and MCB 300. Prerequisite: There are no prerequisites for MCB 100, but some chemistry is recommended.'),
 (5062,118,101,'Intro Microbiology Laboratory',4389,'Laboratory introduction to the techniques employed in the investigation of microbial activities and properties; experiments designed to familiarize the student with the handling, identification, and characterization of microorganisms and their activities, particularly those of interest to man. Credit is not given for both MCB 101 and MCB 301. Prerequisite: Credit or concurrent registration in MCB 100.'),
 (5063,118,103,'Intro to Human Physiology',4390,'Survey of the human body functions and dysfunctions and their underlying molecular, cellular and integrative mechanisms. Offered in two formats: regular (Section B) and self-paced (Section A). In the self-paced format the lecture portion is replaced by intensive readings, quizzes, and exams. The laboratory portion of the course is offered under MCB 104.   Prerequisite: High School chemistry and/or basic biology strongly recommended.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5064,118,104,'Intro to Human Physiology Lab',4391,'Laboratory course to accompany MCB 103. Includes weekly 3-hour human and general physiology laboratory exercises. Enrollment restricted to students in majors REQUIRING a lab course in human physiology. Prerequisite: Concurrent enrollment in MCB 103.'),
 (5065,118,150,'Molec & Cellular Basis of Life',4392,'Introductory course focusing on the basic structure, metabolic, and molecular processes (including membranes, energy metabolism, genes) common to all cells. Emphasis on unique properties that differentiate the major sub-groups of organisms (Archaea, Bacteria, plants, and animals), and will discuss how cells are integrated into tissues and organs in multicellular organisms.'),
 (5066,118,151,'Molec & Cellular Laboratory',4393,'Introductory laboratory course focusing on basic techniques in molecular and cellular biology. Students majoring in Molecular and Cellular Biology, or Integrative Biology may not receive credit for MCB 151. Prerequisite: Concurrent enrollment in MCB 150.'),
 (5067,118,199,'Undergraduate Open Seminar',4394,'May be repeated to a maximum of 10 hours.  Course Information: Approved for both letter and S/U grading.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5068,118,240,'Physiology',4395,'Study of physiological systems in humans and other animals emphasizing cellular and molecular aspects of function in differentiated cells and tissues (e.g. nerve and muscle).  Systems include cardiovascular, respiratory, digestive, renal, blood, immune, reproductive, nervous and endocrine.  Quantitative approaches will be stressed including those used in metabolic physiology and bioenergetics.  Functional adaptations in specific animal species will be studied as well as abnormal function in the disease state when relevant to understanding of normal function.   Prerequisite: MCB 150, or consent of instructor.'),
 (5069,118,250,'Molecular Genetics',4396,'Genetic variation, gene organization, gene expression, and gene regulation in Archaea, Bacteria, and Eukarya.  Prerequisite: MCB 150 or equivalent, or consent of instructor.'),
 (5070,118,251,'Exp Techniqs in Molecular Biol',4397,'Laboratory course emphasizing a range of molecular biology questions, and the experimental approaches and methodologies needed to answer these questions. Lectures will accompany labs to explain theoretical background and experimental rationale.  Students may not receive credit for both MCB 251 and MCB 151. Prerequisite: Concurrent or prior enrollment in MCB 250 or MCB 354 or consent of instructor.'),
 (5071,118,252,'Cells, Tissues & Development',4398,'Functional organization and physiology of cells and tissues, including cellular signaling, cellular interactions, and developmental processes.  Prerequisite: MCB 250 or equivalent with consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5072,118,253,'Exp Techniqs in Cellular Biol',4399,'Laboratory course emphasizing experimental techniques in cellular biology, cellular physiology, and developmental biology. Students may not receive credit for both MCB 253 and MCB 151. Prerequisite: Concurrent or prior enrollment in MCB 252 or consent of instructor.'),
 (5073,118,290,'Individual Topics',4400,'Laboratory work and/or reading in fields selected in consultation with an appropriate faculty member.  Prerequisite: Consent of instructor. May be repeated to a maximum of 10 hours.'),
 (5074,118,299,'MCB Merit Program Discussion',4401,'Provides the extra earned credit hours for students enrolled in the Merit Program in MCB 250, MCB 252, or MCB 354.  Approved for both letter and S/U grading.  May be repeated up to 6 hours in a semester, to a maximum of 10 total hours.  Prerequisite: Consent of instructor.'),
 (5075,118,300,'Microbiology',4402,'Emphasizes fundamental concepts of microbiology, including nutrition, ecology, physiology, genetics and molecular biology of microorganisms, and their role in nature and in infection and immunity. Credit is not given for both MCB 300 and MCB 100.  Prerequisite: MCB 250 and credit or concurrent registration in MCB 252 or consent of instructor.'),
 (5076,118,301,'Experimental Microbiology',4403,'Laboratory emphasizing the fundamentals of microbiology. Topics include growth, isolation, and identification of bacteria; restriction endonuclease analysis of DNA, genetic cloning, and gene transfer. Computer methods are used for the identification of microorganisms and for the analysis of recombinant DNA molecules. Prerequisite: Credit or concurrent registration in MCB 300, and MCB 250 and MCB 251, and credit or concurrent registration in MCB 252 and MCB 253, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5077,118,312,'Applied Microbiology Methods',4404,'Consideration, through experimentation, of properties of bacteria, yeasts, molds, and actinomycetes important to industrial processes; exploration of methods of control of microbial processes in industry and sanitation.  Prerequisite: Credit or concurrent registration in MCB 434, and consent of instructor.'),
 (5078,118,315,'Cells and Tissues Laboratory',4405,'Laboratory course in the functional organization and structure of cells and tissues. Prerequisite: MCB 250, MCB 251, MCB 252, and MCB 253, or consent of instructor.'),
 (5079,118,316,'Intro Eukaryotic Molec Genet',4406,'Introduction of the structure, expression, and regulation of genes of higher eukaryotes with an emphasis upon animal cells. Specific topics will include chromatin structure and its relation to gene expression, regulation of gene expression during development, recombination, molecular genetic technologies, gene replacement therapy, and the molecular genetics of cancers.  Prerequisite: MCB 150 and credit or concurrent registration in MCB 250 or consent of instructor.'),
 (5080,118,334,'Functional Human Anatomy',4407,'Studies the essentials of functional human anatomy with special reference to skeletal, muscular, splanchnic, circulatory, and nervous systems. Lecture and laboratory.  Prerequisite:MCB 103 and MCB 104, or credit or concurrent registration in MCB 252 and MCB 253, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5081,118,354,'Biochem & Phys Basis of Life',4408,'Introduction to biochemistry and structural biology emphasizing the physical and chemical properties of macromolecules. Students may not receive credit for both MCB 354 and MCB 450. Prerequisite: CHEM 232 or CHEM 236, or MCB 250 and MCB 252 or equivalent with consent of instructor.'),
 (5082,118,395,'Special Topics Human Physiol',4409,'Selected topics in general physiology.  Prerequisite: Credit or concurrent registration in MCB 401; consent of instructor.'),
 (5083,118,396,'Special Topics Brain Physiol',4410,'Selected topics in animal physiology.  Prerequisite: Credit or concurrent registration in MCB 402; consent of instructor.'),
 (5084,118,400,'Cell Structure and Function',4411,'Principles of eukaryotic cell biology; consideration of molecular and fine structural components of the cell with an emphasis on experimental analysis of the relationship of structure to function of gene, membrane, cytoskeleton, and extracellular matrix. Prerequisite: MCB 250, MCB 251, MCB 252, MCB 253, and credit or concurrent registration in MCB 254 or MCB 450 or consent of instructor.'),
 (5085,118,401,'Cell & Membrane Physiology',4412,'Cellular and molecular basis of physiological process with an emphasis on phenomena taking place at the membrane of cells and organelles (e.g., signal transduction, ion transport, synaptic transmission, nerve conduction, bioelectricity, synaptic plasticity.) Structure and function of biological membranes through a quantitative lens.   Prerequisite: MCB 252 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5086,118,402,'Sys & Integrative Physiology',4413,'Examines organ physiology of animals; primary emphasis is on the control systems underlying regulation of homeostasis in mammals, including human beings.  Prerequisite: MCB 252 or consent of instructor.'),
 (5087,118,403,'Cell & Membrane Physiology Lab',4414,'Experimental investigation of cellular functions common to most eukaryotic cells; emphasis on biochemical, electrical, and mechanical recording techniques. 2 undergraduate hours, or 1 graduate hour. Prerequisite: Credit or concurrent registration in MCB 401 and previous biology laboratory experience.'),
 (5088,118,404,'Sys & Integrative Physiol Lab',4415,'Introduction to problems and techniques for studying the physiology of organ systems. 2 undergraduate hours, or 1 graduate hour. Prerequisite: Credit or concurrent registration in MCB 402.'),
 (5089,118,405,'Genetics and Genomics',4416,'Study of genetics as a discipline, genetic analysis as a tool to understand biology and the role of genome sciences in biology.   Prerequisite: MCB 250, MCB 251, MCB 252, and MCB 253; or consent of instructor.'),
 (5090,118,406,'Gene Expression',1286,'Introduction to gene expression and how different segments of gene expression pathways including gene transcription, RNA processing, protein translation, targeting, activity and turnover are modulated to maintain cellular homeostasis. The technologies (both general and specialized) used currently to analyze gene expression and the regulation of protein function are also discussed.  Same as BIOC 406. Prerequisite: MCB 354 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5091,118,408,'Immunology',4417,'Introduction to fundamentals of immunology with emphasis on biological application; basic background for understanding immunological responses and techniques applicable to biological research.  Prerequisite: MCB 250, MCB 251, MCB 252, MCB 253, and MCB 354; or consent of instructor.'),
 (5092,118,409,'Bioinformatics & Func Genomics',4418,'Introduction to theoretical and applied aspects of modern systems biology, bioinformatics, and functional genomics.  Topics include public-domain biological databases, sequence alignment and analysis tools, phylogenomics, collection and analysis of genomic and proteomic data, data mining, structural genomics and fundamentals of molecular modeling. Concepts are complemented with hands-on experience with computational biology databases and bioinformatic tools. 3 undergraduate hours. Prerequisite: MCB 354 or consent of instructor.'),
 (5093,118,410,'Developmental Biology',4419,'Survey of molecular and cellular mechanisms involved in development and growth of animals. Topics to be covered include fertilization and early cell lineage, body axis formation, gastrulation, neural induction and patterning, segmentation, and other aspects of pattern formation including organogenesis of branching organs, limb development and regeneration.  Prerequisite: MCB 252 and credit or concurrent registration in MCB 254, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5094,118,412,'Cellular Molec Neurobiology',4420,'Cellular and molecular basis of form and function of the nervous system with emphasis on chemical signaling. Topics will include: combinatorial regulation of neurotransmission, nerve terminal, molecular organization of postsynaptic sites, retrograde signals, neurotrophins, cytoskeleton, growth cone motility, gene regulation, axon target selection, chemoaffinity hypothesis, calcium signaling in plasticity, and neurological disorders and therapies. Same as NEUR 422.  Prerequisite: MCB 252 and credit or concurrent registration in MCB 354 or consent of instructor.'),
 (5095,118,413,'Endocrinology',4421,'Physiology and biochemistry of the endocrine system and its hormones with special reference to vertebrates and to human endocrine disorders.  Prerequisite: MCB 252 or consent of instructor.  One semester of biochemistry is recommended.'),
 (5096,118,414,'Introduction to Neurobiology',4422,'Introduction to the physiology of nerve cells, mechanisms of neural integration, and the organization of sensory and motor systems; also introduces neurochemistry, neuroendocrinology, neural development, neural plasticity, and the physiological basis of behavior. Same as NEUR 404.  Prerequisite: MCB 252 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5097,118,415,'Struct Funct of Nervous System',4423,'Examines the structural organization and function of the major systems of the nervous system. Lecture and laboratory. Same as NEUR 425.  Prerequisite: MCB 401 or MCB 412, or MCB 414, or consent of instructor.'),
 (5098,118,416,'Integrative Neurophysiology',4424,'Advanced studies of single-neuron function and systems neurophysiology. Topics include: ion channels, ionic basis of neural potentials, small neural networks, central pattern generators, neural coding, visual system, auditory system, motor system, sensorimotor integration, learning, and emotion. Same as NEUR 426.  Prerequisite: MCB 401or MCB 412, or MCB 414, or consent of instructor.'),
 (5099,118,417,'Modeling Neural Systems',1328,'This combined lecture/lab course will introduce students to the major paradigms in neural network modeling, and explore their relevance for understanding real neural systems.  In the computer lab, students will learn how to program and use neural networks.  In lecture, students will be given the technical knowledge needed to accomplish the lab, and the neurophysiological knowledge needed to evaluate neural networks as models of real neural systems.  This highly interactive course is open to students from various backgrounds.  Same as BIOP 417 and NEUR 427.  Prerequisite: MCB 414; and MATH 124; and CS 110; or equivalent or consent of instructor.'),
 (5100,118,418,'Human Genetics',4425,'Studies the techniques employed for genetic analysis of human traits; discussion of genetic mechanisms operative in human development, metabolism, and behavior; and genetics and human disease.  Prerequisite: MCB 250, MCB 251, MCB 252, MCB 253, and credit or concurrent registration in MCB 354, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5101,118,419,'Brain, Behavior & Info Process',1309,'Exploration of the neural basis of animal behavior.  Emphasis on the information processing problems that animals face in complex natural environments and how nervous systems have evolved to solve these problems.  Introduction to the use of computer modeling and simulation techniques for exploring principles of nervous system design and function.  Current literature in computational neurobiology and neuroethology will be incorporated in readings and class discussion.  Same as BIOE 419, BIOP 419 and NEUR 419.  Prerequisite:  CS 101; and PHYS 102 or PHYS 212; and MCB 252; or equivalent or consent of instructor.'),
 (5102,118,420,'Molecular Immunology',4426,'Molecular basis of normal immune responses to infectious agents and cancer. Structural and biochemical understanding of key immune molecules. In addition to basic immune system components (such as B and T cells), topics will include autoimmune diseases, allergic responses, and immunotherapeutics.  Prerequisite:  MCB 252 and MCB 354, or consent of instructor.'),
 (5103,118,421,'Microbial Genetics',4427,'Prokaryotic and eukaryotic microbial genetic systems; emphasis on typical data analyses, together with the basic classes of genetic phenomena.  Prerequisite: MCB 300 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5104,118,422,'Microbial Genetics Techniques',4428,'Laboratory emphasizing current molecular genetics techniques in bacteria. Topics include genetic techniques, use of transposons, genetic regulation, nucleic acid hybridization, restriction endonuclease mapping, cloning, and DNA sequencing. 5 undergraduate hours. 4 graduate hours.  Prerequisite: MCB 301, and credit or concurrent registration in MCB 421; or consent of instructor.'),
 (5105,118,423,'Evolution in a Microbial World',4429,'Structure, synthesis, and function of molecules and organelles concerned with the intracellular transmission of genetic information (including gene regulation, transcription, and translation).  Prerequisite: MCB 421 or MCB 340, and MCB 354, or consent of instructor.'),
 (5106,118,424,'Microbial Biochemistry',4430,'Examines the biochemical ecology of diverse microbial groups with emphasis on anaerobic systems. 3 undergraduate hours. 3 graduate hours.  Prerequisite: MCB 350 and MCB 354, or consent of instructor.'),
 (5107,118,425,'Molecular Biophysics',1330,'Same as BIOP 420. See BIOP 420.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5108,118,426,'Bacterial Pathogenesis',4431,'Emphasizes prokaryotes that cause important diseases in humans and other animals; host-parasite bacteriology; and chemistry and genetics of mechanisms of pathogenesis.  Prerequisite: MCB 300 and MCB 354, or consent of instructor.'),
 (5109,118,427,'Infection and Immunity',4432,'Study of the field of immunology with emphasis on the host\'s interaction with microbes.  Topics will include self versus non-self recognition in the immune system, effector mechanisms of immunity to infection, inherited and acquired immunodeficiencies, and vaccine designs.   Prerequisite: MCB 300, MCB 354, and MCB 408; or consent of instructor.'),
 (5110,118,428,'Bacterial Pathogens Laboratory',4433,'Laboratory study of methods of recognition and differentiation, diagnostic tests, and mechanisms of pathogenesis; students are voluntary donors of microorganisms used in experiments.  Prerequisite: MCB 300 and MCB 301; credit or concurrent registration in MCB 354 or consent of instructor.'),
 (5111,118,430,'Molecular Microbiology',4434,'Modern contributions to the science of microbiology; emphasizes the structure, function, and synthesis of informational macromolecules and on the role microorganisms have played in molecular biology.  Prerequisite: MCB 300 and credit or concurrent registration in MCB 354, or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5112,118,431,'Microbial Physiology',4435,'Examines bacterial physiology, including discussions of energetics, regulation of metabolism, and cell structure.  Prerequisite: MCB 300 or equivalent; credit or concurrent registration in a biochemistry course.'),
 (5113,118,432,'Computing in Molecular Biology',4436,'Examination of computational aspects of biology with an emphasis on the relationships between biological questions and their recastings as mathematical or logical problems. Topics are drawn from biochemistry, genetics, molecular sequence analysis, and molecular structure.  Prerequisite: MCB 250, MCB 252, MCB 354, and calculus (MATH 220, MATH 230, or MATH 234); or consent of instructor.'),
 (5114,118,433,'Viral Pathogenesis',4437,'Emphasizes molecular aspects of virology, drawing heavily on animal virus models and focusing on fundamental principles of virus structure, replication, genetics and virus-host interactions that lead to disease development. Stresses both common and unique aspects of strategies employed by the different major families of eukaryotic RNA and DNA viruses and how these lead to acute, persistent or chronic infections, or the development of cancer.  Prerequisite: MCB 300 and credit or concurrent registration in MCB 354; or consent of instructor.'),
 (5115,118,434,'Food & Industrial Microbiology',3021,'Same as FSHN 471. See FSHN 471.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5116,118,438,'Plant Molecular Biology',3641,'Same as IB 407. See IB 407.'),
 (5117,118,441,'Comparative Animal Physiology',4438,'Physiological and biochemical adaptations to environmental challenges in both invertebrates and vertebrates; emphasis on comparative aspects of ionic and osmotic regulation, gas exchange and acid-base balance, circulation, muscles and movement, bioenergetics, neurosensory physiology, and endocrinology. Prerequisite: MCB 252 and credit or concurrent enrollment in MCB 354.'),
 (5118,118,442,'Comparative Immunobiology',513,'Same as ANSC 450, and PATH 510. See ANSC 450.'),
 (5119,118,446,'Physical Biochemistry',1289,'Same as CHEM 472 and BIOC 446. See BIOC 446.'),
 (5120,118,450,'Introductory Biochemistry',4439,'Chemistry and metabolism of carbohydrates, lipids, proteins, nucleic acids, vitamins, and coenzymes and their relation to the regulation and processes of organisms, cells, and subcellular components.   Not intended for students in the MCB or biochemistry curricula.  Students may not receive credit for both MCB 450 and MCB 354.  Prerequisite: CHEM 232 or CHEM 236, or equivalent, or consent of instructor.'),
 (5121,118,452,'Biochemistry I',4440,'Principles, chemistry, and methods of analysis of the composition and processes of living systems. Prerequisite: CHEM 205 or CHEM 222, and CHEM 332 or CHEM 436; or MCB 354; or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5122,118,460,'Regeneration and Medicine',4441,'A survey of regeneration biology and medicine at the organ, tissues, and cellular/genetic/molecular/levels.  Basic concepts are presented with a focus on contemporary methods and seminal experiments.  Students will learn to think critically and creatively about experimentation and analyses of three regenerative medicine strategies: stem cell transplantation, bioartificial tissues, and chemical induction of regeneration in vivo.  Prerequisite: MCB 410 or consent of instructor.  Recommended: knowledge of vertebrate histology and anatomy.'),
 (5123,118,480,'Eukaryotic Cell Signaling',4442,'General principles of molecular signaling regulating membrane, cytoplasmic, and nuclear events in eukaryotic cells with emphasis on mammalian systems. Contemporary methods of investigation and the principles of identifying and solving problems related to signal transduction will be emphasized.  Prerequisite: MCB 400 or consent of instructor.'),
 (5124,118,481,'Developmental Neurobiology',4443,'Principles of vertebrate and invertebrate developmental neurobiology with emphasis on the molecular and cellular mechanisms controlling neuronal determination, axon pathfinding, synapse formation, and plasticity.  Prerequisite: MCB 400 or MCB 412 or consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5125,118,482,'Biological Clocks',4444,'Study of the nature, mechanisms, functions, development, and evolution of the biological rhythms associated with geophysical cycles; emphasizes circadian rhythms and their role as biological clocks for the timing of photoperiodism, celestial orientation, human physiology and behavior.  Prerequisite: MCB 353 or consent of instructor.'),
 (5126,118,484,'Model Organisms & Epigenetics',4445,'Explores the main eukaryotic model genetic organisms/systems used in molecular and biomedical research including plants, animals, insects, fungi, and cell culture.  The framework for lectures and discussions focuses on epigenetic mechanisms of gene regulation and genome organization.  Includes reading, discussion, the presentation of the primary research literature with emphasis on experimental methods and design.   This course may not be repeated for credit.  Prerequisite:  MCB 250-MCB 253 and MCB 354, or graduate standing, or consent of instructor.'),
 (5127,118,492,'Senior Thesis',4446,'Research conducted under the direction of a faculty member in the School of Molecular and Cellular Biology.  Normally, the student enrolls in MCB 492 during the last semester on campus prior to graduation. In the semester preceding enrollment, interested students should consult with their faculty advisors concerning enrollment procedures.  A minimum of 3 credit hours is required, and a thesis must be presented for credit to be received.  Successful completion of MCB 492 is required in order to be eligible for graduation with distinction in MCB.  Prerequisite: Two consecutive semesters of at least 2 credit hours of MCB 290 under the guidance of the same faculty member, or consent of instructor.  No Graduate Credit.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5128,118,493,'Special Topics Mol Cell Biol',4447,'Discussion of current topics of interest within the broad domain of molecular and cellular biology; seminar or lecture format. Topics vary. May be repeated to a maximum of 12 hours. Prerequisite: Junior standing and consent of instructor.'),
 (5129,118,501,'Advanced Biochemistry',4448,'Focuses upon structure-function analyses of biomolecules and the chemical and evolutionary foundations of metabolic networks. Emphasis is on research methodology and current problems.'),
 (5130,118,502,'Advanced Molecular Genetics',4449,'An advanced course in molecular genetics. Emphasis is on research methodology and current problems.'),
 (5131,118,505,'Neurochemistry',4450,'Same as NEUR 505, and PSYC 505. See PSYC 505.'),
 (5132,118,509,'Curr Topics Mol & Int Physiol',4451,'Advanced seminars in current physiological research.  May not be repeated for credit.  Prerequisite: Consent of instructor.'),
 (5133,118,511,'Mol Bio of Microbe-Plant Inter',4452,'Same as PLPA 509. See PLPA 509.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5134,118,512,'Advanced Endocrinology',529,'Seminars, lectures, student reports, and discussions of recent advances in endocrinology Same as ANSC 530, and VB 512. May be repeated to a maximum of 8 hours.  Prerequisite: Consent of instructor.'),
 (5135,118,514,'Physiological Measurements',4453,'Laboratories concerned with introducing at a graduate level current research techniques in the physiological and biophysical sciences; problem-oriented laboratories; students select up to four special topics representing different areas of physiology and biophysics, such as mammalian and human, molecular, cellular and radiation biology, comparative physiology, and biophysical measurements. Emphasis placed on ability to work independently, and students give written reports of their experiments. May be repeated to a maximum of 6 hours.  Prerequisite: Consent of instructor.'),
 (5136,118,516,'Neurophysiology Laboratory',4454,'Neurophysiological techniques and experiments illustrating nerve membrane properties, synaptic action and plasticity, organization and pattern generation in motor systems, and sensory coding in visual and acoustic systems. Same as NEUR 516.  Prerequisite: Credit or concurrent registration in MCB 416 or consent of instructor.'),
 (5137,118,518,'Neuroendocrinology',4455,'Advanced studies on central nervous system/hormone interaction in vertebrates.  Neuroanatomy and maturation of neuroendocrine control systems; production, biochemistry, and physiological effects of neurohormones; and neuroendocrine techniques.  Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5138,118,519,'Computational Brain Theory',4456,'Same as NEUR 591. See NEUR 591.'),
 (5139,118,522,'Human Extremities',4457,'Comprehensive study of the human extremities with emphasis on the principles of systematic anatomy, relations between form and function, and regional dissection. Lecture and laboratory.  Prerequisite: Consent of instructor.'),
 (5140,118,523,'Human Thorax',4458,'Comprehensive study of the human thorax and back with emphasis on the principles of systematic anatomy, relations between form and function, and regional dissection. Lecture and laboratory.  Prerequisite: Consent of instructor.'),
 (5141,118,524,'Human Abdomen and Pelvis',4459,'Comprehensive study of the human abdomen and pelvis with emphasis on the principles of systematic anatomy, relations between form and function, and regional dissection. Lecture and laboratory.  Prerequisite: Consent of instructor.'),
 (5142,118,525,'Human Neck and Head',4460,'Comprehensive study of the human neck and head with emphasis on the principles of systematic anatomy, relations between form and function, and regional dissection. Lecture and laboratory.  Prerequisite: Consent of instructor.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5143,118,527,'Human Neuroscience',4461,'Principles of human neuroscience and mechanisms of neural pathophysiology. Same as NEUR 527.  Prerequisite: Consent of instructor.'),
 (5144,118,528,'Evolution in a Microbial World',4462,'Structure, synthesis, and function of molecules and organelles concerned wtih the intracellular transmission of genetic information (including gene regulation, transcription, and translation). Prerequisite: MCB 421 or MCB 340, and MCB 354, or consent of instructor.'),
 (5145,118,529,'Special Topics Cell Devel Biol',4463,'Discussion of current topics of interest in higher eukaryotic cellular and molecular biology, development, neurobiology; seminar or lecture format. Topics vary. May be repeated to a maximum of 8 hours.  Prerequisite: Consent of instructor.'),
 (5146,118,530,'Reproductive Physiol Seminar',4464,'Presentation and discussion of current literature as well as graduate student and staff research proposals and findings in reproductive physiology. May be repeated to a maximum of 4 hours.  Prerequisite: Consent of instructor.'),
 (5147,118,531,'Adv Reproductive Endocrinology',530,'Same as ANSC 531, and VB 531. See ANSC 531.');
INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5148,118,533,'Repro Physiology Lab Methods',532,'Same as ANSC 533, and VB 533. See ANSC 533.'),
 (5149,118,546,'Bioenergetics',1337,'Same as BIOP 546. See BIOP 546.'),
 (5150,118,550,'Biomolecular Physics',1338,'Same as BIOP 550, and PHYS 550. See PHYS 550.'),
 (5151,118,551,'Adv Top Biochemistry',4465,'Series of quarter or half-term intensive courses on the recent research findings in important areas of biochemistry and molecular biology. Covers such areas as: biophysical methods; enzyme mechanisms; membrane biochemistry; regulation of gene expression; nucleic acid biochemistry; metabolic regulation; cellular communication; and medical biochemistry. Lectures, discussions, student papers, and presentations. May be repeated. Students may register in more than one section per term to a maximum of 4 hours.  Prerequisite: MCB 354 or equivalent or consent of instructor.'),
 (5152,118,552,'Lab Techniques in Biochemistry',4466,'Experiments concerning the detection, isolation, and characterization of macromolecules, including enzymes, antibodies, and nucleic acids; methods of studying the size, shape, and hydrodynamic properties of macromolecules and other compounds. May be repeated to a maximum of 6 hours.  Prerequisite: BIOC 455 or consent of instructor.'),
 (5153,118,553,'Enzyme Reaction Mechanisms',1586,'Same as CHEM 572. See CHEM 572.');
