INSERT INTO `cis_courses` (`id`,`cis_subject_id`,`number`,`title`,`course_dependency_id`,`description`) VALUES 
 (5706,127,444,'Hormones and Behavior',3647,'Course Inforamtion:  Same as IB 430, and PSYC 444.  See PSYC 444.'),
 (5707,127,451,'Neurobio of Aging',4930,'Same as PSYC 451 and NEUR 451. See PSYC 451.'),
 (5708,127,500,'Topics in Neuroscience',4931,'Critical reading and discussion of current papers from the neuroscience literature, and discussion of other relevant topics such as ethics and career and professional skills development.  Grading based on attendance and participation.   Approved for both letter and S/U grading.  This course may be repeated up to 2 hours.  Prerequisite:  Enrollment in Neuroscience Ph.D. program or consent of instructor.'),
